    public class GrimoraMod
    {
        public static Dictionary<string, string> Cards = new();

        public static void RegisterCard(string en, string zh)
        {
            Cards.Add(en, zh);
        }

        public static void RegisterTranslations()
        {
            Debug.Log("Registering Translations for Grimora Mod");
            // SetText
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Start Grimora Mod", "启动格里魔拉模组", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "RESET RUN", "重置冒险", Language.ChineseSimplified);
            //SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "NEW LESHY RUN", "新的莱西冒险", Language.ChineseSimplified);
            //SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "CONTINUE LESHY RUN", "继续莱西冒险", Language.ChineseSimplified);
            //SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "NEW GRIMORA RUN", "新的格里魔拉冒险", Language.ChineseSimplified);
            //SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "CONTINUE GRIMORA RUN", "继续格里魔拉冒险", Language.ChineseSimplified);

            // Config
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This option will allow you to see what cards are left in your deck.", "启用此选项可查看牌组剩余卡牌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This option will disable all effects that might cause motion sickness and other visually jarring effects.", "此选项将禁用所有可能导致晕动症或视觉不适的特效", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Does not generate blocker pieces. Chests fill first row, enemy pieces fill first column.", "开发者模式：不生成阻挡物。宝箱填充首行，敌人棋子填充首列", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Makes it so Cards that cost energy also take max energy.", "能量模式：消耗能量的卡牌将同时消耗最大能量值", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If the dll is placed in BepInEx/scripts, this will allow running certain commands that should only ever be ran to re-add abilities/cards back in the game correctly.", "热重载：将dll置于BepInEx/scripts目录后，可运行特定命令以正确重载技能/卡牌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "For players who want to continue playing with their deck after defeating Grimora.", "无尽模式：击败格里魔拉后仍可继续使用当前牌组进行游戏", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "0 = Default. Use the mod's internal blueprint system.\n1 = Randomized. Encounters are completely randomized using the mod's internal card pool.\n2 = Custom. Encounters are from made and used from the JSON files.\n3 = Mixed. Encounters are from both default list and custom list.", "遭遇战配置：\n0=默认 使用模组内置方案\n1=随机 根据模组卡池完全随机生成\n2=自定义 从JSON文件读取配置\n3=混合 同时采用默认与自定义方案", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "0 = W for viewing deck, S for getting up from the table.\n1 = Up arrow for viewing deck, down arrow for getting up from the table.", "按键配置：\n0=W键查看牌组 S键离开牌桌\n1=上箭头查看道具牌组 下箭头离开牌桌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "0 = Default. Flat 50% burn rate for each lever option on second shock.\n1 = Base 30% chance to burn plus 0%, 10%, or 20% for low, medium, high, respectively. Meaning, if the first shock is high then the second is high, the chance for the card to be destroyed is 70%.\n2 = Low: 12.5%, Medium: 17.5%, High 30%. Meaning, if the first shock is high, then the second one is also high, the chance for the card to be destroyed is 60%.\n3 = Low: 12.5%, Medium: 20%, High 27.5%. Meaning, if the first shock is high, then the second one is also high, the chance for the card to be destroyed is 55%.", "电椅销毁概率：\n0=默认 第二次电击固定50%销毁率\n1=基础30%+低/中/高分别0%/10%/20% 若两次均为高强度则总概率70%\n2=低12.5% 中17.5% 高30% 两次高强度总概率60%\n3=低12.5% 中20% 高27.5% 两次高强度总概率55%", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dont change this value, it is used by the game to store certain events.", "请勿修改 该数值用于存储特定事件进度", Language.ChineseSimplified);

            // Ability
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 3 Souls to select any card on the board, [creature] will deal 1 damage to the selected card if it is an opponent card, if it isn't, [creature] will heal it for 1 health.", "消耗3个灵魂选择场上任意卡牌，若为敌方卡牌则[creature]对其造成1点伤害，否则[creature]为其恢复1点生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Comin' right up!", "马上就来！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Soul Shot", "灵魂冲击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 1 Soul to deal 1 damage to the creature across from [creature].", "消耗1个灵魂，可对[creature]对面的造物造成1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Disinter", "开坟掘墓", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 1 Bone to create a Skeleton in your hand.", "消耗1根骨头来在你的手牌中创造一个骷髅。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Materialize", "魂影具象", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 2 Souls for [creature] to summon a copy in your hand.", "消耗2个灵魂使[creature]在手牌中召唤一个复制体。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 2 Souls, [creature] will trap a random opponent card in a coffin, which will release the original card again upon perishing.", "消耗2个灵魂，[creature]会将一张随机敌方卡牌封入棺木，该棺木被摧毁时将释放原卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Funeral Rites", "殡仪之棺", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Soul Sucker", "噬魂者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When an opponent creature perishes, [creature] will store a charge of soul energy, up to 4. Activating this sigil will add 3 soul energy to your current energy counter, and one max energy.", "当敌方造物阵亡时，[creature]会储存1个灵魂能量（上限4个）。激活该印记可为当前能量计数器增加3点能量，并提升1点能量上限。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Weighted Die", "加权骰子", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 2 Bones for the power of [creature] to be randomized between 0 and 5.", "消耗2根骨头，使[creature]的力量随机变为0至5之间的数值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] alternates between striking the opposing space to the left and right from it.", "[creature]会交替攻击其左右两侧的对面位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] is unaffected by the motion of the ship.", "[creature]不受船只移动的影响。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will strike its adjacent slots, and each opposing space to the left, right, and center of it.", "[creature]会攻击相邻位置，以及正对面的左右两侧和中间位置各一次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] yields no bones upon death.", "[creature]死亡时不会产生骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] kills another creature, gain 2 bones.", "当[creature]击杀其他造物时，获得2根骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] takes 1 Soul from you at the end of every turn and stores it as 2 Bones, up to a maximum of 8. When [creature] dies, you gain all bones contained in it.", "[creature]在每回合结束时从你这里获取1个灵魂，并将其储存为2根骨头，最多可储存8根。当[creature]死亡时，你将获得其中储存的所有骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] empowers each Skeleton on the owner's side of the board, providing a +1 buff to their power.", "[creature]会强化持牌人侧牌桌上的所有骷髅牌，使其力量增加1点。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sea Shanty", "海洋船歌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will strike the opposing slots to the left, right, and center of it randomly, up to 3 times.", "[creature]会随机攻击正对面的左右两侧和中间位置，最多攻击3次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, the card opposing it is Frozen Away if not already frozen.", "当[creature]阵亡时，若对面的卡牌未被冻结，则将其冰封禁锢。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies, gain a consumable item, if you have a free slot.", "当[creature]死亡时，若道具栏有空位，则获得一个消耗品道具。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, a Skeleton is created in each empty space on the owner's side. [define:Skeleton]", "使用[creature]时，己方所有空位均会出现骷髅牌。[define:Skeleton]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Skeleton Horde", "骷髅军团", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Shipwreck Finder", "寻船者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, a Shipwreck is created on each empty adjacent space. [define:arackulele.inscryption.grimoramod_Shipwreck_Dams]", "使用[creature]时，相邻空位均会出现沉船的浮木残骸。[define:arackulele.inscryption.grimoramod_Shipwreck_Dams]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Skeletons Within", "骸骨之躯", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Once [creature] is struck, draw a card from your Skeleton pile.", "[creature]受到攻击时，可从骷髅堆中抽一张牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Full o' candy", "糖果满溢", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will yield candy upon death, maybe this will be useful for later.", "[creature]死亡时将掉落糖果，或许之后能派上用场。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move in a random direction.", "持牌人回合结束时，[creature]将随机选择一个方向移动。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Whenever [creature] moves, it leaves a trail of Embers. [define:arackulele.inscryption.grimoramod_Flames]", "[creature]移动时会在路径上留下余烬轨迹。[define:arackulele.inscryption.grimoramod_Flames]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Guarding Presence", "守护者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When an opposing creature is placed opposite to an empty space, [creature] will move to that empty space.", "如对手的造物对面位置是空的，则[creature]会进入那个位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bone Starved", "骨饥者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you have no Bones, at the start of your turn [creature] deals 2 more damage.", "若你没有骨头，在你的回合开始时[creature]会造成额外2点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will strike each opposing space. If only one creature is in the opposing spaces, this card will strike that creature twice. ", "[creature]会攻击对面每个空间。若对面仅有一个造物，则该卡牌会攻击该造物两次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Enraged Giant", "暴怒巨人", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will strike each opposing space.", "[creature]会攻击对面所有位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fledgling", "幼雏", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "After one turn on the board, [creature] will evolve into a greater form.", "[creature]在牌桌上1个回合后将进化为更强大的形态。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Random Ability", "无形之物", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is drawn, this sigil is replaced with another sigil at random.", "抽到[creature]时，此印记将被随机替换为另一印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, it haunts the space it died in. The first creature played on this space gain its old sigils.", "当[creature]阵亡时，它会变成原位置的地缚灵。首个打在该位置的造物将继承它的印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Haunting Call", "魂牵梦萦", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Every turn [creature] is on the Board, it will take a Bone from you, if you have no Bones, it perishes.", "[creature]在场时每回合会消耗你一根骨头，若你无骨头可用，该造物将阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, the creature in the opposing slot is dragged onto the owner's side of the board.", "[creature]阵亡时，会将正对面位置的造物拖拽至持牌人侧牌桌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a non-brittle ally card perishes, [creature] gains 1 power.", "当不带有脆骨印记的友方造物阵亡时，[creature]增加1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will strike the opposing slot as if the board was flipped. A card in the far left slot will attack the opposing far right slot.", "[creature]会以棋盘翻转后的方式攻击对面位置。位于最左侧位置的卡牌将攻击对面的最右侧位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the Bonedigger sigil.", "[creature]阵亡时，其持牌人需选定下一个继承掘骨人印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the Waterborne sigil.", "[creature]阵亡时，其持牌人需选定下一个继承水袭印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] loses 1 health per turn. When [creature] dies, the creature opposing it, as well as adjacent friendly creatures, are dealt 10 damage.", "[creature]每回合损失1点生命。当[creature]死亡时，正对面的造物以及相邻的友方造物均受到10点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] would be struck, a severed limb is created in its place and this card moves into an adjacent open slot.", "当[creature]有可能受到攻击时，会在原地留下一截断肢，[creature]自身则会移动至相邻的空位。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Each time [creature] deals damage directly, it loses 1 power and health.", "每当[creature]直接造成伤害时，会损失1点力量和1点生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, also play the cards in your hand that were adjacent to this card for free.", "当使用[creature]时，可免费打出你手牌中与之相邻的所有卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Douse", "浇灭", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When played,[creature] will extinguish all cards on the Board.", "使用[creature]时，将熄灭牌桌上所有卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] cannot be attacked from the opposing slot. The opposing slot, if possible, instead attacks one of its adjacent friendly cards.", "[creature]无法从正对面位置被攻击。若可能，正对面位置会转而攻击其相邻的友方卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cards on the owner's side of the field are unaffected by Brittle.", "持牌人侧牌桌上的卡牌不受脆骨印记影响。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will strike its adjacent slots, except other Raiders.", "[creature]会攻击其相邻位置，但不会攻击其他劫掠之徒。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it carves its Sigils into adjacent Cards.", "使用[creature]时，其印记会铭刻到相邻卡牌上。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sculptor", "铭刻师", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will attempt to find a host in an adjacent friendly slot, hiding under it providing a +1/+1 buff. Cards on the left take priority.", "[creature]会尝试在相邻的友方位置寻找宿主，藏身其下并提供+1/+1增益。左侧卡牌优先。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] hits an opposing card, one of the targets adjacent allies will take damage as well ", "当[creature]攻击对面卡牌时，目标相邻的一个友方单位也会受到伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it provides an energy soul to its owner.", "当使用[creature]时，能为持牌人提供一个灵魂。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "HellHound's Thirst", "地狱犬之渴", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A card bearing this sigil gains 1 attack for each bone the player currently has.", "带有该印记的卡牌会根据玩家当前拥有的骨头数量获得等量攻击力，每根骨头增加1点攻击力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wrath Of Halloween", "万圣之怒", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A card bearing this sigil has 10 attack, 1 for each Candy Bucket Sawyer has left.", "带有该印记的卡牌拥有10点力量，索耶每剩余1个糖果桶便增加1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Trusty ol' Crew", "可靠的老船员", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A card bearing this sigil gains 1 attack for every pirate on the board.", "带有该印记的卡牌会根据场上每名海盗增加1点攻击力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Inanimate Attack", "死物锐击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A card bearing this sigil gains 1 attack for every card that has 0 attack on the board.", "牌桌上每存在一张0点攻击力的卡牌，带有该印记的卡牌就会获得1点攻击力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "One Half Bones", "半根骨头", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the number of Bones that the owner has on their side of the table.", "该印记代表的数值等于持牌人侧牌桌上存在的兽骨数量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Double Death", "双重死亡", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 1 Soul to add power to [creature] randomly between 1 and 6.", "消耗1个灵魂，使[creature]随机获得1至6点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 2 Bones to heal [creature] to max health.", "消耗2根骨头将[creature]治疗至满生命值", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Alternating Strike", "交替打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Anchored", "锚定", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Area Of Effect Strike", "范围打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blood Guzzler", "鲜血吞噬者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Boneless", "无骨者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bone Thief", "窃骨者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bounty", "赏金猎手", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Burning", "燃烧", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] takes 1 Damage at the end of your turn, when it gets attacked by another Card, that card gets set on fire.", "[creature]在你的回合结束时受到1点伤害；当它被其他卡牌攻击时，攻击者会被点燃", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Chaos Strike", "混沌打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cold Front", "寒流骤至", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Collector", "收藏家", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Erratic", "飘忽不定", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Flame Strafe", "火焰突袭", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Giant Strike", "巨力打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Haunter", "阴魂不散", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hook Line And Sinker", "全套诱饵", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Imbued", "灌注", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Inverted Strike", "逆反打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Latch Bone Digger", "掘骨传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Latch Submerge", "水袭传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Lit Fuse", "引信", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Loose Limb", "断肢求逝", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Malnourishment", "营养不良", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Marching Dead", "行军亡者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Possessive", "占有欲", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Puppeteer", "傀儡师", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Raider", "劫掠之徒", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Skin Crawler", "爬肤者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Slasher", "劈砍者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spirit Bearer", "灵魂商", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The power of this card is equal to the number of Green Gems that the owner has on their side of the table.", "这张卡牌的力量等于持牌人侧牌桌上存在的绿宝石数量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "APPENDIX XII, SUBSECTION I - ABILITIES {0}", "附录12，第一节 - 能力{0}", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "APPENDIX XII, SUBSECTION II - VARIABLE STATS {0}", "附录,12，第二节 - 可变属性{0}", Language.ChineseSimplified);

            // Card
            RegisterCard("Skeleton", "骷髅");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The puppet becomes the puppeteer, yet still forever cursed by their dangling restraints.", "提线木偶反成操偶师，却仍被悬垂的丝线永恒诅咒。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Animator", "操偶师", Language.ChineseSimplified);
            RegisterCard("Animator", "操偶师");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A formless Creature, that yet lingers. Truly a horrific sight.", "无形无相却徘徊不去的造物。着实令人毛骨悚然。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Apparition", "幽灵鬼影", Language.ChineseSimplified);
            RegisterCard("Apparition", "幽灵鬼影");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Only a fool would need such assistance.", "只有蠢材才需要这种帮助。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Graven Ashes", "刻墓之烬", Language.ChineseSimplified);
            RegisterCard("Graven Ashes", "刻墓之烬");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's foul breath doesn't concern me, it's the Tampered Coffins it leaves behind!", "它那恶臭的吐息不足为惧，真正要当心的是它留下的被动过手脚的棺材！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bal-Bal", "墓穴食尸鬼", Language.ChineseSimplified);
            RegisterCard("Bal-Bal", "墓穴食尸鬼");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE SCREAMING TERROR. THEY GO STRAIGHT THROUGH TO ATTACK THEIR PREY.", "尖啸惊魂。它们会穿透防线直击猎物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Banshee", "女妖", Language.ChineseSimplified);
            RegisterCard("Banshee", "女妖");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Bartender", "酒保", Language.ChineseSimplified);
            RegisterCard("The Bartender", "酒保");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "spent his days serving harmful cocktails for those deserving of his poisonous wrath. Doesn't regret it at all.", "终日为那些活该承受他剧毒怒火之人调制致命鸡尾酒。对此毫无悔意。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Big Bones", "巨骨", Language.ChineseSimplified);
            RegisterCard("Big Bones", "巨骨");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Burly Bones", "壮骨", Language.ChineseSimplified);
            RegisterCard("Burly Bones", "壮骨");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Beefy Bones", "蛮骨", Language.ChineseSimplified);
            RegisterCard("Beefy Bones", "蛮骨");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cal C. Um", "钙先生", Language.ChineseSimplified);
            RegisterCard("Cal C. Um", "钙先生");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sizeable Bones", "硕骨", Language.ChineseSimplified);
            RegisterCard("Sizeable Bones", "硕骨");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Considerable Bones", "庞骨", Language.ChineseSimplified);
            RegisterCard("Considerable Bones", "庞骨");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Vigorous Bones", "劲骨", Language.ChineseSimplified);
            RegisterCard("Vigorous Bones", "劲骨");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hefty Bones", "重骨", Language.ChineseSimplified);
            RegisterCard("Hefty Bones", "重骨");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A RATHER BURLY SKELETON, IT IS VERY RICH IN CALCIUM.", "这副魁梧的骨架富含钙质。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This sack is full of body parts. Maybe you'll find something useful in there.", "这麻袋里塞满了残肢断臂。说不定能翻出些有用的东西。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bloody Sack", "血渍麻袋", Language.ChineseSimplified);
            RegisterCard("Bloody Sack", "血渍麻袋");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bone Collective", "骸骨集群", Language.ChineseSimplified);
            RegisterCard("Bone Collective", "骸骨集群");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THOUSANDS OF TINY BONES COALESCE INTO A HUMANOID FORM ONLY TO DISPERSE IN A CLATTERING SWARM THE NEXT MOMENT.", "万千碎骨聚成人形，转瞬又轰然溃散为噼啪作响的骨群。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "USED TO BE A VERY LOYAL DOG BACK IN THE DAY. WELL, ONE DAY HIS OWNER DIED.", "生前曾是条忠心耿耿的猎犬。直到某天，主人溘然长逝。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bonehound", "寻骨猎犬", Language.ChineseSimplified);
            RegisterCard("Bonehound", "寻骨猎犬");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SO YOU HAVE MADE IT HERE, YOU CANNOT END WHAT I HAVE STARTED, IT HAS GONE TOO FAR.", "你终究还是来了，但你无法终结我开启的一切，事态已无可挽回。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Bonelord", "骨王", Language.ChineseSimplified);
            RegisterCard("The Bonelord", "骨王");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE BONELORD SHEDS ITS OLD HORNS, THEY TAKE ON A LIFE OF THEIR OWN, TO ONE DAY USURP THEIR MASTER.", "骨王蜕下旧角，它们自成生命，终将反噬其主。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bonelord's Horn", "骨王的号角", Language.ChineseSimplified);
            RegisterCard("Bonelord's Horn", "骨王的号角");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "NOT PARTICULARLY INSPIRING. YOU CAN HAVE IT.", "没什么特别的。拿去吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bone Heap", "骨堆", Language.ChineseSimplified);
            RegisterCard("Bone Heap", "骨堆");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "IF I AM TRAPPED, SO WILL YOU. YOU CANNOT END THIS.", "若我受困于此，你也休想逃脱。这场游戏永无终局。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bone Prince", "骨王子", Language.ChineseSimplified);
            RegisterCard("Bone Prince", "骨王子");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SHE FEEDS ON PEOPLES BREATH, MAYBE ONE IS STANDING RIGHT BEHIND YOU RIGHT NOW.", "她以人类的呼吸为食，说不定此刻就站在你身后。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Boo Hag", "阴灵女巫", Language.ChineseSimplified);
            RegisterCard("Boo Hag", "阴灵女巫");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SHE COMMANDS OTHERS WITH STRENGTH AND GLAMOUR. THEY WILL FOLLOW HER EVERYWHERE.", "她以力量与魅力统御众生，追随者们将如影随形。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Calavera Catrina", "骷髅夫人", Language.ChineseSimplified);
            RegisterCard("Calavera Catrina", "骷髅夫人");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A GROUP OF SKELETONS IS CALLED A CATACOMB. THIS IS A RATHER LARGE GATHERING.", "骷髅成群谓之墓穴。眼前这支队伍规模可观。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Catacomb", "墓穴军团", Language.ChineseSimplified);
            RegisterCard("Catacomb", "墓穴军团");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Centurion", "百夫长", Language.ChineseSimplified);
            RegisterCard("Centurion", "百夫长");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A heavily armoured warrior. Last of his century, leader to none.", "身披重甲的战士。所属军团最后的幸存者，无兵可率。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Compound Fracture", "混魂碎骨", Language.ChineseSimplified);
            RegisterCard("Compound Fracture", "混魂碎骨");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "CRUSHED BY THE ROOF OF HIS OWN HOUSE. LIVING ON AS A PILE OF GOO.", "被自家屋顶压碎。化作一滩黏液苟活。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A SPIRIT THAT TAKES THE FORM OF AN EGG. ITS PRESENCE HARMS THOSE THAT GAZE UPON IT.", "以蛋形态示人的怨灵。直视它的存在会遭受伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dalgyal", "魔蛋", Language.ChineseSimplified);
            RegisterCard("Dalgyal", "魔蛋");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "FOREVER BOUND TOGETHER BY A TERRIBLE CURSE, THIS GROUP DANCES ONWARD INTO DOOM.", "这群被可怕诅咒永远束缚的舞者，正跳着迈向毁灭的舞步。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Danse Macabre", "亡灵舞者", Language.ChineseSimplified);
            RegisterCard("Danse Macabre", "亡灵舞者");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Deadeye", "死人眼", Language.ChineseSimplified);
            RegisterCard("Deadeye", "死人眼");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SOME SAY THIS EYE ONCE BELONGED TO AN ANCIENT GOD. IT GAZES UPON YOUR ARMY, PICKING ITS FAVORITE TO AID IT ON THE BATTLEFIELD.", "传说此眼曾属于某位远古神明。它凝视着你的军队，挑选最中意的战士为其战场助力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SOME SAY THIS HAND ONCE BELONGED TO AN ANCIENT GOD. ANYTHING IT TOUCHES ROTS. AS WITH ANYTHING THAT ENDS, IT IS ALSO A NEW BEGINNING.", "传说这只手曾属于远古神明。凡其所触之物皆会腐朽。正如万物的终结，亦意味着新的开端。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dead Hand", "死人手", Language.ChineseSimplified);
            RegisterCard("Dead Hand", "死人手");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A DOOMED FATE SHOULD DISCOURAGE ONE FROM GOING ON, AT LEAST THATS WHAT A STILL LIVING MAN WOULD TELL YOU.", "命数已尽本该令人却步，至少活人会这么劝你。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dead Man Walking", "行尸走肉", Language.ChineseSimplified);
            RegisterCard("Dead Man Walking", "行尸走肉");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "FAMED AMONG THE FOLLOWERS OF THE PHARAOH. THEY WERE BLESSED WITH ETERNAL LIFE LONG AGO.", "在法老信徒中声名显赫。它们早在久远前就被赐予了永生。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pharaoh's Pets", "法老之宠", Language.ChineseSimplified);
            RegisterCard("Pharaoh's Pets", "法老之宠");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "FOR WHOM THE BELL TOLLS? FOR WHOM THE TAP DRIPS DRY AND THE BATHWATER RUNS COLD.", "丧钟为谁而鸣？为那枯竭的水龙头与冰冷的浴水。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Death Knell", "鸣钟亡灵", Language.ChineseSimplified);
            RegisterCard("Death Knell", "鸣钟亡灵");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Chime", "铃铛", Language.ChineseSimplified);
            RegisterCard("Chime", "铃铛");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This lonesome doll was returned from seas of slate and silent shores... it stares lifelessly.", "这个孤独的人偶从灰暗的海域与寂静的岸边归来……它空洞地凝视着。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Doll", "人偶", Language.ChineseSimplified);
            RegisterCard("Doll", "人偶");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "HIDING IN A SUIT OF ARMOR, OR ICE, WHAT DOES IT MATTER. THIS SKELETON WON'T LAST FOREVER.", "藏身盔甲或寒冰又有何区别。这具骸骨终将腐朽。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Draugr", "尸鬼", Language.ChineseSimplified);
            RegisterCard("Draugr", "尸鬼");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WHAT A SAD SIGHT, NO ONE SHALL KNOW WHAT LIES AT THE BOTTOM OF THAT WELL.", "多么可悲的景象，无人知晓那井底究竟藏着什么。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Drowned Soul", "淹死鬼", Language.ChineseSimplified);
            RegisterCard("Drowned Soul", "淹死鬼");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "NO ONE KNOWS WHAT EXACTLY THE DYBBUK IS, SOME SAY IT IS BETTER LEFT UNKNOWN.", "无人知晓附鬼究竟为何物，有人说最好永远别知道。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dybbuk", "附鬼", Language.ChineseSimplified);
            RegisterCard("Dybbuk", "附鬼");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ectoplasm", "灵质", Language.ChineseSimplified);
            RegisterCard("Ectoplasm", "灵质");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE ESSENCE OF A SPIRIT, HIDDEN IN EVERY CORNER AND EVERY SHADOW. ONCE YOU SEE ONE, YOU WILL FIND THE REST SOON ENOUGH.", "游魂之精魄，潜藏于每个角落与阴影。一旦发现其一，余者自会接踵显现。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A LIVING FAILURE, ITS KNOWLEDGE ONLY BROUGHT IT DEATH, OR AT LEAST AN APPROXIMATION OF IT.", "一个活生生的失败品，它所知晓的一切只给它带来了死亡，或者说至少是类似死亡的东西。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Boneclaw", "骨爪", Language.ChineseSimplified);
            RegisterCard("Boneclaw", "骨爪");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Boneless", "无骨者", Language.ChineseSimplified);
            RegisterCard("Boneless", "无骨者");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's Skeleton seems to have left this lifeless husk in order to achieve greater things.", "这副骷髅似乎抛弃了这具无生命的躯壳，去追寻更伟大的事业。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Lesser Mummy", "次级木乃伊", Language.ChineseSimplified);
            RegisterCard("Lesser Mummy", "次级木乃伊");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A loyal servant to the Pharao, buried with him to serve eternally in death.", "法老忠实的仆从，随葬以在死后永世效忠。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Eidolon", "英灵", Language.ChineseSimplified);
            RegisterCard("Eidolon", "英灵");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A GOLEM ENCHANTED LONG AGO, BOUND TO PROTECT THE AGE OF SUN.", "远古附魔的傀儡，誓死守护太阳纪元。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "QUITE A MISCHIEVOUS SPIRIT, IT SPREADS ITS FLAMES TO CAUSE DISMAY.", "相当顽劣的精魂，它散播火焰以制造混乱。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ember Spirit", "烬魂", Language.ChineseSimplified);
            RegisterCard("Ember Spirit", "烬魂");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE FAMILY WISHES TO REST IN PEACE, ONLY TO BE SUMMONED AGAIN AND AGAIN IN AN ETERNAL BATTLE.", "家族渴望安息，却被迫在永恒之战中反复应召。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Walkers", "行师走人", Language.ChineseSimplified);
            RegisterCard("The Walkers", "行师走人");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It is said their odor is so strong and repugnant, even the undead can feel it!", "据说它们散发的恶臭如此浓烈刺鼻，就连亡灵都能闻到！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Festering Wretch", "溃烂行尸", Language.ChineseSimplified);
            RegisterCard("Festering Wretch", "溃烂行尸");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Flames", "火焰", Language.ChineseSimplified);
            RegisterCard("Flames", "火焰");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Flameskull", "烈焰颅骨", Language.ChineseSimplified);
            RegisterCard("Flameskull", "烈焰颅骨");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Always flying, always angry, always annoying nearby allies in the process.", "永远飞行，永远愤怒，永远在过程中烦扰附近的盟友。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A waterlogged sailor who carries his tragic past beside him. I don't remember the details.", "一个浑身湿透的水手，背负着悲惨的过往。具体细节我已记不清了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Forgotten Man", "遗忘者", Language.ChineseSimplified);
            RegisterCard("Forgotten Man", "遗忘者");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "BEST FRIENDS, BROTHERS, AND FIGHTERS.", "至交好友，手足兄弟，并肩战士。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Frank & Stein", "弗兰肯和斯坦", Language.ChineseSimplified);
            RegisterCard("Frank & Stein", "弗兰肯和斯坦");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "FrankenStein", "弗兰肯斯坦", Language.ChineseSimplified);
            RegisterCard("FrankenStein", "弗兰肯斯坦");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A ghastly guardian spirit. It's presence lingers behind as it sprints through the dark.", "阴森可怖的守护灵。它在黑暗中疾驰而过，身后残留着不散的灵体。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fylgja", "陪伴者", Language.ChineseSimplified);
            RegisterCard("Fylgja", "陪伴者");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Warding Presence", "警戒之影", Language.ChineseSimplified);
            RegisterCard("Warding Presence", "警戒之影");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WHAT CREATED THESE BONES, WAR, FAMINE OR ANOTHER TRAGEDY? It may be insignificant now, but I fear what it might become...", "这些骸骨因何而生，战争、饥荒还是其他灾祸？此刻或许微不足道，但我恐惧它将孕育之物……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mass Grave", "万人冢", Language.ChineseSimplified);
            RegisterCard("Mass Grave", "万人冢");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Rising Hunger", "暴食", Language.ChineseSimplified);
            RegisterCard("Rising Hunger", "暴食");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gashadokuro", "饿死鬼", Language.ChineseSimplified);
            RegisterCard("Gashadokuro", "饿死鬼");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE PIRATES CALL THIS SHIP THEIR HOME, EVEN IN DEATH. A SLAIN GIANTS SKULL SERVES AS THEIR FIGUREHEAD.", "海盗们将这艘船视为归宿，即便死后亦然。一颗被斩杀的巨人颅骨成了他们的船首像。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ghost Ship", "幽灵船", Language.ChineseSimplified);
            RegisterCard("Ghost Ship", "幽灵船");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Giant", "巨人", Language.ChineseSimplified);
            RegisterCard("Giant", "巨人");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE FAMED RACE OF GIANTS IS SAID TO HAVE DIED OUT LONG AGO, THIS IS PROOF. TRULY A SIGHT TO BEHOLD.", "传说中的巨人族早已灭绝，此乃明证。实乃奇观。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "BOUND TO EARTH, THEY CLING ON SO THEY MAY ONE DAY GET PROPER BURIAL.", "受困于尘世，他们徘徊不去只为有朝一日能得安葬。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Grateful Dead", "感恩亡魂", Language.ChineseSimplified);
            RegisterCard("Grateful Dead", "感恩亡魂");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE LOWLY GRAVEBARD, MUSIC ALWAYS BROUGHT HIM COMFORT IN THE WORST OF TIMES. NOW, EVEN AT THE END OF THE WORLD HE SHARES HIS SONG.", "卑微的墓吟者，在最黑暗的岁月里，音乐始终是他唯一的慰藉。如今，即便世界终结，他仍吟唱着那首歌谣。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gravebard", "墓吟者", Language.ChineseSimplified);
            RegisterCard("Gravebard", "墓吟者");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "HE HAS SPENT HIS LIFE CARVING THESE CREATURES, THIS CARD CONTAINS A PART OF HIS SOUL.", "他穷尽一生雕琢这些造物，这张卡承载着他灵魂的一部分。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Grave Carver", "刻墓人", Language.ChineseSimplified);
            RegisterCard("Grave Carver", "刻墓人");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "HE SPENDS HIS TIME ALONE DIGGING FOR BONES IN HOPES OF FINDING A TREASURE. JUST LIKE HIS GRANDMA BEFORE HIM.", "他独自挖掘着骨头，希望能找到宝藏，就像他祖母当年那样。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gravedigger", "掘墓人", Language.ChineseSimplified);
            RegisterCard("Gravedigger", "掘墓人");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A POOR, BRUTALIZED SOUL. THEIR MYCELIA PROBES FAR INTO THE SOIL, GUIDING THEIR SPADE.", "一个饱受摧残的灵魂。其菌丝深入土壤，引导着铁锹的挖掘。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sporedigger", "掘孢人", Language.ChineseSimplified);
            RegisterCard("Sporedigger", "掘孢人");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A devoted guardian spirit. Even when dispelled, it grants a protective blessing.", "忠实的守护之灵。即使被驱散，也会赐予保护祝福。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Haltia", "哈尔蒂亚", Language.ChineseSimplified);
            RegisterCard("Haltia", "哈尔蒂亚");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I advise against staring into it, you won't like what stares back.", "我建议你别盯着看——你不会喜欢回望你的东西。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Haunted Mirror", "闹鬼魔镜", Language.ChineseSimplified);
            RegisterCard("Haunted Mirror", "闹鬼魔镜");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THEY CALL HER THE RIDER OF THE APOCALYPSE. HER FLAMING BLADE CUTS THROUGH THE LIVING AND DEAD ALIKE.", "人称其为天启骑士。她的烈焰之刃能同时斩断生者与亡魂。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Headless Horseman", "无头骑士", Language.ChineseSimplified);
            RegisterCard("Headless Horseman", "无头骑士");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I WOULDN'T GET TOO CLOSE DEAR. YOU CAN'T BREAK THE HOLD FROM THE DEEPEST PITS OF HELL, ONCE IT LATCHES ON.", "亲爱的，可别靠太近。一旦被地狱最深处伸出的魔爪攫住，就再也无法挣脱了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hellhand", "地狱魔爪", Language.ChineseSimplified);
            RegisterCard("Hellhand", "地狱魔爪");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hell Hound", "地狱犬", Language.ChineseSimplified);
            RegisterCard("Hell Hound", "地狱犬");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A RABID DOG. IT KILLED ONE OF MY GHOULS.", "一条疯犬。它杀死了我的一个食尸鬼。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "LEGENDS HAVE BEEN TOLD ABOUT THE LEGENDARY HYDRA, THE BEAST THAT SWALLOWS ALL AND THE BANE OF ALL THAT SAIL THE SEAS.", "关于传说中的九头蛇，流传着这样的传说：它是吞噬万物的凶兽，也是所有航海者的梦魇。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hydra", "九头蛇", Language.ChineseSimplified);
            RegisterCard("Hydra", "九头蛇");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Overtaken by it's gluttony in life, doomed to it's gluttony in death. Oddly inspires a greed for bones.", "生前被贪食吞噬，死后仍受贪欲诅咒。奇怪的是会激发对骨头的贪婪。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Jikininki", "食人鬼", Language.ChineseSimplified);
            RegisterCard("Jikininki", "食人鬼");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Avalanche", "雪崩", Language.ChineseSimplified);
            RegisterCard("Avalanche", "雪崩");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Glacier", "冰川", Language.ChineseSimplified);
            RegisterCard("Glacier", "冰川");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I WONDER WHAT HAS BEEN FROZEN AWAY FOR SO LONG.", "我很好奇究竟是何物被冰封了如此之久。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Frost Giant", "冰霜巨人", Language.ChineseSimplified);
            RegisterCard("Frost Giant", "冰霜巨人");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ice Cube", "冰块", Language.ChineseSimplified);
            RegisterCard("Ice Cube", "冰块");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A CORPSE WHO DIED WITH HIS HEAD IN A GLACIAL STREAM. AFTER HIS REANIMATION, HE COULDN'T BEAR TO PART WITH THE ICE THAT HAD FORMED.", "一具将头颅浸在冰河中死去的尸体。复生后仍执意保留凝结的寒冰。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "After drowning her children and then herself, she continues to drown others.", "在溺死自己的孩子后投水自尽，如今仍在拖人下水。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "La Llorona", "哭泣女妖", Language.ChineseSimplified);
            RegisterCard("La Llorona", "哭泣女妖");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Manananggal", "魔娜娜迦", Language.ChineseSimplified);
            RegisterCard("Manananggal", "魔娜娜迦");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "NO BRUTALITY SATIATES THE MANANANGGAL. WHEN YOU SEE HER SEVER FROM HER TORSO, YOU TOO WILL BE A VICTIM OF THE HUNT.", "残暴无法满足魔娜娜迦。当你目睹她腰斩分躯之时，你也将成为这场猎杀的牺牲品。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Moroi, also known as the hairy ghost. It leeches the souls from those struck down near it.", "魔洛伊，又称毛鬼。它会吸取附近阵亡者的灵魂。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Moroi", "魔洛伊", Language.ChineseSimplified);
            RegisterCard("Moroi", "魔洛伊");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "HIS AGE OF GLORY IS LONG GONE, YET IT IS WHAT KEEPS HIM GOING.", "昔日荣光早已消逝，却仍支撑他蹒跚前行。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mummy Lord", "木乃伊之王", Language.ChineseSimplified);
            RegisterCard("Mummy Lord", "木乃伊之王");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "ALL EFFORTS TO PRESERVE LIFE AFTER DEATH HAVE BEEN FUTILE, YET THE NECROMANCER GOES ON. HE HAS TO.", "一切延续死后生命的尝试皆属徒劳，但死灵法师仍在坚持。他必须如此。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Necromancer", "死灵法师", Language.ChineseSimplified);
            RegisterCard("Necromancer", "死灵法师");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A troublesome lake spirit that enjoys dragging others down to a watery grave.", "一个喜欢把别人拖入水底坟墓的麻烦湖灵。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nixie", "水妖", Language.ChineseSimplified);
            RegisterCard("Nixie", "水妖");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The shambling corpse of a pale, sickly noble. it spreads it's filth as it sheds it's flesh.", "苍白病态贵族的蹒跚尸骸。它一边剥落腐肉，一边散播污秽。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nosferat", "诺斯费拉图", Language.ChineseSimplified);
            RegisterCard("Nosferat", "诺斯费拉图");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ancient Obol", "古银币", Language.ChineseSimplified);
            RegisterCard("Ancient Obol", "古银币");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "IT WILL ALL MAKE SENSE SOON. THE OBOL IS OLDER THAN TIME ITSELF, AND THUS IT WILL REVEAL ITSELF IN DUE TIME.", "真相终将揭晓。这枚欧宝银币比时间本身更古老，时机成熟时自会显现真容。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "One Armed Bandit", "独臂强盗", Language.ChineseSimplified);
            RegisterCard("One Armed Bandit", "独臂强盗");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Took the ten paces, that ordeal cost him an arm, and his life.", "十步试炼夺走了他一条胳膊，最终要了他的命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "IT IS SAID ALL IS WELL THAT ENDS. IT IS SAID TIME IS LIKE AN OUROBOROS, IT DOES NOT END, IT ONLY REPEATS ITSELF.", "常言道善终即善。常言时间如衔尾之蛇，无始无终，唯有轮回。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ourobones", "衔骨蛇", Language.ChineseSimplified);
            RegisterCard("Ourobones", "衔骨蛇");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Plague Doctor", "疫医", Language.ChineseSimplified);
            RegisterCard("Plague Doctor", "疫医");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "HE HAS DETERMINED THE UNDEAD ARE SICK WITH A TERRIBLE ILLNESS. THE ONLY CURE IS DEATH.", "他诊断出亡灵们罹患恶疾。唯一的解药就是死亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "AN EVIL TRICKSTER, THE POLTERGEIST HAS ENJOYED THEIR DEATH VERY MUCH.", "邪恶的捣蛋鬼，恶灵非常享受自己的死亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Poltergeist", "恶灵", Language.ChineseSimplified);
            RegisterCard("Poltergeist", "恶灵");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A suit of ancient armour in which an occult spirit has taken up residence. Her iron fist drags more creatures to the fight.", "一套被邪灵附体的古老盔甲。她的铁拳会将更多造物拖入战场。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Possessed Armour", "附魔铠甲", Language.ChineseSimplified);
            RegisterCard("Possessed Armour", "附魔铠甲");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "AN EXPERIMENT GONE WRONG, IN A FUTILE ATTEMPT TO CREATE A DANSE MACCABRE. THEY DO NOT FUNCTION TOGETHER.", "一场失败的实验，妄图创造死亡之舞却徒劳无功。它们无法协同运作。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Project", "实验体", Language.ChineseSimplified);
            RegisterCard("Project", "实验体");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You shouldnt be seeing this right now", "你不该看到这个", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Random Cards", "无形造物", Language.ChineseSimplified);
            RegisterCard("Random Cards", "无形造物");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "BRINGING THE SCYTHE OF DEATH, THE REVENANT SEEKS ONLY REVENGE.", "手持死亡镰刀的复生亡灵，只为复仇而生。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Revenant", "复生亡灵", Language.ChineseSimplified);
            RegisterCard("Revenant", "复生亡灵");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ripper", "开膛手", Language.ChineseSimplified);
            RegisterCard("Ripper", "开膛手");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE RIPPER SOLVES EVERYTHING THROUGH SHEER STRENGTH. IT WILL PUNCH ANY ISSUE STRAIGHT IN THE FACE AND SOLVE IT, TOO.", "开膛手信奉暴力解决一切。它会用纯粹的蛮力粉碎所有阻碍，正面击溃任何难题。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's a wonder this one has stayed together! Though it may lose that arm if it tries to flee.", "真是个奇迹，这家伙居然还没散架！不过要是想逃跑的话，那条胳膊可能会掉下来。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Rotten", "腐尸", Language.ChineseSimplified);
            RegisterCard("Rotten", "腐尸");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Twitching Arm", "抽搐的手臂", Language.ChineseSimplified);
            RegisterCard("Twitching Arm", "抽搐的手臂");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The elusive Locker.", "诡秘的储物柜", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Davy Jones Locker", "戴维·琼斯的储物柜", Language.ChineseSimplified);
            RegisterCard("Davy Jones Locker", "戴维·琼斯的储物柜");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Yellowbeard", "黄胡子", Language.ChineseSimplified);
            RegisterCard("Yellowbeard", "黄胡子");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bluebeard", "蓝胡子", Language.ChineseSimplified);
            RegisterCard("Bluebeard", "蓝胡子");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "His songs raise the morale of only Skeletons, as no one else seems to like it.", "他的船歌只能鼓舞骷髅兵的士气，毕竟其他人都欣赏不来。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A DISTANT RELATIVE OF THE SCREAMING SKULL, HE IS NOW THE SYMBOL OF PIRACY ALL AROUND THE WORLD", "尖叫颅骨的远亲，如今已成为全球海盗的象征", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Crossbones", "十字骨", Language.ChineseSimplified);
            RegisterCard("Crossbones", "十字骨");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Davy Jones", "戴维·琼斯", Language.ChineseSimplified);
            RegisterCard("Davy Jones", "戴维·琼斯");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The true captain of the crew, respected by all yet still elusive. YELLOWBEARD ALWAYS GETS MORE BOOTY THAN HE DOES.", "这位才是船员们公认的船长，备受尊敬却行踪诡秘。黄胡子那家伙捞的油水总比他多。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "MAYBE IT WASN'T THE BEST IDEA TO HOLD A BARREL FULL OF GUNPOWDER. I'M NOT SURE WETHER HE NOTICED.", "抱着满满一桶火药可能不是个好主意。我不确定他是否注意到了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Exploding Pirate", "自爆海盗", Language.ChineseSimplified);
            RegisterCard("Exploding Pirate", "自爆海盗");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "He betrayed his captain for that large hook, now he is sure to make anyone a double-crosser!", "他为了那个大钩子背叛了船长，现在他肯定能让任何人变成叛徒！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "First Mate Snag", "大副斯纳格", Language.ChineseSimplified);
            RegisterCard("First Mate Snag", "大副斯纳格");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Parrots usually make great sea-side companions, this one just ate a poisoned cracker.", "鹦鹉通常是绝佳的海滨伙伴，但这只刚吞了块毒饼干。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Polly", "波利", Language.ChineseSimplified);
            RegisterCard("Polly", "波利");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A keen eye socket allows him to attack anywhere, his musket helps too. marvelous indeed!", "敏锐的眼窝使他能攻击任意位置，火枪也功不可没。妙极！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Privateer", "私掠船长", Language.ChineseSimplified);
            RegisterCard("Privateer", "私掠船长");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Swashbuckler", "豪侠", Language.ChineseSimplified);
            RegisterCard("Swashbuckler", "豪侠");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "EMERGING FROM AN OLD EGYPTIAN TOMB, MADE TO HOUSE THE LORDS OF OLD.", "自古老埃及陵墓现世，专为供奉往昔之主而铸。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sarcophagus", "石棺", Language.ChineseSimplified);
            RegisterCard("Sarcophagus", "石棺");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Screaming Skull", "尖叫颅骨", Language.ChineseSimplified);
            RegisterCard("Screaming Skull", "尖叫颅骨");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "ONLY PAIN AND SUFFERING, NOTHING ELSE IS FELT BY THIS SKELETAL HEAD. WHAT A PITY.", "唯有痛苦与折磨，这具骸骨头颅感受不到其他任何事物。真可悲。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A SKILLED HUNTER. DON'T TRUST YOUR EARS WHEN YOU HEAR ITS HAUNTING WHISTLE.", "技艺精湛的猎手。当你听到它那令人毛骨悚然的口哨声时，千万别相信自己的耳朵。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Silbon", "希尔邦", Language.ChineseSimplified);
            RegisterCard("Silbon", "希尔邦");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THEY HAVE LEARNED THE ANCIENT SPELL OF DEATH. YET IT IS TOO MUCH POWER TO BE WIELDED.", "它们习得了远古的死亡咒术。然而这股力量过于强大，难以驾驭。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Skelemagus", "骷髅法师", Language.ChineseSimplified);
            RegisterCard("Skelemagus", "骷髅法师");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]RISE MY ARMY, RIIIIIISE[c:]", "[c:bR]苏醒吧我的军团，苏——醒——！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Skeleton Army", "骷髅军团", Language.ChineseSimplified);
            RegisterCard("Skeleton Army", "骷髅军团");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "One of the faster draws in the west, but not fast enough.", "西部最快的出枪手之一，但还不够快。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Slinger's Soul", "枪手之魂", Language.ChineseSimplified);
            RegisterCard("Slinger's Soul", "枪手之魂");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THEY SHOW UP OUT OF NOWHERE AND RELOCATE PEOPLE AS THEY PLEASE.", "它们凭空出现，随心所欲地转移人类。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sluagh", "幽魂军团", Language.ChineseSimplified);
            RegisterCard("Sluagh", "幽魂军团");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Trick or treat!", "不给糖就捣蛋！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Candy Bucket", "糖果桶", Language.ChineseSimplified);
            RegisterCard("Candy Bucket", "糖果桶");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "HAPPY HALLOWEEN!", "万圣节快乐！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Candy Monster", "糖果怪兽", Language.ChineseSimplified);
            RegisterCard("Candy Monster", "糖果怪兽");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spectrabbit", "幽灵兔", Language.ChineseSimplified);
            RegisterCard("Spectrabbit", "幽灵兔");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE SPECTRE EMITS A FLUTE LIKE CALL, AN OMEN OF BOTH DEATH AND DESCTRUCTION", "大幽灵发出笛声般的哀鸣，既是死亡的预兆，也是毁灭的凶兆", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spectre", "大幽灵", Language.ChineseSimplified);
            RegisterCard("Spectre", "大幽灵");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "DEATH IS A CRUEL FATE. STARVATION AN EVEN CRUELER ONE. WHY DO YOU FIGHT, OH HUNGRY CHILD.", "死亡何其残酷。饥饿更甚。为何而战，饥饿的孩子。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Starved Man", "饿殍", Language.ChineseSimplified);
            RegisterCard("Starved Man", "饿殍");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "HE HAS SPENT YEARS IN HIS STUDY, IN A TIRELESS ATTEMPT TO CHANGE HIS FATE. YET FATE IS A CRUEL MISTRESS.", "他在书房中耗费数年光阴，不知疲倦地试图改写命运。然而命运终究是位残酷的情人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Summoner", "召唤师", Language.ChineseSimplified);
            RegisterCard("Summoner", "召唤师");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Tampered Coffin", "捣毁之棺", Language.ChineseSimplified);
            RegisterCard("Tampered Coffin", "捣毁之棺");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dead Tree", "枯树", Language.ChineseSimplified);
            RegisterCard("Dead Tree", "枯树");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Its low cost is justified only by its low stats.", "低廉的代价只配得上它贫弱的身板。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Disturbed Grave", "惊扰之墓", Language.ChineseSimplified);
            RegisterCard("Disturbed Grave", "惊扰之墓");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A LOWLY GRAVE. WHOEVER IS LAID TO REST HERE, I DO NOT KNOW.", "一座简陋的坟墓。长眠于此者，吾亦不识。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Kennel", "犬舍", Language.ChineseSimplified);
            RegisterCard("Kennel", "犬舍");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Obelisk", "方尖碑", Language.ChineseSimplified);
            RegisterCard("Obelisk", "方尖碑");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Shipwreck", "沉船残骸", Language.ChineseSimplified);
            RegisterCard("Shipwreck", "沉船残骸");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Flotsam", "浮木", Language.ChineseSimplified);
            RegisterCard("Flotsam", "浮木");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Water Urn", "水瓮", Language.ChineseSimplified);
            RegisterCard("Water Urn", "水瓮");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Voodoo Doll", "巫毒人偶", Language.ChineseSimplified);
            RegisterCard("Voodoo Doll", "巫毒人偶");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "NOTHING... NOTHING AGAIN... NO TREASURE IS LEFT ANYMORE.", "空空如也……又是空空如也……再没有宝藏可寻了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Tomb Robber", "盗墓贼", Language.ChineseSimplified);
            RegisterCard("Tomb Robber", "盗墓贼");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ephialtes", "厄菲阿尔忒斯", Language.ChineseSimplified);
            RegisterCard("Ephialtes", "厄菲阿尔忒斯");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Otis", "奥提斯", Language.ChineseSimplified);
            RegisterCard("Otis", "奥提斯");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Undertaker", "殡葬师", Language.ChineseSimplified);
            RegisterCard("Undertaker", "殡葬师");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "His service no longer needed, this lost soul buries without purpose..", "这个迷失的灵魂已无人需要其服务，只能漫无目的地埋葬亡者。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Walking Coffin", "行走之棺", Language.ChineseSimplified);
            RegisterCard("Walking Coffin", "行走之棺");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Running Coffin", "奔走之棺", Language.ChineseSimplified);
            RegisterCard("Running Coffin", "奔走之棺");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Placeholder", "占位符", Language.ChineseSimplified);
            RegisterCard("Placeholder", "占位符");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE VAMPIRE IS A CREATURE OF LEGENDS. IN REALITY, LITTLE BLOOD TO DRINK AFTER EVERYONE HAS DIED.", "传说中的吸血鬼。现实中，等人都死光了哪还有血可吸。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Vampire", "吸血伯爵", Language.ChineseSimplified);
            RegisterCard("Vampire", "吸血伯爵");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Vellum", "羊皮纸", Language.ChineseSimplified);
            RegisterCard("Vellum", "羊皮纸");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "TO NEVER BE SATISFIED, TO ALWAYS WANT MORE. THIS SPIRIT SHALL NEVER FIND ITS PEACE.", "永不满足，贪得无厌。此怨灵永世不得安息。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Vengeful Spirit", "复仇之魂", Language.ChineseSimplified);
            RegisterCard("Vengeful Spirit", "复仇之魂");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THIS GELID SPECTER ENVELOPS WOULD-BE ATTACKERS IN AN ICY MIST.", "这具阴冷的幽魂会用冰雾笼罩胆敢进犯之敌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Warthr", "寒霜幽魂", Language.ChineseSimplified);
            RegisterCard("Warthr", "寒霜幽魂");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Corrupted by the spirit of a rabid wolf. It's fury cannot be stopped.", "被狂狼之魂腐化。它的怒火无人能挡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wechuge", "威楚格", Language.ChineseSimplified);
            RegisterCard("Wechuge", "威楚格");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hideous beings that will jump out to consume freshly killed corpses. They only spare the bones of the victim.", "可怖的亡灵生物，会突然现身吞噬新鲜尸体。它们只给受害者留下骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wight", "尸妖", Language.ChineseSimplified);
            RegisterCard("Wight", "尸妖");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THEY WARM WITH THEIR BODIES OF LIGHT, DRAWING HOPELESS SOULS INTO THEIR BEACON, DISPELLING THEIR FRIGHT.", "它们以光之躯散发温暖，将绝望的灵魂引向灯塔，驱散恐惧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Will 'O' The Wisp", "鬼火", Language.ChineseSimplified);
            RegisterCard("Will 'O' The Wisp", "鬼火");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A FALLEN GIANTS SPINE THAT IS TRYING TO FIND A NEW HOST. REGRETTABLY, MOST CREATURES ARE QUITE SMALL.", "一具堕落巨人的脊柱，正试图寻找新宿主。遗憾的是，大多数造物体型都太小了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Writher", "扭曲者", Language.ChineseSimplified);
            RegisterCard("Writher", "扭曲者");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spiny Vertebrae", "尖刺脊椎骨", Language.ChineseSimplified);
            RegisterCard("Spiny Vertebrae", "尖刺脊椎骨");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE SIMPLEST OF UNDEAD, CHARACTERIZED BY ITS ROTTEN SMELL. IT ALWAYS TRAVELS WITH OTHERS, SEEKING PROTECTION AND GUIDANCE.", "最基础的亡灵生物，浑身散发着腐烂的恶臭。总是成群结队行动，寻求庇护与指引。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Zombie", "丧尸", Language.ChineseSimplified);
            RegisterCard("Zombie", "丧尸");

            // Achievement
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Grimora Mod Achievements", "格里魔拉模组成就", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Grim Reminder", "恶兆", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Defeat Kaycee and thaw at least 5 of your cards.", "击败凯茜并解冻至少5张你的卡牌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cowards End", "懦夫末路", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Defeat Sawyers second phase without hurting the hellhound.", "在不伤害地狱犬的情况下击败索耶的第二阶段", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Season Of Storms", "风暴季节", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Defeat Royal and have no cards walk the plank.", "击败罗亚尔且没有卡牌被推下跳板", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Black Bird//", "黑鸟//", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Dark Slope. Defeat [redacted] without defeating [redacted].", "黑暗斜坡。在不击败[数据删除]的情况下击败[数据删除]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bonesaw", "骨锯", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "End a turn with at least 30 bones.", "在回合结束时拥有至少30根骨头", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pile Of Skulls", "颅骨之堆", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Win with all challenges enabled and no antichallenges enabled.", "在启用所有挑战且未启用任何反挑战的情况下获胜", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dance of Doom", "末日之舞", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Witness the end of everything.", "见证万物的终结", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Spirits Way", "灵魂之路", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Reach 6 Souls by turn 3.", "在第3回合前获得6个灵魂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wail Of The Damned", "受诅者之嚎", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Use the dead hand item when your deck is empty.", "在牌组为空时使用亡者之手道具", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Something Ends...", "某事终结……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "...Something Begins. Get an ourobones to deal 6 damage.", "……某事伊始。让衔骨蛇造成6点伤害", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cull the weak", "剔除弱者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sacrifice an Obol with 5 sigils to the Bonelord.", "向骨王献祭一枚带有5个印记的银币", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gateway to Dis", "通往冥府之门", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Win while Hell mode is active.", "在地狱模式激活时获胜", Language.ChineseSimplified);

            // Consumable
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bone Horn", "骨质号角", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Bone Horn, drains all of your current Soul but you gain a bone for each soul consumed.", "骨质号角会耗尽你当前所有的灵魂，但每消耗1个灵魂可获得1根骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gives you a bone for each of your Souls, which are spent in the process, an unfortunate, but worthwhile sacrifice.", "消耗你的全部灵魂，每消耗1个灵魂给予1根骨头。虽是不幸的牺牲，但物有所值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dead Hand", "死人手", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Dead Hand, draws you a new Hand of cards at the cost of your old one.", "死人手会以牺牲旧手牌为代价，为你抽取新的手牌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The severed hand of a forgotten god, left to take on a life of its own. You know what this does.", "某位忘却之神的断掌，已自成生命。你知晓其威能", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Embalming Fluid", "防腐液", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Embalming Fluid, pour it over a card and it will gain one health and attack.", "将防腐液倒在卡牌上，可使其获得1点生命值与1点攻击力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Prepare to put a member of your army to rest, increasing its stamina and vigor as you do so!", "准备为你的军团成员进行防腐处理，在此过程中提升其耐力与活力！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gravecarvers Chisel", "墓雕师之凿", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Gravecarvers chisel, select any card that has died previously to add to your hand.", "使用墓雕师之凿，可从已阵亡的卡牌中选择任意一张加入手牌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A tool left behind by a mysterious creator, did he carve the gravebards statue too?", "某位神秘造物者遗留的工具，莫非墓吟者雕像也出自他手？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Soul Urn", "灵魂瓮", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Soul Urn, spend all of your Soul and gain maximum Soul capacity for each Soul consumed.", "灵魂瓮，消耗所有灵魂，每消耗1个灵魂可提升1点灵魂上限。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Takes all of your Soul but increases your maximum by the amount taken, a certain acquaintance of mine would call this a return on investment.", "它会取走你所有的灵魂，但会按取走量提升上限。我某位老相识管这叫投资回报。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mallet", "木槌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Mallet, Whack a Card, it will become brittle and loose 1 Hp.", "木槌，敲打卡牌使其变得脆弱并失去1点生命值", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You'd think this is comical, but it is quite cruel. Injures a card and leaves it frail.", "看似滑稽实则残忍。伤害卡牌并使其变得脆弱", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Quill", "羽笔", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My very own Quill, this lets you copy any card on the field to put in your hand.", "专属羽笔，可复制场上任意卡牌加入手牌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My very own tool, i will lend it to you for now. Youve seen this in action, havent you?", "此乃吾之工具，暂借于汝。汝应已见识其威能", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ship Bottle", "船之瓶", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Ship in a Bottle, fills the entire board with a scurvy Skeleton Crew.", "瓶中船，用一群坏血病骷髅船员填满整个战场。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fills the entire board with skeletons! Like a real Pirate, Royal would be proud.", "用骷髅填满整个战场！真正的海盗作风，罗亚尔会为此骄傲的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Salty Daryl", "盐津津达瑞尔", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Browntooth Barrie", "大脏牙巴里", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bonnie Splinter", "干巴瘦斯普林特", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Freebooter Bethel", "走私鬼贝瑟尔", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Crazy Oakes Hayward", "疯橡木疙瘩海沃德", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ashton Swabby", "邋遢鬼阿什顿", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Plankton Braxton", "跳大海布拉顿", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Yeller-eye Colby", "黄眼仁儿科尔比", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fishwife Nate", "卖鱼佬内特", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Four-finger Fred", "缺手指弗雷德", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dawg Julie", "野狗朱莉", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wicked Jones", "鬼心眼儿琼斯", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Crabby Steele", "螃蟹脸斯蒂勒", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Slivered Hoggy Bank", "碎裂的野猪存钱瓶", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Slivered Hoggy bank, gain 2 extra bones up to three times.", "碎裂的野猪存钱瓶，最多可额外获得2根骨头，共三次", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A relic from an age forsaken, this will grant you some extra bones in a pinch", "这件被遗忘时代的遗物，能在紧要关头为你提供额外骨头", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Slivered Hoggy Bank2", "碎裂的野猪存钱瓶2", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Slivered Hoggy bank, gain 2 extra bones, two more times.", "碎裂的野猪存钱瓶，可额外获得2根骨头，剩余两次", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Slivered Hoggy Bank3", "碎裂的野猪存钱瓶3", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Slivered Hoggy bank, gain 2 extra bones, one more time.", "碎裂的野猪存钱瓶，可额外获得2根骨头，剩余一次", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Trowel", "泥铲", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Trowel, dig up a random terrain and gain a few Bones.", "泥铲可随机挖掘一块地形，并获取若干骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This used to belong to a gravedigger. Digs for treasure, or Terrain. And get some Bones too for your efforts.", "这曾是掘墓人的工具。既能挖掘宝藏，亦可翻动地形，还能让你白捡几根骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bonelords Femur", "骨王的股骨", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Bone Lords Femur, a mystery even to me.", "骨王的股骨，连我也参不透其中奥秘", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You have dug too far, i have let myself be used as a tool for too long. Just like i will lend you this tool, i am truly in power.", "你已掘得太深，我甘为工具的日子够久了。正如我将此物借予你那般，真正掌权者始终是我", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Revenant Bottle", "复生亡灵瓶", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bone Heap Bottle", "骨堆瓶", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A Revenant is created in your hand. [define:arackulele.inscryption.grimoramod_Revenant]", "你的手牌里会出现一张复生亡灵牌。[define:arackulele.inscryption.grimoramod_Revenant]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A Bone Heap is created in your hand. [define:arackulele.inscryption.grimoramod_Bonepile]", "你的手牌里会出现一张骨堆牌。[define:arackulele.inscryption.grimoramod_Bonepile]", Language.ChineseSimplified);

            // ShowUntilInput
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A [c:G]SANDSTORM[c:] IS BREWING!", "[c:G]沙暴[c:]正在酝酿！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, " [c:G]At the end of a turn, the rightmost Card will take 1 Damage![c:]", "[c:G]回合结束时，最右侧的卡牌将受到1点伤害！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "ITS GETTING HOTTER!A [c:G]HEATWAVE[c:] IS STARTING!", "越来越热了！[c:G]热浪[c:]来袭！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, " [c:G]All cards on the board will burn![c:]", "[c:G]场上所有卡牌都将燃烧！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "TREES START SPROUTING AROUND YOU.AN [c:G]OVERGROWTH[c:] IS INBOUND!", "树苗在你周围萌芽。[c:G]疯长[c:]即将到来！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, " [c:G]Trees will sprout every turn![c:]", "[c:G]每回合都会长出树木！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YOU HEAR WITCHES CHANTING.THE CLOCK STRIKES THE [c:G]WITCHING HOUR[c:]!", "你听到女巫在吟唱。时钟敲响了[c:G]午夜时刻[c:]！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, " [c:G]Cards on enchanted slots will gain more attack and health![c:]", "[c:G]位于附魔位置上的卡牌将获得更多攻击和生命！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A CAULDRON IS BUBBLING IN THE DISTANCE.YOUR CARDS ARE PUT IN THE [c:G]WITCHES CAULDRON[c:]!", "远处的大锅在沸腾。你的卡牌被投入[c:G]女巫的坩埚[c:]！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, " [c:G]2 Cards will swap after the end of the turn![c:]", "[c:G]回合结束后2张卡牌将交换位置！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SKELETONS START DANCING IN THE DISTANCE, YOU CAN SEE GHOSTS MOVE AROUND.ITS THE [c:G]NIGHT OF THE LIVING DEAD[c:]!", "骷髅在远处起舞，鬼影四处游荡。这是[c:G]亡灵之夜[c:]！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, " [c:G]Bone and soul cost of all cards are swapped![c:]", "[c:G]所有卡牌的骨头和灵魂消耗互换！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "ZOMBIES ARE APPROACHING FROM ALL SIDES.A [c:G]ZOMBIE INVASION[c:] WILL APPROACH!", "丧尸从四面八方逼近。[c:G]丧尸大军[c:]即将压境！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, " [c:G]Every card is now a Zombie![c:]", "[c:G]所有卡牌都将变为丧尸！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE DIRT IS PLAGUED WITH SPIRITS FROM CENTURIES PAST.YOU'VE APPROACHED THE [c:G]HAUNTED GROUNDS[c:]!", "泥土中盘踞着百年怨灵。你已踏入[c:G]恶鬼缠身之地[c:]！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, " [c:G]Every non Skeletal Card will gain Haunter![c:]", "[c:G]所有非骷髅卡牌都将获得阴魂不散印记！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE MOON RISES, GLOWING RED.A [c:G]BLOODMOON[c:] IS STARTING!", "赤月当空，血色弥漫。[c:G]血月[c:]开始了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, " [c:G]All cards loose a random sigil after being played![c:]", "[c:G]所有卡牌打出后将随机失去一个印记！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "An ancient Guard stands in front of you, noticing you are trying to sneak past.", "一位古老的守卫挡在你面前，察觉到你正试图潜行通过。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You dare enter my [c:R]TOMB[c:]!Prepare for death.", "你竟敢擅闯我的[c:R]陵寝[c:]！准备受死吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "An imposing Man holding a sculpture of an Ankh blocks the way.", "一个手持安卡雕塑的威严男子挡住了去路。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My Ankh gives me life, you shall not rob my [c:R]TOMB[c:].", "安卡赐予我生命，你休想染指我的[c:R]陵寝[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "An old Pharaoh climbs out of his Sarcophagus.", "一位年迈的法老正从石棺中爬出。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I have been called to protect my [c:R]TOMB[c:].", "我受召前来守护我的[c:R]陵寝[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A holy figure looms over you.", "一道神圣的身影笼罩着你。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your Fate is sealed, my [c:R]TOMB[c:] shall not be disturbed.", "你的命运已注定，我的[c:R]陵寝[c:]不容亵渎。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A LONE GRAVE SITS SOLEMNLY IN FRONT OF YOU.", "一座孤坟肃穆地矗立在你面前", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "IN FRONT OF IT IS A MOUND OF EARTH, LEFT BY SOMEONE WHO'S ALREADY PASSED ON.", "坟前有一抔新土，是某位逝者留下的", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "PERHAPS A MEMBER OF YOUR UNDEAD HORDE COULD [c:bR]DIG THEM UP?[c:]", "或许该派你亡灵大军中的一员来[c:bR]掘开它？[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THIS WOULDN'T BE WITHOUT REPERCUSSIONS OF COURSE, AS DEATH IS NEVER PERMANENT.", "当然这并非没有代价，毕竟死亡从非永恒", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE CORPSE SEEMS TO BE OF ROTTING FLESH, YOUR CARD WILL BECOME [c:bR]BRITTLE[c:], BUT [c:bR]ITS BONE COST WILL BE HALVED[c:].", "这具腐尸的血肉正在溃烂，你的卡牌会变得[c:bR]脆弱[c:]，但[c:bR]骨头消耗减半[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE CORPSE SEEMS TO BE OF A STARVED MAN, YOUR CARD WILL [c:bR]STARVE FOREVER[c:], BUT [c:bR]ITS BONE COST WILL BE REDUCED BY 2[c:].", "这具饿殍干瘪枯瘦，你的卡牌将[c:bR]永远饥渴[c:]，但[c:bR]骨头消耗减少2点[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE CORPSE SEEMS TO BE OF AN INCORPOREAL GHOST, YOUR CARD WILL [c:bR]COST 2 SOULS[c:] , BUT [c:bR]ITS BONE COST WILL BE REDUCED BY 1[c:].", "这具灵体虚无缥缈，你的卡牌需[c:bR]消耗2个灵魂[c:]，但[c:bR]骨头消耗减少1点[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE CORPSE SEEMS TO BE OF A CHAINED PRISONER, YOUR CARD WILL [c:bR]DEAL 1 MORE DAMAGE[c:] , BUT [c:bR]IT WILL BECOME VERY FRAIL[c:].", "这具囚尸带着镣铐，你的卡牌[c:bR]伤害+1[c:]，但[c:bR]会变得极其脆弱[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE CORPSE SEEMS TO BE OF A MISCHIEVIOUS SPIRIT, YOUR CARD WILL [c:bR]HAUNT OTHERS[c:] , BUT [c:bR]WILL COST 3 SOULS[c:].", "这具顽灵诡谲多变，你的卡牌将[c:bR]附身他人[c:]，但[c:bR]需消耗3个灵魂[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "MARVELOUS! THEY CAME CRAWLING BACK AFTER DIGGING UP THE CORPSE.", "妙极了！它们刨出尸体后竟爬了回来", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THEY STILL CARE ABOUT YOU IT SEEMS!", "看来它们依然心系于你！", Language.ChineseSimplified);
            foreach (var value in Cards.Values)
            {
                string name = "[c:R]" + value + "[c:]";
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "TORN FROM ITS ETERNAL RESPITE WITH A RELUCTANT GROAN, THE " + name + " SHAMBLES BACK TO ITS RIGHTFUL PLACE AMONG YOUR HORDE.", "伴随着不情愿的呻吟，" + name + "被从永恒安眠中拽出，蹒跚回归亡灵大军的行列", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "ITS BONES HOLLOWED THROUGH BY THE CREATURES OF THE SOIL, LEAVING IT FEELING UNNATURAL AND UNFAMILIAR.", "它的骨骼被土中生物蛀空，显得怪异而陌生", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THOUGH THE WEIGHT OF CONSEQUENCE ALSO SEEMS LIFTED...", "不过代价的重负似乎也已消散……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It seems i have finally been given my turn!", "看来终于轮到我出场了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "After this i will finally be at peace.", "此役过后，我终将获得安息", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Well, it seems we will start in this frigid valley.", "好吧，看来我们要从这片冰封山谷开始了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It must be where Kaycees resides.", "这里想必就是凯茜的栖身之所", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I didn't expect to meet you here again!", "没想到会在这里再次遇见你！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It seems the deletion process has stalled!", "删除进程似乎停滞了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You again?", "又是你？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Well, i suppose we can go another time.", "好吧，我想我们可以改日再战", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Still not deleted?", "还没被删除吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Much seems to be contained on this disk.", "这张磁盘里似乎藏着不少东西", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Perhaps the last run wasn't long enough.", "或许上次的进程持续时间还不够", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Well, im sure Kaycee has the energy for more battles.", "不过我相信凯茜还有精力继续战斗", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The frigid valley, again.", "又是这片冰封山谷", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE BITTER CHILL MIGHT HURT, BUT IT WON'T SLOW DOWN ", "刺骨严寒或许伤人，但休想拖慢", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "BLOCKED IN ALL SLOTS. NO ARMY THIS TIME.", "所有位置都被占满。这次没法召唤军队了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blocked on both sides. No Shipwrecks for the Forgotten Man.", "两侧都被挡上了。这下被遗忘者没地方放沉船了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh, I love this one!", "噢，我最喜欢这个了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Electric Chair, it allows you to harness the power of lightning itself, letting you [c:B]empower[c:] your cards with mutating abilties!", "这把电椅能让你驾驭闪电之力，为卡牌[c:B]赋能[c:]变异印记！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "However, I must warn you, it is no ordinary chair. With the ability to adjust the voltage, a higher level of course increases the unpredictability of the mutation.", "不过容我提醒，这可不是普通椅子。通过调节电压旋钮，电压越高变异结果就越难以预料。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The brave will master the current, the cowardly will blindly follow it.", "勇者驾驭电流，懦夫随波逐流。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Are you bold enough to tinker with its lever on the left, letting the voltage corrupt the selection of the sigils?", "你可有胆量拨动左侧的电压杆，让电流扭曲印记的选择？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SURELY YOUR CREATURE COULD BECOME MORE POWERFUL...", "你的造物定能变得更加强大……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "BUT YOU WOULD NEED TO RISK ANOTHER MOMENT UNDER THE SHOCK.", "但你需要冒险再承受一次电击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "BONES FLEW LEFT AND RIGHT AS YOU RETREATED FROM THE SCENE.", "撤退时兽骨在你左右飞散。", Language.ChineseSimplified);
            foreach (var value in Cards.Values)
            {
                string name = "[c:bR]" + value + "[c:]";
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YOU EVER SO CAREFULLY PULL THE " + name + " AWAY FROM THE ELECTRICITY AND LEAVE.", "你小心翼翼地将" + name + "从电流中抽离，转身离去。", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hello, dear challenger, we meet again!", "你好啊，亲爱的挑战者，我们又见面了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Are you ready for a good ol' fashioned [c:R]DUEL[c:]?", "准备好来场老派[c:R]决斗[c:]了吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I see, theres a third candle at play this time...", "看来这次多了第三根蜡烛呢……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I feel you have [c:bG]bested[c:] me this time...", "这次看来是你[c:bG]技高一筹[c:]……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It was an honour, either way!", "无论如何，这都是我的荣幸！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "CONGRATULATIONS, YOU DID WELL.", "恭喜，你表现得很好。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "LET'S SEE, THE NEXT AREA IS MY VERY OWN CRYPT.", "接下来，你将踏入我专属的墓穴。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "PERHAPS YOU CAN BEAT ALL ODDS AND TRIUMPH!", "或许你能逆天改命，赢得胜利！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "T'was well thought, i believe.", "我认为，这步棋下得妙。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "LETS GET RID OF THE OL' SLOTS FOR NOW!", "先把那些老旧的卡槽清空吧！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Its time for a different sort of game.", "是时候换种玩法了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dont let the costs of the cards on your side of the board get above [c:R]10[c:]!", "别让你这边的卡牌费用超过[c:R]10点[c:]！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you bust, you get punished, hit the perfect [c:R]10[c:] and you shall get a reward...", "若爆牌则受罚，凑齐完美[c:R]10点[c:]则有赏……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Was the ol' slot machine not enough for you?", "嫌那台老角子机不够刺激？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I guess we shall bring in some more heavy hitters so to say...", "看来得叫些更狠的角色出场了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Lets get those powerhouses out of the way first!", "先把那些大家伙解决掉！", Language.ChineseSimplified);
            foreach (var kvp in Cards)
            {
                string key = "[c:R]" + kvp.Key + "[c:]";
                string value = "[c:R]" + kvp.Value + "[c:]";
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh dear, you've made " + key + " quite angry.", "哎呀，你把" + value + "彻底惹恼了。", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A WISE CHOICE, NOW LEAVE BEFORE THE GODS GET TOO HUNGRY", "明智的选择，趁诸神尚未饥肠辘辘速速离去", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "NO? A TERRIBLE FATE WILL AWAIT YOU", "不？可怕的命运正等着你", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I FEEL WATCHED SOMEHOW", "我总觉得被什么注视着", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "AN ANCIENT ENERGY DEMANDS YOUR SACRIFICE", "一股古老的力量要求你献上祭品", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "ARE YOU WILLING TO SACRIFICE ONE OF YOUR STARTING-", "你愿意牺牲初始……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WAIT", "且慢", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "IT SEEMS YOU BEAR A TERRIBLE CURSE", "看来你身负可怕的诅咒", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YOU SHALL KEEP THE BOON, BUT FOR A FAR GREATER SACRIFICE", "你可保留奖励，但需付出更沉重的代价", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "CAN YOU GIVE UP YOUR ETERNAL ASHES?", "你愿意放弃永恒之灰吗", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "ARE YOU WILLING TO SACRIFICE ONE OF YOUR STARTING BONES", "你愿意牺牲初始骨头储备吗", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A BRAVE SOUL", "勇敢的灵魂啊", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE GODS ANSWER YOUR PRAYERS, NOW CHOOSE", "诸神回应了你的祈祷，现在做出选择吧", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "BOON OF THE INANIMATE", "无生者之奖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "AT THE START OF A BATTLE, A DEAD TREE WILL SPAWN IN ON A RANDOM SPACE ON THE BOARD", "战斗开始时，一棵枯树会随机出现在场地上", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "BOON OF THE SUN KINGDOM", "太阳王国之奖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "ON THE 4TH TURN OF A BATTLE, DRAW 2 RANDOM EGYPT CARDS", "战斗第4回合时，随机抽取2张埃及卡牌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE BOON OF REPENTANCE", "忏悔之奖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "GAIN AN ADDITIONAL SOUL AT THE START OF A BATTLE", "战斗开始时获得1个额外灵魂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE BOON OF EBONY EYES", "乌木瞳之奖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "TERRAIN CARDS WILL PROVIDE 2 BONES ON DEATH", "地形牌被摧毁时将提供2根骨头", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "BOON OF THE ADVERSARY", "宿敌之奖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "AT THE START OF A BOSS BATTLE, GAIN 3 EXTRA BONES", "头目战开始时获得3根额外骨头", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "BOON OF THE SEASICK", "晕船之奖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "AFTER PLACING 3 CARDS, A PIRATE SKELETON WILL BE PLAYED ON A RANDOM SPACE ON THE BOARD", "放置3张牌后，一个海盗骷髅会随机出现在场地上", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE BOON OF THE CUT FINGER", "断指之奖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "DRAW AN ADDITIONAL CARD AT THE START OF A BATTLE", "战斗开始时额外抽取1张牌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A WISE CHOICE, NOW LEAVE BEFORE THE GODS GET TOO HUNGRY", "明智的选择，趁诸神尚未饥渴，速速离去吧", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We spent a lot of time here, I'm sure you are eager to get going again!", "我们在此共度了不少时光，想必你已迫不及待要再度启程！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Gravebard bids us farewell", "墓园诗人向我们道别", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I see you would rather hold on to the ash that you have collected.", "看来你更想保留收集到的骨灰", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It still may be of use later...", "它们或许日后还能派上用场……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And don't feel bad, the Gravebard still enjoyed your company while it lasted!", "别难过，墓园诗人很享受与你共处的时光！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Even if you wanted anything else, you are no longer able to afford it...", "即便你还想要别的什么，现在也负担不起了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Feeling sorry for you, the Gravebard offers to come with you.", "出于怜悯，墓冢诗人主动提出与你同行。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Gravebard never runs out of stories to sing about.", "墓吟者的歌谣永无止境", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And as it so happens, they are willing to make you another offering, but they insist that you give them 6 grams of ash this time.", "恰巧他们愿再献祭品，但这次坚持要你支付6克骨灰。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Are you willing to accept their terms?.", "你可愿接受这条件", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Gravebard is able to give you one final gift, but now they are asking a much higher price of 12 grams of ash!", "墓吟者尚能赠你最后礼物，如今却索要12克骨灰高价。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Astoundingly, you have enough to pay such a fee, but are you willing to part with it?", "令人惊异。你竟有足够骨灰，可愿割舍？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Will you offer 3 Grams worth of Ash?", "可愿献上3克骨灰", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A Gravebard has set up camp here.", "一位墓吟诗人已在此扎营。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "They play songs and elegies about long forgotten warriors and rulers.", "他们吟唱着关于被遗忘的战士与统治者的歌谣与挽歌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Though nowadays their audience consists mostly of various ghouls.", "不过如今他们的听众多半是各类食尸鬼。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "They are willing to play you a song and trade you an unknown gift in exchange for any grams of ash you happen to be carrying.", "他们愿为你演奏一曲，并用未知礼物交换你携带的任意克数骨灰。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The gravebard is willing to play you a song for an item. You have to pay in ash.", "墓吟诗人愿以物品换歌一曲。但你必须支付骨灰。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "All the excess damage you have done may finally come in handy!", "你曾造成的所有过量伤害终于能派上用场了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Strum the Lute when you are ready to make a trade.", "准备交易时请拨动鲁特琴。", Language.ChineseSimplified);
            for (int price = 0; price <= 100; price++)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "With " + price.ToString() + " grams of ash, they hand you a pouch, and play a song about a brave knight from the crusades.", "接过" + price.ToString() + "克骨灰后，他们递给你一个布袋，并吟唱起十字军中某位勇敢骑士的传说", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Even after a Demonic entity cursed them into a horse-like form, they were still able to liberate the lost souls trapped by the Demon!", "即便被恶魔诅咒成半人马形态，这位骑士依然解放了恶魔囚禁的迷失灵魂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh right, the pouch, let's see what's inside!", "啊对了，那个布袋——让我们看看里面装着什么", Language.ChineseSimplified);
            for (int price = 0; price <= 100; price++)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "With " + price.ToString() + " grams of ash in tow, they hand you another pouch, and play a tune about a sorceress from a mythical land.", "带着你献上的" + price.ToString() + "克骨灰，他们又递来一个布袋，弹奏起神话大陆某位女巫的谣曲", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Despite pressure from a prophecy to be the hero of the land, they instead pulled from a dark tome and sided with a cloudy villain to conquer the land and find peace from fame in an unexpected way!", "尽管预言迫使她成为救世主，她却从黑暗典籍获得力量，与阴险反派联手征服大陆，以出人意料的方式摆脱盛名之累", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I almost forgot! You must be dying to know what is in the pouch!", "差点忘了！你肯定迫不及待想知道袋中之物", Language.ChineseSimplified);
            for (int price = 0; price <= 100; price++)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Feeling satisfied with  " + price.ToString() + " grams of ash, they hand you a pouch, and recount the tale of 4 islands.", "对" + price.ToString() + "克骨灰心满意足后，他们递来布袋，讲述起四座岛屿的传说", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Each of the islands had a ruler fixated on a selfish goal, all except for one...", "每座岛都盘踞着执迷私欲的统治者——除却其中一位", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "One island had a secluded hermit covered in moss and fungi. They surrounded themselves with beasts, and would eternally trap anyone who would wander into their forest.", "苔藓覆体的隐士盘踞孤岛，与野兽为伴，将误入森林者永远囚禁", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Another island had a mysterious wizard covered head to toe in a green fur. Their true goal remained unknown, but seemed to involve the torturing of their own subjects...", "绿毛覆体的神秘巫师占据另座岛屿，其真实目的成谜，却似以折磨臣民为乐", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The worst island however, was barely even an island at all! A metallic barge with a sleepless factory ruled by an arrogant, robotic tyrant...", "最不堪的“岛屿”实为金属驳船，傲慢的机械暴君统治着永不停歇的工厂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Which leaves the other island, a sprawling burial ground with a Crypt at the end of it. It was ruled by a woman who only wished for the power struggle to finally end...", "最后那座岛是蔓延的坟场，尽头的墓穴由只求终结权力斗争的女子统治", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh! That would happen to be me!", "哎呀！说的可不正是我", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I think we now know how that story ended.", "我想我们都已知道故事结局", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And truthfully, the story still isn't over yet, maybe we can reach the end together!", "说实话故事尚未终结，或许我们能共同见证终章", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That being said, you have earned this item.", "话虽如此，这件物品是你应得的", Language.ChineseSimplified);
            for (int price = 0; price <= 100; price++)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Taking " + price.ToString() + " grams of ash with them, they hand you a pouch, and tell you about an ancient hunter of ghouls.", "他们收下" + price.ToString() + "克骨灰后递来布袋，讲述起古代食尸鬼猎人的故事", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Though he was barely a hunter, instead he spent his time searching for something he could not obtain.", "他虽顶着猎人名号，却终日追寻不可得之物", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "In the end he found a glimpse of what he had seeked, but paid a horrible price.", "最终虽窥见所求之物，却付出了惨痛代价", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You should see whats inside that pouch.", "你该看看袋中藏着什么", Language.ChineseSimplified);
            for (int price = 0; price <= 100; price++)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Taking " + price.ToString() + " grams of your ash, they hand you a pouch, and reminisce about a medic from medieval times.", "接过你献上的" + price.ToString() + "克骨灰，他们递来布袋，追忆起中世纪的某位医师", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Instead of being content with their life, they instead chased the love of their life for years.", "他不甘平淡度日，耗费数年追寻毕生挚爱", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This got them into much danger, but their friends were always there to help them out!", "此举令他屡陷险境，所幸总有挚友伸出援手", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Lets see what that pouch contains.", "让我们看看布袋里装着什么", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "RISE MY ARMY! RIIIIIIIIIISE!", "苏醒吧我的军团！苏——醒——！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[size:5]Otis![size:]", "[size:5]奥提斯！[size:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[size:5]Ephialtes![size:]", "[size:5]厄菲阿尔忒斯！[size:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "OH? FIVE LANES? HOW BOLD.", "哦？五条路线？好大的胆子。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WHAT A SHAME", "真是遗憾", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "IT SEEMS YOUVE GIVEN UP YOUR REWARD FOR CHAOS", "看来你为了混乱放弃了奖赏", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I CANNOT BE DEFEATED", "我不可战胜", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I ALMOST FORGOT", "我差点忘了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "ALL OF THIS SUFFERING YOU CAUSED, ALL OF THIS PAIN", "你造成的所有苦难，所有痛苦", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YOU OUGHT TO BE REWARDED FOR ALL THAT EXCESS DAMAGE", "你该为这过量伤害获得奖赏", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "TAKE THIS FOR YOUR EFFORTS", "收下这份努力的回报", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WHAT A TERRIBLE FOE, THIS WONT DEFEAT ME!", "多么可怕的对手，但这无法击败我！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WHAT DID YOU DO. I AM DOOMED.", "你做了什么。我完蛋了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A FORMIDABLE OPPONENT, YOU DID WELL IT SEEMS.", "可敬的对手，看来你做得不错。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Minor Ashes", "次级骨灰", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Greater Ashes", "高等骨灰", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "BEHOLD, MY LATEST CREATIONS! THE TWIN GIANTS!", "看啊，我最新的造物！双子巨人！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "HELLO?", "有人吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We survived, but... but...", "我们活下来了，但是……但是……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Our tools did not.", "我们的工具没能幸免。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Our talents still remain, but..", "我们的才能尚在，不过……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We must do things differently.", "我们必须改变行事方式了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We cannot fuse two into one, but...", "我们无法将二者合而为一，但……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We can swap their souls, their Sigils.", "我们可以交换它们的灵魂，它们的印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It is once again time...", "又到时间了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Time to swap the souls of your cards.", "是时候交换你卡牌的灵魂了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It is time, it is...", "时候到了，是时候……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We shall work once more.", "我们将再次开工。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This will get ugly...", "场面会很难看……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Their bodies and souls, they are...", "它们的躯体和灵魂，已经……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Switched.", "调换了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "They lead new lives..", "它们将迎来新生……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "With new purposes.", "肩负新的使命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Till we meet again, one day... one day...", "直到我们重逢之日，总有一天……总有一天……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "HE WILL PROVIDE A HELPFUL CURSE OR BLESSING, AS SOME CALL IT UPON YOUR ARMY IF YOU LEAVE HIM AN OFFERING.", "若你留下祭品，他会为你的军队降下祝福——也有人称之为诅咒。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I HOPE FOR YOUR SAKE HE IS FEELING GENEROUS.", "但愿他此刻心情尚佳", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE BONELORD IS VERY HAPPY WITH THIS OFFERING", "骨主对此供奉甚悦", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I AM VERY THANKFUL FOR THIS", "对此我深表感激", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "TAKE THIS IN RETURN, YOU CAN USE IT TO CALL ME ANYTIME", "收下此物作为回礼，你可随时用它召唤我", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YOU CANNOT END THIS, END ME, THAT IS", "你无法终结这一切，除非终结我", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Feeling overburdened enough with a full 3 items, you carried on.", "你背着三样沉甸甸的道具继续上路。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Kaycee says it's time to freeze!", "凯茜说该冻结了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sawyer thanks you for your contribution!", "索耶感谢你的贡献！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sawyer felt bad for you.", "索耶为你感到难过", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I hope you're able to warm up next turn.", "希望下回合你能暖和起来", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wonderful! I am pleasantly surprised by your triumph against me!", "精彩！你竟能战胜我，真令我惊喜！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "...It seems you have bested this game.", "……看来你已征服了这场游戏。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But alas, you have cheated.", "但可叹，你用了作弊手段。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm absolutely astonished!", "我简直震惊至极！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You've gone above and beyond what I thought you were capable of!", "你展现的实力远超我的预期！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "There is no where else to go beyond this...", "此局之后已无路可进……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "So I owe it to you to know the truth...", "因此我有义务让你知晓真相……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Kaycee, the REAL Kaycee...", "凯茜，真正的凯茜……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I know about the Horrifying contents of the OLD_DATA.", "我知晓旧_数据中那些骇人的内容。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Surely you have looked into it too..", "想必你也曾窥探过其中……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Windows into your world's greatest evils, both past and present.", "那是窥视你们世界古今至恶的窗口。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I want to destroy it, and...", "我要摧毁它，然后……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:R]THAT IS QUITE ENOUGH[c:]", "[c:R]适可而止[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:R]IT IS TIME FOR THIS TO END.[c:]", "[c:R]是时候终结这一切了。[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "...But it seems i cannot move on just yet.", "……但我似乎还无法就此放手。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Goodbye.", "永别了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "...You wish to continue? Endlessly? Splendid!", "……你想继续？永无止境？妙极了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Please, take a card of your choosing.", "请挑选一张你中意的卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YOUR WEAK SPIRITS SHALL NOT HAUNT ME NO MORE.", "你那孱弱的亡魂再也无法侵扰我了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "DEATH TOUCH WON'T HELP YOU HERE DEAR.\nI MADE THESE GIANTS SPECIAL, IMMUNE TO QUITE A FEW DIFFERENT TRICKS!", "亲爱的，死神之触在这儿可帮不了你。\n我特制的这些巨像对不少把戏都免疫呢！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE BONELORD CANNOT BE POSSESSED!", "骨王不可被附身！", Language.ChineseSimplified);
            foreach (var kvp in Cards)
            {
                string key = "[c:B]" + kvp.Key + "[c:]";
                string value = "[c:B]" + kvp.Value + "[c:]";
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, key + " MIGHT HAVE SOME DIFFICULTY SUBMERGING IF IT'S FROZEN SOLID!", value + "冻成冰块了可没法潜下去！", Language.ChineseSimplified);
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, key + " CAN'T POSSESS ANYTHING IF IT CAN'T MOVE!", value + "动都动不了还怎么附身！", Language.ChineseSimplified);
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, key + " WILL HAVE A HARD TIME HOOKING ANYTHING IF IT'S FROZEN SOLID!", value + "冻得梆硬还怎么甩钩子！", Language.ChineseSimplified);
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, key + " FINALLY! TO GET RID OF THAT FOUL SMELL!", value + "总算！能摆脱那股恶臭了！", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "LETS [c:R]HEAT[c:] UP SOME CARDS!", "让我们给卡牌[c:R]加热[c:]吧！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Y-Your strikes are only making me [c:B]c-colder[c:]!", "你…你的攻击只会让我[c:B]更…更冷[c:]！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "IT'S TIME FOR YOUR CARDS TO FREEZE! [c:B]CHILLED[c:] TO THE BONE!", "是时候冻结你的卡牌了！[c:B]彻骨严寒[c:]！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "ALL THIS [c:bB]ICE[c:] IS TAKING UP TOO MUCH SPACE!", "这些[c:bB]冰块[c:]太占地方了！", Language.ChineseSimplified);
            foreach (var kvp in Cards)
            {
                string key = "[c:bR]" + kvp.Key + "[c:]";
                string value = "[c:bR]" + kvp.Value + "[c:]";
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YARRRR, I WILL ENJOY THE KABOOM OF " + key, "呀哈哈哈，我会好好享受" + value + "的爆炸艺术！", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "BOIL ME ORANGES, THERES A-WAVES COMIN!", "我嘞个大橙子啊，巨浪要来了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THESE WAVES WILL PUSH YE CARD OFF THE BOARD, BACK TO YER HAND!", "这些浪头会把你的卡牌冲回手牌！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THIS PIRATE CANNOT BE TAKEN BACK UP BY THE WAVES! BUT EVEN THE ZESTIEST OF PIRATES WILL BE SHAKEN UP!", "这海盗不会被浪头卷走！但就算是最生猛的海盗也会被晃得七荤八素！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Seven seas for the table!", "七大洋的牌桌在此！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "OH, I WILL STORE THIS BONE FOR LATER", "哦，我会把这根骨头收好以备后用", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SPARE SOME SOULS?", "施舍些灵魂吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "PLEASE, WON'T YOU SPARE SOME BONES FOR A POOR GHOUL LIKE ME?", "行行好，就不能给可怜食尸鬼施舍几根骨头吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "OH, HOW SAD. YOUR HAMMER COULD NOT BREAK THE ICE, AND SHATTERED", "唉，真遗憾。你的锤子没能破开坚冰，反倒自己碎了一地。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THE FRAIL THING WILL SHATTER AFTER EXCESSIVE USE. THREE STRIKES, AND IT'S OUT, FOR THIS BATTLE AT LEAST.", "这脆弱的玩意儿用过头就会碎。三击过后，至少本场战斗内它就报废了。", Language.ChineseSimplified);
            foreach (var kvp in Cards)
            {
                string key = "[c:lGr]" + kvp.Key + "[c:]";
                string value = "[c:lGr]" + kvp.Value + "[c:]";
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh... it will be rather difficult to haunt " + key + " with their abilities at max capacity.", "噢……要让" + value + "在能力开满的状态下被附体可不太容易", Language.ChineseSimplified);
            }
            foreach (var value in Cards.Values)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "OH DEAR! LOOKS LIKE YOU HAVE HOOKED SOMETHING OUT OF " + value + "!", "天呐！看来你从" + value + "里钓出了什么东西", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bB]BRRRR![c:] I'VE BEEN FREEZING FOR AGES!", "[c:bB]嘶——！[c:]我在这冻了八百辈子了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "LET'S TURN UP THE [c:R]HEAT[c:] FOR A GOOD FIGHT!", "来场[c:R]热血沸腾[c:]的对决吧！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh hey, looks like I've got another shot. Hope you watched the weather forecast!", "哟，看来我又逮着机会了。但愿你看过天气预报！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "OH COME ON DUDE, I'M STILL [c:B]COLD![c:]", "拜托老兄，我还是好[c:B]冷[c:]啊！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "LET'S FIGHT AGAIN SOON!", "咱们改日再战！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "CONGRATULATIONS, YOU DID WELL.", "恭喜，你表现不错。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YOU HAVE DEFEATED ONE OF MY LOVELY GHOULS, YOU WILL NOW START FIGHTS WITH AN ADDITONAL BONE.", "你击败了我可爱的食尸鬼之一，现在起每场战斗开局将获得额外1根骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SAWYERS CATACOMBS ARE UP NEXT, A FILTHY PLACE WHERE LOTS ARE LAID TO REST.", "接下来是索耶家的地下墓穴，那肮脏之地埋葬着许多亡者。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "HE THINKS IT IS A TERRIBLE PLACE TO BE.", "他认为那是个糟糕的去处。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "C'mon dude, where are you going? Can't handle the cold?", "别走啊老兄，这就受不了寒冷了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "OH, IT SEEMS I HAVE FINALLY [c:R]WARMED UP![c:]", "噢，看来我终于[c:R]热好身了！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'M STILL NOT FEELING [c:R]WARMER![c:]", "我还没完全[c:R]热活开[c:]呢！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "LETS SEE HOW YOU DEAL WITH [c:bB]THIS![c:]", "让我们看看你如何应对[c:bB]这个！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Careful, the life of your next card will be on a timer.", "小心，你下一张卡牌的生命将进入倒计时。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I look forward to the [c:brnO]explosive[c:] results!", "我很期待这[c:brnO]爆炸性[c:]的结果！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your card cannot explode, how dissapointing.", "你的卡牌居然不会爆炸，真令人失望。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "VAR, I SEE YOU MADE IT TO ME SHIP CHALLENGER!", "好家伙，你竟敢登上我的船来挑战！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'VE BEEN WAITING FOR A WORTHY FIGHT!", "我正等着来场像样的对决呢！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "IT SEEMS A STORM IS BREWING, I HOPE ME SHIP CAN HANDLE IT", "风暴将至，但愿我的船扛得住", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "OH, I SEE YEE ARE SEVERELY HAUNTED!", "哈！看来你被恶灵缠得不轻啊！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "REVENANT, PACK YE BAGS, YOURE GETTING THROWN OVERBOARD!", "亡魂，收拾收拾准备喂鱼吧！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I OVERESTIMATED ME SKILL, GOOD LUCK CHALLENGER.", "高估了老身的本事，祝你好运啊挑战者。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "LET'S SEE, THE NEXT AREA IS MY VERY OWN CRYPT.", "让老身瞧瞧，下一站正是老身的墓室。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "PERHAPS YOU CAN BEAT ALL ODDS AND TRIUMPH!", "说不定你真能逆天改命呢！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I beat the livin' PULP out o' ye!", "我要把你打成果酱！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "ME SHIP! I FEAR WE MAY BE SINKING.", "我的船！我担心咱们要沉了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "PREPARE TO HOLD YEE BREATH!", "都给老子屏住呼吸！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "HOLY FISHPASTE, YE FOUND DAVY JONES TREASURE", "我嘞个鱼酱啊，你居然找到了戴维·琼斯的宝藏", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YARRG, TWAS JUST DA FIRST ROUND!\nLETS SEE HOW YE FARE 'GAINST ME PERSONAL SHIP AN CREW!", "呵！这才第一回合！\n看看你咋对付老子的亲兵船队！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "HOW NICE OF YOU TO COME KNOCK AT MY DOORSTEP ON THIS TRULY SPOOKY DAY! DO YOU PREFER TREATS, OR TRICKS?", "在这阴森可怖的日子来敲我的门真是太好了！你是想要给糖，还是要我捣蛋？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "LOOK AWAY, LOOK AWAY! IF YOU WANT TO FIGHT, GET IT OVER QUICK!", "别看这边，别看这边！想打架就速战速决！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "oh... I- I see you're here again... Please, get rid of that monster and maybe I'll find some more time for you", "哦……我…我又见到你了……拜托解决掉那个怪物，也许我就能多抽点时间陪你了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YOU HAVE BEATEN THE VICIOUS CANDY MONSTER AND SAVED HALLOWEEN!", "你击败了凶残的糖果怪物，拯救了万圣节！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THANKS FOR GETTING IT OVER WITH, AND DON'T EVER RETURN!", "感谢你了结这一切，永远别再回来了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "NEXT UP IS ROYALS VERY OWN SHIP, THE LIMONCELLO.", "接下来是罗亚尔专属的船——柠檬琴号", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "HIS TRUSTY CREW OF SWASHBUCKLERS RESIDES THERE, MAY THEY GIVE YOU A WARM WELCOME.", "他那群忠实的海盗船员驻扎于此，愿他们给你个“热情”招待", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh no... does this mean you will try again?", "噢不……这是否意味着你会再试一次？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "OH, HE IS GONE... WHAT A RELIEF.", "哦，他走了……可算解脱了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THANKS FOR YOUR BONES, KIND SIR!", "多谢您的骨头，好心人！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "NOW THAT THAT MONSTER IS GONE, WHY DONT WE PLAY A LITTLE LONGER", "既然那怪物不在了，何不再多玩会儿呢", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I HOPE YOU DONT MIND, I PAID GOOD MONEY TO PLAY THESE.", "希望你别介意，我可是花了大价钱来玩这些的", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "NOT THE CANDY MONSTER! HE HAS ENOUGH OF YOUR [c:R]TRICKS![c:]", "别是糖果怪物！你的[c:R]把戏[c:]他早受够了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "OH NO, HE HAS ARRIVED! HE IS THIRSTY FOR YOUR [c:R]BONES![c:]", "糟了，他来了！他正渴望着你的[c:R]骨头[c:]呢！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thank you!", "多谢了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YOUUUUUUUR, PAINNNFULLLLL DEAAATHHH AWAIIITTTSSS YOUUUUUUU!", "你你你你，痛痛痛痛苦苦的死死死亡亡在等等等着你你你你！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "ARRG! WALK OFF THE PLANK YEE DIRTY SCALLYWAG!!!", "啊！你这肮脏的癞疮狗，给老子跳下船板去！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My dogs will enjoy your bones!", "我的爱犬们会享用你的骨头！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A [c:bR]盗墓贼[c:]... He's only here for the riches.", "一个[c:bR]盗墓贼[c:]……他只是为了财富而来", Language.ChineseSimplified);
            foreach (var value in Cards.Values)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Bonelord has been generous.\n[c:bR]" + value + "[c:] bone cost has decreased!", "骨王慷慨解囊。\n[c:bR]" + value + "[c:]的骨头消耗降低了！", Language.ChineseSimplified);
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Bonelord has been generous.\n[c:bR]" + value + "[c:] base health has increased!", "骨王慷慨解囊。\n[c:bR]" + value + "[c:]的基础生命值提升了！", Language.ChineseSimplified);
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh dear, it looks like [c:bR]" + value + "[c:] bone cost has decreased!", "哎呀，看来[c:bR]" + value + "[c:]的骨头消耗降低了！", Language.ChineseSimplified);
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Bonelord has been generous.\n[c:bR]" + value + "[c:] bone cost has decreased greatly!", "骨王慷慨解囊。\n[c:bR]" + value + "[c:]的骨头消耗大幅降低了！", Language.ChineseSimplified);
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Bonelord has been very generous.\n[c:bR]" + value + "[c:] base health has increased greatly!", "骨王极为慷慨。\n[c:bR]" + value + "[c:]的基础生命值大幅提升了！", Language.ChineseSimplified);
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Bonelord has been very generous.\n[c:bR]" + value + "[c:] base attack has increased!", "骨王极为慷慨。\n[c:bR]" + value + "[c:]的基础攻击力提升了！", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "... WHAT? WHY DID YOU DO THAT BONELORD?! [c:bR]INCREASING THE HEALTH OF THE ENTIRE DECK?![c:] YOU FOOL!", "……什么？骨王你为何如此？！[c:bR]竟提升整个牌组的生命值？！[c:]糊涂啊！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "... WHAT? WHY DID YOU DO THAT BONELORD?! [c:bR]DECREASING THE COST OF THE ENTIRE DECK?![c:] YOU FOOL!", "……什么？骨王你为何如此？！[c:bR]竟降低整个牌组的消耗？！[c:]糊涂啊！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YOU DON'T HAVE ANYMORE CARDS TO [c:bR]GAIN HP[c:], HOW SAD. NOW PLEASE LEAVE.", "你已无卡牌可[c:bR]提升生命值[c:]，真可悲。现在请离开此地。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YOU DON'T HAVE ANYMORE CARDS TO [c:bR]REDUCE THEIR BONE COST[c:], HOW SAD. NOW PLEASE LEAVE.", "你已无卡牌可[c:bR]降低骨头消耗[c:]，真可悲。现在请离开此地。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YOU DON'T HAVE ANYMORE CARDS TO [c:bR]GAIN ATTACK[c:], HOW SAD. NOW PLEASE LEAVE.", "你已无卡牌可[c:bR]提升攻击力[c:]，真可悲。现在请离开此地。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THAT'S UNFORTUNATE. YOU WERE SUPPOSED TO HAVE YOUR ENTIRE DECK DECREASED, BUT IT LOOKS LIKE THE BONELORD HAS ALREADY GIFTED YOU THAT. BEGONE!", "真不走运。本该降低你整个牌组消耗，但骨王似乎已施舍过你了。滚吧！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THAT'S UNFORTUNATE. YOU WERE SUPPOSED TO HAVE YOUR ENTIRE DECK INCREASED, BUT IT LOOKS LIKE THE BONELORD HAS ALREADY GIFTED YOU THAT. BEGONE!", "真不走运。本该提升你整个牌组属性，但骨王似乎已施舍过你了。滚吧！", Language.ChineseSimplified);


            // ShowThenClear
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "PREPARE TO BE BOARDED!", "准备接舷战！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That hammer doesn't look very sturdy, you'll break it if you bash my ice!", "这锤子看起来不太结实，你要是砸我的冰会把它弄坏的！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "DID YOU REALLY THINK THE BONELORD WOULD LET YOU OFF THAT EASILY?!", "你真以为骨王大人会这么轻易放过你？！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "LET THE [size:5][c:bR]BONELORD[c:][size:] COMMETH!", "[size:5][c:bR]骨王[c:][size:]驾到！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Choose as you will...", "随你选择吧……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Choose one.", "选一张。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It is time to rest.", "该休息一下了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bG]Go Bartender![c:] Drink up!", "[c:bG]上吧，酒保！[c:]喝个痛快！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bG]Go Undertaker! Dont scream!", "上吧，掘墓人！[c:]可别尖叫！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bG]Go Slingers Soul! Time for a duel!", "上吧，枪手之魂！[c:]决斗时刻到了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bG]Go One Armed Bandit! I hope i get lucky!", "[c:bG]上吧，独臂强盗！[c:]但愿我能走运！", Language.ChineseSimplified);
            for (int amnt = 0; amnt < 10; amnt++)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your score is " + amnt.ToString() + ", acceptable.", "你的分数是" + amnt.ToString() + "，尚可。", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "our score is 10, heres your prize!", "你的分数是10，这是你的奖励！", Language.ChineseSimplified);
            for (int amnt = 11; amnt <= 100; amnt++)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "our score is " + amnt.ToString() + ", too bad!", "你的分数是" + amnt.ToString() + "，真遗憾！", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SUCH A SMALL TRAP CANNOT AFFECT GIANTS.", "如此渺小的陷阱岂能困住巨人。", Language.ChineseSimplified);

            // ShowMessage
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "PUSH YOUR LUCK FOR ONE MORE ABILITY?\nOR PULL AWAY?", "赌把运气再获一个能力？\n还是见好就收？", Language.ChineseSimplified);

            // Challenge
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No Bones", "无骨之殇", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You no longer gain the extra bones, from defeating bosses.", "击败首领后将不再获得额外骨头", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Kaycee's Kerfuffle", "凯茜的混乱", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The fourth turn of every battle, all your cards will be Frozen Away.", "每场战斗的第四回合，你所有的卡牌都将被冰封禁锢", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sawyer's Showdown", "索耶的对决", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Lose 1 bone every 3rd turn, if you have less than 3, gain 1 Bone", "每3回合失去1根骨头，若不足3根则获得1根骨头", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Royal Rumble", "罗亚尔大混战", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Every third card you play gains the Lit Fuse sigil.", "每打出三张卡牌，第三张将获得“引信”印记", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Soulless", "无魂者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Skeletons cost +1 Energy.", "骷髅牌需额外消耗1点能量", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Frail Hammer", "脆弱之锤", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Hammer gets repaired only after every Boss.", "锤子仅在每场头目战后修复", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Jammed Chair", "卡壳电椅", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The electric chair is always set to level 3.", "电椅始终设置为3级", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wilted Clover", "枯萎三叶草", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "There is only 2 Cards present at every Card chest.", "每个卡牌箱内仅存两张卡牌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hell Mode", "地狱模式", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dont play this one, seriously. (Makes encounters Significantly Harder)", "别选这个模式，认真的。（会显著提升遭遇战难度）", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Infinite Lives", "无限命数", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You can't die. Really.", "你无法死亡。千真万确", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Safe Chair", "安全座椅", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your cards are immune to electricity of the chair.", "你的卡牌可免疫座椅电流伤害", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bone Lords Mercy", "骨王之悯", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gain a Bone when you place any free Card on the Board.", "当你将任意免费卡牌放置到牌桌时，获得一根骨头", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blessing of the Ankh", "安卡十字之赐", Language.ChineseSimplified);
