            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While a card bearing this sigil is on the board, all gems on your side of the board gain the Sharp Quills sigil, and 2 health.", "当[creature]在场时，持牌人侧牌桌上所有宝石获得尖刺铠甲印记和2点生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Animator", "操偶师", Language.ChineseSimplified);
            RegisterAbility("Animator", "操偶师");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While a card bearing this sigil is on the board, all non-mox cards with 0 power gain 2 power", "当[creature]在场时，所有力量为0的非玛珂卡牌获得2点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gem Absorber", "宝石吸收者", Language.ChineseSimplified);
            RegisterAbility("Gem Absorber", "宝石吸收者");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil is played, all gems on your side of the board will perish. For each gem absorbed, gain 1 power and 2 health.", "当[creature]使用时，持牌人侧牌桌上的所有宝石将被摧毁。每吸收一个宝石，获得1点力量和2点生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Submerge", "下潜", Language.ChineseSimplified);
            RegisterAbility("Submerge", "下潜");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the turn, a card bearing this sigil will submerge. Upon resubmerging, this card will become a random Tentacle Card.", "回合结束时，[creature]会潜入水下。重新上浮时，该卡牌会变为随机触手卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Familiar", "使魔", Language.ChineseSimplified);
            RegisterAbility("Familiar", "使魔");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A card bearing this sigil will perish if there are no non-gem cards on the board.", "若牌桌上没有非宝石卡牌，[creature]将会阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fading", "消逝", Language.ChineseSimplified);
            RegisterAbility("Fading", "消逝");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A card bearing this sigil will perish at the end of the turn.", "[creature]会在回合结束时消亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Morph", "不定变体", Language.ChineseSimplified);
            RegisterAbility("Morph", "不定变体");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil is played, it will mimic the card opposing it. If there is no card opposing it, this card will perish.", "当[creature]被使用时，会复制对面位置的卡牌。若对面无卡牌，则该卡牌会阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Multiplication", "矩阵乘法", Language.ChineseSimplified);
            RegisterAbility("Multiplication", "矩阵乘法");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil is played, draw another of this card, but with a different gem cost.", "使用[creature]时，抽一张同名卡牌，但其宝石需求会发生变化。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mox Cycle", "玛珂循环", Language.ChineseSimplified);
            RegisterAbility("Mox Cycle", "玛珂循环");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of every turn, all the mox on the board will cycle into different gems.", "每回合结束时，牌桌上所有玛珂牌会循环转换为其他宝石类型。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mox Strafe", "玛珂掉落", Language.ChineseSimplified);
            RegisterAbility("Mox Strafe", "玛珂掉落");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the turn, this card will move to the direction inscrybed on the sigil, and leave behind a mox.", "回合结束时，此卡牌会按照印记标记的方向突进，并在原地留下一颗随机玛珂。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Purist", "纯粹主义", Language.ChineseSimplified);
            RegisterAbility("Purist", "纯粹主义");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Any card directly opposing this one will have all it's sigils removed.", "任何直接与该卡牌对位的卡牌将失去所有印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Brewery", "酿造坊", Language.ChineseSimplified);
            RegisterAbility("Brewery", "酿造坊");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of every turn, you automatically draw a mox card from your side deck.", "每回合结束时，自动从副牌组抽取一张玛珂牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ignite", "引火", Language.ChineseSimplified);
            RegisterAbility("Ignite", "引火");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil is played, every empty opposing slot will be filled with flames. A flame is defined as a 0/1, Annoying.", "当[creature]被使用时，所有对侧空位将被火焰填满。火焰具有：0点力量，1点生命值，惹人生厌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Projection", "星空投影", Language.ChineseSimplified);
            RegisterAbility("Projection", "星空投影");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil is stuck, it will move and leave behind an astral projection of itself.", "[creature]受击时会移动，并留下自身的星体投影。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spell Book", "法术大全", Language.ChineseSimplified);
            RegisterAbility("Spell Book", "法术大全");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil is played, draw a random spell card.", "[creature]打出时，随机抽取一张法术牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Scholar", "法术学者", Language.ChineseSimplified);
            RegisterAbility("Scholar", "法术学者");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil is played, draw a random one of Bleene's Books.", "[creature]打出时，随机抽取一本蓝绿橙大师的书。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Midas' Touch", "黄金之触", Language.ChineseSimplified);
            RegisterAbility("Midas' Touch", "黄金之触");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil attacks another card, the attacked card will lose all of it's attack and turn to gold.", "[creature]攻击其他卡牌时，被攻击的卡牌将失去所有力量并转化为黄金。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Made of Gold", "黄金之躯", Language.ChineseSimplified);
            RegisterAbility("Made of Gold", "黄金之躯");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil perishes, the killer will gain 2 crystals.", "[creature]阵亡时，击杀者将获得2颗水晶。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Summon Runes", "引爆召唤", Language.ChineseSimplified);
            RegisterAbility("Summon Runes", "引爆召唤");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil is played, it will summon two runes beside it. A rune is defined as 0 power, 1 health, Detonator.", "当[creature]被使用时，会在其相邻位置召唤两个魔法炸弹符文。符文具有：0点力量、1点生命，引爆器。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spell of Health", "生命咒术", Language.ChineseSimplified);
            RegisterAbility("Spell of Health", "生命咒术");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a spell bearing this sigil is played, all cards on your side of the board gain +2 health.", "当带有该印记的法术牌被使用时，持牌人侧牌桌上所有卡牌获得+2生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spell of Attack", "攻击咒术", Language.ChineseSimplified);
            RegisterAbility("Spell of Attack", "攻击咒术");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a spell bearing this sigil is played, all cards on your side of the board gain +1 attack.", "当带有该印记的咒术被使用时，持牌人侧牌桌上所有卡牌获得+1攻击力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Frost Spell", "寒霜咒", Language.ChineseSimplified);
            RegisterAbility("Frost Spell", "寒霜咒");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a spell bearing this sigil is played, all cards belonging to the opponent will have their attack set to 0, but given +2 health.", "当使用带有该印记的咒术牌时，对手所有卡牌的攻击力将归零，但生命值+2。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spell of Wind", "强风咒", Language.ChineseSimplified);
            RegisterAbility("Spell of Wind", "强风咒");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a spell bearing this sigil is played, all cards on your side of the board will become airborne.", "当使用带有该印记的法术时，持牌人侧牌桌上的所有卡牌将获得空袭能力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ruckus", "时针顺碎语", Language.ChineseSimplified);
            RegisterAbility("Ruckus", "时针顺碎语");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil is played, all cards on the board will move clockwise.", "当[creature]被使用时，牌桌上所有卡牌均会按顺时针方向移动。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spell of Water", "净水咒", Language.ChineseSimplified);
            RegisterAbility("Spell of Water", "净水咒");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a spell bearing this sigil is played, every card on the board will have their sigils erased.", "当带有该印记的咒语牌被使用时，牌桌上所有卡牌的印记将被清除。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spell of Flame", "火焰咒", Language.ChineseSimplified);
            RegisterAbility("Spell of Flame", "火焰咒");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Select a side of the board, every card on that side will lose 2 health (without perishing) and have their attack increased by 1.", "选择牌桌一侧，该侧所有卡牌生命值减少2点（但不会阵亡）且攻击力增加1点。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Set Ablaze", "点燃", Language.ChineseSimplified);
            RegisterAbility("Set Ablaze", "点燃");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Every card on the opposite side will lose 2 health (without perishing) and have their attack increased by 1.", "对方所有卡牌将失去2点生命值（但不会阵亡）且攻击力增加1点。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "One Time Spell", "一次性法术", Language.ChineseSimplified);
            RegisterAbility("One Time Spell", "一次性法术");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When the turn ends, a spell bearing this sigil will perish and be removed from your deck.", "当回合结束时，带有该印记的法术会阵亡并从你的牌组移除。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Vase of Greed", "贪欲之壶", Language.ChineseSimplified);
            RegisterAbility("Vase of Greed", "贪欲之壶");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a spell bearing this sigil is played, discard your current hand and draw 4 new cards.", "当带有该印记的法术牌被使用时，弃掉当前手牌并抽取4张新牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gnomification", "侏儒化", Language.ChineseSimplified);
            RegisterAbility("Gnomification", "侏儒化");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Select a slot to summon a Gnome card in.", "选择一个空位召唤侏儒卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cursed", "诅咒", Language.ChineseSimplified);
            RegisterAbility("Cursed", "诅咒");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a spell bearing this sigil is played, it will immediately perish.", "带有该印记的法术牌打出时会立即阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Inspiration", "灵感", Language.ChineseSimplified);
            RegisterAbility("Inspiration", "灵感");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Select a card to give Airborne + Looter for 1 turn.", "选择一张卡牌使其获得空袭与劫掠者效果，持续1回合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Calculus", "微积", Language.ChineseSimplified);
            RegisterAbility("Calculus", "微积");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Select a card to swap stats.", "选择一张卡牌交换其属性。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Rage", "狂怒", Language.ChineseSimplified);
            RegisterAbility("Rage", "狂怒");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Select a card to kill. The opponent of the card will recieve damage equal to the amount of health the card had.", "选择一张卡牌消灭。该卡牌的对手本体将受到等同于被消灭卡牌生命值的伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fireball", "火球术", Language.ChineseSimplified);
            RegisterAbility("Fireball", "火球术");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Select a card to take 2 damage.", "选择一张卡牌使其受到2点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cold to the Touch", "冰封之触", Language.ChineseSimplified);
            RegisterAbility("Cold to the Touch", "冰封之触");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigils attack another card, that card will lose 1 attack.", "[creature]攻击其他卡牌时，被攻击卡牌会失去1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Frozen", "冰封", Language.ChineseSimplified);
            RegisterAbility("Frozen", "冰封");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of each turn, this card will lose its frozen health. When all frozen health is depleted, or this card is struck, it will defrost.", "每回合结束时，该卡牌会失去冰封生命值。当冰封生命值耗尽或该卡牌受到攻击时，将解除冰封状态。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Strong Pull", "强力牵引", Language.ChineseSimplified);
            RegisterAbility("Strong Pull", "强力牵引");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Every opposing card will be inclined to hit this card instead of any other.", "所有敌方卡牌都会优先攻击此卡而非其他目标。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Displacement", "置换反应", Language.ChineseSimplified);
            RegisterAbility("Displacement", "置换反应");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil is drawn, another of this card will be added randomly into your deck", "抽到[creature]时，会随机将一张同名卡牌加入你的牌组。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gem Reckoning", "宝石清算", Language.ChineseSimplified);
            RegisterAbility("Gem Reckoning", "宝石清算");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of each turn, if the owner of this card does not control any gems, the leftmost card in your hand is discarded.", "每回合结束时，若持牌人未打出任何宝石，则弃置你手牌最左侧的卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mox Power", "玛珂力量", Language.ChineseSimplified);
            RegisterAbility("Mox Power", "玛珂力量");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the number of Mox Cards that the owner has on their side of the table.", "该印记代表的数值等于持牌人侧牌桌上存在的玛珂牌数量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mox Health", "玛珂生命", Language.ChineseSimplified);
            RegisterAbility("Mox Health", "玛珂生命");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the number of Mox Cards that the owner has on their side of the table.", "该印记代表的数值等于持牌人侧牌桌上存在的玛珂牌数量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Candle Proximity", "烛火临近", Language.ChineseSimplified);
            RegisterAbility("Candle Proximity", "烛火临近");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to how close this card is to the Candle on the table.", "该印记代表的数值等于此卡牌与桌上蜡烛之间的距离。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Murder Power", "杀戮之力", Language.ChineseSimplified);
            RegisterAbility("Murder Power", "杀戮之力");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal the total amount of power on the opponents board.", "该印记代表的数值等于对手牌桌上所有造物的力量总和。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to half the total amount of power on your board.", "该印记代表的数值等于你牌桌上所有造物力量总和的一半。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spell Power", "法术力量", Language.ChineseSimplified);
            RegisterAbility("Spell Power", "法术力量");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to total amount of spells played during this current dungeon.", "该印记代表的数值等于当前地牢中已施放的法术总数。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to total amount of spells played during the battle.", "该印记代表的数值等于战斗期间已施放的法术总数。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Counterbattery Power", "反击力量", Language.ChineseSimplified);
            RegisterAbility("Counterbattery Power", "反击力量");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the amount of damage taken by you this battle, divided by two.", "该印记代表的数值等于本场战斗你受到的总伤害值除以二。", Language.ChineseSimplified);
            foreach (Ability ability in Enum.GetValues(typeof(Ability)))
            {
                var info = AbilitiesUtil.GetInfo(ability);
                if (info == null || info.rulebookName == null || Abilities.ContainsKey(info.rulebookName))
                {
                    Debug.LogWarning($"Ability {ability} 没有有效的info，已跳过。");
                    continue;
                }
                if (info.rulebookName == null || Abilities.ContainsKey(info.rulebookName))
                {
                    Debug.LogWarning($"Ability {ability} 没有有效的rulebookName，或本身已注册，已跳过。");
                    continue;
                }
                RegisterAbility(info.rulebookName, Localization.Translate(info.rulebookName));
            }

            // Card
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mox", "玛珂", Language.ChineseSimplified);
            RegisterCard("Mox", "玛珂", "its a me moxio", "是我，玛珂奥");
            RegisterCard("Ruby Mox", "红宝石玛珂", "its a me moxio", "是我，玛珂奥");
