            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WHAT? BUT DON'T I GET TO LEAVE?", "什么？难道我不能离开吗？", Language.ChineseSimplified);
