            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Did you expect me to restore your [c:g1]life painting[c:]?", "你以为我会修复你的[c:g1]生命画作[c:]？", Language.ChineseSimplified);
