                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The pact takes its effect on your " + kvp.Key + "..", "契约正在对你的" + kvp.Value + "生效……", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The sigil, replaced, for better or for worse.", "印记已被替换，福祸难料。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g3]Hmm..?[c:] [c:g1]Another[c:] [c:g3]student[c:] [c:g1]here?[c:]", "[c:g3]唔…？[c:][c:g1]又来[c:][c:g3]一个[c:][c:g1]学徒？[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g3]Have[c:] [c:g1]you[c:] [c:g3]come[c:] [c:g1]to[c:] [c:g3]assist[c:] [c:g1]my[c:] [c:g3]studies?[c:]", "[c:g3]是[c:][c:g1]来[c:][c:g3]协助[c:][c:g1]我[c:][c:g3]研习[c:][c:g1]的吗？[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g3]Hmm..[c:] [c:g1]I[c:] [c:g3]think[c:] [c:g1]it's[c:] [c:g3]time[c:] [c:g1]to[c:] [c:g3]play[c:] [c:g1]some[c:] [c:g3]more...[c:]  [c:g2]forbidden[c:] [c:g1]books[c:]", "[c:g3]唔……[c:][c:g1]是时候[c:][c:g3]再[c:][c:g1]玩些……[c:][c:g3]禁书[c:][c:g1]了[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g3]An[c:] [c:g1]excellent[c:] [c:g3]portrait[c:] [c:g1]by[c:] [c:g3]the[c:] [c:g1]Master.[c:]", "[c:g3]大师[c:][c:g1]的[c:][c:g3]杰作[c:][c:g1]肖像[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g3]Looks[c:] [c:g1]like[c:] [c:g3]you[c:] [c:g1]got[c:] [c:g3]a[c:] [c:g1]hound[c:] [c:g3]of[c:] [c:g1]your[c:] [c:g3]own![c:]", "[c:g3]看来[c:][c:g1]你[c:][c:g3]也[c:][c:g1]有[c:][c:g3]自己[c:][c:g1]的[c:][c:g3]猎犬[c:][c:g1]了[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g3]Oh[c:] [c:g1]yes,[c:] [c:g3]I[c:] [c:g1]do[c:] [c:g3]love[c:] [c:g1]a[c:] [c:g3]good[c:] [c:g1]book.[c:]", "[c:g3]啊[c:][c:g1]没错，[c:][c:g3]我[c:][c:g1]确实[c:][c:g3]喜欢[c:][c:g1]读[c:][c:g3]好[c:][c:g1]书[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g3]Let[c:] [c:g1]me[c:] [c:g3]share[c:] [c:g1]some[c:] [c:g3]of[c:] [c:g1]my[c:] [c:g3]favourites[c:] [c:g1]with[c:] [c:g3]you.[c:]", "[c:g3]让[c:][c:g1]我[c:][c:g3]来[c:][c:g1]分享[c:][c:g3]几本[c:][c:g1]我[c:][c:g3]最爱[c:][c:g1]的[c:][c:g3]作品[c:][c:g1]吧[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Just imagine.. You are an ice cube, slowly melting in a hot pan..", "……想象你是一个冰块，在热锅上慢慢融化……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "These enchanted spears won't just damage your cards..", "这些附魔长矛可不只是摧毁你的卡牌……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hah! Using one of my own spells against me?", "哈！用我自己的法术来对付我？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Don't you think those cards are a little too powerful?", "你不觉得这些卡牌有点太强了吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That's better.", "这样好多了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g1]How did you get that?[c:]", "[c:g1]你是怎么得到这个的？[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, ".. Where on earth is my [c:g1]Goo Mage[c:]?", "……我的[c:g1]黏液法师[c:]到底在哪？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Have your.. Modifications removed him?", "莫非是你的…模组把他移除了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This tampering has gone on for far too long. I shall not allow it anymore.", "这般篡改持续太久了。我绝不会再容忍。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When we face off, I won't be holding myself back.", "待我们对决时，我绝不会手下留情。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g1]Halt![c:] [c:g2]A[c:] [c:g1]challenger[c:] [c:g2]dare[c:] [c:g1]approach?![c:]", "[c:g1]止步！[c:][c:g2]竟有[c:][c:g1]挑战者[c:][c:g2]胆敢[c:][c:g1]近前？！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g1]Quaint,[c:] [c:g2]I[c:] [c:g1]shall[c:] [c:g2]make[c:] [c:g1]mince[c:] [c:g2]meat[c:] [c:g1]out[c:] [c:g2]of[c:] [c:g1]you.[c:]", "[c:g1]可笑，[c:][c:g2]我[c:][c:g1]必将[c:][c:g2]把[c:][c:g1]你[c:][c:g2]剁[c:][c:g1]成[c:][c:g2]肉[c:][c:g1]酱[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g1]Irritating,[c:] [c:g2]you[c:] [c:g1]persist.[c:] [c:g2]You[c:] [c:g1]have[c:] [c:g2]bound[c:] [c:g1]me[c:] [c:g2]to[c:] [c:g1]excert[c:] [c:g2]my[c:] [c:g1]blade.[c:]", "[c:g1]恼人的是，[c:][c:g2]你[c:][c:g1]执意如此。[c:][c:g2]你[c:][c:g1]已[c:][c:g2]逼得[c:][c:g1]我[c:][c:g2]必须[c:][c:g1]出剑。[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g1]Eh?[c:] [c:g2]Some[c:] [c:g1]sort[c:] [c:g2]of[c:] [c:g1]immitation[c:] [c:g2]of[c:] [c:g1]me?[c:]", "[c:g1]嗯？[c:][c:g2]某种[c:][c:g1]拙劣地[c:][c:g2]模仿[c:][c:g1]我[c:][c:g2]的[c:][c:g1]东西[c:][c:g2]吗？[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "c:g1]Pathetic.[c:] [c:g2]My[c:] [c:g1]Drake[c:] [c:g2]was[c:] [c:g1]much[c:] [c:g2]grander[c:] [c:g1]than[c:] [c:g2]that.[c:]", "[c:g1]可悲。[c:] [c:g2]我的[c:][c:g1]巨龙[c:][c:g2]可比[c:][c:g1]那个[c:][c:g2]宏伟[c:][c:g1]多了。[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "W-What? A challenger?", "什…什么？挑战者？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "FINALLY!! STIMULATION!", "终于！！来点刺激的！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'M STILL NOT STIMULATED ENOUGH!!", "我还不够兴奋！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I HAVE THE URGE TO SEARCH A CARD PILE!", "我忍不住想翻卡堆了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'M GROWING IMPATIENT!\nI NEED MORE STIMULATION!!", "我已经失去耐心了！\n我需要更多刺激！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WOW!! I REMEMBER BREWING THAT!", "哇哦！！我记得调制过这个！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "HEY.. YOU DON'T HAVE ANY CARDS LEFT?", "嘿……你一张牌都不剩了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "BORING!", "无聊！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WOW! COOL CARD!!", "哇！好酷的卡牌！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "FINDERS KEEPERS!", "谁捡到归谁！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm afraid you've doomed us all, challenger..", "恐怕你把我们所有人都祸害了，挑战者……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Perhaps I am slowing down with age..", "或许我确实是年岁渐长，动作迟缓了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I must use the brush to my advantage yet again.", "我必须再次动用画笔的力量了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You have impressed me, you truly have..", "你令我刮目相看，确实如此……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "2 lives down now.", "已失两条性命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Unfortunately for you, I still have a few tricks up my sleeve.", "可惜你算漏了，我尚有几招未出。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I could never allow myself to go down that easily.", "我绝不会让自己这么轻易倒下。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Utter folly.. Getting outside assitance, are we?", "愚蠢至极……竟寻求外援？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Free Legs", "自由之足", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Unsealed Body", "解封之躯", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Released Arms", "解放之臂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Unleashed Tyrant", "解缚的暴君", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Just what do you think you're doing?", "你以为你在干什么？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Do not.", "住手", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Trying to pull a trick as dirty as that..", "竟敢耍这种下作把戏", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "...", "……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Upsetting. I seem to be losing.", "真令人沮丧。我似乎要输了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "How about a new rule.", "不如来条新规则。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Everytime [c:g2]you[c:] play a mox, [c:g2]I[c:] restore 1 health.", "每当[c:g2]你[c:]打出玛珂牌，[c:g2]我[c:]就恢复1点生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Frustrating. I must use my last resort.", "太糟了。我不得不用最后手段了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g1]We[c:] [c:g2]have[c:] [c:g3]become[c:] four!", "[c:g1]我们[c:][c:g2]成[c:][c:g3]了[c:]四个！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]Hmm?[c:] [c:g3]An[c:] [c:g2]opponent..?[c:]", "[c:g2]嗯？[c:][c:g3]一个[c:][c:g2]对手…？[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]I[c:] [c:g3]haven't[c:] [c:g2]had[c:] [c:g3]a[c:] [c:g2]duel[c:] [c:g3]in[c:] [c:g2]a[c:] [c:g3]century![c:]", "[c:g2]我[c:][c:g3]已经[c:][c:g2]有[c:][c:g3]一个[c:][c:g2]世纪[c:][c:g3]没有[c:][c:g2]对决[c:][c:g3]过了！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]Have[c:] [c:g3]at[c:] [c:g2]thee![c:]", "[c:g2]接[c:][c:g3]招[c:][c:g2]吧！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]A[c:] [c:g3]fierce[c:] [c:g2]opponent[c:] [c:g3]you[c:] [c:g2]are![c:]", "[c:g2]真[c:][c:g3]是个[c:][c:g2]难缠[c:][c:g3]的[c:][c:g2]对手！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]I'll[c:] [c:g3]just[c:] [c:g2]have[c:] [c:g3]to[c:] [c:g2]use[c:] [c:g3]some[c:] [c:g2]more[c:] [c:g3]dirty[c:] [c:g2]tricks[c:] [c:g3]to[c:] [c:g2]beat[c:] [c:g3]you![c:]", "[c:g2]看来[c:][c:g3]得[c:][c:g2]用些[c:][c:g3]下三滥[c:][c:g2]的[c:][c:g3]手段[c:][c:g2]才能[c:][c:g3]打败[c:][c:g2]你[c:][c:g3]了！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]And[c:] [c:g3]something[c:] [c:g2]else,[c:] [c:g3]why[c:] [c:g2]not.[c:]", "[c:g2]再[c:][c:g3]来点[c:][c:g2]别的[c:][c:g3]花样[c:][c:g2]也[c:][c:g3]未尝不可[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]Ooh![c:] [c:g3]What[c:] [c:g2]a[c:] [c:g3]great[c:] [c:g2]portrait![c:]", "[c:g2]噢！[c:][c:g3]多么[c:][c:g2]绝妙的[c:][c:g3]肖像画[c:][c:g2]啊！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]A[c:] [c:g3]replica[c:] [c:g2]of[c:] [c:g3]my[c:] [c:g2]loyal[c:] [c:g3]phoenix,[c:] [c:g2]isn't[c:] [c:g3]she[c:] [c:g2]absolutely[c:] [c:g3]stunning?[c:]", "[c:g2]这是[c:][c:g3]我[c:][c:g2]忠诚的[c:][c:g3]凤凰[c:][c:g2]的[c:][c:g3]复制品，[c:][c:g2]难道[c:][c:g3]她不[c:][c:g2]美得[c:][c:g3]惊心动魄吗？[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]I[c:] [c:g3]see[c:] [c:g2]you've[c:] [c:g3]done[c:] [c:g2]a[c:] [c:g3]fair[c:] [c:g2]bit[c:] [c:g3]of[c:] [c:g2]damage![c:]", "[c:g2]我[c:][c:g3]看[c:][c:g2]你[c:][c:g3]打出[c:][c:g2]了[c:][c:g3]不少[c:][c:g2]伤害！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]Don't[c:] [c:g3]mind[c:] [c:g2]if[c:] [c:g3]I[c:] [c:g2]just[c:] [c:g3]place[c:] [c:g2]my[c:] [c:g3]thieves..[c:]", "[c:g2]不[c:][c:g3]介意[c:][c:g2]我[c:][c:g3]放些[c:][c:g2]小偷[c:][c:g3]吧……[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Out of cards..", "没牌了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dissapointing. I had high hopes for you.", "真令人失望。我曾对你寄予厚望。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Such a tiny collection of cards..", "就这点可怜的卡牌……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I highly doubt you will win.", "我严重怀疑你能赢", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "With a blaze of Magick, you end the battle. You managed to deal more damage than needed.", "在魔法的烈焰中，你终结了战斗。你造成的伤害远超所需。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The excess Magick you cast turns into [c:g3] Crystals. [c:] You may spend them at any meek store you happen to find.", "溢出的魔法能量将转化为[c:g3]水晶[c:]。你可以在遇到的任何商店中使用它们。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Welcome back.", "欢迎回来。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You're the first in a while to get this far.", "许久未见能走到这一步的挑战者了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I will now bestow upon you, the highest honour I can possibly give.", "现在，我将授予你至高无上的荣耀。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "To be painted, and immortalised forever, as a card.", "以画笔将你永恒铭刻，化作一张卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The dummy stares lifelessly back at you.", "假人毫无生气地回瞪着你。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Then, suddenly it springs to motion!", "然后，它突然动了起来！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The dummy's hinges creak back into position, and it returns to it's lifeless stare.", "假人的铰链吱吱嘎嘎地回归原位，恢复了毫无生气的茫然凝视状态。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "So you finally arrive..", "你终于来了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hopefully you have prepared yourself well enough to face the Scrybe of Magicks.", "但愿你已经做好充分准备来面对魔法冥刻者了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This will be your final exam.", "这将是你最终的考验", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Let us dance.", "该我们起舞了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]You[c:] are finally here.", "[c:g2]你[c:]终于现身了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I hope you realise that this is truly it.", "希望你明白这次是真正的终局", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You may have removed my student's battles,", "你或许移除了我学生的战斗", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You may have tampered with my entire game..", "你或许篡改了我的整个游戏", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It is time.", "是时候了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Greetings, challenger.", "向你问候，挑战者。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It appears that all of your mox has been erased.", "看来你的所有玛珂都被抹除了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Curiosity killed the cat..", "好奇心害死猫……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's a shame you got defeated so early..", "真遗憾你这么快就败下阵来……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's a shame you got defeated so easily.", "真遗憾你如此轻易就被击败了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's a shame you got defeated so far in.", "真遗憾你在深入之际落败。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I had such high hopes, but you lost it all..", "我曾寄予厚望，可你辜负了一切……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It seems my slime mage got the best of you. How dissapointing.", "看来我的黏液法师技高一筹。真令人失望。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It looks like my pike mage tactically took you out..", "看来我的长矛法师用战术解决了你……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Looks like that lonely mage got to you..", "看来那位孤独的巫师得手了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Though it does not matter.", "不过无所谓了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I do not have any more use for you.", "你对我已无价值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Please, take your leave.", "请自行离开吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Looking for something?", "在找什么东西吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm afraid the object you seek has been.. [c:g1]relocated[c:]..", "恐怕你要找的东西已经……[c:g1]转移他处[c:]……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Look at what you've unleashed..", "看看你都释放了些什么……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Was it truly worth finding?", "这真的值得寻找吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hmph.. I did not intend for you to get out here..", "哼……我本没打算让你走到这一步……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I have forseen it though. Despite my best attempts you persist.", "不过我已预见了。尽管竭力阻拦，你仍执意前行。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We shall duel here..", "我们就在此决斗吧……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Though, lets up the stakes.", "但这次，我们加点赌注。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you lose here, your entire [c:g1]life painting[c:] will get bleached..", "若你败北，你的全部[c:g1]生命画作[c:]都将被漂白……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Step forth if you dare.", "若敢应战，便上前来。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You persist again?", "你还要继续？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I see..", "原来如此……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We shall duel again!", "我们再来一局！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ah.. Finally, you arrive.", "啊……你终于来了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I've been watching you from up here, for your entire journey.", "我在此处俯瞰着你全部的旅程。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You've come a long way, challenger.", "挑战者，你已跋涉甚远。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Unfortunately, that will all come to an end here.", "可惜这一切都将在此时终结。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "One last duel, to decide your fate..", "最后一战，决定你的命运……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It is time to put an end to this.. [c:g1]foolery[c:] of yours.", "是时候终结你这[c:g1]愚行[c:]了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you consider these cheats fair, then I might as well use some cheats of my own.", "若你视这些作弊手段为公平，那我不妨也动用些自己的伎俩。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Normally, I would have you expelled right on the spot.. But I'm feeling generous.", "按常理我该当场将你除名……不过今日我格外宽容。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Let us duel.", "开始对决吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Whats the matter? Stumped?", "怎么了？卡住了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Try searching around the dungeon.. There may be certain [c:g1]markings[c:] you missed..", "在地牢里再找找……你可能漏掉了一些[c:g1]标记[c:]……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hmm?", "嗯？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Are you wondering what this book is for?", "在好奇这本书的用途吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That is your [c:g1]spell book[c:]. You may use it to draw one of your [c:g3]spell cards[c:].", "这是你的[c:g1]法术书[c:]，可用它抽取一张[c:g3]法术牌[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But only [c:g1]one[c:] per turn..", "但每回合仅限[c:g1]一次[c:]……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You land in a sticky puddle of goo, stretching out into what seems like an endless void..", "你跌入一滩黏稠的黏液，延伸向看似无尽的虚空……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ruined pillars and ancient walls surround your vision, overtaken by yet more slime..", "断裂的立柱与古老墙壁充斥视野，又被更多黏液吞没……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The glistening heavens above shine brightly unto you.", "头顶闪烁的天穹向你投下耀眼辉光", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You arrive in the..", "你抵达了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A sudden influx of heat surrounds you..", "突如其来的热浪将你包围……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dimly lit and dingy chambers spread out as far as you can see.", "昏暗肮脏的厅室在视野中无尽延展", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Long forgotten echoes of ancient sparring fill the room.", "远古比斗的回响在厅内萦绕不绝", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You find yourself in the..", "你发现自己置身于……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A dusty old smell fills your nostrills..", "陈腐的尘埃气息涌入鼻腔……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hundreds of walls containing books, laying untouched for eons, fill the quiet landscape.", "沉寂的景致中，无数书墙已尘封万古", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ethereal runes, containing lost, forbidden knowledge surround you.", "蕴含禁忌知识的缥缈符文将你环绕", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You awake in the..", "你苏醒于……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hmmm. It seems a new student is woven onto my canvas.", "嗯。看来又有新学徒被绘入我的画布了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you look behind yourself, you may see a [c:g1]shop[c:].", "若你回头望去，或能看见一间[c:g1]商店[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You may view your deck and rearange your side deck.", "你可以查看牌组并调整副牌组。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Though, do not be alarmed. You may find a shop later on your adventures.", "不过莫要惊慌，冒险途中自会遇到商店。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you are ready to proceed, you may step forth and enter into the unknown.", "若你已准备就绪，便可踏入未知之境。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hmm.. A young student appears,", "唔……又来了个年轻学徒，", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "So foolish, your predesscor didn't even manage to get past my [c:g1]Goo Mage[c:].", "愚不可及，你的前辈连我的[c:g1]黏液法师[c:]都未能击败。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ah well.. Maybe you will manage such a feat.", "也罢……或许你能创造此等壮举。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Another appears.", "又来一个。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Maybe you will manage to get further than those before you.", "兴许你能比前人走得更远。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You young understudies still have much to learn.", "你们这些年轻学徒还有太多要学。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ah. I had expectations for my previous mage,", "啊，我对前任法师寄予厚望，", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "They even managed to get past my [c:g2]Pike Mage[c:].", "他们甚至击败了我的[c:g2]长矛法师[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A great feat, it seems now you must follow in their footsteps...", "了不起的壮举，现在该你追随其脚步了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "So, yet another steps up to be my student.", "那么，又一位自荐成为我的学徒之人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You must learn yourself, to overcome my many obstacles and challenges.", "你需自行领悟，方能跨越我设下的重重考验。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you do succeed, I shall bestow upon you a great honour..", "若你真能成功，我自会赐予你无上荣耀……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hmm.. Something feels off..", "唔…有些不对劲……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Have you [c:g1]modded[c:] my game, challenger?", "挑战者，你可是[c:g1]修改了[c:]我的游戏？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hmph! How disrespectful!", "哼！何等无礼！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Was my game not enough for you?", "莫非我的游戏还不足以满足你？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Alas, I have no choice but to allow it..", "唉，我也只能听之任之……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Just what is going on here..?", "这是怎么回事…？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "These cards,", "这些卡牌，", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Did that [c:g2]foul little..[c:] get in here..?", "那个[c:g2]肮脏的小东西…[c:]闯进来了…？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I believe you are in the wrong place, challenger.", "挑战者，你恐怕来错地方了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hold still.", "别动", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You are a simpleton for expecting scales.", "指望天平？你可真天真。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This is a simple race to 0.", "这不过是场看谁先归零的简单竞赛。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "NOW ITS TIME FOR ME TO PRACTICE MY BREWING MAGICKS!!", "现在该让我施展酿造魔法了！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "IT IS DONE!!! GO TAKE IT!!", "完成了！！！快去拿吧！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "GO!!", "快走！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]GET OUT OF HERE!![c:]", "[c:g2]给我滚出去！！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WOAH! LOOKS LIKE SOMETHING YOU PUT INTO THE POTION MADE IT A BIT.. [c:g1]PRICY[c:]..", "哇哦！看来你往药剂里加的东西让它有点……[c:g1]贵[c:]……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "W-What? Am I finally free?", "什…什么？我终于自由了吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A break from that endless darkness?", "从那无尽黑暗中解脱了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nevermind.. It's just potion making..", "算了…不过是调制药水罢了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh, Hi Challenger!!", "噢，你好啊挑战者！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wow, someone new!!", "哇，新面孔！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Just give me two cards of yours and we'll make a potion out of them!!", "给我两张你的卡牌，咱们就能调制药水啦！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You can later give this potion to your cards and they gain some silly effects!", "之后你可以把药水赋予卡牌，让它们获得些有趣的效果！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Come on!! It will be fun!!", "来吧！！可好玩了！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And stimulating..", "而且超刺激……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "So stimulating", "太刺激了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hello there challenger!!", "嘿你好啊挑战者！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You know how this works now", "现在你懂流程了吧", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Just give me two cards and we'll make a potion!", "随便给我两张卡就能调制药水！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Woah.. What.. Is this place?", "哇哦…这是…什么地方？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "So bright..", "好亮啊……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh, Hi there!!", "噢，你好呀！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Let's make some potions!!", "咱们来调制药水吧！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Just hand over two cards and we'll be done with it..", "递给我两张卡就完事……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WAIT!! YOUR SPELLBOOK IS FULL", "等等！！你的法术书满了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "GET RID OF ONE OF YOUR SPELLS, OR YOU WONT BE ABLE TO TAKE OUR POTION!!", "先丢弃一个法术，否则你拿不走我们的药水！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your card's cost has been changed..", "你的卡牌消耗已经更改……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Do you have a card that has a cost you are unsatisfied with?", "你有对费用不满意的卡牌吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No. It appears not.", "看来没有。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "LETS SEE HERE..", "让我看看……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "HMM?", "嗯？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WHAT DO YOU THINK?", "你觉得呢？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WELL?", "怎么样？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WHAT WOULD THE MASTER THINK?", "大师会怎么想？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WOULD THE MASTER BE PROUD?", "大师会为我骄傲吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "IS IT GOOD?", "这样行吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "GRREETINGS CHALLENGER!", "向挑战者致意！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YOU'RE ALREADY HERE?", "你竟已至此？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I DON'T REALLY RECALL US BATTLING..", "我确实不记得与你交战过……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WELL ANYWAYS.. WHAT CARD WHOULD I PRACTICE ON?", "总之…我该拿哪张牌练习？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "IT WOULD SEEM THAT YOU HAVE DISGRACED ME!", "看来你已使我蒙羞！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "BUT IT IS ALRIGHT, I FORGIVE YOU.", "不过无妨，我宽恕你", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SAY, CHALLENGER, I WOULD LIKE TO PRACTICE MY [c:g2]ART[c:]..", "听着挑战者，我想练习我的[c:g2]美术[c:]……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "ONE DAY I MIGHT EVEN BE AS GOOD AS THE MASTER!", "终有一日我将比肩大师！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YOU WOULDN'T MIND GIVING ME ONE OF YOUR THERE CARDS, RIGHT?", "你不介意给我张牌对吧？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WHAT CARD SHOULD I PRACTICE ON?", "我该练习哪张牌？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'LL NEED A CARD TO HONE MY SKILLS ON.", "我需要张牌来磨砺技艺", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I DON'T THINK WE'VE MET BEFORE!", "我们素未谋面！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "YOU ARENT SUPPOSED TO SEE ME UNTILL LATER, BUT..", "你本不该此时见我，但……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, ".. It appears you have nothing else to offer..", "..看来你已无物可供……", Language.ChineseSimplified);
            foreach (var value in Cards.Values)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your" + value + " sits on the enchanted grounds...\nYou could either leave it there, to enchant it..", "你的" + value + "正位于附魔之地……\n你可以选择让它留在那里接受附魔……", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Or you can slaughter it to improve the enchantment, dictated by the [c:g1]Ethereal Sigils[c:].", "或者根据[c:g1]虚空印记[c:]的指引，献祭它以强化附魔效果", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The choice is yours.", "选择权在你手中", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g1]Ethereal Sigils[c:] hover around the grounds.", "[c:g1]虚空印记[c:]在场地周围浮动", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You may use them to predict a possible outcome..", "你可以借此预判可能的结果……", Language.ChineseSimplified);
            foreach (var value in Cards.Values)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your " + value + " has been burdened with Glass Cannon!", "你的" + value + "被施加了玻璃大炮的诅咒！", Language.ChineseSimplified);
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your " + value + " has been burdened with Tank!", "你的" + value + "被施加了坦克的诅咒！", Language.ChineseSimplified);
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your " + value + " has been blessed!", "你的" + value + "获得了祝福！", Language.ChineseSimplified);
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your " + value + " has been burdened!", "你的" + value + "被施加了诅咒！", Language.ChineseSimplified);
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your " + value + " is now free.. But more unstable..", "你的" + value + "现已费用全免……但变得更加不稳定……", Language.ChineseSimplified);
                foreach (var kvp in Abilities)
                {
                    SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your" + value + " has been blessed with " + kvp.Key + "!", "你的" + value + "获得了" + kvp.Value + "祝福！", Language.ChineseSimplified);
                    SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your" + value + " has been burdened with " + kvp.Key + "!", "你的" + value + "被施加了" + kvp.Value + "诅咒！", Language.ChineseSimplified);
                }
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The [c:g1]Sacrifical Grounds[c:] stand before you.\nLegends tell of cards growing exponentially in power here, under the right circumstances..", "[c:g1]献祭之地[c:]矗立眼前。\n传说在特定条件下，卡牌能在此获得指数级的力量提升……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, ".. However, it appears that you don't have anything to offer to the enchanted grounds..", "……但看来你并未携带适合这片附魔之地的祭品", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Enchanted Grounds seem to be slightly improved.", "附魔之地似乎略有擢升。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Enchanted Grounds are unsatisfied..", "附魔之地显得不太满意……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Enchanted Grounds dimly light up with sacrifical energy.", "附魔之地因献祭能量而微微发亮。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Enchanted Grounds are displeased..", "附魔之地显得很不悦……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Enchanted Grounds light up with supernatural energy.", "附魔之地因超凡能量而闪耀。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Enchanted Grounds are angered..", "附魔之地被激怒了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Enchanted Grounds The Enchanted Grounds are absolutely bursting with sacrifical energy.", "附魔之地因献祭能量而剧烈翻腾。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Enchanted Grounds The Enchanted Grounds are furious.", "附魔之地震怒不已……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Eurgh.. This place is disgusting..", "呃..这地方真恶心……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Seems like I'm forced to train spellcasting in here.", "看来我被迫要在这儿练习法术了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Give me a [c:g1]spell[c:] of yours, and make it quick.", "给我一个你的[c:g1]法术[c:]，动作快点", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Greetings..", "你好……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Give me one of your [c:g1]spells[c:]", "给我一个你的[c:g1]法术[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Give me one of your [c:g1]spells[c:].", "给我一个你的[c:g1]法术[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh.. Greetings... I don't believe we've been introduced..", "哦…你好……我们似乎还没正式认识……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I've been force- er.. asked to train my spellcasting here.", "我被强制…呃…受邀来这儿练习法术", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Why don't you give me some of your [c:g1]spells[c:]?", "不如给我些你的[c:g1]法术[c:]如何？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hm? Oh, hello again.. We still haven't been formally introduced.", "嗯？哦，又见面了…我们还没正式介绍过", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'll need one of your [c:g1]spells[c:] for my training..", "我需要一个你的[c:g1]法术[c:]来训练……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh.. Hello there.", "哦…你好啊", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The master asked me to practice my spellcasting..", "大师让我来练习法术……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh.. Spellcasting. Right..", "哦…法术练习。对……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "What? Oh.. This must be.. [c:g3]his[c:] area..", "什么？哦…这一定是…[c:g3]他[c:]的地盘……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Well, anyway.. The master asked me to train my spellcasting.", "好吧不管怎样…大师让我来练习法术……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hmm..?", "唔……？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Here already? We haven't even battled..", "这么快？我们还没对战过……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Well anyways, just hand over a [c:g1]spell[c:].", "总之，交出一个[c:g1]法术[c:]就行……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh.. You don't have any spells?", "哦…你一个法术都没有吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I guess I could give you one of my upgraded ones.. Hopefully they're good.", "也许我可以给你一个强化过的…希望我的这些足够强……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Now your spell will last forever..", "现在，你的法术将永世长存……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "So, you truly wish to release that tyrant..", "所以，你当真要释放那个暴君……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I hope you understand the type of power you are dealing with..", "希望你明白自己正在染指何种力量……", Language.ChineseSimplified);
            foreach (var kvp in Cards)
            {
                foreach (var kvp2 in Abilities)
                {
                    SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your " + kvp.Key + " is now free of its " + kvp2.Key + "...", "你的" + kvp.Value + "现已摆脱了" + kvp2.Value + "的束缚……", Language.ChineseSimplified);
                }
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hmm.. The essence of that sigil does not resonate with your card..", "唔……该印记的精髓与你的卡牌并不契合……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "..It appears nothing was gained", "……看来一无所获", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The essence of the sigil has imbued the card with more power.", "印记的精髓使卡牌获得了更多能量", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The essence of the sigil has imbued the card with more gourd.", "印记的精髓使卡牌获得了更多脑瓜", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The essence of the sigil has imbued the card with more strength.", "印记的精髓使卡牌获得了更多力量", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hmph.. My bleach.. failed?", "哼……我的漂白剂……失效了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Was this your doing, challenger?", "这是你的手笔吗，挑战者？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hmph..", "哼……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Select a card to imbue with a lost sigils strength..", "选择一张卡牌漂白印记，并灌注等同于已失去印记的力量……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Here I will allow you to [c:g1]bleach[c:] a card of one of its sigils..", "在此我将允许你[c:g1]漂白[c:]一张卡牌的某个印记……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When bleached, the card will imbue the power of its lost sigil,", "被漂白后，该卡牌将继承已失去印记的力量，", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "and get much [c:g2]stronger[c:] depending on the sigils raw strength.", "并根据印记的原始强度变得[c:g2]更强[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ah, but your [c:g1]spell book[c:] is full...", "啊，但你的[c:g1]法术书[c:]已经满了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You step foot into a small store.. A brief respite from your winding travels.", "你踏入一家小店…漫长旅途中的短暂休憩", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You step foot into a small store...", "你踏入一家小店……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your [c:g1]deck[c:] is stored in this pile.. ", "你的[c:g1]牌组[c:]存放在这堆卡牌中……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, ".. And your [c:g1]side deck[c:] is stored here, where you can adjust it as you see fit. ", "……而你的[c:g1]副牌组[c:]存放在此，可随时调整", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, ".. Hmmph, I guess it was not meant to be..", "…哼，看来缘分未到……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Twisted and warped by the fungi, a new card emerges. Only through pain can one grow…", "在真菌的扭曲与侵蚀下，一张新卡牌诞生了。唯有经历痛苦，方能成长……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Now the mushrooms require a card that they will attach.. Increasing the stats of the host.", "现在蘑菇们需要一张卡牌来寄生…从而提升宿主的属性", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You encounter the abnormally disgusting fungi that infests this dungeon.", "你遭遇了侵染地牢的异常恶心的真菌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It is said to possess strange properties..", "据说它们具有奇特的性质……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The mushrooms yearn for a card they can use as a.. [c:g1]host[c:] of sorts.", "这些蘑菇渴望找到一张能作为…[c:g1]宿主[c:]的卡牌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "One that they will attack to.", "它们将依附其上", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You encounter the disgusting fungi yet again..", "你再次遭遇了那些恶心的真菌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The fungi yearn for a card they can use as a [c:g1]host[c:].. to attach to.", "真菌渴求着能作为[c:g1]宿主[c:]…来依附的卡牌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Yet... It seems you do not have two cards worthy of the fungi's touch. Wearisome.", "然而……你似乎没有两张值得真菌触碰的卡牌。真扫兴", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You have the honours of naming it.", "命名权就交给你了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Finished?", "完成了吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The [c:g1]cost[c:] and [c:g2]portrait[c:] I will handle myself..", "[c:g1]费用[c:]和[c:g2]肖像[c:]由我来处理", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It was nice meeting you, my student.", "很高兴认识你，我的学徒", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But this is where we say farewell..", "但此刻我们该道别了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Just hold still for a second.", "保持片刻别动", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I will base it off of cards in your deck.", "我会根据你牌组里的卡牌来决定。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "First, the stats.", "首先，是属性。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The [c:g1]sigils[c:].", "然后是[c:g1]印记[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hmm.. You're out of paint.", "唔……你的颜料用完了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You can bleach your drawing.. or consider it finished.", "你可以漂白画作……或者就此收笔。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A neccesary part of each student's training is art..", "每位学徒的必修课都包含美术训练……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Let's start with the portrait..", "先从肖像画开始吧……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You can use the bleach on the left to wipe your work..", "左侧的漂白剂可用来擦除作品……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You are currently holding a [c:g1]replica brush[c:].. If you feel you need a smaller one, you can grab the one on the right.", "你正握着[c:g1]仿制画笔[c:]……若需要更细的笔，可取用右侧那支", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Use that brush in the back to change your color.", "用后排那支笔切换颜色", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My pupil's portraits never fail to amuse me..", "学徒们的肖像总能取悦我……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Now let's choose which card's [c:g1]stats[c:] to imbue.", "现在选择要为哪张卡的[c:g1]属性[c:]注入魔力", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Now, the [c:g1]sigils[c:].", "现在，是[c:g1]印记[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ah, but your spell book is full..", "啊，但你的法术书已经满了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Either discard this one...", "要么丢弃这张……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Or, remove one of your spells..", "要么移除你的一张法术……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You don't have any cards to use that [c:g1]potion[c:] on.", "没有可施加[c:g1]药剂[c:]的卡牌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You won't have any cards to use that spell on.", "你已无卡牌可施法。", Language.ChineseSimplified);
            foreach (var value in Cards.Values)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your " + value + " wields no mana.", "你的" + value + "未持有魔力。", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That doesn't have any mana.", "该单位未持有任何魔力。", Language.ChineseSimplified);
            foreach (var value in Cards.Values)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The " + value + " has no mana.", value + "未持有魔力。", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You can not draw mana from that.", "你无法从中汲取魔力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Through this maze of paintings and illusions, there may be some that deign to join your deck.\nChoose carefully.", "穿越这幅画作与幻象的迷宫，或有存在愿屈尊加入你的牌组\n认真选吧", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "... Curious.", "……有点意思", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I thought I had completely removed this.. [c:g2]filth[c:].", "我还以为我已经彻底清除了这…[c:g2]污秽[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You humor me challenger..", "你倒挺会逗我开心啊，挑战者……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Did you expect your measely [c:g1]hammer[c:] to work on such a creature as the [c:g1]ouroberyl[c:]?", "你以为区区[c:g1]锤子[c:]能对[c:g1]衔宝蛇[c:]起作用？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "How foolish.", "何其愚蠢。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your hammer is now [c:g1]broken[c:], and may not be used for the remainder of the battle.", "你的锤子现已[c:g1]损毁[c:]，本场战斗无法再使用。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It seems that you lost..", "看来你输了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "See this portrait?", "看到这幅肖像了吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This portrait will act as your life counter.", "这幅肖像将作为你的生命计数器。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Once all the [c:g1]m[c:][c:g2]o[c:][c:g3]x[c:] [c:g1]g[c:][c:g2]e[c:][c:g3]m[c:][c:g1]s[c:] go out.. You lose the game.", "当所有[c:g1]玛[c:][c:g2]珂[c:][c:g3]宝[c:][c:g1]石[c:]熄灭时…你就输了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Since you lost.. I will have to remove one gem.", "既然你输了…我得移除一颗宝石。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The deed has been done.", "事情已成定局。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Looks like you have been knocked down by your opponent.", "看来你被对手击倒了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Stand tall, and continue forth.", "挺直腰杆，继续前进。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It seems as though you should study up on mox.", "看来你该好好研究下玛珂了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Though you should not lose hope, as you still have 2 more lives.", "但别绝望，你还有两次机会。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You make a last effort to succeed, yet fail. It seems you lose, again.", "你做了最后挣扎却仍失败。看来你又输了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You shall continue forward.", "你应当继续前进。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It takes just one more blow for you to fall to the ground.", "再受一击你就会倒下。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Losing a life, you must continue forward.", "失去一条命后，你必须继续前进。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It seems the show shall end… for now..", "看来表演该落幕了…暂时如此……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Just what is going on here?", "这到底是怎么回事？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "'Master Magnus'..", "“至尊大师”……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "There is [c:g2]NO[c:] such master!", "根本[c:g2]没有[c:]这号大师！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Argh.. I guess, if you truly wish to play with such additions, I am truly unable to stop you.", "呃……若你执意要玩这些把戏，我确实无力阻止。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Though, I do NOT approve of this.", "但我绝不认可这种行为。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It appears that the ficticious Master Magnus has showed itself.", "看来那个虚构的至尊大师现身了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That horrid [c:g1]thing[c:] rears it's ugly head.", "那[c:g1]怪物[c:]正探出它丑陋的脑袋。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The released Master Magnus approaches.", "解缚的至尊大师正在逼近。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Careful. That horrible master is here.", "当心，那个可怕的大师来了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Master Magnus has arrived. Be cautious.", "至尊大师已至，务必小心", Language.ChineseSimplified);
            foreach (var kvp in Cards)
            {
                var key = kvp.Key;
                var value = kvp.Value;
                foreach (var kv in CardsDescription[key])
                {
                    var descKey = kv.Key;
                    var descValue = kv.Value;
                    SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You glance at a " + key + ". " + descKey, "你瞥见一张" + value + "。" + descValue, Language.ChineseSimplified);
                    SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A " + key + " appears from within the portrait. " + descKey, value + "从肖像画中浮现。" + descValue, Language.ChineseSimplified);
                    SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You gaze at a " + key + ". " + descKey, "你凝视着一张" + value + "。" + descValue, Language.ChineseSimplified);
                    SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You glance at an " + key + ". " + descKey, "你瞥见一张" + value + "。" + descValue, Language.ChineseSimplified);
                    SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "An " + key + " appears from within the portrait. " + descKey, value + "从肖像画中浮现。" + descValue, Language.ChineseSimplified);
                    SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You gaze at an " + key + ". " + descKey, "你凝视着一张" + value + "。" + descValue, Language.ChineseSimplified);
                }
            }

            // ShowThenClear
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Choose wisely.", "认真选吧", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "ARRGH!!!", "呃啊！！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g1]STRIKE![c:]", "[c:g1]攻击！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]STRIKE![c:]", "[c:g2]攻击！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Au Revoir", "永别了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "How reckless.", "真是鲁莽。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hmm.. I see..", "唔……我明白了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But you'll [c:g2]NEVER[c:] delete me, the Scrybe of Magicks!", "但你[c:g2]永远[c:]无法删除我——魔法冥刻者！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "GRRAHH!!", "嘎啊啊啊！！", Language.ChineseSimplified);
            Dictionary<GemType, string> gemTypesEn = new Dictionary<GemType, string>
            {
                { GemType.Orange, "橙色" },
                { GemType.Blue, "蓝色" },
                { GemType.Green, "绿色" },
            };
            Dictionary<GemType, string> gemTypes = new Dictionary<GemType, string>
            {
                { GemType.Orange, "橙色" },
                { GemType.Blue, "蓝色" },
                { GemType.Green, "绿色" },
            };
            foreach (var gemType in gemTypes.Keys)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You don't have two " + HintsHandler.GetColorCodeForGem(gemType) + gemTypesEn[gemType] + "</color> gems to play that.", "你的" + HintsHandler.GetColorCodeForGem(gemType) + gemTypes[gemType] + "</color>玛珂不足两颗，无法使用此牌", Language.ChineseSimplified);
                foreach (var value in Cards.Values)
                {
                    SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You'll need two " + HintsHandler.GetColorCodeForGem(gemType) + gemTypesEn[gemType] + "</color> gems to play your " + value + ".", "需要两颗" + HintsHandler.GetColorCodeForGem(gemType) + gemTypes[gemType] + "</color>玛珂才能使用你的" + value, Language.ChineseSimplified);
                    SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You don't have enough " + HintsHandler.GetColorCodeForGem(gemType) + gemTypesEn[gemType] + "</color> gems on the board to play that " + value + ".", "牌桌上的" + HintsHandler.GetColorCodeForGem(gemType) + gemTypes[gemType] + "</color>玛珂数量不足，无法使用这张" + value, Language.ChineseSimplified);
                }
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You need two " + HintsHandler.GetColorCodeForGem(gemType) + gemTypesEn[gemType] + "</color> gems on the board.", "牌桌上需要有两颗" + HintsHandler.GetColorCodeForGem(gemType) + gemTypes[gemType] + "</color>玛珂", Language.ChineseSimplified);
            }

            // ShowMessage
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "whats up", "咋了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My [c:g1]Goo Mage[c:] falls by your hands.. I expected more from him..", "我的[c:g1]黏液法师[c:]败于你手……我本对他期望更高些……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hmph.. My [c:g1]Goo Mage[c:] dissapoints me again..", "哼……我的[c:g1]黏液法师[c:]又一次让我失望……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Yet again, my pathetic [c:g1]Goo Mage[c:] falls..", "再一次，我可怜的[c:g1]黏液法师[c:]倒下了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g1]What..?[c:] [c:g2]Me,[c:] [c:g1]defeated..?[c:]", "[c:g1]什么……？[c:][c:g2]我，[c:][c:g1]败了……？[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g1]Blargh..[c:] [c:g2]I've[c:] [c:g1]been[c:] [c:g2]taken[c:] [c:g1]out[c:] [c:g2]by[c:] [c:g1]you??[c:] ", "[c:g1]呃啊……[c:][c:g2]我[c:][c:g1]被[c:][c:g2]你[c:][c:g1]击败了？？[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g1]Impossible..[c:] [c:g2]I've[c:] [c:g1]been[c:] [c:g2]defeated..[c:]", "[c:g1]不可能……[c:][c:g2]我[c:][c:g1]居然[c:][c:g2]输了……[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ahh.. I see you've defeated my [c:g2]Pike Mage[c:]..", "啊……看来你打败了我的[c:g2]长矛法师[c:]……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My [c:g2]Pike Mage[c:] falls.. Dissapointing.", "我的[c:g2]长矛法师[c:]倒下了……真令人失望", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'll admit challenger.. You've gotten farther than most.", "我承认挑战者……你比大多数人都走得更远", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]A[c:] [c:g3]fantastic[c:] [c:g2]duel[c:] [c:g3]we[c:] [c:g2]had![c:]", "[c:g2]真是[c:][c:g3]一场[c:][c:g2]精彩[c:][c:g3]对决[c:][c:g2]啊！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]'Twas[c:] [c:g3]an[c:] [c:g2]excellent[c:] [c:g3]battle.[c:]", "[c:g2]这[c:][c:g3]是[c:][c:g2]场[c:][c:g3]绝妙[c:][c:g2]战斗[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]Great[c:] [c:g3]fighting,[c:] [c:g2]young[c:] [c:g3]mage.[c:]", "[c:g2]打[c:][c:g3]得[c:][c:g2]漂亮[c:][c:g3]年轻法师[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Well challenger.. Now that you have defeated the [c:g3]Lonely Mage[c:]. You may now face me.", "很好挑战者……既然你已击败[c:g3]孤独巫师[c:]，现在可以面对我了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You've defeated all my students.. I will await your arrival.", "你已击败我所有学徒……我将恭候你的到来", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My [c:g3]Lonely Mage[c:] falls.. this is unexpected.. But now, I await your arrival.", "我的[c:g3]孤独巫师[c:]败北了……出乎意料……但现在，我静候你的到来", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g3]An[c:] [c:g1]excellent[c:] [c:g3]fight![c:]", "[c:g3]真是[c:][c:g1]场[c:][c:g3]精彩[c:][c:g1]较量！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g3]Despite[c:] [c:g1]my[c:] [c:g3]deadliest[c:] [c:g1]books,[c:] [c:g3]you[c:] [c:g1]still[c:] [c:g3]won![c:]", "[c:g3]即便[c:][c:g1]动用[c:][c:g3]最致命的[c:][c:g1]典籍，[c:][c:g3]你仍[c:][c:g1]获胜了！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g3]You[c:] [c:g1]managed[c:] [c:g3]to[c:] [c:g1]overcome[c:] [c:g3]my[c:] [c:g1]deadliest[c:] [c:g3]tomes,[c:] [c:g1]impressive![c:]", "[c:g3]你竟[c:][c:g1]能[c:][c:g3]破解[c:][c:g1]我最[c:][c:g3]致命的[c:][c:g1]魔典，[c:][c:g3]令人[c:][c:g1]印象深刻！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g3]Whoops![c:] [c:g1]Looks[c:] [c:g3]you[c:] [c:g1]need[c:] [c:g3]to[c:] [c:g1]study[c:] [c:g3]up[c:] [c:g1]on[c:] [c:g3]mox.[c:]", "[c:g3]哎哟！[c:][c:g1]看来[c:][c:g3]你[c:][c:g1]得[c:][c:g3]好好[c:][c:g1]钻研[c:][c:g3]玛珂[c:][c:g1]了[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My Pike Mage bests another opponent...", "我的长矛法师又击败了一个对手……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dissapointing...", "太令人失望了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g1]Yet[c:] [c:g2]another[c:] [c:g1]soul[c:] [c:g2]I[c:] [c:g1]claim.[c:] [c:g2]Banish[c:] [c:g1]yourself[c:] [c:g2]to[c:] [c:g1]the[c:] [c:g2]depths,[c:] [c:g1]and[c:] [c:g2]do[c:] [c:g1]not[c:] [c:g2]return.[c:]", "[c:g1]又[c:][c:g2]一个[c:][c:g1]灵魂[c:][c:g2]归[c:][c:g1]我[c:][c:g2]所有[c:][c:g1]了。[c:][c:g2]滚[c:][c:g1]去[c:][c:g2]深渊[c:][c:g1]吧，[c:][c:g2]永远[c:][c:g1]别[c:][c:g2]回来[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Yet another student, hit off guard by my Lonely Mage..", "又一位学徒，被我的孤独巫师偷袭得手……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You got so far.....", "你都走到这一步了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Weak.", "真弱。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:g2]Farewell,[c:] [c:g3]young[c:] [c:g2]mage![c:] [c:g3]'Twas[c:] [c:g2]a[c:] [c:g3]fantastic[c:] [c:g2]duel.[c:]", "[c:g2]再会了，[c:][c:g3]年轻的[c:][c:g2]法师！[c:][c:g3]真是[c:][c:g2]一场[c:][c:g3]精彩[c:][c:g2]对决。[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "~ Goo Dungeon ~", "~ 黏液地牢 ~", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "~ Lava Dungeon ~", "~ 熔岩地牢 ~", Language.ChineseSimplified);
