            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hyena", "鬣狗", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bone seeking bandits in the wilderness.", "荒野中的寻骨强盗。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Loky", "狼王洛基", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The king of the whole pack of wolves, very, very powerful.", "狼中之王，非常非常强大。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pastoral Dog", "中华田园犬", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Loyal Chinese pastoral dogs, they guard and protect their owners' houses.", "忠诚的中华田园犬，守卫和保护主人的房子。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spotted Hyena", "斑鬣狗", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spotted hyenas searching for bones, they grow stronger with their thirst for blood.", "斑鬣狗寻找骨头，它们因渴望血液而变得更强壮。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Lion", "狮子", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A strong lion with a fairly strong attack.", "攻击力相当强的健壮狮子。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Lioness", "雌狮", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The lioness guarding the lion herd are always on guard against external invasions.", "守护狮子群的母狮总是警惕外来入侵。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Lynx", "猞猁", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The solitary Lynx attacks with its sharp claws and teeth.", "孤独的猞猁用锋利的爪子和牙齿攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Puma", "美洲狮", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Strong Puma, the leader among feline animals.", "强壮的美洲狮，猫科动物中的领头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Puma Cub", "美洲狮幼崽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The immature Puma cub. It will grow into a Puma after a turn.", "幼年的美洲狮幼崽。它一回合就会长成一只美洲狮。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thunder Tiger", "雷虎", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "They came with thunder and then suddenly left.", "它随雷声而来，然后忽的离去。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Axolotl", "美西螈", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This cute little guy has a very special tail.", "这个可爱的小家伙有一条非常特别的尾巴。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Axolotl Tail", "美西螈尾巴", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Carp", "鲤鱼", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The fish in the Chinese totem is said to have unfathomable powers.", "中国图腾中的鲤鱼据说有着不可估量的力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Coral Polyp", "珊瑚虫", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "They coexist with the terrain.", "它们与地形相伴相生。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Globefish", "河豚", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Any creature that dreams of harming it will regret it.", "任何想伤害它的造物都会后悔的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Shark Egg", "鲨鱼蛋", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I've never seen a shark egg look like this before.", "我从来没见过鲨鱼蛋，原来长这样。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Squidge", "鱿鱼", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "What kind of creature will the little one grow into?", "这个小家伙会长成什么样的造物？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Poor little ones......", "可怜的小家伙们……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Joannes", "约安尼斯", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A peculiar creature that only exists in nightmares.", "一种只存在于噩梦中的奇特造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "God Joannes", "约安尼斯神", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Miemie Method", "方法放寒假", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A little cute who can write code.", "一只会写代码的小可爱。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Potato Elk", "土豆鹿", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The potatoes have sprouted.", "如今土豆已经发芽了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Potato Elk Cub", "土豆小鹿", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A deer with potatoes on its antlers, it seems that the potato is the owner.", "一只鹿角上有土豆的小鹿，看起来土豆才是它的主人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wapiti", "马鹿", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A strong and vital deer.", "强壮而有活力的鹿。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cicada", "蝉", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "They gather in groups, calling on friends, which is annoying.", "它们聚在一起，走亲访友，这很烦人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dragonfly", "蜻蜓", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dragonflies that point to where to hit.", "指哪打哪的蜻蜓。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Giant Centipede", "巨人蜈蚣", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The giant centipedes hidden deep underground use their sharp fangs to defend their territory.", "隐藏在地下深处的巨型蜈蚣用锋利的尖牙保卫自己的领地。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Leafcutter ant", "切叶蚁", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "They have sharp claws.", "它们有锋利的爪子。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Locust swarms", "蝗虫群", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A large number of locusts swept in, and it was a disaster for the peasants.", "大批蝗虫飞来，对农民来说是一场灾难。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Shield bug", "臭蝽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Disgusting stink bugs, no one wants to approach them.", "恶心的臭蝽，没人想接近它们。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Soldier Ant", "军蚁", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Soldier Ant that point to where to hit.", "指哪打哪的军蚁。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Acorn", "橡果", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Armadillo", "犰狳", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The 'Shield Dwarves' of the wilderness have both offense and defense when curled up.", "荒野中的“坚盾矮人”，蜷缩在一起时攻防兼备。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "CowBoy", "西部牛仔", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The skilled marksman cowboy who hits every shot.", "百发百中的神枪手牛仔。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Elephant", "大象", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The largest creation on land, it has tenacious vitality and defense.", "陆地上最大的创造物，它有顽强的生命力和防御力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Grizzly Cub", "灰熊幼崽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The little bear has not yet grown up, but it is already prepared to face the darkness.", "小熊还没有长大，但它已经准备好面对黑暗。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Honey badger", "蜜獾", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The vengeful honey badgers never feel afraid.", "复仇心强的蜜獾从不畏惧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Meerkat", "猫鼬", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The watchful Meerkat, it attacks swiftly upon detecting a threat.", "警惕的猫鼬，一旦发现威胁就迅速攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mongoose", "狐獴", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The incorruptable Mongoose is resistent to most toxins.", "永不腐败的狐獴对大多数毒素都有抵抗力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Stunted Grizzly", "弱熊", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Such a poor, scarred beast- what must have befallen it?", "这么一只可怜的、伤痕累累的野兽，它怎么了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Uri", "小毛球", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Abandoned children learn to be independent outside.", "被遗弃的孩子将在野外学会独立。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Uryli", "野人", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A wild man who has learned to be independent.", "学会了独立的野人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Chipmunk", "花栗鼠", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A small striped rodent of the forest, carrying an acorn with it as a meal.", "森林中的一种带条纹的小啮齿动物，带着橡子作为食物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pushing Mouse", "推推鼠", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A huge southern mouse.", "一只巨大的南方老鼠。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Rat Cub", "鼠崽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A weak rat can grow into a noble rat king.", "弱小的老鼠可以成长为高贵的鼠王。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Buff Frog", "变异牛蛙", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A mutated frog that has gained power.", "获得力量的变异青蛙。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "God Frog", "变异牛蛙神", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Creeper", "苦力怕", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A terrifying creature from the world of Minecraft.", "我的世界里的可怕造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Crocodile", "鳄鱼", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "An animal with a strong biting force.", "有很强咬合力的动物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Rattler egg", "响尾蛇蛋", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A snake egg that is about to hatch, although it is very fragile.", "即将孵化的蛇卵，虽然很脆弱。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sea Turtle", "大海龟", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Vibrant sea turtles will not die even after being sacrificed.", "生命力顽强的海龟即使被献祭也不会死亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thorny Devil", "刺蜥", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This guy's characteristics are just like its name.", "这家伙的性格和名字一模一样。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thorny Devil Tail", "刺蜥的尾巴", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Free Rattler egg", "免费响尾蛇蛋", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Amalgam Egg", "缝合蛋", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Walking Stone", "走石", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A stone that has traveled over mountains and rivers.", "跋山涉水的走石。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Stone Deer", "石鹿", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A stone deer statue carved by skilled craftsmen.", "由能工巧匠雕刻而成的石鹿。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Stone Wolf", "石狼", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A stone wolf statue carved by skilled craftsmen.", "由能工巧匠雕刻而成的石狼。", Language.ChineseSimplified);

            // Other
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wilderness Legend", "荒野传说", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Add some simple cards based on the original imprint.", "在原有印记的基础上添加一些简单的卡片。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Acorns", "找寻橡果", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When placing [creature], draw an acorn.", "当放置[creature]时，获得一张橡果卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cutting Wings", "剪翼者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Flying cards struck by [creature] will take 1 damage each time it declares an attack, and renders their wings useless.", "被[creature]击中的带有空袭印记的造物每次宣告攻击时将受到1点伤害，并使其空袭印记失效。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Swarm", "鸣蝉之夏", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "when [creature] is placed, the remaining slots on the owner's side are filled with cicadas", "当[creature]被放置时，持牌人一侧的剩余空位将被蝉群填满。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Terrain Creator", "地形创建者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When placing [creature] on the field, randomly draw a terrain card.", "将[creature]放置到场上时，随机抽取一张地形卡。", Language.ChineseSimplified);

            //WLD-DLC
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Azure Dragon", "青龙", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The head of the four divine beasts, it has absolute suppressive power.", "四神兽之首，具有绝对的压制力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Chinese Loong", "中国龙", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Chinese Loong in myths and legends is the master of all things.", "神话之龙，乃万物之主宰。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "God long", "降世神龙", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Andrealphus", "安德雷安富", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The sixty-fifth demon god of King Solomon.", "所罗门王的第六十五位门徒。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Awakened Ostrich", "觉醒的鸵鸟", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Black-tailed Gull", "黑尾鸥", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The black tailed gull at the dock will entrust its soul to the next destined person.", "码头的黑尾鸥会把自己的灵魂托付给下一个命定之人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Black tailed gulls at the dock", "码头的黑尾鸥", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Burning Egg", "不死鸟蛋", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "After hatching, there is eternity.", "孵化之后，便是永恒。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Eagle", "猎鹰", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A powerful eagle overlooking the underground.", "一只雄伟的猎鹰俯瞰着大地。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Falcon", "游隼", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Eager peregrine falcons, they attack as soon as they are laid down.", "迫捷的游隼，一上场就会立即攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hot Chiking", "辣鸡", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The chicken that is about to become a god.", "一只即将成神的鸡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ling Bird", "翎羽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A soaring divine bird will bless a friend to inherit its attributes after death.", "翱翔的神鸟会庇护友方造物，其死后继承它的属性。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Night Owl", "夜枭", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "They become stronger when faced with strength.", "它们面对力量时，也会变得更加强大。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ostrich", "鸵鸟", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The ostrich running around, its behavior is unpredictable.", "鸵鸟四处奔跑，其行为变幻莫测。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pelican", "鹈鹕", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The insatiable pelican swallowed the entire creation without digesting it.", "贪得无厌的鹈鹕会吞下整个造物，但不消化它。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pigeon", "鸽子", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The dove symbolizes peace, and its harm depends on the birds on the field.", "鸽子象征着和平，它的危害取决于场上的鸟类。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Rosefinch", "朱雀", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "One of the four great mythical beasts in Chinese mythology, constantly burning itself.", "中国神话中的四大神兽之一，不断自我燃烧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fire God Rosefinch", "火神朱雀", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Secular Bird", "不死鸟", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "God Secular Bird", "不死神鸟", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Super Chiking", "超级鸡", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The chicken that became a god.", "一只已经成神的鸡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "God Chiking", "神之鸡", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Broken Hawk", "破碎鹰", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "These broken cards remind me of my past life, even though they are far weaker than regular cards.", "这些破碎的牌让我想起了前世的记忆，尽管它们比普通卡牌弱得多。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Broken Raven", "破碎渡鸦", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Broken Grizzly", "破碎灰熊", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Broken Grizzly Cub", "破碎灰熊幼崽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Broken Hunter", "破碎猎人", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Broken Stoat", "破碎白鼬", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Broken Wolf", "破碎狼", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Broken Wolf Cub", "破碎狼崽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Broken Elk", "破碎麋鹿", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Broken Adder", "破碎蝰蛇", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Barghest", "犬魔", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Barghest from hell love to see blood.", "来自地狱的犬魔喜欢见血。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hell Barghest", "地狱犬魔", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Furry Man", "毛茸茸的人", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Why is this guy furry?", "这家伙为什么毛茸茸的？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "ikungagaga", "逆天", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "An excellent dog with eternal strengthening and leadership.", "一只拥有永恒力量和领导力的优秀之狗。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ni Tian God Dog", "逆天神犬", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Lone Wolf", "孤狼", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A wolf who is out of the pack, but loneliness will give him strength.", "一只脱离狼群的狼，但孤独会给它力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Naughty dog", "捣蛋狗", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A mischievous little dog, if there is no creation on the other side, he will run to the other side.", "一只淘气的小狗，如果对面没有造物，它就会跑到对面去。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Rabid Dog", "疯狗", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Angry dogs, whoever dares to provoke him will be doubly rewarded.", "愤怒的狗，谁敢惹它，都会被双倍奉还。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Werewolf", "狼人", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Zuo Dog", "大佐狗", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bones and blood are both essential for it.", "骨与血都是其必需品。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Jun Zuo Dog", "俊佐狗", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cheetah", "猎豹", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Agile cheetahs will attack immediately upon their appearance.", "敏捷的猎豹一上场就会立刻攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hua Miao", "花喵", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Dark One who masters the full challenge proficiently, everything is under his control.", "熟练掌握全挑战的神秘之猫，一切尽在其掌握之中。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Leopard", "花豹", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A ferocious Leopard with speed and damage.", "凶猛的花豹，速度快，伤害高。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Smilodon", "剑齿虎", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The smilodon, a beast of prehistoric origin.", "剑齿虎，一种起源于史前的野兽。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Smilodon Cub", "剑齿虎幼崽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Weak saber toothed tiger cubs, they help their mother collect bones.", "脆弱的剑齿虎幼崽，它们帮助母亲收集骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "White Tiger", "白虎", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "One of the four divine beasts, it has the most precious blood.", "四神兽之一，拥有最珍贵的血液。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Yin Tiger", "寅虎", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A strong tiger that is good at hunting from behind.", "一只强壮的老虎，擅长从后方狩猎。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Awakened Yin Tiger", "觉醒的寅虎", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ziqi Cat", "子淇猫", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A cat with strong auxiliary abilities is the overlord of cats.", "辅助能力最强的猫，乃猫中霸主。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ziqi God Cat", "子淇神猫", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Papyrus", "帕派瑞斯", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A little angel from the underworld.", "来自地下世界的小天使。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sans", "杉斯", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A lazy bone and its hellish joke.", "一个懒骨头和它的地狱冷笑话。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Doom Trepang", "毁灭海参", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "An exploding Doom Trepang has always been devastating.", "毁灭海参的爆炸总是毁灭性的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Electric Eel", "电鳗", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Electric eels can cause damage to creations that attack their location.", "电鳗会对攻击其位置的生物造成伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Humpback", "座头鲸", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Those who possess absolute power in water.", "在水中拥有绝对力量的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Paper Boat", "纸船", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A paper boat full of bones is enough to make all animals salivate.", "一艘满载骨头的纸船足以让所有造物垂涎。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Biker", "败客", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sailfish", "旗鱼", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The sailfish that can't wait will attack as soon as they get on the field.", "迫不及待的旗鱼一上场就会立刻攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dunhuang deer", "敦煌神鹿", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dunhuang deer from the East.", "来自东方的敦煌神鹿。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Jackalop with unlimited appreciation is a destroyer of ecology.", "无限增殖的鹿角兔乃生态的破坏者。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ovis Ammon", "盘羊", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The earth shaking ram kills its creations in exchange for power.", "撼地的盘羊杀死它撞到的造物以换取力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ovis Aries", "绵羊", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Weak sheep attract other predators to hunt.", "虚弱的绵羊会吸引其他捕食者狩猎。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Rhinoceros", "犀牛", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "They carry thick skin, which is not easily damaged by ordinary creations.", "它们的皮很厚，难以被普通的造物破坏。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Tibetan Antelope", "藏羚", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The strong Tibetan antelope is a dancer on the plateau.", "强壮的藏羚乃高原的舞者。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "God Antelope", "藏羚神", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wildebeest", "角马", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Herd wildebeest, they unite to resist predators.", "成群的角马，它们联合起来抵抗捕食者。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ant King", "蚁王", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The ant king who commands the army of ants.", "指挥蚂蚁大军的蚂蚁王。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Boss Z", "郑老板", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This guy is greedy but selfless.", "这家伙贪婪却无私。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Big Boss Z", "大郑老板", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Butterfly", "蝴蝶", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Butterflies that love flowers, they fly everywhere.", "爱花的蝴蝶，到处飞。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Rockivores", "源石虫", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This insect can feed on terrain.", "这种昆虫可以以地形为食。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Trilobite", "三叶虫", Language.ChineseSimplified);
