            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This card cannot be played unless you are three or less damage from losing!", "再受到3点或更少伤害即败北时才能使用此牌！", Language.ChineseSimplified);
