            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will not attack unless forced to attack by another effect.", "[creature]不会主动攻击，除非被其他效果强制攻击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Trinket Bearer", "道具商", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it grants it's owner a random item.", "使用[creature]时，将为持牌人随机提供一个道具", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] pulls out a random item.", "[creature]掏出了一个随机道具", Language.ChineseSimplified);
