            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 1 Bone to make [creature] move in the direction inscribed in the sigil.", "消耗1根骨头使[creature]向印记标注的方向移动。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Flip", "赌博按钮", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "50/50 chance to either kill [creature] or give it +1 power and health.", "50%概率消灭[creature]或使其力量与生命各+1。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gorge", "暴食", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] has less than 10 health, pay 1 blood to grant it +3 health.", "若[creature]生命值低于10点，消耗1点血量使其生命值+3。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You spent the last of your foils!.", "你已耗尽最后的箔片！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You have 0 foil remaining.", "你还剩0张箔片", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You have 1 foil remaining.", "你还剩1张箔片", Language.ChineseSimplified);
            for (int currency = 2; currency <= 100; currency++)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You have " + currency + " foils remaining.", "你还剩" + currency + "张箔片", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You do not have enough teeth.", "你的牙齿不够。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You need more teeth to use that ability.", "需要更多牙齿才能使用该能力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You cannot pay the cost", "你付不起这个代价", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Are you deaf? Get more Robobucks.", "你聋了吗？去赚点机器币。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm not running a charity, get more money.", "我这里不是慈善机构，多带点钱来。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No robobucks, no sigil.", "没钱就别想要印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No dice.", "没门。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hmm.", "唔。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You do not have enough teeth to pay the cost.", "你的牙齿不足以支付这个代价。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You'll need to perform better in battle if you wish to use that ability.", "想在战斗中使用这个能力，你得表现得更好些。", Language.ChineseSimplified);
            for (int currency = 0; currency <= 100; currency++)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You require more foils to use this ability! You have " + currency + " foils.", "需要更多箔片才能使用该能力！你目前有" + currency + "张箔片。", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ugh.", "呃。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You need more Robobucks to use that sigil, and I don't give credit.", "想用那个印记就多带点机器币来，我这里概不赊账。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Come back when you're a little... mmm... richer.", "等你稍微……嗯……阔绰些再来吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Picky Eater", "挑食者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 1 blood to draw bones equal to the health of the sacrificed creature.", "消耗1点血量，抽取等同于被献祭造物生命值的骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Punching Bag", "沙包", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Once per turn, choose any creature. That creature will strike this card for 1 damage. Afterwards, this card regains 1 health.", "每回合可选择任意造物，该造物会对本卡造成1点伤害，随后本卡恢复1点生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Remove", "移除按钮", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Kill [creature].", "消灭[creature]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sharpen", "磨砺", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you have an orange mox gem, sacrifice it to give [creature] +1 attack power.", "若你拥有橙色玛珂，可献祭它使[creature]获得+1攻击力", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] sacrifices an orange gem to sharpen it's attack!", "[creature]献祭橙色玛珂以磨砺其攻击！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spellsword", "咒术之剑", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "All gems on the same side of the board as[creature] gain 2 power, attack, and perish.", "与[creature]同侧的牌桌上所有宝石获得2点力量、发动攻击后阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Trained Flier", "飞行能手", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Grant [creature] the effect of the Airborne sigil until next upkeep. If the card already has Airborne, this will remove it's effect until next upkeep.", "使[creature]获得空袭印记效果直至下次维持阶段。若该卡已具有空袭印记，则暂时移除该效果直至下次维持阶段。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Trained Swimmer", "游泳能手", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Creature will gain the effect of the Waterborne sigil for one turn.", "该造物将获得水袭印记的效果，持续一回合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Upgrade Subroutine", "升级子程序", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When[creature] destroys another bot, this gains 1 point. When activated, the owner is presented with a choice of three sigils to permanently augment the card. Selecting a sigil spends points, and the more points accumulated when the sigil is activated, the higher quality the presented sigils will be.", "当[creature]摧毁其他机器人时，可获得1点升级点数。激活时，持牌人可从三个印记中选择一个永久强化该卡牌。选择印记会消耗点数，激活时积累的点数越多，提供的印记品质越高。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Aculeate Grip", "蛰刺握", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a creature is played on the same side of the board as [creature], [creature] will move towards them as far as possible, dealing them one damage and increasing it's own health by 1.", "当有造物被放置在与[creature]同侧的牌桌时，[creature]会尽可能向其移动，对其造成1点伤害并增加自身1点生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Allure", "媚骨天生", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the round, [creature] will draw opposing creatures in front of itself.", "回合结束时，[creature]会将对方造物吸引至自己正前方。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Annihilation", "湮灭", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, all creatures on the board are dealt 10 damage.", "当[creature]阵亡时，牌桌上所有造物均受到10点伤害", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ant Guardian", "蚂蚁守护者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will deal damage equal to it's attack power to any card which strikes a friendly ant.", "当友方蚂蚁遭受攻击时，[creature]会以自身攻击力对攻击者造成等量伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Antimagic", "反魔法", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Any mox gems played while [creature] is on the board will immediately perish. [creature] will move to the position of the destroyed gem, gain 1 health, and immediately attack.", "当[creature]在场时，任何打出的玛珂宝石会立即消亡。[creature]会移动到被摧毁宝石的位置，获得1点生命值并立即攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] destroys the played gem!", "[creature]摧毁了打出的宝石！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Armortify", "装甲化", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] attacks an opposing creature and it perishes, this card gains the armored sigil.", "当[creature]击杀对面造物时，该卡牌将获得身披坚甲印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Artistic License", "艺术许可", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, the creature opposing it loses all sigils.", "当打出[creature]时，其对面的造物将失去所有印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Trying to plunder me sigils? Yer a sour spoilsport!", "想偷老子的印记？你这扫兴的混账！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Despite your artistry, the soul of the moon is impervious...", "纵使技艺精湛，月之魂仍不可撼动……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Backup", "数据备份", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes by combat, the first card in it's owner's hand is played in it's place.", "当[creature]在战斗中阵亡时，其持有者手牌中的第一张卡牌将自动补位。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] calls in backup.", "[creature]呼叫了后备支援", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bandage Weaver", "绷带编织者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the turn,[creature] will heal the cards to it's left and right for 1 health.", "回合结束时，[creature]会为左右两侧的卡牌各恢复1点生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bastion", "堡垒", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] recieves damage, it only takes half the damage it normally would, rounded up.", "当[creature]受到伤害时，实际承受的伤害值减半（向上取整）。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Batch Delete", "批量删除", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] strikes a creature and it perishes, any creatures on the board which share the same cost as the target perish also.", "当[creature]击杀目标造物时，牌桌上所有与该目标费用相同的造物都会阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] shreds any other cards with the same cost as it's victim...", "[creature]会粉碎所有与受害者费用相同的卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bejeweled", "宝石迷阵", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the field, lining up three gems of the same colour will remove those gems from the field. Any creatures which opposed those gems will be removed as well, and the opponent will take damage directly equal to their combined attack power. ", "当[creature]在场时，若同色宝石三连成线，则移除这些宝石。与之对阵的造物也将被移除，对手将受到等同于这些造物总攻击力的直接伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bewilder", "扑朔迷离", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it's owner may choose any creature. Swap that creatures power and health.", "当使用[creature]时，其持牌人可选择任意造物，互换该造物的力量与生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bifurcated When Gempowered", "橙色兵分", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If its owner has an Orange Gem, [creature] will strike each opposing space to the left and right of the space across from it.", "若持牌人拥有橙宝石，[creature]会攻击正对面的左右两侧位置各一次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bisection", "二分法", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] kills another creature, two copies of the victim are created in the owner's hand with zero power and halved health.", "当[creature]击杀其他造物时，会在持牌人手中生成两张力量归零、生命值减半的受害者复制卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bleach", "漂白剂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, all opponent creatures lose all sigils.", "当[creature]被打出时，所有敌方造物将失去全部印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bloated", "肥美多汁", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] is worth blood equal to its health when sacrificed.", "[creature]在献祭时可算作等同于其生命值的血量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blood Artist", "血液艺术家", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Every three cards which perish by combat while [creature] is on the board, [creature] will give it's owner a deathcard constructed from the cost, stats, and sigils of those creatures.", "当牌桌上有[creature]时，每有三张卡牌因战斗阵亡，[creature]将为持牌人制作一张由这些造物的费用、属性和印记构成的死亡卡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] constructs a deathcard from previous creatures deaths...", "[creature]会根据先前阵亡造物制作死亡卡……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is sacrificed, it transforms to or from it's alternate form instead of perishing.", "当[creature]被献祭时，它会切换形态而非阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blood Dependent", "血液依赖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] does not strike another creature during the attack phase, it will perish at the start of the owner's next turn.", "若[creature]在攻击阶段未能击中其他造物，则会在持牌人下个回合开始时阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blood From Stone", "石中取血", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You may sacrifice terrain cards in order to play [creature].", "你可以献祭地形卡来打出[creature]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blood Gusher", "鲜血喷涌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] attacks an opposing creature and it perishes, this card, as well as creatures to the left and right of this card, gain 1 health.", "当[creature]击杀对面造物时，该卡牌及其左右相邻的造物各恢复1点生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blood Guzzler", "鲜血吞噬者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When[creature] deals damage to a creature, it gains 1 Health for each damage dealt.", "当[creature]对造物造成伤害时，每造成1点伤害即恢复1点生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] consumes the blood of its prey!", "[creature]吸食着猎物的鲜血！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blood Magic", "血魔法", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the field, its owner may sacrifice any gems, even if those gems normally cannot be sacrificed.", "当[creature]在场时，其持牌人可以献祭任意宝石，即使这些宝石通常无法被献祭。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bloodrunner", "血行者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] deals damage to a creature, it will move in the direction inscribed in the sigil.", "当[creature]对造物造成伤害时，它将朝印记标注的方向移动。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blue Inspiration", "蓝色灵感", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the board, all friendly creatures which cost a blue gem will gain a random sigil when played.", "当牌桌上有[creature]时，所有以蓝宝石为费用的友方造物登场时将获得随机印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] empowers the Blue Gem cost creature with a random sigil!", "[creature]为以蓝宝石为费用的造物赋予随机印记！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bombjuration", "爆弹咒", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it's owner may choose an empty slot on either side of the board. A magic bomb is created in the chosen slot.", "使用[creature]时，持牌人可选择牌桌任意一侧的空位，在该位置生成一枚魔法爆弹。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bombs Away", "炸弹空投", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, a Payload is created in the opposing slot. [define:SigilNevernamed Payload]", "使用[creature]时，对面位置会出现一个有效载荷。[define:SigilNevernamed Payload]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] drops a payload into the opposing slot!", "[creature]向对面位置空投了一个有效载荷！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bone Dependent", "骨头依赖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If the user has 0 Bones, [creature] will perish.", "若使用者骨头数为零，[creature]将立即阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bone Duke", "骨公爵", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies, 3 Bones are awarded instead of 1.", "[creature]死亡时，原本获得的1根骨头将变为3根。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bonelust", "骨欲", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] kills another creature, it generates 3 bones for its owner.", "当[creature]击杀其他造物时，为持牌人生成3根骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bone Spurs", "骨刺", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, it generates a bone for it's owner.", "[creature]被攻击时，将为持牌人产生1根骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will target the least powerful opposing card when it attacks.", "[creature]会在攻击时优先选择对面力量最低的卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Burning", "燃烧", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] takes 1 damage. Also, when [creature] is struck, the striker gains this sigil.", "持牌人回合结束时，[creature]会受到1点伤害。此外，当[creature]被攻击时，攻击者会获得该印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Buzz Off", "蜉蝣冲击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move in the direction inscribed in the sigil and leave a mayfly in it's old place. A mayfly is defined as 1 power, 1 health, airborne, brittle.", "持牌人回合结束时，[creature]将向印记标注的方向移动，并在原地留下一只蜉蝣。蜉蝣定义为1点力量、1点生命值、具有飞行与脆弱特性。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Call To Arms", "号召行动", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, the creatures to it's left and right will immediately attack.", "当使用[creature]时，其左右两侧的造物会立即发起攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Card Shedder", "牌堆剥离者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move in the direction inscribed in the sigil and leave a card in it's wake.", "持牌人回合结束时，[creature]将向印记标注的方向移动，并在其经过之处留下一张卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Caustic", "物腐毒生", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, it's killer perishes as well.", "当[creature]阵亡时，击杀它的造物也会随之阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Center Strike", "中心打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will attack all opponent slots except for those in the leftmost and rightmost lanes.", "[creature]会攻击除最左和最右路线外的所有对手位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Chaos Strike", "混沌打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is drawn, this sigil is replaced by a random strike modifier sigil.", "抽到[creature]时，此印记将被随机替换为一个打击强化类印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cheater", "欺诈者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] attacks an opposing creature and it perishes, the opponent is dealt 1 damage.", "当[creature]击杀对面造物时，对手将受到1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Circuit Maker", "电路绘制", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, a conduit card is created to its left and right.", "使用[creature]时，其左右两侧会各生成一张导线卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Claw", "利爪", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will lose all attack power if it is not directly adjacent to a card bearing the clawed sigil.", "若[creature]未与带有利喙尖爪印记的卡牌直接相邻，将失去所有攻击力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Clawed", "利喙尖爪", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, claws are created to the left and right. Any creatures in those spaces will be killed.", "当打出[creature]时，会在左右两侧生成利爪，该位置的任何造物将被消灭。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Clawing Hand", "利爪之手", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, the striker is dealt damage equal to the number of cards in the owner's hand.", "当[creature]被攻击时，攻击者会受到等同于持牌人手牌数的伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cleaving", "劈裂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] strikes another creature, that creature will be cut in half. Half of the creature will move to the right, while the other half remains in place.", "当[creature]攻击另一只造物时，该造物会被劈成两半。其中一半会向右移动，另一半则留在原位。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] cleaves it's target in half!", "[creature]将目标劈成了两半！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Coastguard", "海岸警卫", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Submerges during the opponent's turn. When an opposing creature is placed opposite to an empty space, [creature] will move to that empty space while submerged.", "在对手回合潜入水下。当对手造物被放置于空位对面时，[creature]会在潜水状态下移动至该空位。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Collateral Damage", "连带伤害", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, if [creature] is on the board, the nearest creature to this card will be dealt ten damage.", "持牌人回合结束时，若[creature]在场，距离此卡最近的造物将受到10点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Collector", "收藏家", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn,[creature] will move in the direction inscribed on the sigil. Creatures in the way will be returned to the owner's hand.", "持牌人回合结束时，[creature]将向印记标注的方向移动。沿途的造物会被收回持牌人手中。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Commander", "指挥官", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, the creatures to it's left and right will attack the striker.", "当[creature]被攻击时，其左右两侧的造物会反击攻击者。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Double Strike When Powered", "充能双击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is within a circuit, it will strike the opposing space an additional time when it attacks.", "如[creature]处在回路中，则会在攻击时额外攻击对面位置一次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gift When Powered", "充能奖励", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is within a circuit when it perishes, a random card is created in your hand.", "如[creature]阵亡时处在回路中，则会给你随机增加一张手牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Morsel When Powered", "充能一口之量", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When[creature] is sacrificed while inside a completed circuit, ⁣it adds its stat values to the card it was sacrificed for.", "当[creature]在完整回路中被献祭时，其状态值会附加至献祭目标卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Omnipotent When Powered", "充能全向打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is within a circuit, it will strike every opposing space containing a card when it attacks. [creature] will strike directly if no creatures oppose it, or if it is not in a circuit.", "如[creature]处在回路中，攻击时将打击对面所有存在卡牌的空间。若没有造物阻挡或不在回路中，[creature]将直接攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Powered Quills", "充能双重刺甲", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck while inside a completed circuit, the striker is dealt 2 damage.", "当[creature]处在完整回路中受到攻击时，攻击者会受到2点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Print When Powered", "充能玛珂绘制", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is inside a completed circuit, a gem will be created in its owners hand at the start of their turn.", "若[creature]处于完整回路中，持牌人回合开始时其手牌中将生成一颗玛珂宝石。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] prints a mox gem for its owner!", "[creature]为持有者印出一颗玛珂宝石！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Repulsive When Powered", "充能厌恶情绪", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is within a conduit, if another creature would strike it, it does not.", "当[creature]处于回路中时，其他造物将无法对其发起攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Scavenge When Powered", "充能拾荒", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is inside a completed circuit, opposing creatures also grant Bones upon death.", "当[creature]处于完整回路中时，敌方造物死亡时也会提供骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Splash Damage When Powered", "充能溅射", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is within a conduit when it strikes a slot, creatures to the left and right of the struck slot will be dealt 1 damage.", "当[creature]处在回路中攻击某个位置时，被攻击位置左右两侧的造物均会受到1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Stimulate When Powered", "充能刺激", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is inside a completed circuit, it gains 2 health at the start of its owners turn.", "当[creature]处于完整回路中时，持牌人回合开始时其生命值增加2点。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] stimulates its health!", "[creature]强化了生命值！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Billowing Bond", "空袭契约", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Creatures within a bond completed by [creature] gain the power of flight at the start of each turn.", "处在由[creature]构成的契约中的造物会在每回合开始时获得飞行能力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Elder Conduit", "宝石骷髅管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a creature within a circuit completed by [creature] perishes, a Gem Skeleton will be created in it's place.", "当处在由[creature]构成的回路中的造物阵亡时，会在其位置生成一个宝石骷髅。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] calls forth a Gem Skeleton with the old magics!", "[creature]用古老魔法召唤出了宝石骷髅", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Emerald Conduit", "绿宝石管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is part of a completed circuit, its owner gains a green gem.", "如果[creature]参与构成一条完整回路，其持牌人将额外控制一颗绿宝石。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] completes a circuit, and grants its owner a green gem!", "[creature]构成完整回路，并为持牌人提供一颗绿宝石！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] no longer completes a circuit, and no longer grants a green gem!", "[creature]不再构成回路，也不再提供绿宝石！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Friendship Bond", "友谊契约", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Empty spaces within a bond completed by [creature] spawn Squirrels at the end of the owner's turn.", "由[creature]构成的契约中的空位将在持牌人回合结束时生成松鼠。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Grave Conduit", "掘骨管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Completes a circuit. At the end of the owner's turn, [creature] will generate one bone for each creature contained within the circuit it completes.", "构成回路。持牌人回合结束时，[creature]会为回路中的每个造物生成1根骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] grants a bone for every creature in it's circuit.", "[creature]会为回路中的每个造物提供1根骨头", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gun Conduit", "哨兵管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a creature moves into the slot opposing a card in a circuit completed by [creature], the moved creature is dealt 1 damage.", "当造物进入由[creature]构成的回路中卡牌对面的位置时，该造物会受到1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] takes a free shot!", "[creature]的枪先发制人", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hot Conduit", "灼热管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a creature within a circuit completed by [creature] is struck, the striker gains the Burning Sigil. Also, creatures within this card's circuit do not take damage from the Burning sigil.", "当处在由[creature]构成的回路中的造物被攻击时，攻击者将获得燃烧印记。此外，该卡牌回路中的造物不会受到燃烧印记的伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Loot Conduit", "劫掠管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Completes a circuit. At the start of the owner's turn, [creature] will draw 1 card to the owners hand for every empty space in the circuit it completes.", "构成回路。在持牌人回合开始时，[creature]会为回路中每个空位抽取1张牌加入持牌人手牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] draws a card for every empty space in it's circuit!", "[creature]为回路中每个空位抽取一张牌！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Malfunctioning Conduit", "故障管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Completes a circuit. At the end of its owner's turn, random cards from its owners hand will be played in random spaces inside of the circuit completed by [creature]. Those cards gain the Burning Sigil.", "构成回路。持牌人回合结束时，随机从手牌选取卡牌置入由[creature]构成的回路空位，并获得燃烧印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] draws cards into it's circuit!", "[creature]将卡牌吸入回路中！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Meaningless Bond", "空无契约", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] may complete a bond, but provides no additional effect.", "[creature]可构成契约，但无任何额外效果。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nano Conduit", "纳米管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of its owners turn, creatures within a circuit completed by [creature] gain the Armored sigil.", "持牌人回合结束时，处在由[creature]构成的回路中的造物将获得纳米护盾印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] shields all creatures in its circuit!", "[creature]为回路中的所有造物提供护盾！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ruby Conduit", "红宝石管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is part of a completed circuit, its owner gains an orange gem.", "如果[creature]参与构成一条完整回路，其持牌人将额外控制一颗橙宝石。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] completes a circuit, and grants its owner an orange gem!", "[creature]构成回路，并为持牌人提供一颗橙宝石！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] no longer completes a circuit, and no longer grants an orange gem!", "[creature]不再构成回路，且不再提供橙宝石！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sapphire Conduit", "蓝宝石管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is part of a completed circuit, its owner gains a blue gem.", "如果[creature]参与构成一条完整回路，其持牌人将额外控制一颗蓝宝石。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] completes a circuit, and grants its owner a blue gem!", "[creature]构成回路，并为持牌人提供一颗蓝宝石！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] no longer completes a circuit, and no longer grants a blue gem!", "[creature]不再构成回路，也不再提供蓝宝石！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Stim Conduit", "刺激管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of its owners turn, creatures within a circuit completed by [creature] gain 1 Health.", "持牌人回合结束时，处在由[creature]构成的回路中的造物增加1点生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] increases the health of every creature in it's circuit!", "[creature]提升了回路中所有造物的生命值！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Tender Bond", "温柔契约", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Creatures within a bond completed by [creature] heal to full health at the start of the owner's turn.", "由[creature]构成的契约中的造物会在持牌人回合开始时恢复全部生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thorny Conduit", "尖刺管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a creature within a circuit completed by [creature] is struck, the striker is then dealt a single damage point.", "处在由[creature]构成的回路中的造物被攻击时，攻击者也会受到1点伤害", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Confusion", "颠三倒四", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played opposite an opponents creature, swap that creatures power and health.", "当[creature]被放置在对面的造物对面时，交换该造物的力量与生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The card opposing [creature] is confused!", "[creature]对面的卡牌陷入混乱！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Copier", "影印设备", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, a copy of it's murderer is created in your hand.", "[creature]阵亡时，你的手牌里会出现一张杀害它的造物的复制品。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Coward", "贪生怕死", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is targeted, it will leap out of the way.", "当[creature]成为攻击目标时，它会跃开躲避。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Crooked Strike Left", "偏左打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will attack the space to the left of it's intended target.", "[creature]会攻击其预定目标左侧的位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Crooked Strike Right", "偏右打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will attack the space to the right of it's intended target.", "[creature]会攻击其预定目标右侧的位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Crossbones", "十字骨", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it grants 2 bones to it's owner.", "当使用[creature]时，能为持牌人提供2根骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Crunchy", "酥脆", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is sacrificed, it generates one bone for its owner.", "当[creature]被献祭时，将为持牌人产生1根骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cute", "可爱", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Creatures that strike [creature] feel guilty, and will skip their next strike.", "攻击[creature]的造物会产生愧疚感，并跳过下一次攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Data Breach", "数据泄露", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner must draw a card from their deck and immediately discard it. The opponent is dealt damage equal to the drawn creatures attack power.", "当[creature]阵亡时，其持牌人必须从牌组抽一张牌并立即弃置。对手将受到等同于所抽卡牌攻击力的伤害", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Deadringer", "死铃使者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is alive and on the board, it gains +1 power each time the bell is rung, if it has less than 5 power.", "当[creature]存活且在牌桌上时，若其力量低于5点，每次铃响可使其增加1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] powers up!", "[creature]的力量提升了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Death Snatch", "死亡掠夺", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, draw a card from your deck.", "[creature]阵亡时，从你的牌组中抽一张牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Decked Out", "全副武装", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, a card from a random temple's side deck is created in your hand.", "[creature]受到攻击时，会从随机神殿的副牌组中抽取一张牌加入你的手牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Defiler", "污染者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner must draw a card from their deck and immediately discard it.", "[creature]阵亡时，其持牌人必须从牌组抽一张牌并立即弃置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Desperate", "孤注一掷", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, it deals one point of damage to the opponent.", "[creature]阵亡时对对手造成1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] deals a free point of damage as it perishes.", "[creature]在阵亡时造成了1点额外伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Deus Hoof", "鹿神敬拜", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "All friendly hooved creatures gain +1 Power and Health while [creature] is on the board. Also, all hooved creatures will attack anything that strikes [creature].", "当[creature]在场时，所有友方有蹄类造物获得+1力量和生命。此外，所有有蹄类造物会攻击任何袭击[creature]的目标。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dexter Strike", "右前打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will also strike the slot to the right of the opposing slot when it attacks.", "[creature]攻击时会同时攻击正对面位置右侧的格子。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Diagonal Strike", "对角打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] attacks, it will strike either to the left or right of its original target, prioritising striking slots containing creatures. If both or neither slots adjacent to the target contain creatures, [creature] will attack to the left or right at random.", "当[creature]攻击时，会优先攻击原目标左侧或右侧存在造物的位置。若两侧均有或均无造物，则随机选择左侧或右侧攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Disembowel", "开膛破肚", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is sacrificed, an amount of guts equal to its remaining health are created in its owners hand.", "当[creature]被献祭时，其持牌者手牌中将生成等同于剩余生命值的内脏。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s guts explode into your hands.", "[creature]的内脏爆裂到你手中。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Distraction", "魂不守舍", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] would be struck, a distraction is left in its place and [creature] moves to the right.", "当[creature]可能受到攻击时，会在原地留下分心物，[creature]自身则向右移动。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Diver", "潜行者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] can strike the opposing card even while it is submerged.", "[creature]可以直接攻击对位处于潜水状态的卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The next card drawn while [creature] is alive and on the board will be duplicated.", "当[creature]存活且在牌桌上时，下一张抽到的卡牌将被复制。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Divisibility Strike", "可除性打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of each turn, [creature] will alternate between striking all odd indexed opponent slots, and all even indexed opponent slots.", "每回合结束时，[creature]会交替攻击所有奇数编号和偶数编号的对手位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Docile", "温顺", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will wait a set number of turns in between being allowed to attack.", "[creature]需等待固定回合数才能再次攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Doomed", "命定者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the turn, [creature] will perish.", "回合结束时，[creature]会阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Double Tap", "协同攻击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a friendly creature strikes an opponent creature, [creature] will also attack that opponent creature.", "当友方造物攻击敌方造物时，[creature]也会对该敌方造物发起攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] aids its comrades in battle!", "[creature]在战斗中支援它的战友！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Downdraft", "下沉气流", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will grant the power of flight to creatures to it's left and right.", "[creature]会赋予左右两侧造物飞行能力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Draw", "绘牌之手", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, its owner draws cards equal to the number inscribed on this sigil.", "当使用[creature]时，其持有者将抽取等同于该印记所示数量的卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dripping", "流连", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move in the direction inscribed in the sigil and leave part of it's own body in it's wake.", "持牌人回合结束时，[creature]将向印记标注的方向移动，并在移动轨迹上留下部分躯体。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Drop", "得手投弃", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If[creature] is drawn as part of your opening hand, it will be automatically played on a random board space for free.", "若[creature]作为初始手牌被抽到，将免费自动部署在随机空位。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, you may search your deck for any card and create a duplicate of it in your hand.", "使用[creature]时，你可在牌组中搜索任意卡牌并在手牌中生成其复制品。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Duppelgang", "双重奏", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, draw copies of cards adjacent to it from your deck to your hand.", "使用[creature]时，从牌组中抽取与其相邻卡牌的复制品加入手牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Eager", "渴望", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will gain +2 power for the duration of it's first turn on the board.", "[creature]在牌桌上的首个回合内将获得+2力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Easy Pickings", "轻而易举", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, all other creatures on the board with 1 health will perish.", "当[creature]入场时，牌桌上所有生命值为1的造物将立即阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Emancipation", "脱胎换骨", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, copies of any bottled cards the owner possesses are created in the owner's hand.", "使用[creature]时，持牌人拥有的所有瓶中卡牌会复制到其手牌中。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Emerald Core", "绿宝石核芯", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, an Emerald Vessel is created in its place.", "当[creature]阵亡时，会在原地生成一个绿宝石容器。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Emerald Heart", "绿宝石之心", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, an Emerald Mox is created in its place.", "当[creature]阵亡时，会在原地生成一张绿宝石玛珂。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Enchanting", "附魔", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, all creatures you control gain one of its other sigils, at random.", "使用[creature]时，你打出的所有造物将随机获得它的一个其他印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Endangered", "濒危", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the turn, [creature] has a one in four chance to perish.", "回合结束时，[creature]有25%概率阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Enemy Lines", "敌军战线", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] attacks, it will target all opponent slots that it is NOT directly opposing.", "[creature]攻击时，会锁定所有非正对位置的敌方空位。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Energy Dependent", "能量依赖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If the user has 0 Energy, [creature] will perish.", "若使用者能量为0，[creature]将阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Enraged", "愤怒", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, it gains 1 attack power.", "当[creature]被攻击时，获得1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Entangle", "盘根错节", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its killer has their power reduced by half and rounded down for one turn.", "[creature]阵亡时，击杀者力量减半（向下取整）并持续一回合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Erratic", "飘忽不定", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move in the direction inscribed on the sigil. It will not move if the target slot is obstructed. Direction has a 50/50 chance to change after moving.", "持牌人回合结束时，[creature]将向印记标注的方向移动。若目标格位被阻挡则不会移动。每次移动后有50%概率改变方向。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Esprit De Corps", "团队精神", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the board, creatures in the leftmost and rightmost slots on the same side of the board gain 1 power.", "当[creature]在牌桌上时，同侧最左与最右位置的造物增加1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While[creature] is alive and on the board, its owner may not take fatal damage.", "当[creature]存活于牌桌上时，其持牌人不会受到致命伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Even Strike", "偶数打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will strike all opposing spaces in even indexed columns when it attacks. Column index is counted from the left, starting at 1.", "[creature]攻击时会打击所有偶数索引列的对面位置。列索引从左至右计数，起始值为1。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Exceptional Sacrifice", "传说祭品", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] counts as 6 Blood rather than 1 Blood when sacrificed. Does not pair well with many lives.", "[creature]在献祭时可算作6点血量，而非1点。与生生不息印记相性不佳。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bloodstarved Beast", "嗜血狂兽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it's owner may search their graveyard for a card, and add that card to their hand.", "使用[creature]时，持牌人可从墓地中搜索一张卡牌并加入手牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Exploding Corpse", "尸爆", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, all empty spaces on the board are filled with it's innards.", "当[creature]阵亡时，牌桌上的所有空位会自动出现内脏卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Expulsion", "呕物驱逐", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] takes damage, it regurgitates it's contents into the adjacent space to the right.", "当[creature]受到伤害时，会将其内容物吐到右侧相邻位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Exsanguination", "放血", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the board, friendly creatures have their blood cost reduced by 1.", "当[creature]在牌桌上时，友方造物的血量费用减少1点。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Eye For Battle", "战斗之眼", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will lose all attack power if it is not directly opposing an opponent's creature.", "若[creature]未正对敌方造物，将失去所有攻击力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Face To Face", "面面相觑", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is drawn, it morphs to mimic the current opponent.", "当[creature]被抽到时，它会变形模仿当前对手。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fan Tailed", "妖扇之尾", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, all friendly creatures on the board take flight.", "当[creature]入场时，牌桌上所有友方造物获得飞行能力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fatal Flank", "致命侧翼", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes to combat, it creates a specific creature in all empty adjacent slots.", "当[creature]在战斗中阵亡时，会在所有相邻空位生成特定造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fearsome", "凶神恶煞", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] targets a slot occupied by another creature, that creature will move to make way for it if there is room to do so.", "当[creature]试图进入已被其他造物占据的位置时，若存在可移动空间，该造物会为其让路。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "File Explorer", "文件管理", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, choose two creatures from random selection of your deck. These creatures will define this cards power and health at random. If this card dies, the creatures used to define its stats perish as well.", "当使用[creature]时，从牌组随机选择两只造物。这些造物将随机决定该卡牌的力量与生命值。若该卡牌阵亡，用于决定其属性的造物也会同时阵亡", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Filter Feeder", "滤食之物", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a creature perishes adjacent to [creature], the card will gain either 1 power or 1 health.", "当[creature]相邻的造物阵亡时，该卡牌将获得1点力量或1点生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Firebomb", "火焰引爆器", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, creatures adjacent to it and opposing it take 2 damage and gain the Burning sigil.", "[creature]阵亡时，相邻及正对面的造物受到2点伤害并获得燃烧印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] explodes, spraying fire everywhere!", "[creature]爆炸了，火焰四处飞溅！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fire In The Hole", "小心炮弹", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, it's owner may choose an opponent slot. The creature in that slot will take ten damage.", "[creature]阵亡时，其持牌人可选定对手的一个卡槽，该槽位造物将受到10点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fire Resistant", "防火", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will not take damage from the Burning sigil.", "[creature]不会受到燃烧印记的伤害", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Flank Blast", "侧牌引爆器", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies, adjacent friendly creatures are dealt 10 damage.", "当[creature]死亡时，相邻的友方造物会受到10点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Flea Ridden", "跳蚤满身", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes to combat, it creates a Flea in all empty adjacent slots. A Flea is defined as 0 Power, 1 Health, Eager, Frail", "[creature]在战斗中阵亡时，会在所有相邻空位生成跳蚤。跳蚤具有：0点力量、1点生命，渴望，脆弱。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Flighty", "轻浮", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the turn, [creature] will move to a random available space.", "回合结束时，[creature]会随机移动到一个可用位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fossil Record", "化石记录", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the board, terrain cards will award 3 additional bones when destroyed.", "当[creature]在牌桌上时，地形卡被摧毁时将额外获得3根兽骨。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Frail", "脆弱", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, it will perish.", "当[creature]受到攻击时，它将立即阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Frail Sacrifice", "脆弱献祭", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is sacrificed, the creature it was sacrificed for gains the Frail Sigil.", "当[creature]被献祭时，接受献祭的造物将获得脆弱印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Freefall", "自由落体", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the board, all airborne creatures require exactly 1 less of their respective cost to be played.", "当[creature]在牌桌上时，所有具有空袭印记的召唤需求减少1点对应资源。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Friend Finder", "找呀找朋友", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, a random friend is created in the slots to it's left and right.", "使用[creature]时，其左右两侧空位将各生成一个随机Steam好友。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fringe Strike", "边路打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will always attack the leftmost and rightmost opponent lanes.", "[creature]会始终攻击最左和最右路线的敌方位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Frog Friend", "众蛙之友", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move in the direction inscrybed in the sigil. You may search your deck for any 'frog' or 'toad' creatures, and play them in [creature]'s old space.", "持牌人回合结束时，[creature]将向印记标注的方向移动。你可以从牌组中搜寻任意“蛙”或“蟾蜍”造物，并将其放置在[creature]原先的位置上。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Emerald Dependant", "绿宝石依赖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature]'s owner controls no green mox gems, it will perish.", "如果[creature]的持牌人未打出绿色玛珂，则[creature]会阵亡", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] requires an emerald mox to exist and perishes.", "[creature]需要绿宝石玛珂才能存在，否则会阵亡", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ruby Dependant", "红宝石依赖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature]'s owner controls no orange mox gems, it will perish.", "如果[creature]的持牌人未打出橙色玛珂，则[creature]会阵亡", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] requires a ruby mox to exist and perishes.", "[creature]需要红宝石玛珂才能存在，否则会阵亡", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sapphire Dependant", "蓝宝石依赖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature]'s owner controls no blue mox gems, it will perish.", "如果[creature]的持牌人未打出蓝宝石玛珂，则[creature]会阵亡", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] requires a sapphire mox to exist and perishes.", "[creature]需要蓝宝石玛珂才能存在，否则会阵亡", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gem Shedder", "玛珂掉落", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move in the direction inscrybed in the sigil and drop a random Gem in its old place.", "持牌人回合结束时，[creature]将向印记标注的方向移动，并在原地随机留下一颗玛珂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gem Skeptic", "宝石质疑者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the board, its owner may not play any mox gems.", "当牌桌上有[creature]时，其持牌人无法使用任何玛珂宝石。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Giant", "巨人", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the board, any strikes against its owner will target it instead.", "当[creature]在场时，所有对其持牌人的攻击将转而以它为攻击目标。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gift Bearer", "礼品商", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, a random creature is created in your hand.", "[creature]阵亡时，你的手牌中会随机生成一只造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Glitterheart", "辉耀之心", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the board, playing a mox gem will increase its health by 1.", "当[creature]在场时，打出玛珂宝石可使其生命值+1。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] is bolstered by the played gem.", "[creature]因打出宝石而获得强化。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Globetrotter", "四海为家", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn,[creature] will move in the direction inscribed on the sigil. If obstructed, it will move to the other side of the board and begin to circle back.", "持牌人回合结束时，[creature]将向印记标注的方向移动。若遇阻碍，则会绕至牌桌另一侧开始回旋移动。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, a Black Goat is created in its owners hand.", "使用[creature]时，其持有者的手牌里会出现一张黑山羊牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gold Digger", "掘金者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will generate 1 currency, up to five times per battle.", "在持牌人回合结束时，[creature]将产生1枚金币，每场战斗最多触发五次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gold Rush", "淘金热", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, all opponent cards on the board perish, and are replaced by Gold Nuggets.", "当使用[creature]时，牌桌上所有敌方卡牌都会阵亡，并被金块取代。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gooey", "黏液", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, the striker loses 1 power.", "当[creature]被攻击时，攻击者损失1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Scrape that gunk off the hull, lads! Full steam ahead!", "把船体上的黏液刮干净，伙计们！全速前进！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No amount of ooze can slow the motion of a celestial body...", "再多的黏液也无法减缓天体的运行轨迹……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Graveyard Shift", "墓地轮班", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes by combat, it leaves something else in it's old space. Unlike Frozen Away, this new creature will retain the sigils and buffs of the original.", "[creature]在战斗中阵亡时，会在原位置留下其他造物。与冰封禁锢不同，新造物将保留原造物的印记与增益效果。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gravity", "万有引力", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it's owner may choose any opponent creature, and move it to any open space on their side of the board.", "当使用[creature]时，其持牌人可选择任意敌方造物，并将其移至己方牌桌的任意空位。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Green Inspiration", "绿色灵感", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the board, all friendly creatures which cost a green gem gain +2 health.", "当牌桌上有[creature]时，所有有以绿宝石为费用的友方造物获得+2生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gut Spewer", "呕脏者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, its innards are created in your hand.", "使用[creature]时，你的手牌里会出现一张内脏卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Harbinger", "先驱者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a friendly creatures perishes, [creature] will move to fill it's empty space.", "当友方造物阵亡时，[creature]会移动至该空位补位。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Harts Within", "内心之鹿", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, a random creature from the hooved tribe is created in your hand.", "[creature]受到攻击时，你的手牌中将随机出现一张有蹄类族群的卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Haunter", "阴魂不散", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies to combat, it haunts the space it died in. Creatures that enter this space absorb its old sigils.", "当[creature]战死时，其亡魂会盘踞在原位。进入该位置的造物将吸收其原有印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] leaves its sigils behind in the ethereal plane!", "[creature]将印记留在了灵界！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature]s owner may choose a creature they control. That creature gains 1 health.", "在持牌人回合结束时，[creature]的持有者可以选择一个己方造物。该造物恢复1点生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Healshield", "治疗护盾", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] does not take damage during a turn, it will gain Armor at the start of its owner's next turn.", "若[creature]在一回合内未受伤害，持牌人下回合开始时它将获得护甲。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Herald", "使节", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When[creature] attacks an opposing creature and it perishes, creatures to the left and right of this card gain 1 power.", "当[creature]击杀对面造物时，该卡牌左右两侧的造物各获得1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "High Powered", "高功率", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the start of its owners turn, [creature] will consume 1 energy.", "在持牌人回合开始时，[creature]将消耗1点能量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Home Run", "全垒打", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will remember the lane in which it was played, and will always attack that lane.", "[creature]会记住被放置的路线，并始终攻击该路线。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hook Line And Sinker", "全套诱饵", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes by combat, the opposing creature will be pulled into it's old space.", "当[creature]在战斗中阵亡时，对面的造物会被拉入其原先所在位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hypermorphic", "超形态", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of each turn, [creature] will transform into a random card of the same type and quality.", "每回合结束时，[creature]会随机变形为同类型同品质的卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ignition", "点燃", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] strikes a creature, that creature gains the Burning sigil.", "当[creature]攻击造物时，目标将获得燃烧印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Immaculate Form", "无瑕形态", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the board, the gems cost of all friendly creatures is reduced by 2 gems.", "当[creature]在场时，所有友方造物的宝石消耗减少2颗。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Immaterial", "缥缈之物", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Any attacks made against [creature] during the opponents turn will pass straight through, and hit the creature's owner directly.", "对手回合中对[creature]发动的攻击将直接穿透，并击中其持牌人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Immortal Coil", "不朽之环", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is alive on the board, any friendly creatures that die to combat will return to the owner's hand, though slightly weakened.", "当[creature]存活于牌桌上时，任何在战斗中阵亡的友方造物都会以略微削弱的状态返回持牌人手牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Incontinent", "失禁", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] would be struck, a mess is created in its place and a card bearing this sigil moves to the right.", "当[creature]即将受到攻击时，会在其位置生成一团污秽，同时[creature]会向右移动。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Inspiring Sacrifice", "鼓舞之血", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is sacrificed, it grants its sigils to the creature it was sacrificed for.", "当[creature]被献祭时，它的印记会转移给被献祭的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Instant Effect", "即时效果", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "After resolving on the board, [creature] will immediately perish.", "[creature]在牌桌生效后会立即阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Killing Joke", "杀戮玩笑", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the board, all friendly creatures which cost an orange gem will either gain +1 power or -1 power when played.", "当[creature]在场时，所有消耗橙宝石的友方造物登场时将随机获得+1或-1力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] randomly modifies the power of the Orange Gem cost card!", "[creature]会随机调整消耗橙宝石卡牌的力量！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Kin Bearer", "亲族商", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, a random creature of the same tribe is created in your hand.", "当[creature]阵亡时，你的手牌中会随机生成一张同族造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Kindred", "亲族之后", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, random common creatures of the same tribe will be played for free to its left and right.", "当使用[creature]时，会免费在其左右两侧随机召唤同族普通造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "L33p Leaver", "跳跃机器人掉落器", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn,[creature] will move in the direction inscrybed in the sigil and drop a L33pB0t in its old place.", "持牌人回合结束时，[creature]将向印记标注的方向移动，并在原地留下一台跳跃机器人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Landscaper", "景观设计师", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When[creature] is played, any terrain cards on the board are removed and added to it's owner's hand.", "使用[creature]时，牌桌上所有地形卡牌将被移除并返回持牌人手牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Lash Out", "狂乱攻击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn,[creature] will attack a random opposing slot.", "持牌人回合结束时，[creature]会随机攻击一个对面位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Airborne Latch", "空袭传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the airborne sigil.", "[creature]阵亡时，其持牌人需选定下一个继承空袭印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Annoying Latch", "恼人传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the annoying sigil.", "[creature]阵亡时，其持牌人需选定下一个继承惹人生厌印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Burning Latch", "燃焰传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the burning sigil.", "[creature]阵亡时，其持牌人需选定下一个继承燃烧印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Burrower Latch", "钻地龙传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the burrower sigil.", "[creature]阵亡时，其持牌人需选定下一个继承钻地龙印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Frail Latch", "脆弱传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the frail sigil.", "[creature]阵亡时，其持牌人需选定下一个继承脆骨印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Temporarily Gemified", "临时宝石化", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] has been temporarily gemified until the end of battle.", "[creature]被暂时宝石化，效果持续至对战结束。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gem Latch", "宝石传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When[creature] perishes, its owner chooses a creature to become gemified.", "[creature]阵亡时，其持牌人需选定一个造物进行宝石化。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Haunter Latch", "阴魂传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the haunter sigil.", "[creature]阵亡时，其持牌人需选定下一个继承阴魂不散印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Null Latch", "空无传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the null conduit sigil.", "[creature]阵亡时，其持牌人需选定下一个继承空无管道印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Overclocked Latch", "超频传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain 1 power, and the overclocked sigil.", "[creature]阵亡时，其持牌人需选定下一个继承1点力量与超频运转印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sniper Latch", "狙击传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the sniper sigil.", "[creature]阵亡时，其持牌人需选定下一个继承狙击手印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sprinter Latch", "冲刺传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the sprinter sigil.", "[creature]阵亡时，其持牌人需选定下一个继承冲刺能手印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Stalwart Latch", "坚毅传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the stalwart sigil.", "[creature]阵亡时，其持牌人需选定下一个继承坚毅印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Totem Latch", "图腾传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the sigil on their current totem.", "[creature]阵亡时，其持牌人需选定下一个继承当前图腾印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Waterborne Latch", "水袭传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the waterborne sigil.", "[creature]阵亡时，其持牌人需选定下一个继承水袭印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Leaper", "跃行者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move in the direction inscribed in the sigil, leaping over single card obstructions.", "持牌人回合结束时，[creature]将向印记标注的方向移动，并能跃过单个卡牌障碍。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, it creates a number of cards in the owner's hand equal to it's remaining HP, then perishes.", "当[creature]受到攻击时，会根据剩余生命值在持牌人手牌中生成等量卡牌，随后阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Lockdown", "锁关闭隘", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, the opposing creature loses attack power equal to the power of this card and gains the Stalwart sigil.", "当使用[creature]时，对面造物失去等同于该卡牌力量的攻击力，并获得坚毅印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] locks the opposing creature in place!", "[creature]将对面造物牢牢锁定在原位！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Lonesome", "孤独", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] gains +1 power for each adjacent empty space.", "[creature]每有一个相邻空位便增加1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Lurk", "水袭哨兵", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Submerges during the opponent's turn. When a creature moves into the space opposing [creature], they are dealt 1 damage.", "[creature]会在对手回合潜入水下。任何造物在进入[creature]对面的位置时，均会受到1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Magitech", "魔法科技", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is alive and on the board, playing a mox gem will provide it's owner with an energy cell.", "当[creature]存活且在牌桌上时，打出玛珂宝石可为持牌人提供一个能量槽。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] channels electrical power through your gems!", "[creature]通过你的宝石传导电能！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Malware", "恶意软件", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You may not play [creature] unless you are three or less damage away from losing the game.", "除非你再受到3点或更少伤害即败北时，否则无法使用[creature]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Marcescent", "凋而不落", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] attacks an opposing creature and it perishes, this card gains it's attack power.", "当[creature]击杀对面造物时，可获得该造物的攻击力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mason", "石匠", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, a random obelisk is created in your hand.", "使用[creature]时，你的手牌里会随机出现一座方尖碑。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mauler", "抨击者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will deal overkill damage directly to the opponent.", "[creature]造成的过量伤害将直接作用于对手。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Medicinal", "灵丹妙药", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it heals all friendly creatures by 2 and removes all negative sigils from friendly creatures.", "当[creature]入场时，治疗所有友方造物2点生命并移除其负面印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mirrific", "镜像传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, it's owner chooses a creature to gain the Mirror rorriM ability.", "[creature]阵亡时，其持牌人需选定下一个继承镜子子镜能力的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mockery", "嘲弄", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is played opposite an opponent's creature, it's stats change to mimic that creature's stats.", "若[creature]被放置在敌方造物对面，[creature]的力量与生命值将变为与该造物相同。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Morph Mover", "无形移态", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is drawn, this sigil is replaced by a random movement related sigil.", "抽到[creature]时，此印记将被随机替换为移动类印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Motivator", "斗志之源", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] strikes a creature, that creature gains 1 power.", "当[creature]攻击造物时，该造物获得1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Moxcraft", "玛的世界", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, a random mox is created in your hand.", "[creature]受到攻击时，你的手牌中将随机出现一张玛珂牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mox Max", "玛珂最大值", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] cannot be played unless two cards which grant its respective mox cost colour are present on the owners side of the board.", "若持牌人侧牌桌上未同时存在两张提供对应玛珂消耗颜色的卡牌，则无法使用[creature]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Moxwarp", "变形玛珂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is alive on the board, any enemy attack that enters a sapphire mox will be returned out of all ruby mox, and vice versa.", "当[creature]存活于牌桌时，任何进入蓝宝石玛珂的敌方攻击都将从所有红宝石玛珂处反弹，反之亦然。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mystery Mox", "神秘玛珂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is drawn, this sigil is replaced by a random gem-granting sigil.", "抽到[creature]时，此印记将被随机替换为一种宝石供应印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nature Of The Beast", "野性呼唤", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, a completely random creature is created in the owner's hand.", "当[creature]阵亡时，持牌人的手牌中会随机生成一只完全随机的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Obedient", "悉听尊便", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move to an empty space on the same side of the board, chosen by it's owner.", "持牌人回合结束时，[creature]将移动到己方场上的一个空位，具体位置由持牌人选择。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Odd Strike", "奇数打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will strike all opposing spaces in odd indexed columns when it attacks. Column index is counted from the left, starting at 1.", "[creature]攻击时会打击所有奇数索引列的对面位置。列索引从左至右计数，起始为1。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Omni Guardian", "全能守护者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, all friendly creatures on the board gain armour.", "当[creature]入场时，牌桌上所有友方造物均获得护甲。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] gives the Armored sigil to all friendly creatures.", "[creature]为所有友方造物赋予了身披坚甲印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Crusher", "碎石者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will gain 2 power when opposed by a terrain card.", "[creature]与地形卡对阵时将获得2点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Deerly Departed", "屠鹿者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will gain 2 power when opposed by a creature of the Hooved tribe.", "当[creature]对面出现有蹄类族群的造物时，获得2点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dog Gone", "猎犬者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will gain 2 power when opposed by a creature of the Canine tribe.", "当[creature]对阵犬类族群造物时，将获得2点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fowl Play", "戏鸟者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will gain 2 power when opposed by a creature of the Avian tribe.", "当[creature]对阵鸟类族群造物时，获得2点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Insectivore", "食虫者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will gain 2 power when opposed by a creature of the Insect tribe.", "当[creature]对面是昆虫类族群造物时，获得2点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Snakebite", "捕蛇者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will gain 2 power when opposed by a creature of the Reptile tribe.", "[creature]与爬行族造物对战时将获得2点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Orange Inspiration", "橙色灵感", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While[creature] is on the board, all friendly cards which cost an orange gem gain +1 power.", "当牌桌上有[creature]时，所有有以橙宝石为费用的友方卡牌增加1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Organ Thief", "器官盗贼", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] kills another creature, it's remains are created in your hand.", "当[creature]击杀另一只造物时，该造物的残骸会出现在你的手牌中。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Osteoklepty", "窃骨术", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it steals all of the opponents bones for it's owner.", "当[creature]入场时，它会窃取对手所有的骨头归其持牌人所有。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Other Side", "彼岸", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the board, any friendly creatures that die in combat will be returned to the owner's hand as skeletons.", "当[creature]在场时，所有在战斗中阵亡的友方造物将以骷髅形态返回持牌人手牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Painful Presence", "痛苦占场", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "On upkeep, all creatures adjacent to [creature] vertically, horizontally, or diagonally take 1 damage.", "在每个维持阶段，[creature]对面、相邻以及斜对角相邻的所有造物均受到1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Paralytic", "麻痹", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] strikes a creature, that creature loses 1 power.", "当[creature]攻击造物时，该造物损失1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Parthenogenesis", "单性生殖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies to combat, a larval form is left in it's old space that evolves into an exact clone after 1 turn.", "[creature]在战斗中死亡时，会在原位置留下幼虫形态，1回合后将进化为完全相同的克隆体。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Perfect Form", "完美形态", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the board, the gems cost of all friendly creatures is reduced by 1 gem.", "当牌桌上有[creature]时，所有友方造物的宝石消耗减少1颗。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Phantasmic", "移形换影", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the turn, [creature] will move in the direction inscribed in the sigil, phasing past obstacles and wrapping around to the other side of the board if movement is impossible.", "回合结束时，[creature]将沿印记标注方向移动，可穿透障碍物；若无法移动则绕至牌桌另一侧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Piercing", "穿刺", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] strikes a slot which has a creature queued behind it, the queued creature will take 1 damage.", "当[creature]攻击一个已有造物在后方排队的槽位时，排队的造物将受到1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pinnacle Strike", "巅峰打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will strike all opponent spaces when it attacks.", "[creature]会在攻击时打击所有敌方位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Point Nemo", "涨潮者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, all creatures on the board gain the Waterborne sigil.", "使用[creature]时，牌桌上所有造物均获得水袭印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Potent", "强力献祭", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is sacrificed, the creature it was sacrificed for gains a random sigil.", "当[creature]被献祭时，献祭获得的新卡将随机附加一个印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Preemptive Strike", "预先攻击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will attack once when first played.", "[creature]首次打出时会立即攻击一次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] makes a free attack!", "[creature]发动了一次先制攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Propagation", "传播者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] attacks an opposing creature and it perishes, a creature is created on the board to the left or right of this card.", "当[creature]击杀对面造物时，会在该卡牌左右两侧的牌桌位置生成一个造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Prophecy", "预言家", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, you may look at three cards from your deck and choose them in order. The next three cards you draw will be the cards you were shown, in the order that you chose them.", "当使用[creature]时，你可以检视牌组中的三张卡牌并按顺序选择。接下来你抽的三张牌将是你所选择的牌，且顺序与你在此处选择的顺序一致。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I will allow you to choose from three cards. These three cards will be the next three cards you draw from your deck, in the order you choose them here.", "我将允许你从三张卡牌中选择。这三张牌将是你接下来从牌组中抽到的牌，顺序与你在此处选择的顺序一致。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Choose three creatures, and alter your next draws.", "选择三个造物，改变你接下来的抽牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You know how this goes.", "你知道该怎么做的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Choose from three cards. These three cards will be the next three cards you draw from your deck, in the order you chose them.", "从三张卡牌中选择。这三张牌将是你接下来从牌组中抽到的牌，顺序与你在此处选择的顺序一致。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Choose your next three draws.", "选择你接下来要抽的三张牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hmmm... your new prophecy overwrites the old, it would seem.", "唔……看来你的新预言覆盖了旧的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your new prophecy overwrites the old!", "你的新预言覆盖了旧的！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hmm... very well", "唔……很好", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You do not have enough cards left for a prophecy.", "你剩余的卡牌不足以进行预言。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You don't have enough cards left to make a new prophecy!", "你剩余的卡牌不足以进行新的预言！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pure Heart", "纯净之心", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, a Pinnacle Mox is created in its place.", "当[creature]阵亡时，会在原地生成一张巅峰玛珂。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pushover", "软柿子", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is opposed by an enemy creature, the card will perish.", "若[creature]对面存在敌方造物，该卡牌将直接阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Put Sentry Here", "步哨工程师", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, a Sentry Drone is created to its left and right.", "使用[creature]时，会在其左右两侧各部署一台哨兵无人机。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Quad Strike", "四重攻击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will strike each opposing space to the left and right of the spaces across from it as well as striking the space in front of it an additional time.", "[creature]会攻击正对面的左右两侧位置各一次，并额外攻击正前方位置一次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Quickdraw", "即刻抽取", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will automatically play itself from your deck onto a random space at the start of each battle.", "[creature]会在每场战斗开始时自动从牌组中打出至随机空位。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ranger", "突击员", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it's owner may choose an opponent creature. That creature will take 10 damage.", "当[creature]入场时，其持牌人可选择一名敌方造物，该造物将受到10点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ravenous", "贪婪之噬", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] kills a creature, it will gain an additional strike against the opposing space until the end of combat.", "当[creature]击杀一只造物时，它将在本场战斗中额外攻击对面位置一次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Recharge", "蓄势", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it's owner regains 1 energy. Does not affect the current energy maximum.", "使用[creature]时，持牌人恢复1点能量。不影响当前能量上限。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Recycle", "回收利用", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the board, when any friendly creature perishes by combat a special card will be created in the owners hand. The card created depends on the temple of the friendly card which perishesd.", "当[creature]在场时，任何友方造物因战斗阵亡时，其持有者手牌中将生成一张特殊卡牌。生成的卡牌类型取决于阵亡友方卡牌所属的神殿。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Reflective", "镜面反击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is opposed by a creature, it gains power equal to the opposing creatures power.", "当[creature]对阵造物时，其力量将提升至与对面造物相同。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Regenerator", "万象更新", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the start of the owners turn, [creature] will heal 1 health.", "在持牌人回合开始时，[creature]将恢复1点生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] regenerates one health!", "[creature]恢复了1点生命值！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Rememberance", "纪念之花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it's owner may search their graveyard for a card. This sigil is replaced by that cards sigils.", "当使用[creature]时，其持牌人可从墓地检索一张卡牌，此印记将被该卡牌的印记所取代。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Remote Controlled", "遥控", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, a Remote Controller is created in the owner's hand. When the remote controller is struck, [creature] will attack the striker.", "使用[creature]时，持牌人将获得一个遥控器。当遥控器受到攻击时，[creature]会反击攻击者。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Reroute", "重新定向", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the turn, [creature] will swap places with a random creature on the same side of the board.", "回合结束时，[creature]将与同侧随机一名造物交换位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Resilient", "百折不挠", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "All damage taken by [creature] is reduced to 1.", "[creature]受到的所有伤害均降为1点。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Retreat", "退却", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, it will move to a random empty space on the same side of the board.", "当[creature]受到攻击时，会随机移动至牌桌同侧的空位。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Return Strike", "复仇攻击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, it attacks in retaliation.", "[creature]受到攻击时会进行反击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Revolve", "时针顺之魂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, all cards on the board are moved one space clockwise.", "使用[creature]时，牌桌上所有卡牌均会按顺时针方向移动一格。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ripper", "撕裂者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will move to and destroy any free, 1 health, non-terrain creatures played, and becomes stronger for each one consumed.", "[creature]会移动并消灭任何生命值为1的非地形造物，每消灭一个都会变得更强。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Rock Eater", "磐石吞噬者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a Terrain card is played on the same side of the board as [creature], it will be consumed, granting it's stats and sigils to the card.", "当[creature]同侧的牌桌出现地形卡时，该卡会被吞噬，其属性与印记将转移至这张地形卡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ruby Core", "红宝石核芯", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, a Ruby Vessel is created in its place.", "当[creature]阵亡时，会在原地生成一个红宝石容器。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Running Strike", "奔袭打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "After attacking, [creature] will move in the direction inscribed on the sigil, striking the space opposing it's destination as it does so.", "攻击后，[creature]将沿印记标注方向移动，并在移动过程中攻击目标位置对面的空间。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Rupture", "割裂之触", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] deals damage to a creature, half of that damage, rounded down, is also dealt to that creature's owner.", "当[creature]对造物造成伤害时，该伤害值的一半（向下取整）将同时作用于该造物的持有者。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sanguine", "血祭力量", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a creature is sacrificed, [creature] gains 1 power until the end of combat.", "当有造物被献祭时，[creature]的力量会临时增加1点直至战斗结束。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sapphire Core", "蓝宝石核芯", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, a Sapphire Vessel is created in its place.", "当[creature]阵亡时，会在原地生成一个蓝宝石容器。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sapphire Heart", "蓝宝石之心", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, a Sapphire Mox is created in its place.", "当[creature]阵亡时，会在原地生成一张蓝宝石玛珂。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Savage Ritual", "野蛮仪式", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is sacrificed, it attacks once before dying.", "当[creature]被献祭时，会在死亡前发动一次攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sentriple", "三重哨兵", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a creature moves into the space opposing [creature], they, as well as any creatures to their left or right, are dealt 1 damage.", "任何造物在进入[creature]对面的位置时，该造物及其左右相邻的造物均会受到1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sharp Bones", "骨骼刺甲", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, the striker is dealt damage equal to half the number of bones its owner has, rounded down.", "[creature]被攻击时，攻击者会受到等同于持牌人持有兽骨数量一半的伤害（向下取整）。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sharper Quills", "双重刺甲", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, the striker is dealt 2 damage.", "[creature]被攻击时，攻击者会受到2点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sharpest Quills", "三重刺甲", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, the striker is dealt 3 damage.", "[creature]被攻击时，攻击者会受到3点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sharp Shot", "致命触击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, it gains +2 power and attacks once before dying.", "当[creature]阵亡时，获得+2力量并在死亡前发动一次攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sigil Mimic", "印记模仿", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is played opposite an opponent's creature, this sigil is replaced by copies of that creatures sigils.", "若[creature]被放置在敌方造物对面，此印记将被替换为对方造物印记的复制。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will also strike the slot to the left of the opposing slot when it attacks.", "[creature]攻击时还会同时攻击对面位置左侧的槽位。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Siphon", "虹吸", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Creatures to the left of [creature] lose all attack power. That attack power is instead added to this card's power.", "[creature]左侧的造物失去全部攻击力，该攻击力会转移至本卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Skin Animator", "毛皮操偶师", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is alive on the board, all pelts gain it's attack power.", "当[creature]存活于牌桌时，所有兽皮卡牌将获得其攻击力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Skull Swarm", "骷髅双击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is alive and on the board, all Skeletons will strike twice when attacking.", "当[creature]存活于牌桌时，所有骷髅造物攻击时将进行两次打击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Slimeball", "黏液球", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it permanently stains the opposing slot with slime. Any creatures in a slimy slot without made of stone lose 1 power.", "当使用[creature]时，它会在对面的位置永久留下黏液痕迹。任何处于黏液位置且不具备磐石之身的造物都会损失1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Snapshot", "快照", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, the entire board reverts to the state it was in when [creature] was placed.", "[creature]阵亡时，整个牌桌将恢复至该造物登场时的状态。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Snare", "恶食陷阱", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played opposite a creature, that creature is ensnared and stored within the bearer, being released when the bearer perishes.", "当[creature]被放置在对立位置时，该位置的造物会被捕获并储存在持有者体内，持有者阵亡时释放被捕获的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Snip", "剪刀之魂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, the creature opposing it is removed from the board without triggering on-death effects.", "当使用[creature]时，其正对面的造物会被移出牌桌且不会触发死亡效果。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] cuts it's opponent in half.", "[creature]将对手一分为二", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Soak", "浸微浸消", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Any damage taken by [creature] is reduced by 1. Taking damage decrements the timer rendered on this sigil. When the timer rendered on this sigil reaches 0, the bearer will attack with power equal to it's health, then perish.", "[creature]受到的伤害减少1点。每次受伤会减少该印记显示的计时器数值。当计时器归零时，持有者将以等同于生命值的攻击力发动攻击，随后阵亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spiders Web", "蛛猎之网", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When any opponent card moves, with the exception of entering the board for the first time,[creature] will strike them for one damage.", "当任何敌方卡牌移动时（首次登场除外），[creature]会对其造成1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Splash Damage", "溅射伤害", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] strikes a slot, creatures to the left and right of the struck slot will be dealt 1 damage.", "当[creature]攻击一个位置时，被攻击位置左右两侧的造物均会受到1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Splash Zone", "溅射领域", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, the creatures to it's left or right are also dealt 1 damage.", "当[creature]被攻击时，其左右两侧的造物也会受到1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The damage dealt to [creature] splashes out to its comrades.", "对[creature]造成的伤害溅射到了其同伴身上。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Split Detonation", "对角引爆器", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies, diagonally adjacent enemy creatures are dealt 10 damage.", "当[creature]死亡时，对角相邻的敌方造物会受到10点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Split Sentry", "分裂哨兵", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a creature moves into the space opposing [creature], any creatures to their left or right are dealt 1 damage.", "当有造物进入[creature]对面的位置时，其左右两侧的造物均会受到1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spurred", "驱使者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] gains 1 power while the slot opposing it is occupied.", "当[creature]对面位置有造物时，增加1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Squirrel Dependent", "松鼠依赖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature]'s owner controls no Squirrels, [creature] will perish. If this card is a squirrel, it cannot support itself.", "若[creature]的持牌人打出的造物中没有松鼠，[creature]将阵亡。若该卡牌为松鼠，其不可自我维持。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Squirrel King", "松鼠王", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] attacks an opposing creature, a squirrel is created in the owner's hand.", "当[creature]攻击对面造物时，持牌人手牌中将出现一张松鼠牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Stalwart", "坚毅", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is moved from the space it was placed in, it will immediately move back, shunting any card which may have moved there in the meantime out of the way.", "若[creature]从其初始位置被移动，它会立即返回原位，并将期间占据该位置的卡牌强制移开。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sticky Trail", "黏性痕迹", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] enters a space on the board, it permantently marks that space with its slime. Any creatures that enter that space without the Made of Stone sigil lose 1 power.", "当[creature]进入牌桌某位置时，会永久用黏液标记该处。任何进入该位置且不具有磐石之身印记的造物将失去1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sturdy", "坚不可摧", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "All damage taken by [creature] is reduced by 1.", "[creature]受到的所有伤害均减少1点。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Subaquatic Spines", "水下刺", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will submerge during the opponent's turn. While submerged, opposing creatures attack it's owner directly, and will take 1 damage after attacking.", "[creature]会在对手回合潜入水下。潜水时，敌方造物将直接攻击其持有者，并在攻击后受到1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Summoner", "召唤师", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it's owner may choose from a selection of random cards to add to their hand.", "使用[creature]时，持牌人可从随机卡牌中选择一张加入手牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Supercharge", "超载充能", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it refreshes it's owner's energy to the current maximum.", "当使用[creature]时，会将持牌人的能量恢复至当前最大值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] restores it's owner's energy.", "[creature]为持牌人恢复了能量", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Surging Quills", "激流刺甲", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, the striker is dealt damage equal to the amount of times this has been struck.", "[creature]被攻击时，攻击者会受到等同于该[creature]被攻击次数的伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sweeping Strike Left", "横扫左击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will always attack the leftmost opponent creature. Overkill damage travels to the right instead of to the backlane.", "[creature]总是攻击最左侧的敌方造物。过量伤害会向右传递而非作用于后排。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sweeping Strike Right", "横扫右击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will always attack the rightmost opponent creature. Overkill damage travels to the left instead of to the backlane.", "[creature]总会攻击最右侧的敌方造物。过量伤害会向左传递而非作用于后排。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Switch Strike", "交替打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of its owners turn [creature] will alternate between striking to the left or right of the opposing slot when it attacks, in addition to striking the opposing slot.", "持牌人回合结束时，[creature]会在攻击对面位置的同时，交替攻击其左侧或右侧位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Swooping Strike", "翻飞打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will strike the opposing space a second time when attacking. Also, [creature]'s first strike each turn will occur directly, over opponent creatures.", "[creature]会在攻击时对对面位置发动第二次打击。此外，[creature]每回合的首次攻击将越过敌方造物直接命中对手。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Telepathic", "心灵感应", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the board, all friendly creatures will stike the most powerful opponent card when they attack.", "当[creature]在场时，所有友方造物攻击时会优先攻击敌方最强卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Tempting Target", "诱人的目标", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If an opponent creature would strike a space adjacent to [creature], it will instead strike [creature].", "若敌方造物试图攻击[creature]相邻位置，则改为攻击[creature]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Termatriarch", "白蚁后", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] and a different card bearing the Termite King sigil are alive and on the board, a termite will be created in your hand at the start of your turn.", "当[creature]与另一张带有白蚁王印记的卡牌同时存活在场时，你的回合开始时手牌中将生成一张白蚁卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Termite King", "白蚁王", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] and a different card bearing the Termatriarch sigil are alive and on the board, a termite will be created in your hand at the start of your turn.", "当[creature]与另一张带有白蚁后印记的卡牌存活于场上时，你的回合开始时将获得一张白蚁卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "TestSigil", "测试印记", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will most likely fuck shit up.", "[creature]多半会把事情搞砸。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thief", "盗贼", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will grant an amount of currency when it kills another creature.", "[creature]击杀其他造物时将获得一定数量的货币。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Time Travel", "时间旅行", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is played, the opponent is obligated to skip their next turn.", "当使用[creature]时，对手必须跳过其下一回合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'll pass my next turn.", "我会跳过我的下一回合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your opponent must skip their next turn!", "你的对手必须跳过下一回合！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ugh. Fine. I'll pass.", "呃。好吧。我跳过。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fair enough dear, I'll pass.", "很公平亲爱的，我选择跳过", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Tooth Puller", "拔牙器", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will add one damage to the scales in the owner's favour, regardless of it's attack power or obstruction.", "持牌人回合结束时，[creature]会无视其攻击力或阻挡状态，为天平增加1点对持牌人有利的伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Toxic", "剧毒之物", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck but does not perish, the striker perishes. Does not affect Made of Stone cards.", "当[creature]受到攻击但未阵亡时，攻击者将立即阵亡。对磐石之身卡牌无效。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s toxicity kills instantly!", "[creature]的剧毒一击必杀！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Traitor", "叛徒", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it will move onto the opponent's side of the board if unobstructed.", "当使用[creature]时，若路径未被阻挡，它将移动至对手侧牌桌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Trajectile Quills", "狙击刺甲", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, an opponent creature of the owner's choosing is dealt 1 damage.", "[creature]被攻击时，其持牌人可选定一个敌方造物造成1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Trampler", "撼地者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move in the direction inscribed on the sigil. Cards in the way will be killed, and grant +1 attack power to the sigil bearer.", "持牌人回合结束时，[creature]将向印记标注的方向移动。沿途的卡牌会被击杀，并使印记持有者获得+1攻击力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Transformer", "易形者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the turn, [creature] will transform to or from it's alternate form.", "回合结束时，[creature]会在当前形态与转化形态间切换一次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Transmogrification", "变形术", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] strikes a creature, that creature will be changed into another form.", "当[creature]攻击造物时，该造物会转变为另一种形态。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Trapjaw", "陷阱颚", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will attack to the left and the right of the opposing space if those slots are obstructed. If those slots are not obstructed, the strikes will be transferred to the directly opposing space.", "若对面位置的左右两侧被阻挡，[creature]会攻击这些位置；若未被阻挡，则攻击会转移至正对面的位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Trifling Sacrifice", "精良祭品", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] counts as 2 Blood rather than 1 Blood when sacrificed.", "[creature]在献祭时可算作2点血量，而非1点。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Trio", "禁忌三重奏", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, a copy of it is created to the left and right.", "当打出[creature]时，会在其左右两侧各生成一张相同的卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Triple Strike", "三重攻击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will strike the opposing space two extra times when attacking.", "[creature]会在攻击时额外攻击对面位置两次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Trophy Hunter", "战利品猎手", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will target the most powerful opposing card when it attacks.", "[creature]攻击时会锁定对面力量最强的卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Tuck And Roll", "缩滚乐", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of combat, [creature] will move to the free space opposed by the least amount of attack power, taking into account queued creatures, flight, and Touch of Death.", "战斗结束时，[creature]会移动到受攻击力最小的空闲位置，计算时考虑后排待命造物、飞行与死神之触。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Tug", "生拉硬拽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it will draw the queued opposing creature into the main opposing space, if possible.", "当打出[creature]时，若条件允许，它会将排队中的对方造物拖拽至正对面的主位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Twin Bond", "双生契约", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, a copy of it is created adjacent, killing other creatures to make room if there is none. These cards share a health pool, and when either card dies, it's twin will die as well.", "当使用[creature]时，会在相邻位置生成其复制体（若无空位则消灭其他造物腾出空间）。这两张卡牌共享生命值，其中任意一张死亡时，另一张也会随之死亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Twister", "星轮变换", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When struck, [creature] will transform to or from it's alternate form.", "当[creature]受到攻击时，会在当前形态与另一形态间转换。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Unbalanced Leadership", "失衡领袖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The creature directly to the left of [creature] gains 2 power.", "[creature]左侧相邻的造物获得2点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Under Pressure", "压力之下", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, 2 damage is dealt to the opponent creature. Also, when [creature] perishes, any adjacent creatures with this sigil also take ten damage.", "当[creature]阵亡时，对正对面的造物造成2点伤害。此外，当[creature]阵亡时，所有相邻且带有该印记的造物均会受到10点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] pops, dealing 2 damage to the opposing creature!", "[creature]爆裂，对正对面的造物造成2点伤害！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Unfocused Strike", "胡乱打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will target a random space when it attacks.", "[creature]攻击时会随机选择一个目标位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Unhammerable", "不可锤击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] cannot be killed using the hammer.", "[creature]无法被锤子击杀", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Unlucky", "命途多舛", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is alive on the board, cards played by the opponent have a chance to have debuffed stats, or lose a random sigil.", "当[creature]存活于牌桌时，对手打出的卡牌有几率被削弱状态值或随机失去一个印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Unspeakable", "不可言喻", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card you control resolves on the board without being played from your hand, you may choose to play [creature] from your hand or deck instead.", "当你打出的卡牌未经手牌使用便结算至牌桌时，可选择从手牌或牌组中打出[creature]替代。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Unstable Gems", "不稳定宝石", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on the board, all friendly creatures which cost a mox gem will gain the brittle sigil when played.", "当[creature]在场时，所有以玛珂宝石为费用的友方造物在打出时将获得脆骨印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] renders the gem cost creature unstable!", "[creature]使需要宝石的造物变得不稳定！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Valuable", "吉光片羽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will grant an amount of currency when it perishes.", "[creature]阵亡时将给予一定数量的货币。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Vampiric", "吸血鬼", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] attacks an opposing creature and it perishes, this card gains 1 health.", "当[creature]击杀对面造物时，可增加1点生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Venator", "易形猎手", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] kills another creature, it will transform to or from it's alternate form.", "当[creature]击杀另一只造物时，会在当前形态与第二形态间切换。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Vessel Shedder", "容器掉落", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move in the direction inscrybed in the sigil and drop an Empty Vessel in its old place.", "持牌人回合结束时，[creature]将向印记标注的方向移动，并在原地留下一个空容器。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Visceral", "感官异能", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is sacrificed, any leftover blood that is not used for the sacrifice will be converted into bones.", "当[creature]被献祭时，未被消耗的剩余血量将转化为骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Viva La Revolution", "革命万岁", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the start of its owners turn, [creature] will revolve all creatures on the board clockwise. Also, [creature] will not attack it's original owner.", "在持牌人回合开始时，[creature]会使牌桌上所有造物顺时针旋转。此外，[creature]不会攻击其原主人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Waterbird", "水鸟", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] strikes a slot containing a submerged creature, that creature will re-emerge and [creature] will attack it instead of attacking directly.", "若[creature]攻击的位置有潜水的造物，该造物会浮出水面，[creature]将转而攻击它而非直接攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wavering Strike", "摇摆打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will alternate between striking to the left or right of where it would otherwise attack at the end of each turn.", "[creature]会在每回合结束时交替攻击原本攻击位置的左侧或右侧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Weaver", "织网者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move in the direction inscribed on the sigil and leave a Web in it's old space. [creature] can move back over webs it has placed.", "持牌人回合结束时，[creature]将向印记标注的方向移动，并在原地留下一张蛛网。[creature]可以重新移动到自己放置的蛛网上。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Creatures in the insect tribe will not attack [creature].", "昆虫类族群的造物不会攻击[creature]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Weird Latch", "无形传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is drawn, this sigil is replaced by a random latch sigil.", "抽到[creature]时，此印记将被随机替换为某个传花类印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wild Shape", "狂野易形", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it transforms based on what gem colours its owner controls.", "当使用[creature]时，它会根据持牌人提供的宝石颜色进行变形。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] transforms based on its owner's gems...", "[creature]会根据持牌人的宝石进行变形……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wimpy", "怯战", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] deals damage to a creature, it will always deal 1 less damage than it should.", "[creature]对造物造成伤害时，实际伤害值总会比应有值少1点。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wing Clipper", "碎翼者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, all opponent creatures with the Airborne sigil lose the Airborne sigil.", "当[creature]入场时，所有带有空袭印记的敌方造物将失去该印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] grounds all its opponents!", "[creature]让所有对手落地了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wingrider", "骑翼者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If a friendly airborne creature is played while [creature] is in the owner's hand, this card will be played adjacent to it.", "当持牌人使用任意友方飞行造物时，若[creature]位于手牌中，该卡牌会自动部署至其相邻位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Woodsie Lord", "法树领主", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, Enchanted Pines are created on every empty space. An enchanted pine is defined as 0 power, 3 health.", "使用[creature]时，所有空位将生成附魔松树。附魔松树具有：0点力量、3点生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wriggle", "蠕形螨动", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played it deals 1 damage to all opponent creatures.", "当使用[creature]时，会对所有敌方造物造成1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Writhe", "垂死挣扎", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, it will attack all opposing spaces for 1 damage.", "[creature]阵亡时，会对所有正对面的位置造成1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ye filthy dog! Now one o' me maties needs to swab the poop deck!", "你这肮脏的野狗！现在得让我的水手去擦洗甲板了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "1d4", "四面骰", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil starts at 2, and will become a random number between 1 and 4 at the start of each subsequent turn.", "该印记代表的数值初始为2，之后每回合开始时将随机变为1至4之间的数字。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s power starts at 2, and will become a random number between 1 and 4 at the start of each subsequent turn.", "[creature]的力量初始为2，之后每回合开始时将随机变为1至4之间的数字。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ambitious", "野心家", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to highest power number present on the board, regardless of whether the card bearing it is friend or foe. Updates at the start of both the player and opponent turn.", "该印记代表的数值始终等于场上最高力量值（无论敌我）。在玩家与对手回合开始时更新。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s power is equal to the power of the creature with the highest attack power on the board. Recalculates each turn.", "[creature]的力量等于场上攻击力最高的造物力量值。每回合重新计算。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ant Plus Two", "#蚂蚁+2", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil is equal to the number of ants on the board, plus two.", "该印记代表的数值等于牌桌上蚂蚁数量加二。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s power is equal to the number of friendly ants on the board, plus two.", "[creature]的力量等于牌桌上所有友方蚂蚁数量加二。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Birds of a Feather", "鸟为媒", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the number of avian tribe creatures on the board, including opponent creatures.", "该印记代表的数值等于牌桌上所有鸟类族群造物的数量（包含对手的造物）。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s power is equal to the number of avian tribe creatures on the board, including opponent creatures.", "[creature]的力量等于牌桌上所有鸟类族群造物的数量（包含对手的造物）。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blood and Bone", "血与骨", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the bone cost of the opposing card, or double the blood cost of the opposing card.", "该印记代表的数值等于对面卡牌的骨头费用，或对面卡牌血量费用的两倍。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s power is equal to the bone cost of the opposing creature plus twice the blood cost of the opposing creature.", "[creature]的力量等于对面造物的骨头的费用加上其血量费用的两倍", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Boned", "不变之骨", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the number of bones the owner possessed when the bearer was placed. Does not fluctuate with bone count changes post placement.", "该印记代表的数值等于持牌人在放置该造物时持有的骨头数量。放置后骨头数量变化不会影响该数值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s health is equal to the number of bones the owner possessed when it was placed. Does not fluctuate with bone count changes post placement.", "[creature]的生命值等于持牌人在放置该造物时持有的兽骨数量。放置后兽骨数量变化不会影响该数值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Crab Dance", "螃蟹舞", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the number of crustacean tribe creatures on the board, including opponent creatures.", "该印记代表的数值等于牌桌上所有甲壳类族群造物的数量（包括对手的造物）。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s power is equal to the number of crustacean tribe creatures on the board, including opponent creatures.", "[creature]的力量等于牌桌上所有甲壳类族群造物的数量（包括对手的造物）。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Damage Dice", "伤害骰子", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil starts at 3, and will become a random number between 1 and 6 at the start of each subsequent turn.", "该印记初始数值为3，之后每回合开始时将随机变为1至6之间的数值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s power starts at 3, and will become a random number between 1 and 6 at the start of each subsequent turn.", "[creature]的攻击力初始为3，之后每回合开始时将随机变为1至6之间的数值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Damage Scaling", "伤害缩放", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to five minus the amount of damage until you lose the game.", "该印记代表的数值等于5减去你距离败北还需承受的伤害值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s power is equal to five minus the amount of damage until you lose the game.", "[creature]的力量等于5减去你距离败北还需承受的伤害值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Director Of The Board", "董事长", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the number of cards on the board, including opponent cards.", "该印记代表的数值等于牌桌上所有卡牌的数量，包括对手的卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s power is equal to the total number of cards on the board, including opponent creatures.", "[creature]的力量等于牌桌上所有卡牌的总数，包括对手的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Drawn Out", "抽离之力", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the number of cards you have drawn this turn while it's bearer is on the board.", "该印记代表的数值等于本回合内当持有者位于牌桌上时你抽牌的数量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s power is equal to the number of cards you have drawn this turn while it is on the board.", "[creature]的力量等于本回合内当它位于牌桌上时你抽牌的数量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fabled", "稀有传奇", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to twice the number of rare creatures on the board.", "该印记代表的数值等于牌桌上稀有造物数量的两倍。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s power is equal to twice the number of rare creatures on the board.", "[creature]的力量等于牌桌上稀有造物数量的两倍。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Firepower", "火焰之力", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the number of creatures on the board with the Burning sigil.", "该印记代表的数值等于场上带有燃烧印记的造物数量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s power is equal to the number of creatures on the board with the Burning sigil.", "[creature]的力量等于场上带有燃烧印记的造物数量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Follow The Leader", "跟随领导", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the attack power of the first card in your hand.", "该印记代表的数值将等于你手牌中第一张卡牌的攻击力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s power is equal to the attack power of the first card in your hand.", "[creature]的力量等于你手牌中第一张卡牌的攻击力", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Greenhorn", "绿芽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to twice the number of Green gems on the owner's side of the board when the bearer was played.", "该印记代表的数值等于卡牌打出时持牌人侧牌桌上绿宝石数量的两倍。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]s health is equal to twice the number of Green gems on it's owner's side of the board when it was played.", "[creature]的生命值等于其打出时持牌人侧牌桌上绿宝石数量的两倍。", Language.ChineseSimplified);
