            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blocked on both sides. No Eggs for you.", "两侧堵塞。没有蛋给你。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No empty spaces. No firs for you.", "没有空位，无法给你大冷杉。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Explosive Squirrel Reproduction", "松鼠泛滥", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, fill your side deck with squirrels.", "当打出[creature]时，你的副牌组将被松鼠填满。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hauler", "运输者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move in the direction inscribed in the sigil. Adjacent friendly creatures will be pulled in the same direction.", "在持牌人的回合结束时，[creature]会向印记标注的方向移动。相邻的友方生物会被拉向同一方向。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Heart Swap", "心灵交换", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, all cards on the board swap their power and health.", "当打出[creature]时，场上所有卡牌将交换其力量和生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mutual Hire", "相互雇佣", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, a copy of it is created in the opposing space.", "当打出[creature]时，会在对方区域创造一个它的副本。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Charged Barrage", "充能弹幕", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 4 life to increase the power of all cards on your side of the board by 1.", "消耗4点生命值，增加同侧场上所有卡片1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Health Roll", "生命骰子", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 1 Energy to set the health of [creature] randomly between 1 and 6.", "消耗1点能量，将[creature]的生命值设置为1到6的随机数字。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sigil Roll", "印记骰子", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 4 Life to add a random sigil to [creature].", "消耗4点生命值，将一个随机印记添加到[creature]上。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wildlife Camera", "野兽相机", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] kills another card, a copy of the killed card is created in your hand.", "当[creature]击杀另一张卡牌时，会在你的手中创造该被击杀卡牌的副本。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dusty Quill", "尘埃羽毛笔", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Whenever a creature dies while [creature] is on the board, a corpse is raised in it's place. Corpses are defined as: 0/1.", "当[creature]在场上时，若其他造物死亡，会在其位置召唤一具尸体。尸体被定义为：0点力量，1点生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bleached Brush", "漂白之笔", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, the opposing card will lose all its sigils.", "当[creature]被打出时，对位的卡牌将失去所有印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Strong Soul", "剽悍之魂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] may not recieve sigils from another card.", "[creature]不能接受其他造物的印记转移。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Weak Soul", "孱弱之魂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] may not have its sigils transferred to another card.", "[creature]不能转移自己的印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nonexistent Soul", "缥缈之魂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] may not have sigils transferred to, or from this card.", "[creature]不能接受其他造物的印记转移和转移自己的印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pyrophobia", "恐火症", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] may not be buffed at campsites.", "[creature]不能在篝火事件获得增益。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pyrophobia (Attack)", "恐火症（力量）", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] may not recieve power buffs from campsites.", "[creature]不能从篝火事件获得力量增益。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pyrophobia (Health)", "恐火症（生命）", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] may not recieve health buffs from campsites.", "[creature]不能从篝火事件获得生命增益。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Repulsion", "排斥", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] may not attack.", "[creature]不能攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mysterious Mushrooms", "神秘蘑菇", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the turn, if there is a card on either side of [creature], they are fused together.", "回合结束时，如果[creature]的两侧有牌，它们会融合在一起。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move the opposing card and itself in the direction inscribed in the sigil.", "在持牌人的回合结束时，[creature]会带动对位卡牌和自身向印记标注的方向移动。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Counter Attack", "反伤刺甲", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Once [creature] is struck, the striker is then dealt damage equal to this card's attack.", "一旦[creature]被攻击，攻击者将受到等同于这张卡牌攻击力的伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mirror Counter", "镜面刺甲", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Once [creature] is struck, the striker is then dealt damage equal to the striker's attack.", "一旦[creature]被攻击，攻击者将受到等同于攻击者攻击力的伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Board Shifter", "全场移相", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move in the direction inscribed in the sigil. All cards will shift in the same direction, looping to the other edge of the board.", "在持牌人的回合结束时，[creature]将按照印记标注的方向移动。所有卡牌将朝同一方向移动，绕到场地的另一侧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Board Shifter (Owner)", "友军移相", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move in the direction inscribed in the sigil. Friendly cards will shift in the same direction, looping to the other edge of the board.", "在持牌人的回合结束时，[creature]将按照印记标注的方向移动。友方卡牌将朝同一方向移动，绕到场地的另一侧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Board Shifter (Opposing)", "敌军移相", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will move in the direction inscribed in the sigil. The opposing cards will shift in the same direction, looping to the other edge of the board.", "在持牌人的回合结束时，[creature]将按照印记标注的方向移动。对手的卡牌将朝同一方向移动，绕到场地的另一侧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Deathbell", "丧钟", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the start of the battle phase, [creature] perishes.", "每个回合开始时，[creature]会消亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Clockwise", "顺时针", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, move all cards on the board clockwise.", "当一张[creature]被打出时，场上所有卡牌顺时针移动一格。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Stubborn", "顽固不化", Language.ChineseSimplified);
