            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Shield Absorption", "坚盾吸收", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, all creatures lose their shields. This creature gains 1 attack for each shield lost this way.", "当[creature]被使用时，所有造物都会失去护盾。每失去一个护盾，该造物便增加1点攻击力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Rapid Recycle", "极速回收", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 1 Energy to choose another card you control to die and immediately respawn. You may only activate this ability once per turn.", "消耗1点能量选择你控制的另一张卡牌死亡并立即重生。每回合只能激活此能力一次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Supercharge", "超载充能", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spend one fuel: friendly cards gain 1 power until the end of the turn. This ability can only be activated once per turn.", "消耗1点燃料：友方卡牌获得1点力量，持续至回合结束。该能力每回合只能激活一次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Copy and Paste", "复制粘贴", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The controller of [creature] chooses two cards they control. The second card's sigils are replaced with the sigils of the first.", "[creature]的持牌人需选择其控制的两张卡牌，将第二张卡的印记替换为第一张卡的印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Choose a card to copy sigils from", "选择要复制印记的卡牌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Choose a card to paste sigils onto", "选择要粘贴印记的卡牌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cannon Fire", "炮火轰击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spend one fuel: Create a Blast! card in hand. Blast! is defined as a spell that costs 2 energy and sets the target on fire. This ability can only be activated once per turn.", "消耗1点燃料：在手牌中生成一张“轰爆！”牌。“轰爆！”具有：2点能量，是可以使目标着火的法术牌。该能力每回合只能激活一次。", Language.ChineseSimplified);
             SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spend one fuel: Create a Blast! card in hand. Blast! is defined as a command that costs 2 energy and sets the target on fire. This ability can only be activated once per turn.", "消耗1点燃料：在手牌中生成一张“轰爆！”牌。“轰爆！”具有：2点能量，是可以使目标着火的命令牌。该能力每回合只能激活一次。", Language.ChineseSimplified);
           SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Recharge Reserves", "充能储备", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 2 Energy to create a Charge in your hand. Charge is defined as a spell that refills 1 Energy when played.", "消耗2点能量在手牌中生成一张“充能！”牌。“充能！”是使用时可恢复1点能量的法术牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 2 Energy to create a Charge in your hand. Charge is defined as a command that refills 1 Energy when played.", "消耗2点能量在手牌中生成一张“充能！”牌。“充能！”是使用时可恢复1点能量的命令牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Holo Weaver", "全息编织者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Once per turn, create a Defend! card in hand. Defend! is defined as a spell that costs 2 energy and gives the target a shield.", "每回合可在手牌中生成一张“防御！”牌。“防御！”具有：2点能量，是能为目标提供护盾的法术牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Once per turn, create a Defend! card in hand. Defend! is defined as a command that costs 2 energy and gives the target a shield.", "每回合可在手牌中生成一张“防御！”牌。“防御！”具有：2点能量，是能为目标提供护盾的命令牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Future Sight", "未来视界", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Look at the top card of your deck. You can play it.", "查看牌组顶端的卡牌。你可以直接使用它。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fossil Fuel", "化石燃料", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Provides an additional energy cell.", "可提供1点额外能量槽。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dispenser", "售货机", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gain a random item.", "获得一个随机道具。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Overcharge", "电能过载", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 4 Energy to increase the Power of this card by 1.", "消耗4点能量使该卡牌的力量+1。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Enrage", "超频领袖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay Energy equal to the Power of this card plus 2 to increase the Power of this card by 1.", "消耗等同于该卡牌力量值加2点的能量，使该卡牌的力量值增加1点。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Drive", "驱动", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spend one fuel: move in the direction inscribed in this sigil. This ability can only be activated once per turn.", "消耗1点燃料：向该印记标注的方向移动。该能力每回合只能发动一次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "D-Pad", "十字键", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Move to an adjacent empty lane.", "移动至相邻的空位", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "D-Pad Left", "十字键左进", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Move to the lane to the left, if it is empty.", "若左侧路线为空，则移动至该位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "D-Pad Right", "十字键右进", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Move to the lane to the right, if it is empty.", "若右侧路线为空，则移动至该位置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Submerge", "下潜", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spend one fuel: submerge during the opponent's turn. While submerged, opposing creatures attack its owner directly.", "消耗1点燃料：在对手回合期间潜入水下。潜水时，敌方造物将直接攻击其持有者。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Tow Hook", "拖拽钩", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spend one fuel: tow an opposing creature to your side of the board until end of turn. Creatures being towed cannot be hammered. This ability can only be activated once per turn.", "消耗1点燃料：将对方一个造物拖曳至你这一侧牌桌，持续到回合结束。被拖曳的造物无法被锤击。该能力每回合只能激活一次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Button Pusher", "按键狂魔", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, all activated sigils and sigils that trigger on play or on death are activated.", "当使用[creature]时，所有可激活印记以及登场或死亡时触发的印记都会立即激活。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Parasitic Control", "寄生控制", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, all opposing cards are moved to your side of the board.", "当使用[creature]时，所有对手卡牌都将转移至你这一侧的牌桌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Energy Power", "能量力量", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the number of times the player has spent energy this turn.", "该印记代表的数值等于玩家本回合消耗能量的次数。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Holo Trap", "全息陷阱", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil perishes, the creature opposing it perishes as well. A Vessel is created in your hand.", "带有该印记的卡牌阵亡时，它对面的造物也会同时阵亡。你的手牌中将出现一张容器卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Brittle Lord", "脆骨王", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "As long as [creature] is alive, cards with Brittle have Unkillable.", "只要[creature]存活，带有脆骨印记的卡牌都将获得不死之虫。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Buck Wild", "狂野冲撞", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Upon taking damage, this card will charge into the opposing slot, killing anything in its way.", "受到伤害时，此卡牌会冲入对面空位，沿途消灭一切阻挡之物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Burning Slot (1)", "燃烧卡槽(1)", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This slot is on fire! It deals one damage to the card in it at the end of the turn. This effect lasts one turn.", "这个卡槽着火了！它会在回合结束时对其中卡牌造成1点伤害。该效果持续1回合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Burning Slot (2)", "燃烧卡槽(2)", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This slot is on fire! It deals one damage to the card in it at the end of the turn. This effect lasts two turns.", "这个卡槽着火了！它会在回合结束时对其中卡牌造成1点伤害。该效果持续2回合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Burning Slot (3)", "燃烧卡槽(3)", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This slot is on fire! It deals one damage to the card in it at the end of the turn. This effect lasts three turns.", "这个卡槽着火了！它会在回合结束时对其中卡牌造成1点伤害。该效果持续3回合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Burning Slot (4)", "燃烧卡槽(4)", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This slot is on fire! It deals one damage to the card in it at the end of the turn. This effect lasts four turns.", "这个卡槽着火了！它会在回合结束时对其中卡牌造成1点伤害。该效果持续4回合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Burnt Out", "过燃", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies, it sets the land on fire for three turns.", "当[creature]死亡时，其所在位置将燃烧3回合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "on fire", "燃烧中", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Catch Fire", "引燃", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] targets a slot, the target is set on fire.", "当[creature]选定一个位置时，该位置将被引燃。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Transforms When Unpowered", "失能退化", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is NOT within a circuit at the beginning of the turn, it will transform back into its original form.", "若回合开始时[creature]未处于回路中，则会退化回原始形态。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Waterborne When Unpowered", "失能水袭", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is NOT within a circuit it submerges itself during its opponent's turn. While submerged, opposing creatures attack its owner directly.", "若[creature]不在回路中，则会在对手回合潜入水下。潜水时，敌方造物将直接攻击其持牌人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Short Circuit", "充能短路", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is within a circuit at the beginning of the turn, create a Zap! in your hand. Zap! is defined as a spell that costs 2 energy and deals 1 damage to any target.", "若[creature]在回合开始时处于回路中，则会在你的手牌中生成一张“电击！”牌。“电击！”具有：2点能量，是可对任意目标造成1点伤害的法术牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is within a circuit at the beginning of the turn, create a Zap! in your hand. Zap! is defined as a command that costs 2 energy and deals 1 damage to any target.", "若[creature]在回合开始时处于回路中，则会在你的手牌中生成一张“电击！”牌。“电击！”具有：2点能量，是可对任意目标造成1点伤害的命令牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Transforms When Powered", "充能进化", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is within a circuit at the beginning of the turn, it will transform into a stronger form.", "如[creature]在回合开始时处于回路中，则会进化为更强形态。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Explodonate When Powered", "充能樱桃炸", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is within a circuit, it detonates itself and deals 10 damage to all five adjacent spaces.", "如[creature]处在回路中，则会引爆自身并对周围五个相邻位置各造成10点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mental Gemnastics When Powered", "充能金属风宝", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, if it is within a circuit, its owner draw cards equal to the number of Mox Cards on their side of the game board.", "当使用[creature]时，若其处于回路中，持牌人可抽取等同于己方牌桌上玛珂牌数量的手牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Trap When Powered", "充能陷阱", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil perishes while in a circuit, the creature opposing it perishes as well. A Vessel is created in your hand.", "如带有该印记的卡牌阵亡时处在回路中，它对面的造物也会同时阵亡。你的手牌中将出现一张容器卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Unkillable When Powered", "充能不死", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is within a circuit when it perishes, a copy of it is created in your hand.", "如[creature]阵亡时处在回路中，则会在你的手牌中生成一张同样的牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Splice Conduit", "接合管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] completes a circuit, all cards within that circuit are moved towards it, splicing with it if possible.", "如果[creature]构成完整回路，该回路中的所有卡牌都会向其靠拢，并在可能时与之接合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Stinky Conduit", "臭臭管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cards within a circuit completed by [creature] have Stinky.", "处在由[creature]构成的回路中的卡牌将获得臭臭。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Airborne Conduit", "空袭管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cards within a circuit completed by [creature] have Airborne.", "处在由[creature]构成的回路中的卡牌获得空袭。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gas Conduit", "燃气管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cards within a circuit completed by [creature] have Fire Strike and Burnt Out.", "处在由[creature]构成的回路中的卡牌会获得火焰打击和过燃效果。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bedazzling Conduit", "璀璨管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cards within a circuit completed by [creature] are Gemified.", "处在由[creature]构成的回路中的卡牌将被宝石化。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Static Electricity", "静电传导", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will cause all friendly cards to behave as if they are part of a completed conduit.", "[creature]会使所有友方卡牌表现得如同处于完整回路中。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gas Generator", "燃气发电机", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "As long as it has fuel, [creature] will cause all friendly cards to behave as if they are part of a completed conduit.", "只要燃料充足，[creature]就能让所有友方卡牌表现得如同处于完整回路中。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Conduit Protector", "管道守护者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is within a completed circuit, damage that would be dealt to the conduits is dealt to this creature instead.", "当[creature]处于完整回路中时，原本会作用于管道的伤害将转由该造物承担。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dead Byte", "死亡字节", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil perishes, take 1 damage.", "带有该印记的卡牌阵亡时，你会受到1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Double Sprinter", "双重冲刺", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, a card bearing this sigil will move in the direction inscribed in the sigil twice.", "持牌人回合结束时，带有该印记的卡牌会按照印记标记的方向连续移动两次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Brittle Summoner", "脆骨召唤师", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, a random brittle card is created in your hand.", "使用[creature]时，你的手牌中会随机生成一张脆骨卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Iterate", "迭代", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, a copy of it with a different cost is created in your hand.", "使用[creature]时，你的手牌中将出现一张费用不同的复制卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Tinkerer", "机械师", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, create three spell cards selected at random.", "当使用[creature]时，随机生成三张法术牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, create three command cards selected at random.", "当使用[creature]时，随机生成三张命令牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Phasers Ready", "相位炮就绪", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, create two Zap! cards in hand. Zap! is defined as a spell that costs 2 energy and deals 1 damage to any target.", "使用[creature]时，在手牌中生成两张“电击！”牌。“电击！”具有：2点能量，是对任意目标造成1点伤害的法术牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, create two Zap! cards in hand. Zap! is defined as a command that costs 2 energy and deals 1 damage to any target.", "使用[creature]时，在手牌中生成两张“电击！”牌。“电击！”具有：2点能量，是对任意目标造成1点伤害的命令牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Combat Research", "作战研习", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] deals damage, create a Forced Upgrade card in hand. Forced Upgrade is defined as a spell that costs 2 energy and caused any target to upgrade to a new version.", "当[creature]造成伤害时，在手牌中生成一张强制升级牌。强制升级具有：2点能量，是可使任意目标升级为新版本的法术牌。", Language.ChineseSimplified);
           SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] deals damage, create a Forced Upgrade card in hand. Forced Upgrade is defined as a command that costs 2 energy and caused any target to upgrade to a new version.", "当[creature]造成伤害时，在手牌中生成一张强制升级牌。强制升级具有：2点能量，是可使任意目标升级为新版本的命令牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Electric", "电力制导", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] declares an attack, it will deal half the damage to cards adjacent to the target.", "当[creature]发动攻击时，会对目标相邻的卡牌造成一半伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Green Mox Buff", "绿色玛珂增益", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it gains two health for each Green Mox its owner controls.", "当使用[creature]时，每打出一张绿色玛珂，该造物便获得2点生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Emerald Blessing", "绿宝石祝福", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] provides +2 Health to all creatures you control.", "[creature]为你打出的所有造物提供+2点生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Emerald Shard", "绿宝石碎片", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] would be struck, an Emerald Mox is created in its place and a card bearing this sigil moves to the right.", "当[creature]即将受到攻击时，会在其位置生成一颗绿宝石玛珂，同时带有该印记的卡牌会向右移动。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Armor Giver", "坚盾予者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] enters, all opposing cards gain Nano Armor.", "当[creature]入场时，所有敌方卡牌获得纳米铠甲。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Energy Siphon", "能量虹吸", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Whenever another card you own dies in combat, [creature] creates a Charge in your hand. Charge is a spell that refills 1 Energy when played.", "当你拥有的其他卡牌在战斗中死亡时，[creature]会在你的手牌中生成一张“充能！”牌。“充能！”是使用时可恢复1点能量的法术牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Whenever another card you own dies in combat, [creature] creates a Charge in your hand. Charge is a command that refills 1 Energy when played.", "当你拥有的其他卡牌在战斗中死亡时，[creature]会在你的手牌中生成一张“充能！”牌。“充能！”是使用时可恢复1点能量的命令牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Explodonate", "樱桃炸", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies, it explodes and deals 10 damage to all five adjacent slots.", "当[creature]死亡时，会爆炸并对周围五个相邻位置各造成10点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fire Strike", "火焰打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] attacks, it sets the target space on fire for three turns.", "当[creature]攻击时，会使目标位置燃烧3回合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fire Strike When Fueled", "燃料火击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] attacks, if it is fueled, it consumes one fuel to set the target space on fire for three turns.", "当[creature]攻击时若处于填充燃料状态，将消耗1点燃料使目标区域燃烧3回合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Flame Stoker", "煽风点火", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While [creature] is on board, all fires you start will be stronger, causing them to last one turn longer.", "当[creature]在场时，由你点燃的所有火焰都会更加强力，持续时间延长一回合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fling", "抛掷", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, if one or more cards was sacrificed to play it, [creature] deals damage to a card of the player's choice equal to the total attack value of all sacrificed cards.", "当[creature]被使用时，若曾献祭一张或多张卡牌来使用它，[creature]将对玩家选择的一张卡牌造成等同于所有被献祭卡牌攻击力总和的伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Upgrade", "强制升级", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] targets a slot, the target is immediately upgraded to a stronger version of itself.", "当[creature]选定一个位置时，该位置上的单位会立即升级为更强版本。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blood Donation", "献血装置", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "As long as [creature] is alive, all friendly cards can be sacrificed to pay blood costs even if they normally could not.", "只要[creature]存活，所有友方卡牌，就算它们通常不可被献祭，也均可用于支付血量费用。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Stone Giver", "磐石之赠", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "As long as [creature] is alive, all friendly cards are Made of Stone", "只要[creature]存活，所有友方卡牌都将获得磐石之身。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dust Giver", "风尘之赠", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "As long as [creature] is alive, all friendly cards have Magic Dust.", "只要[creature]存活，所有友方卡牌都会获得魔法粉尘。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Coal Roller", "煤炭滚轮", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "As long as [creature] is alive, all friendly cards which have fuel are Stinky.", "只要[creature]存活，所有拥有燃料的友方卡牌都会获得臭臭。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gem Ignition", "宝石点火器", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "As long as [creature] is alive, all friendly gems have +1 Power, Flame Strike, Brittle, and Burnt Out.", "只要[creature]存活，所有友方宝石获得+1点力量、火焰打击、脆骨与过燃效果。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fuel Strike", "燃料打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] consumes one fuel to attack and can only attack if it has fuel.", "[creature]每次攻击需消耗1点燃料，且仅在有燃料时才能攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This card carries an external fuel source which powers one or more of its sigils. When a card's fuel tank empties, those sigils cease to function.", "该卡牌带有外部燃料源，可为它的一个或多个印记提供能量。当燃料耗尽时，这些印记将停止运作。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fuel", "燃料", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fuel Shield", "燃料护盾", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "As long as [creature] has fuel, it takes damage by losing fuel instead of health.", "只要[creature]还有燃料，受到伤害时会优先消耗燃料而非生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fuel Siphon", "燃料虹吸", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Whenever [creature] deals damage directly to the opponent, it gains that much fuel.", "当[creature]直接对对手造成伤害时，获得等量燃料。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Full of Blood", "血液满溢", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] can be sacrificed to pay blood costs.", "[creature]可被献祭以支付血量费用。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Full of Oil", "燃油满溢", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies, it adds 3 health to each card on either side and across from it.", "当[creature]死亡时，会为它两侧及对面的每张卡牌增加3点生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fully Loaded", "满载", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies, it leaves a permanent +1 attack bonus in the lane it occupied.", "[creature]死亡时，会在其所在列留下永久的+1攻击力加成。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The card in this slot has its power increased by one.", "该卡槽的卡牌将获得+1攻击力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Defend", "防御", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] targets a slot, the target gains Nano Armor.", "当[creature]选定一个位置时，目标将获得纳米铠甲。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blue Mox Gift", "蓝色玛珂之赠", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, if you control an Blue Mox, gain a random card.", "[creature]阵亡时，若你打出了蓝色玛珂，则获得一张随机卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blue Mox Looter", "蓝色玛珂劫掠", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] deals damage directly, if you control a Blue Mox, draw a card for each damage dealt.", "当[creature]直接造成伤害时，若你打出了蓝色玛珂，每造成1点伤害即可抽一张牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Purist With Blue", "纯蓝主义", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If the owner of [creature] controls a Blue Mox, cards opposing [creature] have their sigils removed.", "若[creature]的持牌人打出有一张蓝色玛珂牌，则与[creature]对战的卡牌将失去所有印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Annoying Without Green", "无绿生厌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The creature opposing [creature] gains 1 power unless the owner of [creature] also controls a Green Mox.", "若[creature]的持牌人未打出绿宝石，则与之对阵的造物获得1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Green Mox Gift", "绿色玛珂之赠", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, if you control a Green Mox, gain a random card.", "[creature]阵亡时，若你打出了绿色玛珂，则获得一张随机卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Brittle Without Orange", "无橙即碎", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "After attacking, [creature] will perish if its owner does not control an Orange Mox.", "攻击之后，若持牌人未打出橙色玛珂，[creature]会自毁。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Orange Mox Airborne", "橙色玛珂空袭", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you control an Orange Mox, [creature] will ignore opposing cards and strike an opponent directly.", "若你打出了橙色玛珂，[creature]将无视对面卡牌直接攻击对手。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Orange Mox Printer", "橙色玛珂打印机", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you control an Orange Mox, [creature] will draw a card from your side deck at the start of every turn.", "若你打出了橙色玛珂，[creature]将在每回合开始时从副牌组抽一张牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gem Strike", "宝石打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] attacks once for each gem provider its owner controls.", "[creature]会根据持牌人打出的宝石提供器数量进行多次攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Emerald Power", "绿宝石力量", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the number of green gems its owner controls.", "该印记代表的数值等于持牌人控制的绿宝石数量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hopper", "跑赢", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of each turn, [creature] moves to an empty space of its owner's choosing.", "每回合结束时，[creature]会移动到持牌人指定的空位。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Kraken Transformer", "克拉肯易形", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the beginning of your turn, [creature] will transform to, or from, Kraken mode.", "在你的回合开始时，[creature]会在当前状态和克拉肯状态间切换一次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Annoying Latch", "恼人传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the Annoying sigil.", "[creature]阵亡时，其持牌人需选定下一个继承惹人生厌印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Death Latch", "死亡传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will die at the end of the turn. When [creature] perishes, its owner chooses a creature to gain this sigil. This will repeat.", "[creature]将在回合结束时死亡。当[creature]阵亡时，其持牌人需选定下一个继承该印记的造物。此过程将循环往复。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "First Latch", "首印传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the first sigil this card has.", "[creature]阵亡时，其持牌人需选定下一个继承该卡牌首个印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Airborne Latch", "空袭传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the Airborne sigil.", "[creature]阵亡时，其持牌人需选定下一个继承空袭印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Conduit Latch", "管道传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the Null Conduit sigil.", "[creature]阵亡时，其持牌人需选定下一个继承空无管道印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Rampage Latch", "冲撞传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the Rampager sigil. The target will immediately rampage once when the latch is applied.", "[creature]阵亡时，其持牌人需选定下一个继承横冲直撞印记的造物。目标获得印记时将立即发动一次冲撞攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Stinky Latch", "臭臭传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the Stinky sigil.", "[creature]阵亡时，其持牌人需选定下一个继承臭臭印记的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Swapper Latch", "交换传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] perishes, its owner chooses a creature to gain the Swapper sigil. The target will immediately swap stats once when the latch is applied.", "[creature]阵亡时，其持牌人需选定下一个继承交换器印记的造物。目标获得印记时将立即触发一次属性交换。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Lootbox", "战利品箱", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Whenever [creature] kills a card, a random card is created in your hand.", "当[creature]击杀一张卡牌时，你的手牌中会随机生成一张卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Synchronized Fate", "生死与共", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies, you lose the game.", "当[creature]死亡时，你将输掉游戏。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Magic Dust", "魔法粉尘", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies, it leaves one of its gems behind on the board. Any future cards in that space will provide those gems.", "当[creature]死亡时，会在牌桌上留下一颗宝石。之后放置在该位置的卡牌都会提供相同类型的宝石。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blue Mox Slot", "蓝色玛珂卡槽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The card in this slot will provide a Blue Mox.", "放置在该卡槽的卡牌会提供蓝色玛珂。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Orange Mox Slot", "橙色玛珂卡槽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The card in this slot will provide an Orange Mox.", "放置在该卡槽的卡牌会提供橙色玛珂。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Green Mox Slot", "绿色玛珂卡槽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The card in this slot will provide a Green Mox.", "放置在该卡槽的卡牌会提供绿色玛珂。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Great Mox Slot", "至尊玛珂卡槽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The card in this slot will provide all three moxen.", "放置在该卡槽的卡牌会同时提供三种玛珂。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Miner", "探矿者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] buries itself during its opponent's turn. While buried, opposing creatures attack its owner directly. When it comes back up, it creates a card in its owner's hand.", "[creature]会在对手回合时潜入地下。潜地期间，敌方造物将直接攻击其持牌人。当它重返地面时，会为持牌人创造一张新卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Rubber Stamp", "橡皮图章", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Whenever you play [creature], it becomes a copy of another creature of your choosing. If this creature has other abilities, those abilities will be transferred (up to the maximum of 4).", "当你使用[creature]时，它会变成你选择的另一个造物的复制品。若该造物具有其他能力，这些能力也将被转移（最多4个）。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Launch Missile", "导弹发射器", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] can skip its attack this turn to launch a missile that lands on the next turn, splashing damage to adjacent spaces. Use carefully - ammo is limited.", "[creature]可跳过本回合攻击以发射导弹，导弹将在下回合落地并对相邻位置造成溅射伤害。弹药有限，请谨慎使用。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Launch Self", "自我发射器", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] can sacrifice itself to launch a missile that lands on the next turn, splashing damage to adjacent spaces.", "[creature]可牺牲自己发射一枚导弹，导弹将在下回合落地并对相邻位置造成溅射伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Flammable", "易燃", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies, it detonates and sets adjacent spaces on fire for three turns.", "当[creature]死亡时，它会爆炸并使相邻位置燃烧3回合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Arsonist", "纵火狂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it sets all spaces on the board on fire for four turns.", "当使用[creature]时，牌桌上所有位置将燃烧4个回合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Refueler", "加油站", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "As long as it has fuel, [creature] will attempt to move beside any creatures you play that also use fuel. Each turn, [creature] will refuel adjacent creatures.", "只要燃料充足，[creature]会主动靠近你打出的其他需燃料造物。每回合为相邻造物补充燃料。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Second Chance", "死灰复燃", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Whenever a friendly non-Brittle creature dies, if [creature] is on the battlefield, a 1/1 skeleton will be created in its place.", "当友方非脆骨造物死亡时，若[creature]在战场上，将在其位置生成一个1/1的骷髅。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bonus Energy Conduit", "额外能量管道", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If [creature] is part of a completed circuit, it provides a reserve of energy equal to the player's current maximum energy.", "如果[creature]参与构成一条完整回路，那么它会提供一个等同于玩家当前最大能量值的能量储备。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Skeleclocked", "外骨骼超频", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies, it permanently becomes an Exeskeleton with the same abilities. If [creature] has Unkillable, it will be unaffected.", "当[creature]死亡时，它会永久变为具有相同能力的机械外骨骼。若[creature]带有不死之虫则不受影响。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Overheat", "过热", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When the scales are in your favor, [creature] loses 1 power. When the scales aren't in your favor, [creature] gains 1 power.", "当战局对你有利时，[creature]损失1点力量。当战局不利时，[creature]获得1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Power Drain", "能量流失", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] consumes all of its owner's available energy each turn.", "[creature]每回合会消耗其持牌人所有的可用能量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Purist", "纯粹主义", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cards opposing [creature] have their sigils removed.", "与[creature]对位的卡牌将失去所有印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This handles actually removing abilities. You should never see this ability on a card!!!", "该效果会实际移除能力。你本不该在卡牌上看到这个能力！！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bounty Hunter", "赏金猎人", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When drawn, [creature] will turn into a random bounty hunter.", "当被抽到时，[creature]会随机变成一名赏金猎人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nerf This!", "来Nerf这招！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is drawn, it gains a randomly selected negative ability.", "当[creature]被抽到时，会随机获得一个负面印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Apotheosis", "无形神物", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is drawn, it gains a randomly selected rare ability.", "当[creature]被抽到时，会随机获得一项稀有能力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Recharge", "蓄势", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, one energy will be refilled (up to the player's maximum energy)", "当使用[creature]时，将恢复1点能量（不超过玩家能量上限）。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Alarm Clock", "闹钟机器人", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The creature that this sigil is pointing to will gain +1 attack. The clock will turn at the beginning of the player's turn.", "该印记当前指向的造物将获得+1点力量。闹钟会在玩家回合开始时旋转方向。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ruby Blessing", "红宝石祝福", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] provides +1 Attack to all creatures you control.", "[creature]为你打出的所有造物提供+1力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Orange Mox Warrior", "橙色玛珂战士", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it attacks all enemy cards opposite Orange Mox providers its owner controls.", "当使用[creature]时，它会攻击所有与持牌人打出的橙玛珂提供者相对的敌方卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Burst", "轰然炸裂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cards sacrificed to pay for [creature] explode, dealing 10 damage to each adjacent card.", "为召唤[creature]而献祭的卡牌会爆炸，对每张相邻卡牌造成10点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gem Consumer", "宝石吞噬者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, all gem vessels on its owner's side of the board are sacrificed.", "使用[creature]时，持牌人侧牌桌上所有宝石容器均会被献祭。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Emergence Latch", "奔涌传花", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When cards sacrificed to pay for [creature] die, their owner chooses a card to gain their first sigil.", "当为[creature]献祭的卡牌死亡时，其持牌人需选定一张卡牌继承它们的首个印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Macabre Growth", "惊悚蜕变", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] gains the attack and health of all cards sacrificed to play it.", "[creature]将获得所有被献祭卡牌的攻击力和生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Shatter", "分崩离析", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cards sacrificed to pay for [creature] drop 4 bones instead of 1 when they die.", "为支付[creature]的费用而献祭的卡牌死亡时将掉落4根骨头而非1根。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Guts Sacrifice", "内脏祭品", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cards sacrificed to pay for [creature] slime the opposing slot when they die. Cards in slimed slots have one less power.", "为支付[creature]的费用而献祭的卡牌死亡时，会在对面位置留下黏液。位于黏液位置的卡牌减少1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Blue Mox Charger", "蓝色玛珂充能器", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it creates a Charge in your hand for each Blue Mox its owner controls. Charge is a spell that refills 1 Energy when played.", "当使用[creature]时，持牌人每打出一张蓝宝石玛珂，就会在手牌中生成一张“充能！”牌。“充能！”是使用时可恢复1点能量的法术牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it creates a Charge in your hand for each Blue Mox its owner controls. Charge is a command that refills 1 Energy when played.", "当使用[creature]时，持牌人每打出一张蓝宝石玛珂，就会在手牌中生成一张“充能！”牌。“充能！”是使用时可恢复1点能量的命令牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sapphire Blessing", "蓝宝石祝福", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] reduces the cost of all cards in your hand by 1.", "[creature]可使你手牌中所有卡牌的费用降低1点。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Scrap Dumper", "废料倾卸机", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil is played, a broken bot is played on the opposing space.", "使用带有该印记的卡牌时，对面位置会出现一个损坏的机器人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Scrap Salvage", "废物利用", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, add the total cost of all cards sacrificed to play it, then create a Charge! for each energy, create a Zap! for each gem, create an Upgrade! for each blood, and create a Defend! for each bone.", "使用[creature]时，将献祭卡牌的总成本转化为资源：每点能量生成1张“充能！”卡牌，每颗宝石生成1张“电击！”卡牌，每点血量生成1张“升级！”卡牌，每根骨头生成1张“防御！”卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bulldoze", "推土机", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 1 Energy to cause this card to move in the direction inscribed in the sigil. Creatures that are in the way will be pushed in the same direction.", "消耗1点能量使该卡牌朝印记标注的方向移动。沿途的造物会被推向同一方向。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Shred", "零敲碎打", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Shred a card from your hand to get +1 Attack (until end of turn). The shredded card behaves as if it died. Unkillable cards and cards Made of Stone cannot be shredded.", "粉碎一张手牌以获得+1攻击力（持续至回合结束）。被粉碎的卡牌视同死亡。无法被消灭的卡牌和石制卡牌不可被粉碎。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Side Deck Power", "副牌组力量", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the number of cards its owner has drawn from the side deck.", "该印记代表的数值等于持牌人从副牌组中抽取的卡牌数量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Slimed Slot", "黏液卡槽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The card in this slot will have its attack reduced by one.", "该卡槽中的卡牌攻击力将降低1点。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Phase Through", "相位穿梭", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of its controller's turn, [creature] moves one space in the direction indicated, moving through other cards if necessary until it finds an empty space.", "持牌人回合结束时，[creature]将向指定方向移动一格，必要时可穿透其他卡牌直至找到空位。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Solar Heart", "太阳之心", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies, its heart stays behind and provides conduit power to the slot.", "当[creature]死亡时，其心脏会留在原地并为该卡槽提供管道能量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Solar Powered", "太阳能供电", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This card slot stays powered at all times, even if it is not in a completed circuit.", "该卡槽将始终保持充能状态，即使未处于完整回路中。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Overwhelming Entrance", "强势入场", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, all opposing creatures are tossed into new slots. Non-conduit terrain is not affected.", "当[creature]被使用时，所有对位的造物都会被抛到新的位置。非管道地形不受影响。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Summon Familiar", "使魔召唤", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, it plays a techbeast in an empty adjacent slot.", "当使用[creature]时，会在相邻空位放置一只机械兽。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Zip of Death", "死亡时速", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Take 2 damage.", "受到2点伤害", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Target Required", "目标锁定", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] can only attack lanes that have cards in them.", "[creature]只能攻击有卡牌存在的路线。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Controlled", "操控", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] is under control of its opponent. At the end of the turn, it will return to its owner. While being controlled, it cannot be hammered.", "[creature]当前由对手操控。回合结束时将回归原主。被操控期间无法被锤击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fireball", "火球术", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of its turn, [creature] chooses a card slot to set on fire for three turns.", "[creature]回合结束时，将选择一个卡槽点燃，持续三回合。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Slimeball", "黏液球", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of its turn, [creature] will choose one slot to become slimed. Cards in a slimed slot lose one power.", "[creature]会在其回合结束时选择一个卡槽进行黏液覆盖。处于黏液卡槽中的卡牌会减少1点力量。", Language.ChineseSimplified);
