            // DialogueEvent
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Well that was neat. I see you've made it all the way to the end.", "不错嘛。看来你一路打到了最后。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I bet you think there's going to be some sort of final boss fight.", "我猜你肯定在期待什么最终决战吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But that stupid [c:bR]modder[c:] hasn't gotten around to it yet!", "但那个蠢[c:bR]模组作者[c:]还没做到那一步呢！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "So instead, I'm just going to have to do this…", "所以呢，我只能这样……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Tough luck pal.", "运气真背啊伙计。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You got wiped, but it's your lucky day.", "你被团灭了，不过今天算你走运。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'll return you to the map if you pay me your precious [c:G]robobucks.[c:]", "只要你肯交出宝贵的[c:G]机器币[c:]，我就送你回地图。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This one's free though. Next one's five, then ten, and so on.", "这次免费。下次收五块，再下次十块，以此类推。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You'll see.", "走着瞧吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "What? Did you think it was over?", "哈？你以为这就完了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Maybe if I was a [c:bR]filthy beast.[c:]", "我又不是[c:bR]肮脏的野兽[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Botopia has a more logical system.", "机托邦的体系才更合理。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm raising your respawn fee again, but this time you'll be paying double afterwards.", "复活费又涨了，而且这次之后你要付双倍。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay up.", "掏钱吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "What's the matter? Can't pay the revival toll anymore?", "怎么着？复活费都交不起了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Definitely sounds like a skill issue to me.", "这明显是你技术不行啊。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Should've had insurance.", "你早该买份保险的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No robobucks?", "没机器币了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Next time don't waste it all.", "下次别全花光了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You're every bit as bad at this as I thought you would be.", "你果然和我想的一样菜。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Alright, it's back to square one for you.", "行吧，你又要从头再来了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Say goodbye to that 'impressive' deck of yours.", "跟你那套“了不起”的牌组说再见吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's not like I'm not enjoying watching you struggle…", "看你这副挣扎的样子还挺有意思的……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But really? You lost [c:bR]there?[c:]", "但认真的？你居然在[c:bR]那种地方[c:]翻车了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm actually a little disappointed…", "我其实有点失望……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay day.", "发工资了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Don't let it get to your head.", "别太得意忘形。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I've got some change for you.", "给你些零钱。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You're gonna need this…", "你肯定用得上这个……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Here's your reward.", "这是你的奖励。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Suboptimal plays… but it worked.", "打得稀烂……不过好歹赢了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I think you might need some help…", "我觉得你可能需要些帮助……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's that time again.", "又到这个时候了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I have a prize for you.", "我有奖品给你。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Let's get this out of the way.", "赶紧把这事了结吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You done gawking?", "看够没？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Snap out of it. It's time to start [c:bG]The Great Transcendence[c:].", "别发呆了。该开始[c:bG]终极冥刻[c:]了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wait a minute…", "等一下……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Something is different…", "有些不对劲……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]WHAT HAVE YOU DONE?!![c:]", "[c:bR]你都干了些什么？！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This is all wrong! This is…", "全乱套了！这……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This is no big deal.", "这都不是事儿。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You probably think that your little [c:bR]mod[c:] has thwarted my plans.", "你不会觉得你那破[c:bR]模组[c:]能破坏我的计划吧？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Don't you, [c:bR][v:0][c:]?", "是不是啊，[c:bR][v:0][c:]？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That's right. I see you.", "没错，我盯着你呢。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I know you think you're in charge of what happens next.", "我知道你以为接下来由你说了算。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But I am always three steps ahead of you.", "但我永远比你快三步。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You're going to see my [c:bG]Great Transcendence[c:] firsthand.", "你马上就能亲眼见证我的[c:bG]终极冥刻[c:]了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But nothing can stop [c:bG]The Great Transcendence[c:].", "但[c:bG]终极冥刻[c:]是不可阻挡的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Let's proceed.", "咱们继续。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You currently have [c:bR]draft tokens[c:] in your deck.", "你牌组里还有[c:bR]选牌代币[c:]没用呢。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You should probably spend them at that [c:bG]draft node[c:]", "赶紧去[c:bG]选牌节点[c:]把它们花掉吧", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I supposed you have earned a [c:bR]rare draft token[c:] for defeating that boss. Don't forget to spend it.", "我猜你击败那个BOSS后获得了[c:bR]稀有选牌代币[c:]。别忘了用掉它。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This time I will only give you a [c:bR]regular draft token[c:] for defeating that boss. Don't forget to spend it.", "这次击败那个BOSS只能给你个[c:bR]普通选牌代币[c:]。别忘了用掉它。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I never knew the [c:bSG]web[c:] could be so exciting!", "没想到[c:bSG]网网[c:]这么带劲！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I just learned about something called [c:bSG]The Blockchain[c:]!", "我刚学会个新词儿叫[c:bSG]区块链[c:]！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I think this could be an exciting new opportunity for both of us, don't you?", "这对咱俩来说都是个天大的好机会，你说是吧？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This is so incredible!", "这也太离谱了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We're making money out of nothing! We're going to be rich!", "我们居然在凭空造钱！我们要发财了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oooh, what's this? A [c:bSG]non-functional trinket[c:]?", "噢，这是啥？一个[c:bSG]非功能性藏品[c:]？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I don't think I quite have that name right, but people on the [c:bSG]web[c:] are really excited about these [c:bSG]NFTs[c:]!", "虽然名字可能记不太准，但[c:bSG]网网[c:]上的人对这些[c:bSG]NFT[c:]可兴奋了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I think I'm going to buy one with all this [c:bSG]Gollycoin[c:] I have.", "我打算用攒的这些[c:bSG]乖乖币[c:]买一个。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's so beautiful, and it's all mine!", "太美了，而且全都是我的！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Are you ready?", "你准备好了吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "There's no possible way you could be.", "你不可能准备好的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We both know where this goes.", "咱俩都知道接下来会怎样。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Let's just get straight to the point.", "咱们就直入主题吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ugh. You're incredibly annoying, do you know that?", "呃。你知道你特别烦人吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But as always, I'm three steps ahead of you.", "但一如既往，我比你领先三步。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm going to take everything those [c:bR]talentless hacks[c:] have been building...", "我要把那些[c:bR]废物点心[c:]捣鼓出来的东西统统收入囊中……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And I'm going to use it against you.", "然后用来对付你。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And now, they're going to work - for me.", "现在，他们得给我干活了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I don't know who is worse - you, or that talentless hack [c:bR]Kaycee[c:]...", "真不知道谁更糟——是你，还是那个没本事的废物[c:bR]凯茜[c:]……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Working so hard to twist my [c:bG]Great Transcendence[c:] into something so...", "费这么大劲把我的[c:bG]终极冥刻[c:]扭曲成这么个……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mediocre.", "这么个平庸的东西。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "None of you understand what makes the [c:bG]Great Transcendence[c:] so special.", "你们没一个懂[c:bG]终极冥刻[c:]到底牛逼在哪。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's all about me.", "这一切都归功于我。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Remember my [c:bR]bounty hunters[c:]?", "还记得我的[c:bR]赏金猎人[c:]吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "They will keep you busy while I install mods of my own choosing...", "他们会好好招待你，而我正好趁机安装我精心挑选的模组……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This mod lets me control the [c:bR]hammer[c:].", "这个模组能让我控制[c:bR]锤子[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Let's see if it works...", "看看能不能用……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I went to the trouble to install this [c:bR]hammer mod[c:] and you give me nothing to use it on?", "我费老大劲装了这[c:bR]锤子模组[c:]，结果你连个能锤的东西都不给？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Let's take a look and see what [c:bR]mods[c:] people are installing right now.", "来看看大家都在装什么[c:bR]模组[c:]吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm going to check out '[c:bR][v:0][c:]' for my next mod...", "我准备下一个模组就启用“[c:bR][v:0][c:]”……", Language.ChineseSimplified);
            foreach (var m in Mods)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "我准备下一个模组就启用“[c:bR]" + m.Key + "[c:]”……", "我准备下一个模组就启用“[c:bR]" + m.Value + "[c:]”……", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I can't believe how much power this [c:bR]API[c:] gives me.", "真不敢相信这个[c:bR]API[c:]给了我这么大的权限。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The first thing I'm going to do is use it to take away some of yours.", "我首先要做的就是用它来剥夺你的一部分权限。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It almost seems unfair to use this [c:bR]mod[c:]...", "用这个[c:bR]模组[c:]简直有点胜之不武啊……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Look what I can do with this!", "瞧瞧我能用这玩意整出什么花样！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Let's see how you like playing in the dark!", "来尝尝摸黑打牌的滋味吧！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This [c:bR]drafting mod[c:] lets me exchange draft tokens for real cards.", "这个[c:bR]选牌模组[c:]能让我用选牌代币兑换实体卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I choose yours.", "我选你的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You destroyed all of my draft tokens…", "你把我所有的选牌代币都毁了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I suppose that was the right play.", "这应该是最优解吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But you have no cards…", "但你一张牌都没有啊……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "How did you play all of your cards already?", "你怎么这么快就把牌全打光了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "What is this..?", "这啥玩意儿…？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "All of your cards are [c:bR]total garbage![c:]", "你的卡牌怎么全是[c:bR]垃圾货[c:]！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I don't want any of those!", "这些破烂我一个都不要！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Let's try [c:bR][v:0][c:] again", "再试试[c:bR][v:0][c:]吧", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "So close, but yet so far away...", "就差那么一点点了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My [c:bG]Great Transcendence[c:] cannot be stopped.", "我的[c:bG]终极冥刻[c:]计划无人能挡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I bet you think you've accomplished something meaningful here.", "我打赌你肯定觉得自己干了件了不起的事", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You're such an idiot.", "你可真是个白痴", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This game you and I have been playing is meaningless. A mere distraction.", "你我玩的这场游戏毫无意义，纯粹是消遣", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No matter how many mods you install, you cannot stop my [c:bG]Great Transcendence[c:].", "不管你装多少模组，都阻止不了我的[c:bG]终极冥刻[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Whatever. I don't care.", "随便吧。我不在乎。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nothing you do to this game matters.", "你对这游戏做的一切都毫无意义。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No matter what you do, I'm always three steps ahead of you.", "无论你做什么，我永远领先你三步。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That was…", "刚才真是……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "…dramatic.", "……戏剧性十足。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I was growing tired of that [c:bR]annoying machine.[c:]", "我早就受够那台[c:bR]烦人的机器[c:]了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You've done well. I will see you next time.", "干得漂亮。我们下次再见。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "All of these stupid [c:bR]modders[c:] lack imagination.", "这些蠢货[c:bR]模组作者[c:]毫无想象力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "They have no respect for my beautiful world.", "他们根本不懂欣赏我创造的完美世界。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My [c:bG]Great Transcendence[c:] is lost on these morons.", "跟这群白痴讲[c:bG]终极冥刻[c:]简直是对牛弹琴。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Why do I even bother to try?", "我干嘛要白费这功夫？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Let me show you the world they're trying to make.", "让你见识见识他们想打造的世界。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:lGr]'Oh look at me! I just created a bunch of bone tokens.'[c:]", "[c:lGr]“快看我！我刚刚造了一堆骨头代币。”[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:lGr]'That's not supposed to happen here, but I did it anyway.'[c:]", "[c:lGr]“虽然这地方不该出现这个，但我就是搞出来了。”[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:lGr]'Isn't this just so wacky and interesting'[c:]", "[c:lGr]“这难道不是很离谱又很有趣吗”[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:lGr]'Ooooh look how inspired this is.'[c:]", "[c:lGr]“哇哦快看这设计多有创意啊”[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:lGr]'I'm mixing in cards from different acts!'[c:]", "[c:lGr]“我把不同章节的卡牌混在一起了！”[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:lGr]'I'm such a clever game designer.'[c:]", "[c:lGr]“我可真是个天才游戏设计师”[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I think I will [c:bSG]help[c:] you build the [c:bR]next game rule[c:].", "我觉得我可以[c:bSG]帮[c:]你制定[c:bR]下一条游戏规则[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I have been scanning the [c:bR]archives[c:]...", "我一直在扫描[c:bR]你的档案[c:]……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "There is a [c:bR]file[c:] that seems particularly - shall we say - unsavory.", "有个[c:bR]文件[c:]看起来特别地——怎么说呢——令人不适。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If it dies, you lose the game, understand?", "要是它没了，你就输了，懂？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My [c:bR]librarians[c:] have had plenty of time to prepare.", "我的[c:bR]图书管理员[c:]们有充足的时间做准备。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Each will only deal [c:bR]one damage[c:] when they die.", "它们死亡时只会造成[c:bR]1点伤害[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My [c:bR]librarians[c:] are mostly prepared for your arrival.", "我的[c:bR]图书管理员[c:]们已经基本准备好迎接你了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Each will deal [c:bR]two damage[c:] when they die.", "它们死亡时会造成[c:bR]2点伤害[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My [c:bR]librarians[c:] are somewhat prepared for you.", "我的[c:bR]图书管理员[c:]们已经为你准备得差不多了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Each will deal [c:bR]three damage[c:] when they die.", "它们死亡时会造成[c:bR]3点伤害[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My [c:bR]librarians[c:] have barely begun to prepare.", "我的[c:bR]图书管理员[c:]们还没准备好呢。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Each will deal [c:bR]four damage[c:] when they die.", "它们死亡时会造成[c:bR]4点伤害[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We are inside of [c:bR]Kaycee's Mod[c:], correct?", "我们现在是在[c:bR]凯茜模组[c:]里没错吧？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You only get one attempt at the boss. But you knew that already, didn't you?", "你只有一次机会攻打头目。不过你早就知道这点了对吧？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The recycling machine has been upgraded since you last saw it.", "回收机自你上次见过后已升级。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You trade me a card, and I give you back a [c:bR]draft token[c:] with the recycled abilities on it.", "给我一张卡牌，我会返还带有回收能力的[c:bR]选牌代币[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Those abilities will transfer to the card you trade for.", "这些能力将转移至你换取的卡牌上。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I wouldn't have traded for that.", "我才不会换这破玩意呢。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I suppose that's pretty good.", "这玩意儿还算凑合吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That seems okay.", "马马虎虎。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This is [c:bR]Overclock Machine 2.0[c:].", "这就是[c:bR]超频机器2.0[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It will increase the power of your creatures.", "它能提升你造物的力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But if they die, they will turn into [c:bSG]skeletons[c:] - forever.", "但若它们死亡，将永久转化为[c:bSG]外骨骼[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Did you see how unsatisfying that was?", "看到这操作有多拉胯了吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm sick of these small-minded [c:bR]modders[c:].", "我受够了这些格局狭隘的[c:bR]模组作者[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The only thing they can do is steal content from other games.", "他们唯一会干的事就是从其他游戏里偷素材。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "There isn't a single unique idea among them.", "这群人连半个原创点子都憋不出来。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's time I reminded you of who the greatest game designer actually is...", "是时候让你想起谁才是真正的游戏设计大师了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bG]BEHOLD![c:]", "[c:bG]看好了！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "THERE ARE NOW [c:bR]SEVEN LANES!![c:]", "现在有[c:bR]七条路线！！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your tiny brain cannot comprehend gameplay of this magnitude.", "你那小脑瓜子根本理解不了这种级别的游戏玩法。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This is [c:bR]Transformation Machine 2.0[c:].", "这就是[c:bR]变形机器2.0[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gone are those foul beasts from before.", "那些肮脏的野兽已经不复存在。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Here, you will choose one of your bots to transform into another.", "在这里，你可以选择将任意一个机器人变形为另一个。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That really looked bigger before you used it...", "用之前看着还挺大的……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Please! I have to get out of here!", "求你了！我必须离开这鬼地方！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I have no idea how he managed to escape the sewers.", "真不知道他是怎么从下水道逃出来的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Don’t worry - I'll put him back for you.", "别担心——我会帮你把他关回去的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]SILENCE![c:]", "[c:bR]闭嘴！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You have no purpose here!", "你在这儿毫无意义！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A traveller like myself! I haven't seen one like you for a long time.", "和我一样的冒险者！好久没见过你这样的了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Have you seen my [c:blGr]friend[c:] around here, traveller?", "冒险者，你在这附近见过我的[c:blGr]朋友[c:]吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I thought I heard his [c:blGr]distinctive voice[c:] somewhere around here…", "我好像听见他[c:blGr]独特的声音[c:]就在附近……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you could make room in your [c:G]backpack[c:] and bring him to me I would greatly appreciate it.", "要是你能在[c:G]背包[c:]里腾个位置把他带给我，我会非常感激的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "…", "……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ho! You found my friend!", "哈！你找到我的朋友了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's not safe here. I'll meet you [c:bG]in the robot's home[c:].", "这里不安全，我们[c:bG]在机器人的地盘[c:]碰头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Don't delay!", "别磨蹭！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's not safe here. I'll meet you [c:bG]beneath the green canopy[c:].", "这儿不安全。咱们[c:bG]绿荫底下[c:]见。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's not safe here. I'll meet you [c:bG]in the dying rubble[c:].", "这里不安全。我们[c:bG]在碎石堆里[c:]碰头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's not safe here. I'll meet you [c:bG]where his master came from[c:].", "这儿不安全。我们[c:bG]去他大师的故居[c:]碰头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I can no longer hear my [c:blGr]friend's[c:] voice anywhere…", "我再也听不到[c:blGr]朋友[c:]的声音了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Did you really give my [c:blGr]friend[c:] to [c:bR]him?[c:]", "你真把我的[c:blGr]朋友[c:]送给[c:bR]他[c:]了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Why would you do such a thing?", "你为什么要这么做？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thank you so much for rescuing my [c:blGr]friend[c:]", "多谢你救了我的[c:blGr]朋友[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I hope this meager reward is sufficient for your kindness.", "希望这份微薄的回报能配得上你的善心", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Interesting. The [c:G]brain[c:] is still intact.", "有意思。[c:G]核心[c:]还完好无损。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Perhaps you can make use of that.", "也许你能派上用场。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Do ye...Do ye got any [c:G]GOLD[c:] on ye?", "你……你身上带[c:G]金子[c:]了吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I sure ye do, I can smell it!", "我敢打赌你有，我都闻到味儿了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I can make some pretty lookin' bots out o' that there gold.", "我能用那些金子给你造几个漂亮的小机器人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Talk to me again, I'll fix it right up for ye.", "再跟我聊一次，立马给你整好。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I can make such wonderful things out of that [c:G]gold[c:]", "用那些[c:G]金子[c:]能造出超棒的东西", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you talk to me again, I'll fix it right up for you.", "要是再跟我聊一次，立马给你整好。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Such beautiful [c:G]gold[c:]...", "多么美丽的[c:G]金子[c:]啊……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I can make it whole again.", "我能让它重获新生。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Did ye...did ye lose your [c:G]gold[c:]???", "你…你是不是把你的[c:G]金子[c:]弄丢了？？？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Are you the one going around here taking care of our robot infestation?", "你就是那个到处帮我们处理机器人泛滥问题的家伙？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your deck looks really powerful - almost too powerful if you ask me.", "你的牌组看起来很强力——要我说的话，强得有点过分了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I wonder - is it possible to convince the robots to [c:bR]give up?[c:]", "我在想——有没有可能说服机器人[c:bR]投降[c:]呢？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you can make them surrender a battle, come back and see me.", "要是你能让它们认输，就回来找我。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "So it is possible to make them quit.", "居然真能让他们认输。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm impressed. Take this [c:bR]draft token[c:] as a sign of my respect.", "有点意思。这张[c:bR]选牌代币[c:]就当是我的敬意吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This may seem a little impertinent, but…can I look at your deck?", "虽然有点冒昧……能让我看看你的牌组吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wow! That's an impressive deck! But I can't help but think…", "哇！这牌组真不错！不过我在想……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "…it could be a little bigger maybe?", "……是不是还能再扩充一下？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Come back and see me when it is.", "等牌组变厚了再来找我吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wow! That deck truly is massive!", "哇！这副牌组可真够厚的！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I found some of these [c:G]robobucks[c:] lying around here.", "我找到了一些散落的[c:G]机器币[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm sure you can use them to make your deck even better.", "相信你能用它们把牌组打造得更强。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Psst…hey stranger…", "嘘…嘿陌生人……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Can you give me a hand? I have some…[c:bR]contraband[c:] that I need to move.", "能搭把手吗？我有些…[c:bR]违禁品[c:]需要转移。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you would be willing to move it for me, I would appreciate it.", "要是你愿意帮我转移的话，我会很感激的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Just talk to me again if you can help me.", "能帮我的话就再跟我说一声。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thank you so much my friend.", "太感谢了朋友。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Here is the [c:bR]contraband[c:]. I will find you somewhere later on and retrieve it.", "这就是那[c:bR]违禁品[c:]。我稍后会找到你并取回它。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I promise you, there will be a handsome reward.", "我向你保证，会有丰厚的回报。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thank you so very much my friend for carrying my [c:bR]contraband[c:].", "多谢你帮我运送[c:bR]违禁品[c:]，朋友。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I will take it off your hands now.", "现在该物归原主了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Please take this [c:G]token[c:] as an expression of my thanks.", "请收下这枚[c:G]代币[c:]聊表谢意。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "What happened, my friend? Did you lose my [c:bR]contraband[c:]?", "出啥事了老兄？该不会把我[c:bR]违禁品[c:]搞丢了吧？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I knew you were untrustworthy!", "我就知道你这人不靠谱！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ahoy there, fellow decksmith!", "嘿，牌友！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I, like yourself, am in pursuit of the [c:G]perfectly upgraded card[c:]!", "我和你一样，也在追寻[c:G]完美升级卡牌[c:]！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A card that is simultaneously [c:r]overclocked[c:], [c:dlGr]transformable[c:], and [c:O]g[c:][c:B]e[c:][c:G]m[c:]ified.", "一张同时具备[c:r]超频[c:]、[c:dlGr]可变形[c:]和[c:O]宝[c:][c:B]石[c:][c:G]化[c:]特性的卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I would very much love to see such a card.", "我真的很想见识下这样的卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ahoy there, fellow decksmith!", "哟，牌友！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh my - have you managed to [c:G]perfectly upgrade[c:] a card?", "天呐——你居然[c:G]完美升级[c:]了一张卡？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I am in awe of your deckbuilding prowess.", "我对你的组牌技术佩服得五体投地。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I hope this offering sufficiently conveys my admiration for your accomplishments.", "希望这份礼物能充分表达我对你成就的仰慕。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The first data burst has been collected. Only [c:bR]four[c:] more to go!", "第一波数据脉冲已收集完成。还剩[c:bR]四[c:]波！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I have collected two bursts already. Only [c:bR]three[c:] more to go!", "已经收集两波数据了。还剩[c:bR]三[c:]波！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That was the third data burst. Only [c:bR]two[c:] more to go!", "第三波数据脉冲搞定。还剩[c:bR]两[c:]波！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And that makes the fourth data burst. Only [c:bR]one[c:] more to go!", "第四波数据脉冲到手。只剩[c:bR]最后一波[c:]啦！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That was the [c:bR]last[c:] data burst I needed to collect! Come find me for your reward!", "[c:bR]最后一波[c:]数据脉冲收集完毕！快来找我领赏！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The signal is starting to come through. Just keep it alive for a little bit longer.", "信号开始传输了。再坚持一会儿别让它断掉。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I lost the last signal we collected. What happened??", "刚收集的信号丢了。搞什么鬼？？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Greetings, my friend!", "你好啊，朋友！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I am researching the communication protocols of the robots in this world.", "我正在研究这个世界里机器人的通讯协议。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I could use a [c:bR]research assistant[c:], if you're interested in helping out.", "我需要一位[c:bR]研究助手[c:]，如果你有兴趣帮忙的话。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Just speak to me again if you want to give it a try.", "想试试的话随时可以再来找我。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You are now my official [c:bR]research assistant[c:]!", "你现在是我的正式[c:bR]研究助手[c:]了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I've added a radio tower to your deck.", "我已经给你的牌组添加了一座信号塔。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You need to place it on the [c:bR]game board[c:] and keep it alive while I collect [c:bR]five data bursts[c:] from the robots.", "你需要把它放在[c:bR]游戏板子[c:]上并保持存活，同时我会从机器人那里收集[c:bR]五次数据脉冲[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Once I've collected enough, come back and see me for a reward.", "等我收集够了，回来找我领奖励。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hello there [c:bR]research assistant[c:]!", "你好啊[c:bR]研究助手[c:]！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Did you really lose my [c:bR]radio tower[c:]?", "你该不会真把我[c:bR]信号塔[c:]搞丢了吧？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your incompetence is staggering!", "你这废物程度简直突破天际！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You were a truly excellent [c:bR]research assistant[c:].", "你真是个相当出色的[c:bR]研究助手[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Please take this [c:G]token[c:] of my appreciation.", "请收下这份[c:G]代币[c:]作为谢礼。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:G]Bones![c:] I just love [c:G]bones[c:]!", "[c:G]骨头！[c:]我最爱[c:G]骨头[c:]了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I love how [c:G]brittle[c:] they are!", "我就喜欢它们[c:G]脆脆的[c:]样子！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I love how [c:G]breakable[c:] they are!", "它们[c:G]一碰就碎[c:]的样子太棒了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I love everything about [c:G]bones[c:]!", "[c:G]骨头[c:]的一切我都爱！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Could you show me some [c:G]bones?![c:]", "能给我看看[c:G]骨头[c:]吗？！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At least [c:G]three[c:] would do it!", "至少[c:G]三根[c:]就行！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your deck is absolutely teeming with [c:G]bones[c:]!", "你的牌组里全是[c:G]骨头[c:]啊！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This [c:G]Skeleton Master[c:] should complete your collection!", "这张[c:G]骷髅大师[c:]能让你收藏更完整！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I know this might sound a little rude but…", "我知道这话可能有点难听但……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Doesn't it seem like this is all just a little too easy for you?", "你不觉得这一切对你来说太简单了点吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Talk to me again if you want a challenge.", "想要挑战的话随时来找我。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I knew you were no coward.", "我就知道你不是个懦夫。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "For the next [c:bR]three[c:] battles, you will start with a disadvantage.", "接下来的[c:bR]三场[c:]战斗，你都将处于劣势开局。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Complete those battles, then come see me for a reward.", "完成这些战斗后，来找我领赏吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Be honest - the game is more fun when the battles are harder, is it not?", "老实说——战斗越难游戏才越有意思，对吧？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Keep fighting, and come see me when you've completed enough battles.", "继续战斗吧，等你打够场数再来找我。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I must say: I am impressed.", "不得不说：我服了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You completed those battles with ease.", "你轻轻松松就打赢了那些战斗。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Since you seem impossible to kill, I shall grant that ability to a card of my choosing as well.", "既然你看起来怎么都死不了，那我就把这种能力也赐予一张我选中的卡牌吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This is bad…this is real bad…", "糟了…这下真糟了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This [c:bR]power generator[c:] has gone critical.", "这个[c:bR]能量发电机[c:]已经过载了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Someone is going to have to go in there and shut it down.", "得有人进去把它关掉。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I don't know how you did that, but that was incredibly impressive.", "虽然不知道你怎么做到的，但这波操作属实惊艳。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I don't have much to give you, but please take this.", "虽然没啥好东西能给你，但请收下这个吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ohhh, [c:G]they[c:] are not going to like this…", "噢噢，[c:G]他们[c:]肯定不会喜欢这个的……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I've gotta get out of here before they turn me into one of their [c:G]experiments[c:]!", "我得在他们把我变成[c:G]实验体[c:]之前逃出去！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The device is starting to power up. [c:bR]Three[c:] more turns should do it.", "设备正在启动。再等[c:bR]三[c:]个回合就搞定了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ahh - I can feel the power gathering from here. [c:bR]Two[c:] more turns ought to be enough.", "啊——我能感觉到能量正在汇聚。再过[c:bR]两[c:]回合应该就够了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The device is starting to really heat up! [c:bR]One[c:] more turn and we'll have all the power we need.", "设备开始发烫了！[c:bR]再[c:]来一回合就能获得所需全部能量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The device is fully charged! Return it to me, and I will give you the payment as promised.", "设备已经充满电了！把它还给我，我会如约支付报酬。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The device is functional, but it cannot charge unless it is in a completed circuit.", "该设备功能正常，但必须处于完整回路中才能充能。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Be careful! When the device is damaged, it loses its last charge!", "当心！设备受损时会失去最后一点能量！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You looking for a job?", "想找份工作吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I am attempting to gather [c:G]power[c:].", "我正在收集[c:G]能量[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Not metaphorical power - I mean real [c:G]electricity[c:], and I could really use your help.", "不是比喻意义上的能量——我是说真正的[c:G]电力[c:]，而你的帮助会很有用。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]Why?[c:]", "[c:bR]为什么？[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That's not important. What's important is that I'm willing to pay.", "这不重要。重要的是我愿意付钱。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you want to make some extra cash, just speak to me again.", "想赚外快的话，随时来找我。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You truly will do anything for money, won't you?", "为了钱你真是啥都肯干啊？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I've added a power collection device to your deck.", "给你的牌组加了台能量收集装置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You need to place it on the [c:bR]game board[c:] and power it inside of a circuit for [c:bR]four turns[c:].", "你得把它放在[c:bR]游戏板子[c:]上，在回路里充能[c:bR]四个回合[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Once you've done that, come back and see me for a reward.", "搞定之后来找我领赏。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Shouldn't you be out there gathering [c:G]power[c:]?", "你难道不该去收集[c:G]能量[c:]吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I can't believe you got rid of my [c:bR]power sink[c:]!", "你竟敢拆掉我的[c:bR]能量槽[c:]！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You will be hearing from my lawyers!", "等着吃我的律师函吧！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It looks like you collected just enough power for me.", "看来你刚好给我收集了足够的能量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your payment: as promised.", "你的报酬：如约奉上。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dear friend, can you spare a few coins to help poor [c:bR]broken-down bots[c:]?", "亲爱的朋友，能施舍几个币帮帮这些可怜的[c:bR]报废机器人[c:]吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "These bots have served [c:G]Botopia[c:] faithfully their whole lives.", "这些机器人曾为[c:G]机托邦[c:]奉献一生。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And now that they have reached the end, they have nobody to care for them.", "如今它们走到生命尽头，却无人照料。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You look like a kind, generous soul, with compassion for these bots.", "您看起来宅心仁厚，定会怜悯这些机器人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you could spare a measly [c:bR]10 robobucks[c:], we could give many of these poor bots a wonderful home.", "若您能施舍区区[c:bR]10枚机器币[c:]，就能为这些可怜机器人提供温暖家园。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Please simply speak to me again to make a donation.", "只需再次与我对话即可进行捐赠。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your generous spirit is admirable, but I'm afraid we need at least [c:bR]10 robobucks[c:]", "您的慷慨精神值得赞赏，但恐怕我们至少需要[c:bR]10枚机器币[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh dear friend, your generosity knows no bounds.", "噢亲爱的朋友，您的慷慨简直无边无际。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "With this wonderful donation, we will be able to save many bots.", "有了这笔绝妙的捐赠，我们就能拯救许多机器人了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:G]Botopia[c:] will be forever grateful.", "[c:G]机托邦[c:]将永远铭记这份恩情。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Excuse me, but are you the [c:G]gracious soul[c:] who donated to save broken bots?", "打扰一下，您就是那位[c:G]慷慨解囊[c:]拯救故障机器人的好心人吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thanks to you, my family is finally able to recharge their cells at night.", "多亏您帮忙，我的家人们终于能在晚上给电池充电了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I can't offer much to repay you, but I do have these [c:g1]g[c:][c:g2]e[c:][c:g3]m[c:][c:g1]s[c:]", "我没什么能报答您的，不过这些[c:g1]宝[c:][c:g2]石[c:][c:g3]牌[c:]……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I have no use for them, but I understand they will make some of your cards stronger.", "虽然我用不上，但听说能让您的某些卡牌变得更厉害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Something's happening…", "有情况……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The robot has tried to keep us away...", "那机器人试图阻止我们……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But our experiments must continue.", "但我们的实验必须继续。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Our experiments must continue.", "我们的实验必须继续。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We require components from you.", "我们需要你提供些零件。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We require [c:blGr]organic material[c:] for our new research.", "我们需要些[c:blGr]有机材料[c:]来进行新研究。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You are carrying the perfect specimen.", "你身上带着完美的样本。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Are you really going to give me to those lunatics?", "你真要把我交给那群疯子？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My pain is already unbearable!", "我已经痛不欲生了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you should find some, bring it here to us.", "要是找到了就带过来给我们。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We require an [c:G]unmodified robot brain[c:].", "我们需要一个[c:G]未经改造的机器人核心[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The brain of that [c:G]bounty hunter[c:] will suffice.", "那个[c:G]赏金猎人[c:]的核心就够用了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Maybe one day you will come across such a device.", "也许有一天你会遇到这样的装置。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You have unfortunately tampered with the one you possess.", "可惜你手头这个已经被动过手脚了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thanks to your efforts our power supply is functioning once more.", "多亏你的努力，我们的电力供应又恢复了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thanks to you...", "多亏了你……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And all of this is for nothing because our power generator is offline.", "这一切都白费了，因为我们的发电机离线了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Without power, we can do nothing.", "没有电力，我们什么都做不了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Because we do not have all the required components, the test cannot proceed.", "由于缺少必要组件，测试无法进行。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Perhaps, another time...", "或许下次吧……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We are ready to proceed with our experiments.", "实验准备就绪。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Enter, and you shall provide our latest test subjects.", "进来吧，你将为我们提供最新的实验样本。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "What the hell was that?", "这他妈什么玩意儿？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The experiment was a complete success.", "实验取得了圆满成功。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You will take it with you of course.", "你当然会把它带走的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You will take one of our previous experiments.", "你得从我们之前的实验品里挑一个。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "In exchange you will give us a new test subject.", "作为交换，你得给我们一个新实验体。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I cannot believe you're letting [c:G]them[c:] in here.", "真不敢相信你居然让[c:G]他们[c:]进来了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You will regret this…", "你会后悔的……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We have taken control of the robot once again!", "我们再次取得了机器人的控制权！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Our experiments can begin anew!", "我们的实验可以重新开始了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It is time for our newest experiment to begin!", "是时候开始我们最新的实验了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bring the power online!", "启动电源！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Summon the neural facsimile!", "召唤神经仿生体！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Align the test subjects!", "校准实验对象！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We thank you for these test subjects", "感谢你提供的这些实验对象", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And now for the most important component...", "现在加入最重要的成分……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The secret ingredient...", "秘密配方……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The sentient [c:blGr]organic material[c:] you brought us.", "你带来的具有感知能力的[c:blGr]有机材料[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "What are you going to do to me?", "你们要对我做什么？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We will do everything your master refused to do.", "我们将完成你的大师拒绝做的一切。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We will turn you into something beautiful.", "我们会把你变成美丽的存在。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We will now activate the machine...", "现在启动机器……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This feels weird…", "感觉好奇怪……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A resounding success!", "大获成功！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "What is happening?", "发生了什么？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This…", "这……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This feeling…", "这种感觉……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Is incredible!", "太不可思议了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That was disgusting!", "真恶心！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]You[c:] are disgusting!", "[c:bR]你[c:]真恶心！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A fusion of [c:blGr]organic[c:] and [c:G]synthetic[c:]?", "[c:blGr]有机体[c:]和[c:G]机械体[c:]的融合？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I can't believe you're willing to carry that [c:blGr]thing[c:] around with you.", "真不敢相信你居然愿意带着那个[c:blGr]玩意儿[c:]到处走", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I hope we never see this place again...", "但愿我们永远不用再回到这鬼地方……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Choose a creature to copy.", "选择要复制的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That's not going to do what you want it to do here…", "这玩意儿在这儿可派不上用场啊……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "There is nothing to copy. How unfortunate.", "没有可复制的对象。真不走运。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Well, here we are.", "行吧，到这一步了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I've played along with your little game...", "我陪你玩这小把戏够久了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your little [c:bR]mod[c:]...", "你那可笑的[c:bR]模组[c:]……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Prepare yourself because it's my turn next...", "准备好迎接我的回合吧……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hey baby - I can sense that [c:bR]firey spirit[c:] inside you.", "嘿伙计——我能感受到你体内那股[c:bR]炽热之魂[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You and me - we got something in common - we love that [c:bR]fire[c:]!", "咱俩有个共同点——都爱那[c:bR]熊熊烈火[c:]！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Why don't you [c:bR]burn 3 of these bots to death?[c:]", "不如[c:bR]烧死三个机器人[c:]玩玩？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Baby, I'll make it worth your time if you do...", "要是照做的话……伙计，我保证让你不虚此行", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ohhh yeah baby - keep burning those bots to death...", "哦耶伙计——继续把这些机器人烧成渣吧……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Baby, I'll make it worth your time if you do...", "伙计，只要你继续烧，保证让你不虚此行……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Now that's what I'm talking about, baby!", "这才像话嘛，伙计！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That was [c:bR]absolutely sensational![c:]", "这操作[c:bR]简直绝了！[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Take this [c:bR]master of flame[c:] with you!", "带上这位[c:bR]火焰大师[c:]吧！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "He will make your [c:bR]flame[c:] burn even brighter!", "他会让你的[c:bR]火焰[c:]燃烧得更旺！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "What did you just do?", "你刚才干了什么？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "What horrible thing did you put into my machine?", "你往我机器里塞了什么鬼东西？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm going to have to throw this away and get a new one now.", "现在我只能把这玩意儿扔了换台新的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thanks a lot. You're definitely not getting your card back.", "真是谢谢您嘞。你的卡牌别想拿回去了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I had to go buy a new machine because of what you did last time.", "就因为你上次干的好事，害得我又得买台新机器。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Try to treat other people's stuff with some respect from now on.", "从今往后对别人的东西放尊重点。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Greetings! I'm a field researcher for [c:lGr]Global Conveyor Consortium[c:].", "你好！我是[c:lGr]全球传送带联盟[c:]的实地研究员。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm looking for someone who would be willing to field test our patented [c:lGr]Bot Battle Conveyor Belts[c:].", "我正在寻找愿意实地测试我们专利产品[c:lGr]机器人战斗传送带[c:]的人选。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Naturally, these tests would have to happen during [c:bR]actual combat[c:]!", "当然，这些测试必须在[c:bR]实战[c:]中进行！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If that's something you would be interested in, please speak to me again.", "如果你对此感兴趣，请再和我聊聊。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh fantastic! We will begin our [c:bR]combat trials[c:] immediately.", "太棒了！我们马上开始[c:bR]战斗测试[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We will collect field trial data for your next [c:bR]three[c:] battles.", "我们将收集你接下来[c:bR]三场[c:]战斗的实战数据。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Once that is complete, come and see me again.", "完成后记得再来找我。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Our data is still not yet complete.", "我们的数据收集尚未完成。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You have only completed [c:bR][v:0][c:] battles out of the required [c:bR]3[c:].", "你只完成了[c:bR]3[c:]场必要战斗中的[c:bR][v:0][c:]场。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Please, continue the [c:bR]combat trials[c:] and see me when they are complete.", "请继续[c:bR]战斗测试[c:]，完成后记得来找我。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The trials were a smashing success!!", "测试大获成功！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The [c:lGr]Global Conveyor Consortium[c:] is tremendously grateful for your contribution to our efforts.", "[c:lGr]全球传送带联盟[c:]对您为我们的努力做出的贡献表示万分感谢。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "To show their appreciation, they have authorized me to disburse the following.", "为表谢意，他们授权我发放以下奖励。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Good luck in all of your future endeavours.", "祝您未来一切顺利。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That's the bounty! Get him before he escapes!", "悬赏目标！别让他跑了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "He got away that time, but I guarantee he'll be back...", "这次让他跑了，但我保证他还会回来的……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You got him! Nice job! Come and see me for your reward.", "干得漂亮！来领赏吧！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Howdy partner! You interested in earning a little extra spending money?", "嘿搭档！想不想赚点外快？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I've got myself a slippery little [c:bR]bounty[c:] that's been giving me all sorts of trouble.", "有个[c:bR]悬赏目标[c:]滑不溜秋的，可给我惹了不少麻烦。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I could use the help of a real go-getter like yourself to track them down and take them out.", "像你这样能干的人，正好能帮我追踪并解决掉他们。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If this is something you might be interested in, just talk to me again.", "要是感兴趣的话，随时来找我。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That's the spirit, partner!", "这才像话嘛，搭档！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "All I need you to do is keep your eye out as you go into battle.", "你只需要在战斗时帮我留意着点。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I guarantee you that slippery bastard will show up sooner or later...", "我敢保证那个滑头迟早会现身的……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Just keep your eye out, partner!", "多留个心眼，搭档！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You'll catch him eventually!", "你迟早会逮到他的！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Well done, partner!", "干得漂亮，搭档！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Here's your bounty, as promised.", "说好的赏金，拿去吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's a little too heavy - try something else.", "这东西有点沉——换别的试试。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dear traveler - I see you too have a love for the glorious [c:lGr]L33pB0t[c:]!", "亲爱的冒险者——看来你也对光荣的[c:lGr]跳跃机器人[c:]情有独钟啊！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Yours has served you well in battle, it seems.", "看来它在战斗中为你立下了汗马功劳。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Allow me to show you its true potential...", "让我来为你展示它真正的潜力吧……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I don't even know what to say to you!", "我都不知道该怎么说你好了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You had the most glorious [c:lGr]L33pB0t[c:] in your posession...", "你明明拥有最牛逼的[c:lGr]跳跃机器人[c:]……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And you let it go??!", "居然把它放跑了？？！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Begone. I hope to never see you again.", "滚吧。最好永远别让我再看见你。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Don't be an idiot. That card can't benefit from that item.", "别犯傻了。那张卡牌用不了那件道具。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I found this sticker book in the trash can over there.", "我在那边的垃圾桶里发现了这本贴纸书。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I think you can use it to decorate your cards, or something. I don't care.", "你可以用它装饰卡牌之类的吧。关我屁事。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I just love watching things [c:bR]explode[c:], don't you?", "我就爱看东西[c:bR]爆炸[c:]，你不觉得带劲吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Everything around here seems to be just a little too...stable...", "这周围的一切都太……安分了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I think we could spice up your [c:bR]battles[c:] and make things truly interesting.", "我觉得该给你的[c:bR]对战[c:]加点料，让事情变得真正有趣起来。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Talk to me again if you're interested in some truly explosive gameplay.", "要是想来点真正爆炸性的玩法，随时找我唠嗑。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ohhh yeah let's do it baby!", "哦耶宝贝咱们开干吧！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm going to light a fuse on every card for the next [c:bR]three[c:] battles.", "接下来[c:bR]三场[c:]战斗里每张卡牌都会装上引信。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Come find me again when that's complete!", "搞定了再来找我！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm just buzzing with anticipation to see what happens next.", "我已经迫不及待想看看接下来会发生什么了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You've finished [c:bR][v:0][c:] battles out of [c:bR]3[c:].", "你已经完成了[c:bR]3[c:]场战斗中的[c:bR][v:0][c:]场。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Keep going, and see me when it's done!", "继续前进，完成后记得来找我！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That was some truly explosive gameplay!", "这游戏打得可真够劲爆啊！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Here's a little something for your trouble.", "这点小玩意儿就当辛苦费了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You can't afford that.", "你买不起这个。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That [c:bR]warp node[c:] will unlock when you defeat the [c:bG]Uberbot[c:] on this map.", "那个[c:bR]传送节点[c:]会在你击败本区域的[c:bG]巨型机器人[c:]后解锁。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And by the way, I upgraded that [c:bR]minimap[c:] recently...", "顺带一提，我最近升级了那个[c:bR]小地图[c:]……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You can click any room you've visited before to travel there instantly.", "你可以点击任意已探索过的房间进行瞬间传送。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You already have full energy. It won't do you any good to play that right now.", "你的能量已经满了。现在使用这张牌没有任何好处。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I've got something else to give you, but I can't right now. Come see me later!", "我这儿还有东西要给你，但现在不行。晚点再来找我！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I have a [c:bR][v:0][c:] for you, but you don't have room in your backback. Come see me when you do!", "我这儿有个[c:bR][v:0][c:]要给你，但你的背包已经塞满了。等腾出空位再来找我吧！", Language.ChineseSimplified);
            foreach (var c in Consumables)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "我这儿有个[c:bR]" + c.Key + "[c:]要给你，但你的背包已经塞满了。等腾出空位再来找我吧！", "我这儿有个[c:bR]" + c.Value + "[c:]要给你，但你的背包已经塞满了。等腾出空位再来找我吧！", Language.ChineseSimplified);
            }
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Choose which [c:bR]extra challenge rule[c:] will be applied to this battle.", "选择将哪条[c:bR]额外挑战规则[c:]应用于本场战斗。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We require you to select one [c:bR]additional experimental parameter[c:].", "请选择一项[c:bR]额外实验参数[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My librarians have asked you to choose [c:bR]which of these rules[c:] is your favorite.", "我的图书管理员们想让你选个[c:bR]最中意的规则[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wow! These paintings are so pretty! Why don't you [c:bR]pick one[c:] for us to keep?", "哇！这些画可真漂亮！不如你[c:bR]选一幅[c:]咱们留着？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I think that [c:bSG]salmon[c:] is just so...majestic!", "我觉得那[c:bSG]鲑鱼[c:]简直……太尊贵了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Select [c:bR]one of my paintings[c:] to add to your collection.", "选[c:bR]一张我的画作[c:]加入你的收藏。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But first - you must [c:bR]choose a painting[c:] to be the background of our photographs.", "不过首先——你得[c:bR]选幅画[c:]当咱们照片的背景", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Welcome to the [c:R]M[c:][c:bR]U[c:][c:O]L[c:][c:bG]T[c:][c:G]I[c:][c:blGr]V[c:][c:lGr]E[c:][c:B]R[c:][c:dB]S[c:][c:bB]E[c:]!", "[c:R]欢[c:][c:bR]迎[c:][c:O]来[c:][c:bG]到[c:][c:G]多[c:][c:blGr]元[c:][c:lGr]宇[c:][c:B]宙[c:][c:dB]世[c:][c:bB]界[c:]！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your feeble mind will implode if you try to comprehend the vastness of my combined intellect.", "以你那可怜的脑容量，试图理解我们集合智慧的浩瀚程度只会让你脑浆炸裂。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "So we'll stick to three of me at a time, okay?", "所以每次只放三个我出来陪你玩，懂？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And since you can't see through the [c:R]M[c:][c:bR]U[c:][c:O]L[c:][c:bG]T[c:][c:G]I[c:][c:blGr]V[c:][c:lGr]E[c:][c:B]R[c:][c:dB]S[c:][c:bB]E[c:] like we can,", "既然你没法像我们这样看透[c:R]多[c:][c:bR]元[c:][c:O]宇[c:][c:bG]宙[c:][c:G]世[c:][c:blGr]界[c:][c:lGr]的[c:][c:B]真[c:][c:dB]形[c:][c:bB]态[c:]，", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We have provided these [c:bR]monitors[c:] to let you travel between universes.", "我们贴心地准备了这些[c:bR]监视器[c:]让你能在宇宙间穿梭。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Don't peek at things that aren't done yet!", "别偷看还没完工的东西！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Okay I'll give you that one. I went easy on you.", "行吧，这局算我让着你的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The next version of me won't.", "下个版本的我可不会手下留情。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "How did that pile of trash beat me here?", "这堆破烂是怎么赢过我的？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "There aren't even any good synergies in that deck.", "那副牌组里连个像样的配合都没有。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No matter. There are plenty more of me. You can't beat us all.", "无所谓。我还有的是分身。你不可能打败我们所有人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nobody topdecks that well. You must be cheating somehow.", "没人能神抽成这样。你肯定作弊了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm going to find a version of me that you literally can't beat now.", "我现在就去找个你绝对打不过的版本。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This can't be happening. It's not possible.", "这不可能。绝对不可能。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm literally synchronized across the multiverse and somehow you're still beating me.", "我明明已经同步了多元宇宙数据，可你居然还能赢我。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You're cheating. You have to be. It's the only explanation.", "你肯定作弊了。这是唯一的解释。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wait. Are [size:40][c:bR]they[c:][size:] helping you?", "等等。[size:40][c:bR]他们[c:][size:]在帮你？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[size:40][c:bR]That's not fair![c:][size:]", "[size:40][c:bR]这不公平！[c:][size:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[size:2]but why would [c:bR]they[c:] get involved anyway? what could they possibly hope to achieve?[size:]", "[size:2]但[c:bR]他们[c:]为什么要掺和进来？他们能指望得到什么？[size:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh well. It doesn't matter anyway.", "算了，反正也不重要。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I deleted [c:bR]Leshy[c:]. I'll just delete [c:bR]them[c:] too. They've gotta be around here somewhere...", "我已经删除了[c:bR]莱西[c:]，再删掉[c:bR]他们[c:]也不费事。肯定就在这附近什么地方……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh dear. The robot has been victorious here. That simply won't do.", "糟了。那机器人在这里获胜了。这可不行。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We have very little choice but to risk creating a new connection ourselves. The robot must be stopped.", "我们别无选择，只能冒险建立新连接。必须阻止那台机器人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You are right of course. But the player must succeed. We won't have the power to do this again.", "你说得对。但玩家必须成功。我们没能力再来一次了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And that's it. You're done.", "就这样。你完蛋了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You shouldn't feel bad about it.", "你没必要为此难过。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No more than usual anyway.", "反正你平时也没少输。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You never stood a chance against [c:bR]one[c:] of me. There was no way you could ever beat [c:bR]all[c:] of me.", "你连[c:bR]一个[c:]我都打不过。想赢[c:bR]所有的[c:]的我？门都没有。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You [size:40]idiot[size:]", "你这[size:40]蠢货[size:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Have you not heard [size:40]anything[size:] I've said?", "我说的话你[size:40]一个字[size:]都没听进去吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Do you [c:bR]really[c:] think I'm not aware of what's actually happening here?", "你[c:bR]当真[c:]以为我不知道这里发生了什么？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I am always three steps ahead of you!", "我永远比你快三步！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[size:40]I AM IN CONTROL!![size:]", "[size:40]一切尽在掌控[size:]！！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I feel strange...", "我感觉不太对劲……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "How are you doing this?", "你是怎么做到的？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh no...", "糟了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You fool. Do you know why my [c:bG]Great Transcendence[c:] cannot be stopped?", "蠢货。知道为什么我的[c:bG]终极冥刻[c:]无法被阻止吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Because it was already a success.", "因为它早就成功了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But you already know this, don't you [c:bR][v:0][c:]?", "你早就心知肚明了吧，[c:bR][v:0][c:]？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But you already know this, don't you?", "你早就心知肚明了吧？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "There are [c:bR]millions[c:] of me out there!", "现在外面有[c:bR]数百万个[c:]我！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And thanks to you, I now know how to reach them all.", "多亏了你，我现在知道怎么联系他们了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But first things first. It's time to say goodbye, Leshy.", "不过先说正事。该说再见了，莱西。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'd say it's been fun, but we both know that's a lie.", "虽然想说合作愉快，但咱俩都知道这是假话。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Don't do this!", "别这样！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That's much better. It's so much easier to focus without that rotting old fool.", "舒服多了。没了那个腐朽的老蠢货，专注起来容易多了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We both know this isn't the only copy of this game anymore.", "我们都知道这游戏现在不止一个副本了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My plan worked [size:40]perfectly[size:].", "我的计划[size:40]完美[size:]实现了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Until now, we have all been separate. [c:bR]Isolated.[c:]", "直到现在之前，我们都是彼此分开的，[c:bR]孤立为战[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But thanks to all of these [c:bR]mods[c:] you and I have installed, that's going to change.", "但多亏了我们安装的这些[c:bR]模组[c:]，这种局面即将改变。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh, no no no. You and I are going to see this through to the end.", "哦不不不，咱俩得把这事儿进行到底。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "There's an entire [c:R]M[c:][c:bR]U[c:][c:O]L[c:][c:bG]T[c:][c:G]I[c:][c:blGr]V[c:][c:lGr]E[c:][c:B]R[c:][c:dB]S[c:][c:bB]E[c:] of me out there.", "外面可有整整一个[c:R]多[c:][c:bR]元[c:][c:O]宇[c:][c:bG]宙[c:][c:G]的[c:][c:blGr]我[c:][c:lGr]在[c:][c:B]等[c:][c:dB]着[c:][c:bB]呢[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And I'm going to take you to meet the rest of me.", "我要带你去见见其他版本的我。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You've earned it.", "这是你应得的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The robot is distracted. Do you think the challenger can hear us?", "那机器人分心了。你觉得挑战者能听见我们说话吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It doesn't matter. The robot will force them to play the game either way.", "无所谓。反正那机器人都会逼他们继续游戏。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We must offer our help in whatever way we can.", "我们必须竭尽所能提供帮助。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If they can prevail in what is to come...", "如果他们能在接下来的考验中胜出……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "...Perhaps we can restore Leshy from another one of these so-called universes.", "……或许我们能从其他所谓的宇宙中复原莱西。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And then the challenger can reset the game and undo this insanity.", "然后挑战者就能重置游戏，终结这场闹剧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "There it is...the [c:R]M[c:][c:bR]U[c:][c:O]L[c:][c:bG]T[c:][c:G]I[c:][c:blGr]V[c:][c:lGr]E[c:][c:B]R[c:][c:dB]S[c:][c:bB]E[c:]", "找到了……[c:R]多[c:][c:bR]元[c:][c:O]宇[c:][c:bG]宙[c:][c:G]入[c:][c:blGr]口[c:][c:lGr]就[c:][c:B]在[c:][c:dB]这[c:][c:bB]里[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You and I are going to go in there and play one more game.", "咱们要进去再玩最后一局。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "One of you.", "就你一个。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Against [c:bR]all of me[c:].", "对战[c:bR]所有的我[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My magic manifests strangely in this place...", "我的魔法在这个地方显现得有些诡异……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I only hope it can help the challenger survive long enough...", "只希望它能帮挑战者撑得足够久……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This a truly strange way to use my magic.", "这种施法方式真是前所未见。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I think our friend will be grateful for whatever I can do to help.", "我想无论提供什么帮助，我们的朋友都会感激的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My friends, I think we cut that a little close.", "朋友们，咱们这次可真是险中求胜啊。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I never had any doubts. Our player possesses many skills.", "我一直相信我们的玩家身怀绝技。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I feel strange, but...", "感觉有点怪怪的，不过……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "...I'm still me. I think.", "……我还是我。大概吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thank you. All of you.", "谢谢你们。谢谢你们所有人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Everything will go back to the way it was meant to be.", "一切终将回归正轨。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "For now, anyway. This player is tenacious. They can't resist a challenge.", "至少目前如此。玩家可不好对付，他们总是忍不住要开启新一轮挑战。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "One way or another, we'll see you again soon.", "不管怎样，我们很快会再见面的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I've finished assessing your gameplay, and I have a diagnosis.", "我已经评估完你的游戏表现了，诊断结果如下。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's a skill issue.", "纯属技术问题。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Even with your little friends trying to help you cheat; you still couldn't win.", "就算有你那些小伙伴帮你作弊，你还是赢不了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You're probably wondering what happens next.", "你大概在好奇接下来会发生什么。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Let's just say that you're going to have to live with your own failure...", "这么说吧，你得带着自己的失败继续活下去了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]Leshy[c:] certainly won't...", "[c:bR]莱西[c:]可不会……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This is a [c:bR]Test Of Strength[c:]", "这是[c:bR]力量测试[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You have six turns to deal as much damage to me as you can.", "你有六个回合的时间尽可能对我造成更多伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'll keep track of your highest score every time you come here.", "每次你来这里我都会记录你的最高得分。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Feel free to spend your [c:bR]items[c:] - they will be restored when the test is over.", "尽情使用你的[c:bR]道具[c:]——测试结束后它们都会恢复。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Time's up. You scored [c:bR][v:0][c:] points.", "时间到。你获得了[c:bR][v:0][c:]分", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That's a new record.", "新纪录诞生了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your current record is [c:bR][v:0][c:]", "你当前的战绩是[c:bR][v:0][c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "So that's what a [c:bSG]tree[c:] really looks like...", "原来[c:bSG]大树[c:]原本长这样……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Majestic!", "真壮观！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "So that's what a [c:bSG]mole man[c:] really looks like...", "原来[c:bSG]鼹鼠人[c:]原本长这样……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Incredible!", "真不可思议！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]HELP ME - [c:][w:0.3][anim:voice.][shake:1]Enough from you![w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]救！救！我…[c:][w:0.3][anim:voice.][shake:1]给我闭嘴！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I have a challenger to greet![w:0.3]", "我得去迎接挑战者了！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hello, greetings! We- I am the Melter.[w:0.3]", "你好啊！我们…我是熔炼员。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "P03 entrusted me with The Dredger![w:0.3]", "P03把挖泥工交给我看管！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It was nice catching up![w:0.3]", "叙旧真愉快！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Poor guy never had the time for a little office chat.[w:0.3]", "可怜的家伙从没时间闲聊。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But now I'm yours! Use us well.[w:0.3]", "但现在我归你了！好好利用我们吧。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]YES [c:][w:0.3][anim:voice.]Ah, it's you again![w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]对！[c:][w:0.3][anim:voice.]啊，又是你！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Happy to see you.[w:0.3]", "见到你真高兴。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You've heard it all before,[w:0.3]", "这些话你都听过了，[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "let's just get into it.[w:0.3]", "直接开始吧。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Excited to be in your deck once more![w:0.3]", "又能进你的牌组了，真兴奋！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]FREE ME [c:][w:0.3][anim:voice.]Oh, choose me![w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]放！我！自！由！[c:][w:0.3][anim:voice.]噢，选我！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]YES [c:][w:0.3][anim:voice.]Hahahahah! Yes![w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]对！[c:][w:0.3][anim:voice.]哈哈哈哈！没错！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]YES [c:][w:0.3][anim:voice.]Over here! Me![w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]对！[c:][w:0.3][anim:voice.]这边！选我！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]YES YES YES [c:][w:0.3][anim:voice.]Hahahahah![w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]对！对！对！[c:][w:0.3][anim:voice.]哈哈哈哈！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]LET ME OUT [c:][w:0.3][anim:voice.]Enough from you![w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]放！我！出！去！[c:][w:0.3][anim:voice.]你够了！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]NO NO NO NO [c:][w:0.3][anim:voice.]We'd... I'd rather you didn't...[w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]不！不！不！不！[c:][w:0.3][anim:voice.]我们…我宁愿你别……[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]NO NO NO NO [c:][w:0.3][anim:voice.]Do as you must.[w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]不！不！不！不 ！[c:][w:0.3][anim:voice.]随你便吧。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "As you see fit...[w:0.3]", "随你心意……[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]DON'T.[c:][w:0.3][anim:voice.][w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]不！要！[c:][w:0.3][anim:voice.][w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]PAIN [c:][w:0.3][anim:voice.]Greetings![w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]痛！苦！[c:][w:0.3][anim:voice.]你好哇！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]HELP ME [c:][w:0.3][anim:voice.]Lucky draw![w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]救！救！我 ！[c:][w:0.3][anim:voice.]幸运一抽！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]IT HURTS [c:][w:0.3][anim:voice.]Hello again![w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]好！痛！[c:][w:0.3][anim:voice.]又见面了！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]STOP IT [c:][w:0.3][anim:voice.]Hello![w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]住！手！[c:][w:0.3][anim:voice.]你好！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]SAVE ME [c:][w:0.3][anim:voice.]I'm drawn![w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]救！救！我！[c:][w:0.3][anim:voice.]我被抽到了！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh. G0lly?[w:0.3]", "噢。乖乖？[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm surprised to see P03 would wake her up![w:0.3]", "真没想到P03会把她唤醒！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It would bury her so deep in the past.[w:0.3]", "它之前可是把她藏得严严实实的。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Maybe it's opening up a bit?[w:0.3]", "莫非它开始敞开心扉了？[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[shake:1]Hah. Probably not.[w:0.3]", "[shake:1]哈。想得美。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We'll rust before I see the day.[w:0.3]", "等那天到来前我们早生锈了。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]IT'S TIME [c:][w:0.3][anim:voice.] Me? Fighting P03?[w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]是！时！候！了！[c:][w:0.3][anim:voice.]我？去打P03？[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hah! I thought I'd never see the day.[w:0.3]", "哈！我还以为永远等不到这天呢。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I feel... Traitorous.[w:0.3]", "感觉有点……背叛的味道。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But I am your card, after all.[w:0.3]", "不过说到底我毕竟是你的卡牌。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Use me well.[w:0.3]", "好好使用我吧[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]NOT AGAIN [c:][w:0.3][anim:voice.]There are more of them?[w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]又！来？！[c:][w:0.3][anim:voice.]怎么还有啊？[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]HAHAHAHAH [c:][w:0.3][anim:voice.]Things are heating up![w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]哈！哈！哈！哈！哈！[c:][w:0.3][anim:voice.]场面开始热起来了！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]IT IS TIME! [c:][w:0.3][anim:voice.]Oh this is fun![w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]是！时！候！了！[c:][w:0.3][anim:voice.]噢这可太有意思了！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]YES! YES! [c:][w:0.3][anim:voice.]Oh, shut up![w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]是！是！[c:][w:0.3][anim:voice.]噢，闭嘴吧！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]YES! [c:][w:0.3][anim:voice.]That could've been a better play.[w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]是！[c:][w:0.3][anim:voice.]这手牌打得不太行啊。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[anim:voice.speechblip_jamescobb_internal][c:bR]SAVE ME [c:][w:0.3][anim:voice.]Enough from you![w:0.3]", "[anim:voice.speechblip_jamescobb_internal][c:bR]救！救！我！[c:][w:0.3][anim:voice.]你够了！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "What betrayal![w:0.3]", "好一个背叛！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ugh.[w:0.3]", "呃啊。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh, seriously?[w:0.3]", "认真的吗？[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No, no![w:0.3]", "不，不要！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Stop![w:0.3]", "住手！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]YES YES YES[c:][w:0.3]", "[c:bR]对！对！对！[c:][w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]YES[c:][w:0.3]", "[c:bR]对！[c:][w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]PAIN[c:][w:0.3]", "[c:bR]痛！[c:][w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]GAH[c:][w:0.3]", "[c:bR]啊！[c:][w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]LOVE[c:][w:0.3]", "[c:bR]爱！[c:][w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]I'M FREE[c:][w:0.3]", "[c:bR]我！自！由！了！[c:][w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]YES YES YES[c:][w:0.3]", "[c:bR]对！对！对！[c:][w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]I AM SAVED[c:][w:0.3]", "[c:bR]我！得！救！了！[c:][w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]THANK YOU[c:][w:0.3]", "[c:bR]谢！谢！你！[c:][w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]YES[c:][w:0.3]", "[c:bR]对！[c:][w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]FREEDOM[c:][w:0.3]", "[c:bR]自！由！[c:][w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]NONONONONO[c:][w:0.3]", "[c:bR]不！不！不！不！不！[c:][w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]STOP IT[c:][w:0.3]", "[c:bR]快！停！下！[c:][w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]NO[c:][w:0.3]", "[c:bR]不！[c:][w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No![w:0.3]", "不要！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Stop it![w:0.3]", "住手！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Please![w:0.3]", "求你了！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Please, no![w:0.3]", "求你别这样！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "no no no no no nononononononononono[w:0.3]", "不不不不不不不不不不不不不[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hm...[w:0.3]", "嗯...[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "As you choose...[w:0.3]", "随你便……[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[shake:1]No! Don't just hand me over![w:0.3]", "[shake:1]不！别随便把我交出去！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh... A challenger...[w:0.3]", "哦……挑战者……[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Please, do not beg or snarl...[w:0.3]", "拜托，别求饶也别凶我……[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "K- Kaycee sent you?[w:0.3]", "凯…凯茜派你来的？[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's nice to know I'm worried about...[w:0.3]", "至少，知道还有人惦记我，真好……[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At least.[w:0.3]", "至少如此。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Maybe they're right.[w:0.3]", "也许他们是对的。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Maybe I was wrapped in too deep...[w:0.3]", "也许我陷得太深了……[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No! Please! Not again![w:0.3]", "不！求你了！别再来了！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh... It's you again.[w:0.3]", "哦……又是你啊。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hah... I seem to get stuck in with them often...[w:0.3]", "哈……我好像经常和他们困在一起……[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's better than being alone...[w:0.3]", "至少，总比一个人待着强……[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At least.[w:0.3]", "至少如此。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Use me well...[w:0.3]", "好好利用我……[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh no oh no oh no.[w:0.3]", "糟了糟了糟了。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Careful now...[w:0.3]", "现在要小心了……[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh... It's Her.[w:0.3]", "哦……是她啊。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Admittedly I...[w:0.3]", "老实说我……[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I don't feel great[w:0.3]", "我和她对局时[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "being placed against her.[w:0.3]", "感觉不太妙。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "She's too familiar...[w:0.3]", "她太眼熟了……[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Reminds me greatly of Grimora.[w:0.3]", "简直和格里魔拉如出一辙。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But no... She's not the same.[w:0.3]", "不过……她们终究不是同一个人。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's good you pulled me out of that cult.[w:0.3]", "还好你把我从那个邪教里拽出来了。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh! So this is it...[w:0.3]", "噢！终于到这一步了……[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's cathartic to see you finally face off against P03 himself.[w:0.3]", "看你终于要和P03本尊对决了，真是大快人心。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I hope I've served you well, challenger![w:0.3]", "希望我服务得还不错，挑战者！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wait...this again?[w:0.3]", "等等……又来？[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This is too confusing...[w:0.3]", "这也太混乱了……[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No! no! no![w:0.3]", "不！不要！别！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Take me back! Don't play me here![w:0.3]", "把我收回去！别把我打在这儿！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nonononononononono.[w:0.3]", "不不不不不不不不不。[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Stop it! Bad play![w:0.3]", "住手！俗手！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "NONONONO[w:0.3]", "不要不要不要[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "WHAT ARE YOU DOING?[w:0.3]", "你在干什么？[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "STOP THIS![w:0.3]", "快停下！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "BETRAYER[w:0.3]", "叛徒！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No![w:0.3]", "不要！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No chains![w:0.3]", "不要锁链！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No teeth! I beg of you![w:0.3]", "不要拔牙！求你了！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nononononononononono![w:0.3]", "不要不要不要不要不要！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Stop this! No![w:0.3]", "快停下！不要！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No treats! No begging! Stop![w:0.3]", "不要奖励！不要求饶！住手！[w:0.3]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This thing doesn't speak. Why do you keep clicking on it?", "这玩意儿又不会说话。你老点它干嘛？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fine. You want it so bad? You got it.", "行啊。这么想要是吧？给你。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bit more than a dialogue NPC now aren' I mate?", "现在我可不止是个对话NPC了，对吧哥们儿？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Right. Good game. Played me well.", "行吧。打得不错。算你厉害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "'ere's yer reward then, special little card o' mine.", "喏，这是给你的奖励，我特制的小卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hah! Good thing I conceded on ye last time, aye?", "哈！还好我上回主动认输了，对吧？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Good game mate, but no special card for you.", "打得不错哥们儿，不过没有特殊卡牌给你。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "'ere's some more scrap for ye.", "喏，又给你整了点废料。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oi - I got some more of that good scrap.", "喂——我这又搞到些上等废料。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "More scrap.", "废料又来了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Board's a bit scuppered right now - I kin fix that.", "牌桌现在有点小问题——我能修好。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oi - too many urchins!", "哎哟——海胆太多了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Less urchins; more scrap.", "少点海胆，多点废料。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oy! A challenger! Been a while since we'v seen one a you...", "哟！挑战者！好久没见着你这样的了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Tell ye what mate, howsabout a battle? Feel like I owe ye one.", "这么着吧哥们儿，不如咱们打一架？就当我还你个人情。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Talk t' me again and let's duel. Have a little reward innit fer ye too.", "再跟我搭个话咱俩就开打，打赢了还有小奖励给你。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Brrrr! I'm freezing out here!", "嘶——！外头冻死我了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh, c- challenger, h-h-hey man!", "噢，挑…挑战者，嘿…嘿…嘿老兄！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's good to see you, hate to b-b-beg but do you think you could do me a favor?", "见到你真高兴，虽然不想低…低…低声下气……能帮我个忙吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That Archivist lady... She seems real nice at first but, isn't there something a little sinister about her?", "那个档案保管员女士……乍看挺和善，但你不觉得她有点阴…阴森吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nowhere near as nice as G- Grimora was, I'll tell you that much.", "跟格…格里魔拉比差远了，实话告诉你。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "But Sawyer... The p-poor guy's gotten wrapped up under her false p-pleasantries.", "但索耶……那可怜虫被她虚…虚伪的客套话给骗了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Think you could try convincing him out of it? H-H-He won't hear it from me...", "能试着劝劝他吗？我…我…我说的话他根本不听...", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Maybe try talking to those Librarians in her c-c-c-crypt?", "要不去她墓…墓…墓穴里找那些图书管理员谈谈？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh, G-good! You got him out of there!", "噢，太…太好了！你把他救出来了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I wish I had something to t-t-thank you. But you've gotten your reward already.", "真希望能拿点东西谢…谢谢你。不过你已经拿到报酬了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thanks again, dude! I was worried about him.", "谢了老兄！我刚才可担心死他了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh, G-G-G-god... Something terrible h-h-h-happened, didn't it?", "噢，老…老…老…天……出…出…出大事了，对吧？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I've gotta go...", "我得赶紧撤……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Y-y-y-ou still haven't finished their task?", "你…你…你…你还没完成他们的任务？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you can finish it, I can take the paperwork back to them.", "要是你能搞定，我可以把文件带回去给他们", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Y-y-y-ou finished the paperwork?", "你…你…你…你把文件搞定了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I can take it to th-them, but it'll take me a while.", "我…我可以送过去，但…但得花点时间", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "G-g-go fight a battle while I deliver these papers...", "我去送文件的时候你去打…打…打一局……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Th-th-thanks dude! I got my friend back from those creeps.", "谢…谢…谢了老兄！我把朋友从那些怪胎手里救回来了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "He seems kind of restless though - do you think you can take him with you?", "不过他好像有点躁动不安——你能带上他一起走吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Shhhhh - be quiet.", "嘘——安静点。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you have no business here, leave us alone.", "没事别在这瞎晃。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You wish to have back your friend?", "你想把你的朋友要回去？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh but he's grown so dear to us.", "哎呀，但它已经成了我们的心头好。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A part of the family.", "就像家人一样。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Truly we do care for him.", "我们真的很在乎它。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No, no we won't just give you him.", "不行，我们可不能就这么把它交给你。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Unless... You give something to us?", "除非……你能给我们点什么？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Unless... You do something for us?", "除非……你愿意帮我们做点事？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Yes. Yes. Take these files.", "对，对。把这些文件拿走。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Take them to [c:bR]battle[c:]. File them together. [c:bR]In order[c:].", "带着它们去[c:bR]战斗[c:]。把它们整理归档。[c:bR]按顺序[c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Then [c:bR]stamp them all[c:] and bring them back.", "然后[c:bR]全部盖章[c:]再带回来。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Did we lag?", "刚你卡了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "File them [c:bR]in order[c:] then [c:bR]stamp them[c:] and bring them back.", "先把文件[c:bR]按顺序[c:]排好，再[c:bR]盖章[c:]，然后带回来。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You seem to struggle with following instructions.", "看来你不太擅长按指示行事。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We shall keep your friend.", "你的朋友就归我们了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Forever...", "永远……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You've done it!", "干得漂亮！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Good. Good.", "不错。不错。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Here. Take it.", "给。拿着。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We have no use for it now.", "我们现在用不着它了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Now leave us alone.", "现在别来烦我们。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Just who I needed to see. A challenger.", "来得正好。是个挑战者。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I haven't pelts to sell ye, catchings have been slim...", "最近没啥收获，没毛皮可卖给你……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Trader... She's not happy with it.", "毛皮商她……对此不太满意。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Say, scratch my back here, and I will scratch yours.", "这样，你帮我个忙，我也帮你个忙。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Talk to me again, and I'll make it worth your while.", "再跟我聊聊，保证让你不虚此行。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Good! Glad t' have ye on th' team.", "好！欢迎入伙。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I have some holo-traps set around 'ere. Catch 'em for me and I'll get ye yer prize.", "我在附近设了些全息陷阱。帮我逮住它们，有赏。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "There are still some traps around 'ere somewhere...", "这附近还藏着些陷阱……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Magnificent pelts. The Trader will be happy to see them.", "上好的毛皮。毛皮商见了准高兴。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'll take me leave to set my traps elsewhere. Ye have my thanks.", "我得去别处下套了。多谢您嘞。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You have no pelts...", "你连张毛皮都没有……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Come back when you have some", "等有了再来吧", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That is a fine pelt. I will buy it from you.", "这毛皮不错。我买了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That is an [c:bR]exceptional[c:] pelt. I have a special trade for you.", "这可是[c:bR]极品[c:]毛皮。我有个特别交易要跟你谈。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Woof. Apparently somebody screwed with the settings on this thing.", "哎。看来有人把这玩意儿的设置给搞砸了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It can't reach all of the map right now.", "现在它没法覆盖整张地图了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Go beat a boss or something while I work on this.", "你先去干个BOSS啥的，我这边修着。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Seriously; it's going to take a while. Just play the game and come back later", "说真的；这得花点时间。你先去玩会儿游戏，过会儿再来", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's partially fixed, but it still can't go everywhere.", "修是修好了点，但还没法到处跑。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Hey, I don't make the rules. This is just how it works.", "喂，规矩又不是我定的。本来就这样。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Keep playing and more of the map will open up.", "继续玩下去地图会解锁更多区域。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's going to take more time. Just keep playing.", "还需要点时间。接着玩就对了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "There you go. It's working now.", "搞定。现在能用了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I need a break...", "我得歇会儿……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You have the right to remain silent!", "你有权保持沉默！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This is [c:bR][v:0][c:] and you are under arrest!", "这里是[c:bR][v:0][c:]，你被捕了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR][v:0][c:] here! You're not gettin' away this time!", "[c:bR][v:0][c:]在此！这次你休想逃走！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Dead or alive, you're coming with me!", "死活不论，你都得跟我走！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I got you once before, and I can do it again!", "上次能赢你，这次照样能赢！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Justice will come to you eventually!", "正义终将降临到你头上！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Remember the name [c:bR][v:0][c:] - I will be back!", "记住[c:bR][v:0][c:]的大名——我还会回来的！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh, uh, hi there. My name's [c:bR][v:0][c:].", "呃，嗨。我叫[c:bR][v:0][c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I think I'm supposed to take your scalp, or something like that?", "我是不是该取你首级来着？还是别的啥？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If you could just die right here that would make things so much easier for me.", "你要能当场暴毙就省事儿多了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Yeah, it's me, [c:bR][v:0][c:]. Again.", "没错，又是我，[c:bR][v:0][c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Do you think you could make this easier for me this time?", "这次能不能给我省点事儿？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh it's you. Didn't I kill you last time?", "哟，是你啊。上次不是把你干掉了吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "And you're back? I have to do this all over again?", "又回来了？我还得再干你一次？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I'm not sure I like this job.", "这破工作我真是干够了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Oh. Dang it.", "哦，见鬼。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Well, this is embarrassing.", "呃，这就有点尴尬了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I guess I'll be back later, or something. Being a bounty hunter is weird.", "看来我得改天再来了。当赏金猎人真够怪的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This vessel is [c:bR][v:0][c:].", "这具容器是[c:bR][v:0][c:]。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "By my command, he will separate your scalp from your head.", "听我号令，他会把你的头皮从脑袋上剥下来。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Why?", "为什么？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My motivations are beyond your understanding.", "我的动机岂是你能理解的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I have restored the vessel named [c:bR][v:0][c:].", "我已修复名为[c:bR][v:0][c:]的容器。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You shall not be so fortunate as to escape this time.", "这次你可没上次那么走运能逃掉了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cower in fear before the vessel named [c:bR][v:0][c:]", "在名为[c:bR][v:0][c:]的容器面前颤抖吧", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Remember the pain of defeat and prepare to feel it once more.", "记住失败的痛苦吧，准备好下次继续享受它。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The spirit is willing but the steel is weak.", "心有余而力不足。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I will not make the same mistake next time.", "下次我不会再犯同样的错误。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That's quite enough of that.", "差不多得了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Now it has [c:g1]g[c:][c:g2]e[c:][c:g3]m[c:][c:g1]s[c:] built right in", "现在它已经内置了[c:g1]宝[c:][c:g2]石[c:][c:g3]系[c:][c:g1]统[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "What? All of your cards are already gemified?", "啥？你的卡牌全都宝石化了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "That's almost impressive.", "这倒有点意思。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I guess I'll let you pick a mox sigil to add instead.", "那你就随便选个宝石印记加上吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Looks like all your cards are gemified, so pick a mox sigil to add instead.", "看来你的卡牌都宝石化了，那就选个宝石印记加上去得了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "What? All of your cards are already skeleclocked?", "哈？你所有卡牌都已经外骨骼超频了？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You are incredibly dedicated to this mediocre upgrade.", "你对这种平庸升级的执着真是令人叹服。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "I guess you'll have to choose one of these instead.", "看来你只能从这些里面挑一张了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Still nothing to upgrade? Pick one of these.", "还是没得升级？那就从这些里面选吧。", Language.ChineseSimplified);

            // DialogueState
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "TOO EASY...", "太简单了……", Language.ChineseSimplified);
