            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "With this boon equipped, Start each battle with a black goat on the board.", "当装备该奖励时，每场战斗开始时，在你一侧的场上放一只黑山羊。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Start each battle with +3 bones.", "每一场战斗开始时就有3块骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "With this boon equipped, Start each battle with +3 bones.", "当装备该奖励时，每一场战斗开始时就有3块骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Draw 2 cards each turn instead of 1, but it will only function if you have less than 20 cards in your deck while battling.", "每回合抽2张牌，而不是1张，但只有当你战斗时牌组中的牌少于20张时，它才会起作用。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "With this boon equipped, Draw 2 cards each turn instead of 1, but it will only function if you have less than 20 cards in your deck while battling.", "当装备该奖励时，每回合抽2张牌，而不是1张，但只有当你战斗时牌组中的牌少于20张时，它才会起作用。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Start each battle with 2 firs in your hand.", "每一场战斗开始时，你手中获得两张大冷杉。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "With this boon equipped, Start each battle with 2 firs in your hand.", "当装备该奖励时，每一场战斗开始时，你手中获得两张大冷杉。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Boon of Fossils", "化石之奖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Terrain cards yield 3 bones on death instead of 1.", "地形卡在死亡时产生3根骨头，而不是1根。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "With this boon equipped, Terrain cards yield 3 bones on death instead of 1.", "当装备该奖励时，地形卡在死亡时产生3根骨头，而不是1根。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Boon of Marrow", "骨髓之奖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sacrificing undead cards yields 2 bones instead of 1.", "献祭亡灵卡牌会产生2根骨头而不是1根。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "With this boon equipped, Sacrificing undead cards yields 2 bones instead of 1.", "当装备该奖励时，献祭亡灵卡牌会产生2根骨头而不是1根。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Boon of Bloodstone", "石中取血之奖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You may sacrifice terrain cards.", "你可以献祭地形卡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "With this boon equipped, You may sacrifice terrain cards.", "当装备该奖励时，你可以献祭地形卡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Boon of Ouroboros", "衔尾蛇之奖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You get a different effect depending on the region:\nBeast Dungeon:+2 Starting bones each battle.\nDeath Dungeon:+1 Starting Energy each battle\nTech Dungeon:Start the game with two random mox in your hand.\nMagick Dungeon:Start the game with 2 Squirrels in your hand.", "根据区域不同，您会获得不同的效果：\n野兽地牢：每场战斗开始时+2根起始骨头。\n死亡地牢：每场战斗开始时+1个起始能量。\n科技地牢：每场战斗开始时获得额外两张随机玛珂牌。\n魔法地牢：每场战斗开始时获得两张额外松鼠。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "With this boon equipped, You get a different effect depending on the region:\nBeast Dungeon:+2 Starting bones each battle.\nDeath Dungeon:+1 Starting Energy each battle\nTech Dungeon:Start the game with two random mox in your hand.\nMagick Dungeon:Start the game with 2 Squirrels in your hand.", "当装备该奖励时，根据区域不同，您会获得不同的效果：\n野兽地牢：每场战斗开始时+2根起始骨头。\n死亡地牢：每场战斗开始时+1个起始能量。\n科技地牢：每场战斗开始时获得额外两张随机玛珂牌。\n魔法地牢：每场战斗开始时获得两张额外松鼠。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ay yo yaw cant stand right here", "哎呀，你不能站在这儿。", Language.ChineseSimplified);
