            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Just what do YOU think you're doing? Wandering around and destroying our dungeons.", "你以为你在干什么？四处游荡，摧毁我们的地牢。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A little mouse wanders into my sight..", "一只小老鼠闯入我的视线……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At last, I have found you..", "我终于找到你了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Beasts", "野兽", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Technology", "科技", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Magick", "魔法", Language.ChineseSimplified);
