            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My beasts are incensed by your creature's presence.", "我的野兽被你造物的存在激怒了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Alchemist", "炼金术士", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 2 Energy to discard your current hand and draw cards equal to the amount you discarded.", "消耗2点能量以弃掉当前手牌，并抽取等同于弃牌数量的牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The unending faith of countless promises.", "无数承诺的永恒信念。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] replaces your hand with a new one!", "[creature]洗掉了你的手牌替换为新牌！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Assimilator", "吸纳者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When this card kills an opposing card, it gains 1 Power and 1 Health.", "当[creature]杀死其他卡牌时，它将获得1点力量和1点生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "From the many, one.", "从万物汲取。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] makes its victim a part of itself.", "[creature]使受害者成为自己的一部分。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Barreler", "匠心独运", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, this card moves in the sigil's direction to the end of the board, moving any cards in the way.", "在持牌人的回合结束时，[creature]将按印记标注的方向移动到场地末端，同时移动途中遇到的任何卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Make room.", "创造房间。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] moves to the end of the board, tossing anything in its way.", "[creature]移动到场地末端，同时抛掷任何挡道的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Binding Strike", "捆绑打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] strikes an opposing creature, inflict Bind equal to twice this card's Power.", "当[creature]攻击造物时，施加等同于该卡牌两倍力量的束缚。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your beast falls behind the pack.", "你的野兽落在狼群后面。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bitter Enemies", "死敌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This card gains 1 Power for each other card on the board that also bears this sigil.", "场上每有一张带有此印记的卡牌，[creature]获得1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A bitter grudge laid bare.", "怨恨显露出来。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bloodfiend", "血魔", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When this card deals damage, it gains 1 Health.", "当[creature]造成伤害时，其获得1点生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Accursed fiend.", "被诅咒的恶魔。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] sucks out fresh life!", "[creature]吸取新鲜的生命！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bloodletter", "血之函", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a [creature] is struck with a nonfatal attack, absorb 1 Health from the striker.", "当[creature]受到非致命攻击时，从攻击者身上吸取1点生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The blood runs warm with sweet vitality.", "血液流动温暖，充满甘甜活力。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] absorbs nutrients!", "[creature]吸收养分！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Broodmother", "母巢", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, create a Spiderling in your hand. [define:wstl_spiderling]", "当[creature]受到攻击时，在你的手上创造一只幼蛛。幼蛛具有：0点力量，1点生命，幼雏。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A small spider takes refuge in your hand.", "一只小蜘蛛躲在你手里。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] drops a spiderling!", "[creature]掉落了一只小蜘蛛！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Conductor", "交响乐指挥", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The effect of this sigil will change over the next 3 turns while this card is on the board.", "当[creature]在场上时，此印记的效果会在接下来的3个回合中不断改变。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "From break and ruin, the most beautiful performance begins.", "从破碎和毁灭中，最美的演出起始。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] plays a quiet symphony.", "[creature]演奏寂静的交响乐。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Copycat", "模仿者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will transform into a copy of the first creature that opposes it.", "[creature]将转变为第一个对位它的造物的副本。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A near perfect impersonation.", "近乎完美的模仿。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] tries to mimick the opposing creature.", "[creature]试图模仿对位的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Corrector", "校正者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is drawn, randomly change its stats according to its total play cost.", "当[creature]被抽取时，根据其花费成本随机更改其属性值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "How balanced.", "如何平衡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] stats are forcefully 'corrected'.", "[creature]属性被强制“修正”。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Courageous", "无所畏惧", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Creatures adjacent to this card lose up to 2 Health. For each point of Heath lost, the affected creature gains 1 Power. This effect cannot kill cards.", "与[creature]相邻的造物会损失至多2点生命值。每损失1点生命值，受影响的造物就会获得1点力量。此效果不会消灭卡牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Life is only given to those who don't fear death.", "生命只属于那些不惧怕死亡的人。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cursed", "诅咒", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies, the killer transforms into a copy of this card.", "当[creature]死亡时，杀死此牌的牌会变成这张卡牌的副本。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The curse continues unabated.", "诅咒有增无减。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] passes the curse on.", "[creature]将会继续诅咒。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cycler", "循环器", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, this card moves in the sigil's direction, looping around the owner's side of the board.", "在持牌人的回合结束时，[creature]会向印记标注的方向移动，在场上持牌人一侧循环。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A never-ending cycle.", "一个永无止境的循环。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] moves to a new space, going around its side of the board.", "[creature]移动到一个新的空间，绕过场地的边缘。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "False Throne", "虚伪王座", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Once per turn, pay 1 Health to give Neutered to a chosen creature, then create a free, unaltered copy of it in your hand.", "每回合限一次，消耗1点生命值然后选定一个造物，然后在你手中创建一个免费的、未更改的副本。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A simple little magic trick.", "一个简单的小魔术。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] gives a false present to the chosen creature.", "[creature]给被选中的造物一份仿制品。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Flag Bearer", "旗手力量", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While this card is on the board, adjacent creatures gain 2 Health.", "当[creature]在场上时，相邻的造物获得2点生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Morale runs high.", "士气高涨。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Ruler of Frost", "冰霜统领", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Once per turn, pay 2 Bones to choose a space on the board. If the space is empty, create a Block of Ice. Otherwise, if this card can kill the occupying card, transform it into a Frozen Heart.", "每回合限一次，消耗2根骨头选择一个场上位置。如果位置为空，则创建一块冰块。否则，如果这张牌可以杀死占位牌，将其变成一颗冰封之心。冰封之心具有：0点力量，2点生命，高跳。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "With a wave of her hand, the Snow Queen blocked the path.", "冰雪女王挥了挥手，挡住了去路。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] freezes the path.", "[creature]冻结了路径。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Frozen Heart", "冰封之心", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies, the killer gains 2 Health.", "杀死[creature]的牌获得2点生命值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spring arrives with blossoming roses.", "春天来了，玫瑰花开了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] releases warm life.", "[creature]释放温暖的生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gardener", "园丁", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When an allied card is killed, create a Sapling in their place. [define:wstl_parasiteTreeSapling]", "当一张同盟卡牌被杀死时，在其位置创建一颗树苗。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "They proliferate and become whole. Can you feel it?", "它们增殖并变得完整。你能感觉到吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A sapling grows out of the dead card's corpse.", "一棵小树从死亡的卡牌尸体上长了出来。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Gift Giver", "赠礼者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, create a random card in your hand.", "放置[creature]时，你获得一张随机的牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A gift for you.", "一个给你的礼物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] has a gift for you!", "[creature]有一个礼物要给你！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Greedy Healing", "贪婪之愈", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of its owner's turn, this card gains 2 Health. If 2 turns pass without this card taking damage, it will die.", "在持有者的回合结束时，[creature]获得2点生命值。如果两回合内[creature]没有受到攻击，它将死亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your beast has Health in excess.", "你的野兽有过量生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] gives itself more Health!", "[creature]给予自己更多生命！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Grinder", "粉碎机", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This card gains the stats of the creatures sacrificed to play it.", "[creature]将获得为放置它而献祭的造物属性。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Now everything will be just fine.", "现在一切都会好起来的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Group Healer", "团队治疗师", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the start of its owner's turn, this card will heal all allies that have taken damage by 1 Health.", "在持牌人的回合开始时，[creature]将治愈所有受伤盟友1点生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You only delay the inevitable.", "你只会拖延不可避免的事情。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] heals all its friends.", "[creature]能治愈所有的盟友。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Healer", "治疗师", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of your turn, you may choose one of your other cards to heal by 2 Health.", "在持牌人回合结束时，其可以选择一张其他卡牌，恢复该牌2点生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Never underestimate the importance of a healer.", "永远不要低估治疗师的重要性。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] heals the chosen creature!", "[creature]治愈被选中的造物！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "High-Strung", "高度紧张", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the current turn, this card gains Haste equal to twice the opposing card's Power.", "在当前回合结束时，[creature]将获得的匆忙值等于对位卡牌力量的两倍。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Idol", "偶像", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While this card is on the board, all opposing creatures lose 1 Power.", "当[creature]在场上时，所有敌对造物都会失去1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "My beasts defer to you.", "我的野兽听命于你。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Left-Veering Strike", "偏左打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will strike the opposing space to the left of the space across from it.", "[creature]的攻击将始终在正常位置左侧一格。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Right-Veering Strike", "偏右打击", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] will strike the opposing space to the right of the space across from it.", "[creature]的攻击将始终在正常位置右侧一格。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Lonely", "孤独", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Choose a card on the board to gain 1 Pebble unless a card with Pebble already exists, then return this card to your hand. If a card with Pebble dies, all ally cards receive 1 additional damage until a new card is chosen.", "除非已经存在一张带有卵石的牌，否则选择在场一张牌以获得1个卵石，然后[creature]将回到你手中。如果带有卵石的卡牌死亡，所有友方卡牌将受到1点额外伤害，直到选择新牌为止。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A friend to stay.", "留下来一位朋友。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Martyr", "烈士", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] dies, all allied creatures gain 2 Health and lose any negative status effects.", "当[creature]死亡时，所有友方造物获得2点生命并失去所有负面状态效果。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A selfless death to cleanse your beasts of evil.", "无私的死亡，净化你造物的邪恶。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature]'s death cleanses your other creatures!", "[creature]的死亡会净化你的其他造物！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nettle Clothes", "荨麻衣", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When this card is played, fill all empty spaces on the owner's side of the board with random Brothers. This card gains the first sigil of each allied Brother.", "当放置[creature]时，用随机的兄弟卡牌填补场上持牌人一侧的所有空位。[creature]获得每个友方兄弟卡牌的首个印记。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "These clothes will surely restore our happy days.", "这些衣服一定会让我们恢复快乐的时光。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] brings out its family!", "[creature]带出了它的家人！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Neutered", "绝断", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] has its Power reduced to 0. At the start of the owner's turn, remove this sigil.", "[creature]的力量降低至0。在持牌人的回合开始时，此印记将被删除。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Neutered Latch", "绝断激转", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Once per turn, pay [sigilcost:2 Bones] to choose a creature to gain the Neutered sigil, then increase this sigil's activation cost by 1 Bone.", "每回合限一次，消耗2根骨头选择一个造物来获得绝断印记，然后将此印记的激活成本增加1根骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The will to fight has been lost.", "战斗的意志已经丧失。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] prevents the chosen creature from attacking.", "[creature]阻止被选中的造物攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nimble-Footed", "灵巧之足", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the start of the owner's turn, this card gains 2 Haste.", "在持牌人的回合开始时，[creature]获得2点匆忙值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Opportunistic", "机会主义", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] deals 1 additional damage to opposing cards that cannot attack this card during their own turn.", "[creature]对在自己回合中无法攻击此牌的对手卡牌造成1点额外伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A cheap hit.", "一次廉价的攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Persecutor", "迫害者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a card bearing this sigil is played, create a Nail in the adjacent left space and a Hammer in the adjacent right space if they are empty.", "当放置[creature]时，若相邻的左侧和右侧位置均为空，则分别在左侧和右侧空位上创建一个钉子和一把锤子。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Are you guilty of having a closed heart?", "你是否为自己封闭的心感到内疚？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] reveals its hidden tools!", "[creature]露出了它隐藏的工具！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Persistent", "坚持不懈", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Attacks by this card cannot be avoided or redirected by sigils like Loose Tail or Waterborne.", "类似断尾求生和水袭这样的印记无法避免或转移[creature]的攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Prey cannot hide so easily.", "猎物可没那么容易藏匿自己。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] chases its prey down.", "[creature]追捕猎物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Damage dealt by this card cannot be negated or reduced by sigils such as Armoured or Thick Skin.", "[creature]造成的伤害无法被身披坚甲或茂密皮毛等防御印记所抵消或减少。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your beast runs mine through.", "你的野兽把我撕碎了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Creatures adjacent to this card take 1 less damage when struck.", "与[creature]相邻的造物在被攻击时受到的伤害减少1点。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your beast shields its ally against the blow.", "你的野兽为友方抵挡攻击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] shields its friend!", "[生物]为它的友方提供保护！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Punisher", "惩罚者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, the striker is killed.", "当[creature]被击中时，攻击者会被杀死。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Retaliation is switft and brutal.", "报复行动迅速且残忍。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] swiftly retaliates!", "[creature]迅速反击！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Queen Nest", "蜂后", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Whenever another card is killed, create a Worker Bee in your hand. [define:wstl_queenBeeWorker]", "[creature]在场时，每当有其他卡牌被消灭时，在你的手牌中生成一只工蜂。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "For the hive.", "为了蜂巢。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Another worker is born to serve [creature].", "另一工人天生就是为了服务[creature]而存在的。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Reflector", "反射者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When this card is struck, the striker is dealt damage equal to the striker's Power.", "当[creature]被击中时，攻击者会受到与其力量值相等的伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "What goes around comes around.", "一报还一报。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] returns the damage.", "[creature]返还了伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Refresh Decks", "牌组重塑", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Refreshes and reshuffles both draw piles.", "将主牌堆和副牌堆的牌重新洗牌。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the start of its owner's turn, this card heals adjacent cards by 1 Health.", "在持牌人的回合开始时，[creature]会治愈相邻卡牌1点生命。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wounds heal, but the scars remain.", "伤口会愈合，但疤痕依然存在。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] heals adjacent creatures.", "[creature]治愈邻近的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Return Card to Hand", "回归手牌", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Returns the selected card to your hand with all changes and statuses retained. Change the selected card's cost to 0-2 Bones based on how recently it was played.", "将所选卡片归还回至你手中，并保留所有更改和属性。根据最近放置的卡牌时间，将所选卡牌的成本更改为0-2块骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Return to Nihil", "回归虚无", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, deal damage equal to this card's Power to all other cards on the board.", "在持牌人的回合结束时，[creature]将对场上的所有其他卡牌造成与此牌力量值相等的伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "One step closer to oblivion.", "离遗忘又近了一步。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The void calls.", "虚空召唤。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Rightful Heir", "法定继承者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Once per turn, pay [sigilcost:1 Bone] to transform a chosen creature into a Pumpkin, then increase this sigil's activation cost by 1. [define:wstl_ozmaPumpkin]", "每回合一次，消耗1根骨头将选定的造物变成南瓜，然后将此印记的激活成本增加1。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "All she has left now are her children.", "她现在只剩下孩子了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] turns the creature into a pumpkin.", "[creature]把造物变成南瓜。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Roots", "缠根", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When this card is played, create Thorny Vines on adjacent empty spaces. [define:wstl_snowWhitesVine]", "放置[creature]时，在相邻的空位生成刺藤。刺藤具有：0点力量，1点生命，尖刺铠甲。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Resentment bursts forth like a weed.", "怨恨像野草一样爆发。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sharp thorns shoot out around [creature]!", "尖锐的刺在[creature]周围射出！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Scorching", "烈炎", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The creature opposing this card takes 1 damage at the end of its owner's turn.", "与[creature]对位的造物在回合结束时受到1点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A slow and painful death.", "缓慢而痛苦的死亡。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The creature opposing [creature] is burned!", "对抗[creature]的造物被烧毁了！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Scrambler", "扰频器", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is sacrificed, give its stats to the sacrificing card then randomise the resulting stats.", "当[creature]被献祭时，将其属性提供给献祭其上场的牌，然后将最终属性随机化。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, " Works for Spells.", "适用于法术。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Do you love your city?", "你爱你的城市吗？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Serpent's Nest", "裸巢", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is struck, the striker gains 1 Worms.", "当[creature]被攻击时，攻击者获得一点裸虫值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It can enter your body through any aperture.", "它可以通过任何孔进入你的身体。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Made of Slime", "史莱姆之身", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, give adjacent cards this sigil, and deal 1 damage to this card if it is not a Slime. This card transforms into a Slime on death.", "在持牌人的回合结束时，[creature]将给予相邻的卡牌此印记，如果这张牌不是史莱姆，则对其造成1点伤害。此牌在死亡时会变成史莱姆。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Its army grows everyday.", "它的军队每天都在壮大。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] melts into slime!", "[生物]融化成史莱姆！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sporogenic", "孢子囊", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Creatures adjacent to this card gain 1 Spores at the end of its owner's turn. This sigil activates before other sigils.", "与[creature]相邻的造物在其持牌人的回合结束时获得1个孢子。此印记将在其他印记之前生效。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "They will love this curse like a blessing.", "它们会像爱祝福一样爱这个诅咒。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] scatters spores on the adjacent cards!", "[creature]在相邻的卡片上散布孢子！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Team Leader", "团队领袖", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "While this card is on the board, all allied creatures gain 1 Power.", "当[creature]在场上时，所有友方造物都会获得1点力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your beast emboldens its allies.", "你的野兽让它的盟友更加大胆。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Train", "列车", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is played, kill all creatures on the board. Creatures killed this way do not drop bones.", "放置[creature]时，杀死场上的所有造物。以这种方式杀死的造物不会掉骨头。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The train boards those that don't step away from the tracks.", "列车会消灭那些没有离开轨道的造物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The train blows its mighty horn.", "列车鸣响警笛。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thick Skin", "茂密皮毛", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Whenever [creature] takes damage, reduce that damage by 1.", "每当[creature]受到伤害时，该伤害-1。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your creature's hide absorbs the blow.", "你的造物的皮毛吸收了打击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] absorbs the blow.", "[creature]吸收了这一击。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Volatile", "易爆", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When this card dies, adjacent and opposing cards are dealt 10 damage.", "当[creature]死亡时，相邻和对位的卡牌将受到10点伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "An explosive finish.", "爆炸性的完成。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] detonates! Adjacent creatures are killed in the blast.", "[creature]爆炸了！邻近的造物在爆炸中被杀死。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Witness", "目击者", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pay 2 Bones to increase the selected creature's Health by 2 and their taken damage by 1. This effect stacks up to 3 times.", "消耗2根骨头，使所选造物的生命值增加2，受到的伤害增加1。此效果可叠加3次。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The truth will set you free.", "真相会让你获得自由。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The chosen beholds [creature] and is reborn.", "被选中的造物看到[creature]并获得新生。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Woodcutter", "樵夫", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When a creature moves into the space opposite this card, they take damage equal to this card's Power.", "当其他造物移动到[creature]对面的空间时，它们受到等于[creature]力量值的伤害。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "No matter how many trees fall, the forest remains dense.", "无论有多少棵树倒下，森林仍然茂密。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] takes a free swing.", "[creature]自由摆动。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of its owner's turn, this card moves in the sigil's direction, looping around the owner's side of the board. Allied creatures towards this card in the sigil's direction as far as possible.", "在持牌人的回合结束时，[creature]会按印记标注的方向移动，在持牌人的场地上循环。友方造物会尽可能朝着这张牌运动的方向前进。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Let's go, together.", "我们一起走吧。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[creature] leads your creatures forward.", "[creature]带领你的造物前进。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A card bearing this sigil is immune to the effects of Touch of Death, Stinky, Punisher, Cursed, and Idol.", "[creature]不受死神之触、臭臭、惩罚者、诅咒和偶像印记的影响。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Adjacent creatures gain Power equal to half this card's base Power, rounded down. This effect changes next turn.", "相邻造物获得的力量等于带有该印记的卡牌的基础力量的一半，四舍五入。下一回合，此印记效果会发生变化。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Allied creatures on the board gain Power equal to half this card's base Power, rounded down. This effect changes next turn.", "场上的友方造物获得的力量等于带有该印记的卡牌基础力量的一半，四舍五入。下一回合，此印记效果会发生变化。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "All other creatures on the board gain Power equal to this card's Power.", "场上的所有其他造物获得与带有该印记的卡牌的力量值相等的力量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "There are no cards you can choose.", "没有你可以选择的卡片。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You can't target the air.", "你不能瞄准空气。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You must choose one of your other cards.", "你必须从其他卡片中选择一张。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It's already latched...", "它已经锁上了……", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You must choose one of your other cards.", "你必须从其他卡片中选择一张。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Some things in this world are too unique to replicate.", "这个世界上有些东西太独特了，无法复制。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The lie falls apart, revealing your pitiful true self.", "谎言破灭了，暴露了你可怜的真实自我。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your creature's consitution is too weak.", "你的造物构造太弱了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Cowards don't get the boon of the brave.", "懦夫得不到勇者的恩惠。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Love yourself.", "爱惜自己。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Your beast's moxie withers away.", "你的野兽的勇气枯萎了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The curse abates.", "诅咒减弱了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "With a single kiss, the Snow Queen froze their hearts.", "因一个吻，冰雪女王冻结了它们的心。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The snow melts away. Perhaps spring is coming.", "雪融化了。也许春天来了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Why do they always leave?", "为什么他们总是离开？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "She saw her dear brothers in the distance.", "她看见她亲爱的兄弟们在远处。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Her family that needed the nettle clothing to be free of the curse.", "她的家人需要荨麻衣才能摆脱诅咒。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "She fell to the ground, vomiting ooze like the rest of [c:bR]the City[c:].", "她倒在地上，呕吐得像城市里的其他人一样。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The lake ripples gently. As if a number of swans just took flight.", "湖面轻轻泛起涟漪。仿佛许多天鹅刚刚飞走。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The punishment for greed is getting everything you wanted.", "贪婪的惩罚就是得到你想要的一切。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "A new nest is born.", "一个新的巢穴诞生了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Status Effect Overflow", "状态溢出", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If a card has more than 5 status effects, display the overflow here.", "如果一张卡有5个以上的状态效果，将由此印记显示溢出。", Language.ChineseSimplified);

            // Card
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Abnormal Sigils is disabled in the configuration. This will likely break things.", "配置中禁用了异常印记。这可能会打破现状。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Abnormal Sigils loaded!", "异常印记已加载！", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Little Witch's Friend", "蕾蒂西亚的小伙伴", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Little Witch's Big Friend", "蕾蒂西亚的大伙伴", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Slime", "史莱姆", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nail", "钉子", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Jack", "杰克", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pumpkin", "南瓜头", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sapling", "树苗", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Worker Bee", "工蜂", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Block of Ice", "冰块", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Frozen Heart", "冰封之心", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Thorny Vines", "刺藤", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spider Brood", "蜘蛛巢", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spiderling", "幼蛛", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "First Brother", "大哥", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Second Brother", "二哥", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Third Brother", "三哥", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fourth Brother", "四哥", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Fifth Brother", "五哥", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sixth Brother", "六哥", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spore Mold Creature", "孢子衍生物", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nihil", "虚无", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented by this sigil will be equal to the number of empty spaces on the board.", "此印记表示的值等于全场的空位数量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sigil Power", "印记力量", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the power level of this card's strongest sigil.", "此印记表示的值将等于带有此印记的卡牌拥有的最强印记的力量等级。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Loving Slimes", "史莱姆之爱", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented by this sigil will be equal to the number of Slimes that the owner has on their side of the table.", "此印记表示的值将等于持牌人一侧的史莱姆数量。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Passing Time", "流逝的时间", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The value represented with this sigil will be equal to the number of turns that have passed since this card was placed on the board.", "此印记表示的值将等于自带有此印记的牌上场后经过的回合数。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Bind", "绑定", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This card attacks after ally cards with less Bind. At 4 Bind, attack after opposing cards as well. Remove all Bind from this card when it attacks or on next upkeep.", "[creature]在绑定值较低的友方卡牌之后进行攻击。当绑定值为4时，也会在对位卡牌攻击之后进行攻击。此卡牌在攻击时或下一回合开始时，会移除所有绑定值。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This card attacks before ally cards with less Haste. At 4 Haste, attack before opposing cards as well. Remove all Haste from this card when it attacks or on next upkeep.", "[creature]在攻击匆忙值较低的友方卡牌之前先行攻击。当其匆忙值为4时，也会优先于敌方卡牌进行攻击。此卡牌在攻击时或下一回合开始时，其匆忙值将被移除。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Pebble", "卵石", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "This card regains 1 Health at the end of the owner's turn. When this card is struck, reduce the received damage by 1.", "[creature]在持牌人的回合结束时恢复1点生命值。当此牌被攻击时，将受到的伤害减少1。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Flagellated", "鞭笞", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When this card is struck, take damage equal to this status effect.", "[creature]被攻击时，受到的伤害与此印记值相等。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Spores", "孢子", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the start of its owner's turn, this card takes damage equal to its Spores. Upon dying, create a Spore Mold Creature with stats equal to its Spores.", "在持牌人的回合开始时，[creature]受到的伤害等于它孢子值。死亡后，创建一个属性等于其孢子值的孢子衍生物。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Worms", "裸虫", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the start of the owner's turn, this card gains 1 Worms. At 5+ Worms, attack allied creatures instead with a chance to give 1 Worms to struck cards.", "在持牌人的回合开始时，[creature]将获得1裸虫值。在裸虫值大于5时，改为攻击友方造物，有概率给被击中的卡牌1点裸虫值。", Language.ChineseSimplified);

        }
    }
    public class TVFsBossesMod
    {
        public static void RegisterTranslations()
        {
            Debug.Log("Registering Translations for TVFsBosses Mod");
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Though there was no one in sight, some offerings were laid out before you.", "虽然这里空无一人，但有一些祭品摆在你面前。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "There was no [c:bR]Woodcarver[c:] nearby.", "附近不再有[c:bR]木雕师[c:]了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Still, her totem pieces remained.", "尽管如此，她的图腾部件仍然存在。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The old [c:bR]Woodcarver[c:] was nowhere to be found.", "那个年老的[c:bR]木雕师[c:]已经不见了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Her carvings remained by the log she once sat upon.", "她曾经坐过的那根木凳旁还遗留着她的祭品。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The [c:bR]Woodcarver[c:] emerged from the thicket.", "[c:bR]木雕师[c:]从灌木丛中走了出来。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Her face looked somehow more stern than usual.", "她的脸看起来比平时更严肃了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "An ancient woman approached.", "一位老妇人走近了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "She looked intensely displeased with you.", "她看起来对你非常不满。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Nearby branches rustled as The [c:bR]Woodcarver[c:] revealed herself.", "附近的树枝沙沙作响，[c:bR]木雕师[c:]露出面容。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "She had no offerings this time.", "这次她没有拿出祭品。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[c:bR]Leave.[c:]", "[c:bR]离开。[c:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Turn back.", "往回走。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You are not prepared.", "你还没准备好。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Woodcarver Boss", "木雕师头目", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Prospector is replaced as the boss of the Woodlands.", "矿工头目战被木雕师所替换了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You found yourself among a grove of thick and pungent mushrooms.", "你发现自己身处一片茂密且刺鼻的蘑菇林中。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The operating table lay abandoned.", "曾经的手术台被遗弃了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The [c:bR]Mycologists[c:] stepped out from behind an enormous mushroom.", "[c:bR]菌学家[c:]从一个巨大的蘑菇后面走了出来。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "One of them looked anxious, but the other bore a very serious expression.", "其中一个看起来很焦虑，但另一个表情非常严肃。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You squinted to see a figure through the thick cloud of spores.", "你眯起眼睛，透过厚厚的孢子云看到一个人影。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The [c:bR]Mycologists[c:]' shared body was posed in a threatening stance.", "[c:bR]菌学家[c:]共用的身体摆出了威胁的姿势。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The [c:bR]Mycologists[c:] appeared more crazed with each breath they took.", "[c:bR]菌学家[c:]每次呼吸都显得更加疯狂。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "It was clear they wanted to experiment on more than just your creatures.", "很明显，他们不仅仅想在你的造物身上进行实验。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[size:3]It will suffice.[size:]", "[size:3]这样就足够了。[size:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[size:3]For our experiments, yes.[size:]", "[size:3]为了我们的实验，就是如此。[size:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[size:3]Do not worry. I have a backup plan.[size:]", "[size:3]别担心，我们会有一个备用计划。[size:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "[size:3]Satisfactory results.[size:]", "[size:3]这样的结果令人满意。[size:]", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "B- But there is only one challenger?", "但…但是只有一个挑战者？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "We... We need your body?", "我……我们需要你的身体？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Y- You will... not submit your body for our experiments?", "你…你不会……把你的身体献给我们做实验吧？", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Mycologists Boss", "菌学家头目", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The Angler is replaced as the boss of the Wetlands.", "钓鱼人头目战被菌学家所替换了。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Experiment", "实验体", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "GOOD.", "非常好。", Language.ChineseSimplified);

        }
    }
    public class ZephtSunMod
    {
        public static void RegisterTranslations()
        {
            Debug.Log("Registering Translations for ZephtSun Mod");
            // Card
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "DebugSun", "调试阳光", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sun: ", "阳光：", Language.ChineseSimplified);
            for (int count = 0; count <= 100; count++)
            {
                SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Sun: " + count.ToString(), "阳光：" + count.ToString(), Language.ChineseSimplified);
            }

            // Challenge
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Night time", "夜幕降临", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You will no longer get passive sun falling from the sky.", "你将无法从天空中获取阳光。", Language.ChineseSimplified);

            // Ability
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SunBoost", "阳光激励", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] is placed on the board, the owner will gain sun.", "当[creature]被放置时，持牌人会得到一定数量的阳光。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Wise use of emergency supplies.", "明智地使用应急物资。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SunKill", "阳光杀戮", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "When [creature] defeats another card, the other card will drop sun.", "当[creature]击败另一张卡牌时，另一张卡牌将掉落阳光。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Recycle your enemies back into nutrients", "将你的敌人重新转化为营养。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SunMaker 1", "阳光生产", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will produce 1 sun, if it belongs to the player.", "在持牌人的回合结束时，若[creature]在玩家一侧，它将生产1个阳光。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "Every little bit counts", "每一点都很重要", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SunMaker 2", "双重生产", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will produce 2 sun, if it belongs to the player.", "在持牌人的回合结束时，若[creature]在玩家一侧，它将生产2个阳光。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "If only the moon was this useful", "要是月亮这么有用就好了", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SunMaker 3", "三重生产", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will produce 3 sun, if it belongs to the player.", "在持牌人的回合结束时，若[creature]在玩家一侧，它将生产3个阳光。", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "The power of the sun, in the palm of your hand", "太阳的力量，在你的掌心", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "SunMaker", "阳光生产", Language.ChineseSimplified);
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "At the end of the owner's turn, [creature] will produce X sun, if it belongs to the player. Some cards produce more sun than others.", "在持牌人的回合结束时，若[creature]在玩家一侧，它将生产X个阳光。有些牌比其他牌产生更多的阳光。", Language.ChineseSimplified);

            // ShowThenClear
            SimplifiedChineseLanguagePackPlugin.Translate(SimplifiedChineseLanguagePackPlugin.GUID, null, "You dont have enough sun for that one", "你没有足够的阳光。", Language.ChineseSimplified);

        }
    }
